INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86036458818F59468C43E364C2C837A5', X'CB1FA7DD4D15834794495BA5A1C534EF', X'CB1FA7DD4D15834794495BA5A1C534EF', 13, NULL, NULL, 2, NULL, 0, 'Sprinkle the top generously with bread crumbs and bake for 10 to 15 minutes, or until the top is crisp and golden-brown.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'113EFC904C488940B4FACB4FA2140212', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEBD9DCB855C2346BB83C1210C90501E', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F (180 C/Gas 4).');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95C671B8D835EF4588E0EE99DB4D258D', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 2, NULL, NULL, 2, NULL, 0, 'Grease a 2-quart baking dish or spray with cooking oil spray.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4CA55ED6D9D00418D68854E7DCF2661', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 3, NULL, NULL, 2, NULL, 0, 'Cook the macaroni following package directions; drain in a colander and rinse with hot water.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CF18CBD06C2C14BBDA47DBC3F27AFF3', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 4, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the cheddar and American cheeses. Set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0282BFBE5A19942A9BE71405854C14E', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 5, NULL, NULL, 2, NULL, 0, 'In a medium saucepan melt the 4 tablespoons of butter over medium heat until hot and foamy; stir in the flour until bubbly. Cook, stirring, for 2 minutes. Gradually add the milk and cream along with the salt, pepper, and ground mustard. Cook, stirring constantly, until thickened. Add 2 cups of the cheese blend and continue cooking until melted, stirring constantly.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A539E13BAC328458A2E4D598698B368', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 6, NULL, NULL, 2, NULL, 0, 'Add the drained macaroni to the sauce and stir to blend. Pour the macaroni and cheese into the prepared baking dish. Top with the remaining 1 cup of shredded cheese');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B679E0DB3644F4298F1484834FB86B0', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 7, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter and toss with the breadcrumbs. Sprinkle over the cheese layer.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FBCEC9236D75444BDE8A0FA441583FE', X'AD67FF2641E2D24CAB259D009E7D3633', X'AD67FF2641E2D24CAB259D009E7D3633', 8, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 35 minutes, until bubbly and nicely browned.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F043BC2E8088E478C18807D4557FCF2', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7DFA37849A6FB4E83C9F5A31CA7BF4C', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2B97CA0B651BE47940F33C53A6EE32B', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 2, NULL, NULL, 2, NULL, 0, 'Butter a 2 1/2 to 3-quart shallow baking dish or casserole.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BBED11EB3FC5C048AF9B49D60CE5BD38', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 3, NULL, NULL, 2, NULL, 0, 'Cook the penne pasta in boiling salted water as directed on package; drain well and set aside.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB2A4F126FD2F74E919DE40115702D0D', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a medium saucepan over medium heat. Add the onion to the pan and cook for about 4 minutes, or until it is translucent. Add the garlic to the onion and continue cooking for 1 minute.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7581F5D6D70FD0408F353524268C23A4', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 5, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, basil, and oregano to the onion and garlic. Cook until heated through, stirring frequently.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C85FADC9B88EBD42B253A44AED553AA3', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 6, NULL, NULL, 2, NULL, 0, 'Taste and add salt and pepper.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B351847C2856A948ACAF367318E9AADF', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 7, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the cooked, drained penne, the tomato sauce mixture, and 1 cup of the shredded mozzarella cheese.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AC0EA1310242048801C3C3A37BE92FA', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 8, NULL, NULL, 2, NULL, 0, 'Stir to blend the ingredients thoroughly.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46CCFF23335D4344831ECFE4C4D9B7BC', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 9, NULL, NULL, 2, NULL, 0, 'Spoon the pasta and sauce mixture into the prepared casserole.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB22BF0D564A234CA7DD83C224D9B28D', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle the remaining 1 cup of shredded mozzarella cheese over the top.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0C451ECB0F0DC45BFF906AC72B3A259', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 11, NULL, NULL, 2, NULL, 0, 'Bake the casserole for about 20 to 25 minutes, or until the pasta is hot and bubbly and the cheese has melted.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0460B9E81E295946A5242FCAB301955D', X'FFCF8D0EC10B8742A0D6539254FC9382', X'FFCF8D0EC10B8742A0D6539254FC9382', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0BFE8D72777ED4196E8A9E4F9292F4B', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3D1C7C0587FF64FB76FF0D6A0A4280A', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 1, NULL, NULL, 2, NULL, 0, 'Cook the pasta according to the package instructions. If you are using gluten-free pasta, be sure to rinse with cool water after cooking.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0948498C48D2F4DBF5536F9D8103F5E', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 2, NULL, NULL, 2, NULL, 0, 'After the pasta has cooked, return to the pot and stir in the 1 teaspoon olive oil and 1/2 teaspoon salt. Fold in gently to evenly coat the pasta.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7096B6366D82B45BE55E579E0B97650', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle with black pepper and then set aside while you prepare the sauce.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3757585982E574095004035591CA926', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 4, NULL, NULL, 2, NULL, 0, 'In a small saucepan (about 2 to 3 quarts) heat the minced garlic, grated onion, and 1 teaspoon cooking oil over medium-high heat until the garlic and onion are fragrant and slightly darker in color, about 4 minutes. Stir often so as not to burn the garlic and onion.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B7137BE3A25694CBB8527FECD9E601E', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 5, NULL, NULL, 2, NULL, 0, 'Add the canned pumpkin, nutritional yeast flakes, salt, and milk and warm gently over low heat about 5 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CB76C9372CAB84B912C6768A72405CD', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 6, NULL, NULL, 2, NULL, 0, 'Transfer the pumpkin sauce mixture to a high-speed blender and add the pecans. (You may need to allow the sauce to cool briefly before proceeding.)');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D19EF0E29AFBF04DAAC9BAD8D89C8E46', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 7, NULL, NULL, 2, NULL, 0, 'Blend the mixture until the sauce is completely smooth, stopping and scraping the sides of the blender as needed.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E4B903D03002C41991169ACEFB3C375', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 8, NULL, NULL, 2, NULL, 0, 'Add the sauce to the cooked pasta and stir gently to completely coat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D79503F4F676BB4CAE89CC8E902B0093', X'1FE9B7306121524D9171B7981C604C72', X'1FE9B7306121524D9171B7981C604C72', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A153D0B7D542F4A9893F656BD4B974C', X'971CF8E7A3B516438C9A66598C00277B', X'971CF8E7A3B516438C9A66598C00277B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78AA839D07108145B7C976F1590AD716', X'971CF8E7A3B516438C9A66598C00277B', X'971CF8E7A3B516438C9A66598C00277B', 1, NULL, NULL, 2, NULL, 0, 'Gently warm the olive oil in a large saucepan to medium-hot. Add the onion, carrot, and celery, stir into the oil, and cook on medium heat for 10 minutes until the vegetables are softened. Add the garlic, stir and cook for a further 3 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5A2078D564DEC4BBFBA1D00B021F057', X'971CF8E7A3B516438C9A66598C00277B', X'971CF8E7A3B516438C9A66598C00277B', 2, NULL, NULL, 2, NULL, 0, 'Add the cans of chopped of tomatoes and torn basil leaves, cooking over a gentle heat for 20 to 30 minutes, or until the sauce is thickened and all the vegetables have softened. You can make the sauce to this point ahead of time if you wish, then cool it and store until required.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E636C8C5DBAEE948BD19B08A77A70FB0', X'971CF8E7A3B516438C9A66598C00277B', X'971CF8E7A3B516438C9A66598C00277B', 3, NULL, NULL, 2, NULL, 0, 'Warm the tomato sauce through until it is just gently bubbling, stir in the heavy cream, keep the sauce on a very low heat while you cook the pasta.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'580B077AC7FCD946ACBA9E66F6530798', X'971CF8E7A3B516438C9A66598C00277B', X'971CF8E7A3B516438C9A66598C00277B', 4, NULL, NULL, 2, NULL, 0, 'Cook the penne according to the packet instructions, but stop two minutes befroe the recommended cooking time. Use a large volume of rapidly boiling water with the sea salt added.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7CDEAAABA93EB43A0D9F2DA571D4390', X'971CF8E7A3B516438C9A66598C00277B', X'971CF8E7A3B516438C9A66598C00277B', 5, NULL, NULL, 2, NULL, 0, 'Once cooked, drain the penne, then return it to the pan, add the vodka and butter and stir. Add the cooked, warm tomato sauce and toss together with the pasta.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'269E0834934D2C49BCB2BD736647F736', X'971CF8E7A3B516438C9A66598C00277B', X'971CF8E7A3B516438C9A66598C00277B', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately into hot bowls with a good sprinkling of freshly grated Parmesan cheese, a little black pepper, and chili flakes if you like a little heat.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6A32ECD17F87F4A8C9DF2DA1E3AC742', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'795DDFCFD6A00945AD1F767CA4411C21', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 1, NULL, NULL, 2, NULL, 0, 'Set a large pot of water to boil over high heat for the pasta.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DC28CA4DE1C3C4B9D1E195C411D7A8C', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan over medium heat, sauté the onion (if you are using it) and the garlic in half (2 tablespoons) of the oil, until fragrant and just barely golden - about 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EBB919BF5C6B64695B4F738ACE8FB8B', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 3, NULL, NULL, 2, NULL, 0, 'Add the crushed red peppers or the fresh chile pepper and cook for another 30 seconds, until fragrant.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CFB935CB7F1924DB37A50AD4B454521', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 4, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and lower the heat to low. Simmer, covered, while you cook the pasta.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAAD27E0F70B2F499A1D1B28EDA25083', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 5, NULL, NULL, 2, NULL, 0, 'In the meantime, cook the pasta in the salted boiling water to al dente.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB09E6A1128F5B4EBB266E01B44CCD0A', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 6, NULL, NULL, 2, NULL, 0, 'Drain it and return it to the pot.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5B359FCABD2D741BEB70E5DC73A5038', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 7, NULL, NULL, 2, NULL, 0, 'Check the seasoning of the sauce and stir in the basil and the remainder of the oil.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A6B145419E8054BB3353ECFA647F123', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 8, NULL, NULL, 2, NULL, 0, 'Pour the sauce over the pasta and toss to cover evenly.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A741885209A96C4D9E483AB5D4A069A2', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 9, NULL, NULL, 2, NULL, 0, 'Serve the pasta with the grated cheese on the side.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41451B1C8C73CE49B615C80BF3E43C65', X'92C1DD97C9725143B1501F65FC5437F5', X'92C1DD97C9725143B1501F65FC5437F5', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE20429159F867428B8188C027C684F6', X'75D7D70C1AA6C74DBCADE404167BB0A3', X'75D7D70C1AA6C74DBCADE404167BB0A3', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A0AD3968A99A349867FBD29BE50B07F', X'75D7D70C1AA6C74DBCADE404167BB0A3', X'75D7D70C1AA6C74DBCADE404167BB0A3', 1, NULL, NULL, 2, NULL, 0, 'Cook the tortellini according to the package directions and drain.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F7FE529F2520B499AAA276AEE9A11AD', X'75D7D70C1AA6C74DBCADE404167BB0A3', X'75D7D70C1AA6C74DBCADE404167BB0A3', 2, NULL, NULL, 2, NULL, 0, 'Combine the pasta with the broccoli, sauce, and chicken in a large bowl.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D6A0B8306DA8846AAD617B8BAA18850', X'75D7D70C1AA6C74DBCADE404167BB0A3', X'75D7D70C1AA6C74DBCADE404167BB0A3', 3, NULL, NULL, 2, NULL, 0, 'Transfer to a 1-1/2 quart casserole dish and top with the bread crumbs.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59E7906E6B9F4340B6E150A1E3349EFC', X'75D7D70C1AA6C74DBCADE404167BB0A3', X'75D7D70C1AA6C74DBCADE404167BB0A3', 4, NULL, NULL, 2, NULL, 0, 'Bake at 350 F for 25 to 35 minutes or until the casserole is bubbling and the bread crumbs are browned.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C72BAFA7E2BB54297C77E03BF57712A', X'A4956C564B2B8544AA8D4BC8DA8011DB', X'A4956C564B2B8544AA8D4BC8DA8011DB', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE2E1BF3E41B2146B3BDF53EC6CB05AB', X'A4956C564B2B8544AA8D4BC8DA8011DB', X'A4956C564B2B8544AA8D4BC8DA8011DB', 1, NULL, NULL, 2, NULL, 0, 'In a three-quart glass baking dish, layer the frozen tortellini and the spaghetti sauce. Put the water into the empty sauce jar, close the lid tightly, and shake well. Pour this mixture over the tortellini. That''s how you get all of the spaghetti sauce out of the jar, and the water helps the tortellini cook and stay tender.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'072E931ADDE69D4F8A4CCB99F607D85C', X'A4956C564B2B8544AA8D4BC8DA8011DB', X'A4956C564B2B8544AA8D4BC8DA8011DB', 2, NULL, NULL, 2, NULL, 0, 'Spoon the Alfredo sauce over the tortellini, making sure that all of the little pasta is completely covered. Place the milk into the empty Alfredo sauce jar, close the lid tightly, and shake. Then drizzle this mixture over the casserole to get all of the sauce out of the jar.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E67BA70E291AD46A20729A911A26563', X'A4956C564B2B8544AA8D4BC8DA8011DB', X'A4956C564B2B8544AA8D4BC8DA8011DB', 3, NULL, NULL, 2, NULL, 0, 'At this point, you can top the casserole with frozen meatballs, if using. Sprinkle with the cheese.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'773972EE6EA2904FA234CF7C449F18FE', X'A4956C564B2B8544AA8D4BC8DA8011DB', X'A4956C564B2B8544AA8D4BC8DA8011DB', 4, NULL, NULL, 2, NULL, 0, 'Cover the casserole tightly with foil and bake at 350 F for 55 minutes. Then uncover the casserole and bake for 10 to 15 minutes longer, until the tortellini is tender, the sauce is bubbling, the cheese is melted, and the meatballs are hot.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84B89FFABD6D2B4098C8DE943BFE5FB4', X'A90831D0121C1A47AE28FA1AF0F8ED19', X'A90831D0121C1A47AE28FA1AF0F8ED19', 0, NULL, NULL, 2, NULL, 0, 'Cook the sausage in a heavy skillet over medium heat until browned, about 10 minutes. Stir the sausage frequently to break up as it cooks.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B31AAA015362D488702FFDDA80F5262', X'A90831D0121C1A47AE28FA1AF0F8ED19', X'A90831D0121C1A47AE28FA1AF0F8ED19', 1, NULL, NULL, 2, NULL, 0, 'Drain well and place in 4 to 5-quart slow cooker. Add the pasta sauce, tomatoes, and Italian seasoning and stir well.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B39843A0CD27D4A90E515C546AC2025', X'A90831D0121C1A47AE28FA1AF0F8ED19', X'A90831D0121C1A47AE28FA1AF0F8ED19', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 7 to 8 hours. Then stir in the tortellini, cover again, and cook on low for 20 to 40 minutes or until the pasta is tender and heated. Sprinkle with cheese and let stand 5 minutes before serving.');
INSERT INTO `VWR INTERNAL RecipesbyCourse3 recipes` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'ADE798348EE1F54C8BCD028E170F0293', X'D19D7E2CB396794DA8F854A5FB84C890', X'D19D7E2CB396794DA8F854A5FB84C890', 0, NULL, NULL, 2, NULL, 0);
