INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DF2634FA4E613499C38388180C73267', X'DB94145F78A8D347A3D7C860AA79EA0B', X'DB94145F78A8D347A3D7C860AA79EA0B', 1, NULL, NULL, 2, NULL, 0, 'Secure your watermelon on the table using towels or cloth napkins to keep it from rolling. Draw a circle on the top side of the watermelon with a marker. Make sure it is slightly larger than the widest part of the vodka bottle''s neck.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B4FC848F98B9F46903C6F2868C3952E', X'DB94145F78A8D347A3D7C860AA79EA0B', X'DB94145F78A8D347A3D7C860AA79EA0B', 2, NULL, NULL, 2, NULL, 0, 'Cut around the circle with a large knife, digging deep into the melon''s flesh. Remove this chunk of rind and flesh.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'347278284B04894D9C02B97DD65281CE', X'DB94145F78A8D347A3D7C860AA79EA0B', X'DB94145F78A8D347A3D7C860AA79EA0B', 3, NULL, NULL, 2, NULL, 0, 'Use a spoon to scoop out enough melon so the bottle''s neck can fit inside the hole. You want to go deep, but will also be able to gently push the bottle in further.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E0F8C33DADA324C9B316109CA59199D', X'DB94145F78A8D347A3D7C860AA79EA0B', X'DB94145F78A8D347A3D7C860AA79EA0B', 4, NULL, NULL, 2, NULL, 0, 'Open the bottle of vodka and quickly place it upside down in the hole of the watermelon. It should fit securely and be able to stabilize the bottle.');
