INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0E02A98115C854B92344C5D6AA747E7', X'E97E86DF2699E44C8C54932772E4D906', X'E97E86DF2699E44C8C54932772E4D906', 6, NULL, NULL, 2, NULL, 0, 'Top with whipped cream and a bit of cocoa powder to serve in the traditional manner or just serve with whipped cream or plain. Enjoy!');
