INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAFFD40EBDE6C543B3E576B7368FC630', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B99537AE1E8E924AB40F8520ABABE211', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 1, NULL, NULL, 2, NULL, 0, 'Sterilize two 1-quart jars and lids by boiling in water for 2 minutes. Let air dry on a clean, lint-free dish towel and set aside until needed.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'021A6E1D08962C4CAECBB8574D3D8571', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 2, NULL, NULL, 2, NULL, 0, 'Use a small, sharp knife to cut a small X shape at the base of each peach. Place peaches in a large, deep heatproof bowl and cover with boiling water. Allow peaches to sit for 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E1395053A5D6C44A0687E6085874181', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 3, NULL, NULL, 2, NULL, 0, 'Using tongs, transfer one peach to a plate. Try to peel the skin off. If it comes off easily, then drain and peel the remaining peaches. If not, let them sit in the boiling water for a further minute or so.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17A19287593FC446B21532403E542C8C', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 4, NULL, NULL, 2, NULL, 0, 'Peel the peaches and cut in half and remove the pits. Cut each half into 4 pieces, so each peach yields 8 slices.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'441C9642A9742F44BFA78355B0D0EC6D', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 5, NULL, NULL, 2, NULL, 0, 'Arrange peaches in layers in the jars alternating layers of caster sugar. Place half of a split vanilla bean down the side of each jar.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12FE015CA8AA1346B6315313CD2E8C2C', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 6, NULL, NULL, 2, NULL, 0, 'Pour over the brandy making sure the peaches are completely covered. Seal tightly then shake gently to help dissolve the sugar.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA0BF8EFB5818D45941E3A86889BFF94', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 7, NULL, NULL, 2, NULL, 0, 'Place jars in a cool, dark place for 3 months. Make sure you shake the jar gently once a week.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FD0F3394D901C4B927F98CED9371F21', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 8, NULL, NULL, 2, NULL, 0, 'After 3 months serve brandied peaches with pancakes, ice cream or enjoy with a dollop of clotted cream for dessert.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6909891234790A408BE05120BCE07F16', X'6EDF4B89FA6D5D42AF197933E729A0AF', X'6EDF4B89FA6D5D42AF197933E729A0AF', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
