INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'489CBD906549804A8E6ACFD62830C1E8', X'118EA7D1823D264A89F4A400B8C20E07', X'118EA7D1823D264A89F4A400B8C20E07', 2, NULL, NULL, 2, NULL, 0, 'Dry shake (without ice) vigorously.');
