INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EACB97BD275B344943E2E89682223B3', X'0888660E09DDEB43A824930448565C0C', X'0888660E09DDEB43A824930448565C0C', 4, NULL, NULL, 2, NULL, 0, 'Garnish with a lime wedge.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7294E3D86FBD334E8149C626A50ED312', X'0888660E09DDEB43A824930448565C0C', X'0888660E09DDEB43A824930448565C0C', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D81DC061FD452D4FB64C39775252B270', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'690EC9E32FF1FE45A46C0AFDE7C9D5FF', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 1, NULL, NULL, 2, NULL, 0, 'In separate cups, measure the rum, coffee liqueur, and brewed coffee and have at hand the spices.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20FBF08E10026444A79180C6B4B5CDCD', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 2, NULL, NULL, 2, NULL, 0, 'Using the sugar and lemon juice, rim a heat-resistant wine glass. Place the lemon juice in a shallow bowl and the sugar in another. Dip the rim of the glass into the lemon juice and then, while it''s still wet, dip it into the sugar.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'827D365647815A49BC26906779D65F9D', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 3, NULL, NULL, 2, NULL, 0, 'Pour the rum into the wine glass.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70CA329A5126A744B69FE746D3AB12F0', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 4, NULL, NULL, 2, NULL, 0, 'With extreme care, tilt the glass at a ?45-degree angle. With a long match or lighter, light the rum on fire, keeping it away from your face and hair. Don''t be startled if it lights on fire before the fire touches the rumthis is normal. Return the glass to its vertical position and place it on a flat surface. If you used a match to light the fire, blow the match out. With your hand flat and facing down toward the table, hold the stem of the glass firmly between your index and middle fingers. Moving your hand in a circular motion, swirl the flaming rum in the glass to caramelize the sugar. Once the sugar is mostly caramelized, sprinkle the ground nutmeg and cinnamon over the drink. The spices will flavor the drink and add exciting visual sparks to the preparation of your cocktail.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'547A50C07484754D8EC35CB6718D07FD', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 5, NULL, NULL, 2, NULL, 0, 'Quickly and carefully pour the desired liqueur on top of the cocktail. Do not worry if the liquid is boiling at first. It will cool down as you add more ingredients. Add the coffee. Add this point the glass should be 2/3 full.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ADF8CAEC332824E8FF4DB8E727AB2B9', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 6, NULL, NULL, 2, NULL, 0, 'After the fire is out, your cocktail is complete. However, you can choose one or more of the optional garnishes to finish it off.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9971B3718E7494C8EB9EEAAA7B2CB99', X'04423B347D474D4589080DB710FC224A', X'04423B347D474D4589080DB710FC224A', 7, NULL, NULL, 2, NULL, 0, 'It might still be hot, so enjoy with care!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A003BB2195A0DD4E99C198D212996EFF', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA8350C5E0B36643AC6C2F9B9D2ED98C', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 1, NULL, NULL, 2, NULL, 0, 'In a 24-ounce (or larger) canning jar, add the pear slices and spices.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0353CD2A83C95B4AB66D3597CD77C57B', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 2, NULL, NULL, 2, NULL, 0, 'Pour in the pear brandy and simple syrup.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C5838934DCF5845BC43BF2856B56E33', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 3, NULL, NULL, 2, NULL, 0, 'Secure the lid and shake the jar so everything is combined.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAD4A387288750459FF0ED033563E6FE', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 4, NULL, NULL, 2, NULL, 0, 'Set in a cool, dark place and allow it to infuse for 2 to 3 days. Shake it once a day and give it a taste test on the second day to see how the flavor''s developing. Do this daily until it reaches your desired taste.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDCCB780D68D8442B2422BD75C77F992', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 5, NULL, NULL, 2, NULL, 0, 'Once you like the flavor, strain out the spices and fruit using a fine mesh strainer or cheesecloth, squeezing or pressing out any excess liqueur.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA144A4F0E4ADE4BA1172554DE4D1774', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 6, NULL, NULL, 2, NULL, 0, 'Bottle the spiced pear liqueur under a tight seal and add a label with the date and flavor.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C91916270A52A4FB3E5A3FB030FEBBF', X'0D3A125571458B499C257944F7FB6732', X'0D3A125571458B499C257944F7FB6732', 7, NULL, NULL, 2, NULL, 0, 'Serve in your favorite cocktails and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F89BA2526045244B9CDB9400529A7C6', X'6FCB6BA54E198C4ABAF48C4646D8FBB1', X'6FCB6BA54E198C4ABAF48C4646D8FBB1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
