INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8821914BB5BEF84B8975DF2FB9D7E861', X'6FCB6BA54E198C4ABAF48C4646D8FBB1', X'6FCB6BA54E198C4ABAF48C4646D8FBB1', 2, NULL, NULL, 2, NULL, 0, 'Shake very well for at least 15 seconds to ensure everything''s mixed and the cream becomes frothy.');
