INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93C2B3DB0632FE49A1A7427DFFA012CA', X'6BCADA7CBE194D479333D3C9663A8B33', X'6BCADA7CBE194D479333D3C9663A8B33', 4, NULL, NULL, 2, NULL, 0, 'Place the lemon halves face up in a muffin tin. Pour the mixture into lemon halves.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'981FCC289AA13848AA8FCB1476829144', X'6BCADA7CBE194D479333D3C9663A8B33', X'6BCADA7CBE194D479333D3C9663A8B33', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate until set, 4 to 24 hours.');
