INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A33E6643B5CA7C4480302DD912DD546B', X'854F6DCBDE4B294DB1C0B0E65B8F74EA', X'854F6DCBDE4B294DB1C0B0E65B8F74EA', 3, NULL, NULL, 2, NULL, 0, 'Add a splash of cranberry to the first shot glass, a splash of orange juice to the next, and a splash of melon liqueur to the third.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FA7A9BCBE98A7419ED7E10DCDD858D2', X'854F6DCBDE4B294DB1C0B0E65B8F74EA', X'854F6DCBDE4B294DB1C0B0E65B8F74EA', 4, NULL, NULL, 2, NULL, 0, 'Drink right down the row.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B38D1E13801D748B129FBAAD8487984', X'854F6DCBDE4B294DB1C0B0E65B8F74EA', X'854F6DCBDE4B294DB1C0B0E65B8F74EA', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96D7ED8F179A8E4881C25E66DFBD00CD', X'0B1D41BED9358C479D5CB4043F8AB29D', X'0B1D41BED9358C479D5CB4043F8AB29D', 0, NULL, NULL, 2, NULL, 0, 'Spray a silicone mini muffin or candy-making pan (cavity volume = 2 to 4 tablespoons) lightly with cooking spray. Wipe the excess spray from the pan so there is no visible residue.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB8621CE2AB3A84A9746E9F6D076BDB0', X'0B1D41BED9358C479D5CB4043F8AB29D', X'0B1D41BED9358C479D5CB4043F8AB29D', 1, NULL, NULL, 2, NULL, 0, 'Pour lemonade into a small pot or deep pan and sprinkle with both packets of gelatin. Let sit for about 2 minutes until gelatin blooms. Youll see a ripple film form on the top.?');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BF0D47427D05C4DA45CB5E5AED05E85', X'0B1D41BED9358C479D5CB4043F8AB29D', X'0B1D41BED9358C479D5CB4043F8AB29D', 2, NULL, NULL, 2, NULL, 0, 'Put the mixture on a low heat. Heat and continually whisk until the gelatin evenly dissolves about 2 to 5 minutes. Be careful not to let the mixture come to a boil.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11CBD2ABE5B6F94597CC7197FB0DDE5F', X'0B1D41BED9358C479D5CB4043F8AB29D', X'0B1D41BED9358C479D5CB4043F8AB29D', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat and let the mixture cool a bit. Whisk in the vodka and sweetened condensed milk.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBA2B6304C48354386610673A8B159C4', X'0B1D41BED9358C479D5CB4043F8AB29D', X'0B1D41BED9358C479D5CB4043F8AB29D', 4, NULL, NULL, 2, NULL, 0, 'Divide the mixture evenly into 6 separate bowls. Put a few drops of different colored food coloring in each and whisk to thoroughly mix.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13738A9B410654D969D5994597E3A74', X'0B1D41BED9358C479D5CB4043F8AB29D', X'0B1D41BED9358C479D5CB4043F8AB29D', 5, NULL, NULL, 2, NULL, 0, 'Spoon about 1 teaspoon of the first color mixture into the pan cavities. Refrigerate until set, usually about 20 minutes. But not too long because if they are sticky all the layers will set together better.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4650882423B8734B9A7279AC4F03CAD4', X'0B1D41BED9358C479D5CB4043F8AB29D', X'0B1D41BED9358C479D5CB4043F8AB29D', 6, NULL, NULL, 2, NULL, 0, 'Repeat with all colors, letting each layer set for about 15 minutes. Refrigerate overnight.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F0CCDFE81647140AB95FFFD6093B009', X'BA523986CE67974CA6D6B98861E5B124', X'BA523986CE67974CA6D6B98861E5B124', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F8F01A90CE9464E9E3C5221735F06C4', X'BA523986CE67974CA6D6B98861E5B124', X'BA523986CE67974CA6D6B98861E5B124', 1, NULL, NULL, 2, NULL, 0, 'Mix the whiskey, lemon juice, and simple syrup with cold water and chill in the refrigerator until the mix becomes a consistent temperature.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFD91D7675EB984D972AF88EC58C83D6', X'BA523986CE67974CA6D6B98861E5B124', X'BA523986CE67974CA6D6B98861E5B124', 2, NULL, NULL, 2, NULL, 0, 'Add the unflavored gelatin and let it sit for about 3 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58B8C3564FF16F46B190A610DC592064', X'BA523986CE67974CA6D6B98861E5B124', X'BA523986CE67974CA6D6B98861E5B124', 3, NULL, NULL, 2, NULL, 0, 'Add boiling water, stirring until the gelatin is completely dissolved. Stir in a few drops of food coloring if you want to customize the color.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'884D5430C3225F41AF48A2DFB076EAA3', X'BA523986CE67974CA6D6B98861E5B124', X'BA523986CE67974CA6D6B98861E5B124', 4, NULL, NULL, 2, NULL, 0, 'Pour into shot glasses, molds, or a baking pan for a sheet of gelatin to cut up after it sets.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A3281E38DA15F4F9EE1A6DABB25D24E', X'BA523986CE67974CA6D6B98861E5B124', X'BA523986CE67974CA6D6B98861E5B124', 5, NULL, NULL, 2, NULL, 0, 'Refrigerate until the liquid sets up; at least 2 hours, but overnight is recommended.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAF221551B160749BDA5FC1C85D56BB9', X'BA523986CE67974CA6D6B98861E5B124', X'BA523986CE67974CA6D6B98861E5B124', 6, NULL, NULL, 2, NULL, 0, 'Serve cold and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7B17897BE3251468E2B8C92B15F5528', X'53E32ED7C88185458DC6B220255114A0', X'53E32ED7C88185458DC6B220255114A0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A758066877C814AA9687C827AF1C33B', X'53E32ED7C88185458DC6B220255114A0', X'53E32ED7C88185458DC6B220255114A0', 1, NULL, NULL, 2, NULL, 0, 'Fill each shot syringe with 1 ounce of vodka.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0184E3DA1C54F4D8E29F07C9E816233', X'53E32ED7C88185458DC6B220255114A0', X'53E32ED7C88185458DC6B220255114A0', 2, NULL, NULL, 2, NULL, 0, 'Fill a pint glass (or other tall glass) half-way with chilled soda.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DAE88A900983F4F97C61BFC3C3486DA', X'53E32ED7C88185458DC6B220255114A0', X'53E32ED7C88185458DC6B220255114A0', 3, NULL, NULL, 2, NULL, 0, 'Place the vodka-filled syringe into the soda.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1074745816DE1A4186325B90E08E7FC3', X'53E32ED7C88185458DC6B220255114A0', X'53E32ED7C88185458DC6B220255114A0', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D204F11DDF809C4B8FB714C639425CA6', X'ED003A633F748F4D8F27031CDACE7979', X'ED003A633F748F4D8F27031CDACE7979', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2D349C22C62FF4EB02FBF04FD7EE232', X'ED003A633F748F4D8F27031CDACE7979', X'ED003A633F748F4D8F27031CDACE7979', 1, NULL, NULL, 2, NULL, 0, 'In a shot glass, pour the grenadine until it''s one-third full.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'786089645714D04EB2A48AF83CE85505', X'ED003A633F748F4D8F27031CDACE7979', X'ED003A633F748F4D8F27031CDACE7979', 2, NULL, NULL, 2, NULL, 0, 'Float the same amount of green crme de menthe on top by pouring it slowly over the back of a bar spoon.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30355B93D68AEF4C99692C2FAB07073F', X'ED003A633F748F4D8F27031CDACE7979', X'ED003A633F748F4D8F27031CDACE7979', 3, NULL, NULL, 2, NULL, 0, 'Float peppermint schnapps on top to fill the glass, again using the bar spoon.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'548B4FA2BB2D594093A45F7ABE51F8E1', X'ED003A633F748F4D8F27031CDACE7979', X'ED003A633F748F4D8F27031CDACE7979', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A7396ED8EA49747AEE4DAAC147181B8', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 0, NULL, NULL, 2, NULL, 0, 'Cut apples in half, down the stem. Using a paring knife and melon baller hollow out the center being careful not to puncture the skin.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C51DB2802C26A4FAE9C749495A08FC6', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 1, NULL, NULL, 2, NULL, 0, 'Brush the inside and edges of the cup apples thoroughly with lemon juice to prevent browning. Placing them face down will also help.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44DF1514A025A49A088E4F6A1E9C9AF', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 2, NULL, NULL, 2, NULL, 0, 'Pour the cider into a small pot or deep pan. Sprinkle both packets of gelatin on top and let it sit for about 2 minutes to bloom. Youll see a ripply film form on the top.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AC88ABBB1250548ABB14CFF56F45FB0', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 3, NULL, NULL, 2, NULL, 0, 'Place the mixture on low heat. Heat and continually whisk until the gelatin evenly dissolves, about 2-5 minutes. Be careful not to let the mixture come to a boil. Then whisk in the melted caramel. Once everything is evenly combined, remove from the heat and let cool a bit.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBE898BF9DA95140924819FCF001A9EF', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 4, NULL, NULL, 2, NULL, 0, 'Add vodka and stir to thoroughly combine.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3847EA63D1881B499645C6B56E333B00', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 5, NULL, NULL, 2, NULL, 0, 'Place the apple halves in a muffin tin, facing up. Pour the mixture into each hollowed-out apple.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BE89EE02DD6654F91AE876DAD6006FC', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 6, NULL, NULL, 2, NULL, 0, 'Refrigerate until firm, 4 to 24 hours.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CAFE4FE5AEA4A4B976369D40ACB338B', X'9C03FA0E9ADBFB418FE0984F5B34B643', X'9C03FA0E9ADBFB418FE0984F5B34B643', 7, NULL, NULL, 2, NULL, 0, 'Cut the apple halves carefully into wedges and serve.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9B4D43D2E3C0444997220F458B8F497', X'F527CE16967CDB45B6E5CE27DA4809A8', X'F527CE16967CDB45B6E5CE27DA4809A8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
