INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10EDF55EFE28224881F1425391049F34', X'5EE24351F33C1E4F8EABA831EE92B92F', X'5EE24351F33C1E4F8EABA831EE92B92F', 5, NULL, NULL, 2, NULL, 0, 'Add the 2 cans of soda and stir (if you are making this ahead of time, dont add the soda until your guests are about to arrive). Taste for sweetness. If desired, add more sugar or soda. Make sure that the sugar is completely dissolved.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1AECC70E43EE442A6118A78E18D80A2', X'5EE24351F33C1E4F8EABA831EE92B92F', X'5EE24351F33C1E4F8EABA831EE92B92F', 6, NULL, NULL, 2, NULL, 0, 'Add lots of ice to the punch bowl to chill completely. If you are serving in pitchers, fill with ice and then pour the sangria over the ice.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB829D611D00914A859520E0C33AE56B', X'8185E81CF7EDA944B48A332AC1B7CE14', X'8185E81CF7EDA944B48A332AC1B7CE14', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
