INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D93AA4C97AC8642A394E8AE7B7B95C7', X'8185E81CF7EDA944B48A332AC1B7CE14', X'8185E81CF7EDA944B48A332AC1B7CE14', 2, NULL, NULL, 2, NULL, 0, 'Serve the punch in ice-filled glasses, garnishing each with a lime twist.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'215BA40F2ABB804CAD4BAF3F25C7CB90', X'8185E81CF7EDA944B48A332AC1B7CE14', X'8185E81CF7EDA944B48A332AC1B7CE14', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1841BC901654945BF17B12DA6A05A44', X'0379BDA3816CE94283D3AD2880BCF3D4', X'0379BDA3816CE94283D3AD2880BCF3D4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'132A274A516EEF449564EFC15A955BCC', X'0379BDA3816CE94283D3AD2880BCF3D4', X'0379BDA3816CE94283D3AD2880BCF3D4', 1, NULL, NULL, 2, NULL, 0, 'In a large pitcher or punch bowl with ice, pour the vodka, tequila, rum, gin, orange juice, and sports drink. Stir well.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'174F9620C7B2F4429AD8019F915FB892', X'0379BDA3816CE94283D3AD2880BCF3D4', X'0379BDA3816CE94283D3AD2880BCF3D4', 2, NULL, NULL, 2, NULL, 0, 'Add a splash of cola, stir the punch, and add a little more if needed to get a `tea` color.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7CC9924DBFEB942B7D19F0747BA04D5', X'0379BDA3816CE94283D3AD2880BCF3D4', X'0379BDA3816CE94283D3AD2880BCF3D4', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D42AAAADF1DEDD419753CF1F0B52AB3F', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B2215DC0C73AD45AEA713400461A547', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', 1, NULL, NULL, 2, NULL, 0, 'Place 2 quarts sliced strawberries in a medium bowl. Sprinkle with 1/2 cup sugar or to taste. Wash the 2 lemons and zest them. Then squeeze out the juice. Add both the juice and zest to the strawberries and let sit for 2 hours in the refrigerator.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6E66C5994B3094682B61B1D0A973DB2', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', 2, NULL, NULL, 2, NULL, 0, 'Drain and reserve the strawberry juice. Add 1 bottle dry white wine to the strawberries and marinate in the refrigerator for 1 hour, or until the guests start arriving. Transfer the marinated strawberries to a punch bowl, and add the reserved strawberry juice and stir. Pour the 1 bottle of sparkling white wine into the bowl and serve.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A5CA1CE7596D94893522A9D553A1660', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', X'2D9D0C5C9F40A1439AE40DB50D2FAACE', 3, NULL, NULL, 2, NULL, 0, 'To serve, ladle punch and strawberries into the glass, garnish with a sprig of fresh mint or fresh lemon balm, if desired, and serve with a cocktail pick, so that the guests can eat the strawberries.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F145D6C194A7241BC2CF335A8365567', X'0BE4B4339217C842A47E1E5603254C7F', X'0BE4B4339217C842A47E1E5603254C7F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4C2A29960BFBC499509284ADA5D560B', X'0BE4B4339217C842A47E1E5603254C7F', X'0BE4B4339217C842A47E1E5603254C7F', 1, NULL, NULL, 2, NULL, 0, 'In a medium-sized pot on the stove, combine all of the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E7E84314320724D85B3777D9D7AB6CB', X'0BE4B4339217C842A47E1E5603254C7F', X'0BE4B4339217C842A47E1E5603254C7F', 2, NULL, NULL, 2, NULL, 0, 'Over medium heat, warm the mixture, stirring regularly. It should take just 10 minutes or so, but you can turn down the heat to let it brew longer (the flavor gets better with time).');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C6DB9E5AC9E134B92A9716BF9783998', X'0BE4B4339217C842A47E1E5603254C7F', X'0BE4B4339217C842A47E1E5603254C7F', 3, NULL, NULL, 2, NULL, 0, 'Transfer to a large heatproof punch bowl. Serve in coffee mugs or Irish coffee glasses, garnishing each drink with shaved nutmeg.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'873E282958DF0347801EEB38CD62A964', X'0BE4B4339217C842A47E1E5603254C7F', X'0BE4B4339217C842A47E1E5603254C7F', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'550E86F7337C7E43BA9FF9539ADEBDA8', X'B79EDCD6BED9EA498D52CE0AD18D74FE', X'B79EDCD6BED9EA498D52CE0AD18D74FE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C64BAFE94ADA04186015C159E00B8A2', X'B79EDCD6BED9EA498D52CE0AD18D74FE', X'B79EDCD6BED9EA498D52CE0AD18D74FE', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine the water and spices. Simmer over low heat for 10 minutes, without allowing it to reach a boil. Stir occasionally.');
