INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeIngredients` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, recipeIngredient) VALUES (X'10E8B30652ED214990630D09605DED75', X'734BE372063C494DB744D2F5A28CF53C', X'734BE372063C494DB744D2F5A28CF53C', 5, NULL, NULL, 2, NULL, 0, 'Optional: green food coloring');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45A9D2610D56E6498856854A5F03BBA8', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and tools.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FFD3CB29BBD344AB3CA7FFD892A149A', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 1, NULL, NULL, 2, NULL, 0, 'Bring about 1 cup of water to a near boil; measure out 1/4 to 1/2 cup and reserve for making the tea. Fill the matcha bowl about halfway with the remaining hot water. Gently move the bamboo whisk around in the water to soak while the water warms the bowl (about 30 seconds). Discard the water, in the bowl then wipe the bowl dry with a clean towel.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8BA8858A84F7F4181057139DDB75FD5', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 2, NULL, NULL, 2, NULL, 0, 'Place a fine-mesh strainer over the bowl. Using the bamboo scoop, sift 2 to 4 heaping piles of matcha into the bowl. Sifting produces a fluffier powder that helps ensure your tea is free of clumps and reduces the amount of whisking required. Any fine-mesh strainer (including a metal tea ball) will work.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F85E1A21333B647B6CA3DFCD362C320', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 3, NULL, NULL, 2, NULL, 0, 'Ideally, the reserved water should have cooled down to about 175 F after a few minutes. Pour a couple of tablespoons into the bowl and over the matcha.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2A9AAB1D305E140A34198630A19F245', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 4, NULL, NULL, 2, NULL, 0, 'Using your thumb and index finger, grasp the bamboo whisk with your dominant hand just above the strings (use the other fingers for support). Hold the bowl with your other hand. Whisk gently in circles to dissolve all the matcha and eliminate clumps.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7034F880A290FA42B086B1A5BD7CAD93', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 5, NULL, NULL, 2, NULL, 0, 'Pour the remaining water into the bowl and whisk in a `W` pattern until a foam with tiny bubbles forms on the entire surface. When whisking, the ultimate goal is to create a foam with tiny bubbles that are almost unnoticeable. At first, it may take a couple of minutes for the foam to form and it takes practice to master the technique.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0434776BB8D90C4B868F073CBB76B702', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 6, NULL, NULL, 2, NULL, 0, 'Drink the matcha directly from the bowl or pour it into a small cup. It should be drunk immediately and, traditionally, in three sips while cupping the bowl with both hands.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87F30A06FF60A842A77690DA32E6CA2A', X'4D21823CB8E9084AA3C3FB3F752EB02B', X'4D21823CB8E9084AA3C3FB3F752EB02B', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F7E2C5520A53444B594750039C7C585', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E81C8B23F9CA35429A8252393E2325DA', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', 1, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except ice and sliced cucumber in a large pitcher or bowl.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'565BFEDA85D3CC4ABC271721BB47CA47', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', 2, NULL, NULL, 2, NULL, 0, 'Cover the pitcher or bowl and chill the mixture for several hours.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B193C3E023D6FB42B60B81387B998F4F', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', 3, NULL, NULL, 2, NULL, 0, 'When the mixture is well chilled, strain the mint and cucumber if you like.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'123E988A90758649880CF8AE51849252', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', 4, NULL, NULL, 2, NULL, 0, 'Add ice to each glass and garnish the rim with sliced cucumbers (and mint if desired). Cucumber also looks nice in the glass not just on the rim.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'669BB06B469617408BF3244D27B58A0A', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', X'2EF02AE2CA11D542A8D6D9CEB7ED3FFF', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34B8A707C24F6D4299F17F48E837377C', X'9ACBFD7DE743374B93D98655B9E83196', X'9ACBFD7DE743374B93D98655B9E83196', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6811CF0D427BF24E8EA36DB502EB066C', X'9ACBFD7DE743374B93D98655B9E83196', X'9ACBFD7DE743374B93D98655B9E83196', 1, NULL, NULL, 2, NULL, 0, 'In a mug, add the honey, cinnamon stick, and lemon twist.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A37A2B8D87B20499DDF81E3F0CE7F8F', X'9ACBFD7DE743374B93D98655B9E83196', X'9ACBFD7DE743374B93D98655B9E83196', 2, NULL, NULL, 2, NULL, 0, 'Add the tea bag and fill with hot water. Let steep for 3 to 5 minutes, according to the tea''s recommended time (typically on the packaging or you can use suggested brewing times for the type of tea).');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E413E0E590D6BF4D9970D9159D726F87', X'9ACBFD7DE743374B93D98655B9E83196', X'9ACBFD7DE743374B93D98655B9E83196', 3, NULL, NULL, 2, NULL, 0, 'Remove the tea bag and add a splash of cream.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A33C58E3E9224488BCD2B0F369D3138', X'9ACBFD7DE743374B93D98655B9E83196', X'9ACBFD7DE743374B93D98655B9E83196', 4, NULL, NULL, 2, NULL, 0, 'Stir well.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BBD598E665CD34B90258832C4D90BCA', X'9ACBFD7DE743374B93D98655B9E83196', X'9ACBFD7DE743374B93D98655B9E83196', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A1728FF6FD0E942A44C0464CD4D550B', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81628DFA2A5DB46912359968EFF9FDB', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 1, NULL, NULL, 2, NULL, 0, 'Pre-heat the oven to 425 F.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17E810D7B37859459E13B387EC2415D5', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 2, NULL, NULL, 2, NULL, 0, 'Add the all-purpose flour, granulated sugar, baking powder, baking soda, salt, ground cinnamon and ground ginger to a large bowl. Add the chilled butter pieces and cut the butter in using a pastry cutter, a fork, or even your fingers, until all the dry ingredients are coated and the mixture resembles course crumbs. Place the bowl in the refrigerator for about 10 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15BD65CE2B2D9C4194BCB66EE3CF0379', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 3, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whisk together the unsweetened pumpkin puree, heavy cream and vanilla extract. Stir the wet ingredients into the flour and butter mixture, just until combined. The dough will be sticky.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'097D6EE5A4EE5446B5C364A1FD94EF55', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 4, NULL, NULL, 2, NULL, 0, 'Turn the dough out onto a well floured board and roll, or pat, out the dough to about an inch thick. Fold it over and repeat about 6 times. This will give you the classic layered look of a scone.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'403626309AB1F74BB1A747BC0310723A', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 5, NULL, NULL, 2, NULL, 0, 'Roll, or pat, the final time to an inch thick circle. Cut into 8 pieces and place the scones on a baking sheet lined with parchment paper.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6375AD714DD3049915FE6F54E348C9A', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 6, NULL, NULL, 2, NULL, 0, 'Bake in the pre-heated oven for approximately 18 minutes, or until the scones are a very light golden brown. Do not over-bake.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E410F1128A400A41ACC784C6B955BA36', X'26E89A47BCF2A4458AC77723BC2703B0', X'26E89A47BCF2A4458AC77723BC2703B0', 7, NULL, NULL, 2, NULL, 0, 'While the scones are cooling, make the sweet tahini glaze by whisking together the powdered sugar, milk or cream, sesame paste and salt until smooth. Drizzle over the cooled scones.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B40CC378DA8984A9C2869639810279A', X'BEFD58A9BF7C85438A9995A739D0F8D2', X'BEFD58A9BF7C85438A9995A739D0F8D2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A539A3C24B78E4EB1411189A8CBC303', X'BEFD58A9BF7C85438A9995A739D0F8D2', X'BEFD58A9BF7C85438A9995A739D0F8D2', 1, NULL, NULL, 2, NULL, 0, 'In a double old-fashioned glass, add bourbon and limoncello.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F40A1A53F7262A42990E6BFAEA4B1902', X'BEFD58A9BF7C85438A9995A739D0F8D2', X'BEFD58A9BF7C85438A9995A739D0F8D2', 2, NULL, NULL, 2, NULL, 0, 'Fill the glass with ice and top with iced tea.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2849ED54618C834EAD93E1CBE9E4A5CE', X'BEFD58A9BF7C85438A9995A739D0F8D2', X'BEFD58A9BF7C85438A9995A739D0F8D2', 3, NULL, NULL, 2, NULL, 0, 'Garnish with lemon. Serve and enjoy.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFDA61C8C7CCC9428CC21D9647F88745', X'F085E99AA11264468C2FAD586B412752', X'F085E99AA11264468C2FAD586B412752', 0, NULL, NULL, 2, NULL, 0, 'Pour the hot water over the tea leaves.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65E2617D3E801842AA84B919C9B7D22E', X'F085E99AA11264468C2FAD586B412752', X'F085E99AA11264468C2FAD586B412752', 1, NULL, NULL, 2, NULL, 0, 'Steep for 4 to 6 minutes and then remove the tea leaves.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE61AB865E8D704D93E93217361336D8', X'F085E99AA11264468C2FAD586B412752', X'F085E99AA11264468C2FAD586B412752', 2, NULL, NULL, 2, NULL, 0, 'Add the honey and stir.');
