INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBFC3CE3BE19ED4E85AA46B797F07AF6', X'971228B8B8233F4B8ADDE8BFDB618265', X'971228B8B8233F4B8ADDE8BFDB618265', 4, NULL, NULL, 2, NULL, 0, 'Filter the beans from the coffee. Pour slowly and stop as needed to avoid overflowing the filter.');
