INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21301497A5A62541B73AAE54D958B1F9', X'4FE4395A13BA2C419B27058A503D95F4', X'4FE4395A13BA2C419B27058A503D95F4', 1, NULL, NULL, 2, NULL, 0, 'Add the coffee grounds and cinnamon to the filter basket of your coffee pot. Then add the 5 cups of water to the pot and brew as you normally would.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BF9F4CBC183E1468767EDD4F679BD7F', X'4FE4395A13BA2C419B27058A503D95F4', X'4FE4395A13BA2C419B27058A503D95F4', 2, NULL, NULL, 2, NULL, 0, 'While the coffee is brewing, bring the milk, heavy cream, brown sugar, and chocolate syrup to a gentle simmer in a small saucepan. Make sure to cook on low heat so you dont burn the mixture.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D1C0C6820A10248B287DFE5CEA54E94', X'4FE4395A13BA2C419B27058A503D95F4', X'4FE4395A13BA2C419B27058A503D95F4', 3, NULL, NULL, 2, NULL, 0, 'Remove from the heat and add the brewed coffee to the milk mixture and whisk in the vanilla extract.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F453C925598D494D849A14B918A2F146', X'4FE4395A13BA2C419B27058A503D95F4', X'4FE4395A13BA2C419B27058A503D95F4', 4, NULL, NULL, 2, NULL, 0, 'Divide the coffee among four cups and top with whipped cream and a sprinkle of cinnamon. Enjoy immediately.');
