INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9493CE1A58FEBA4494631FA334431799', X'5FFAEAAEFA65DF40B287892135686036', X'5FFAEAAEFA65DF40B287892135686036', 1, NULL, NULL, 2, NULL, 0, 'Add the instant coffee, sugar and hot or boiling water to large liquid measuring cup.');
