INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EA5CA52C027E746A9F5E6C6ED5AFCC0', X'5EA38AD2B93ECA4E95A29CE40644D563', X'5EA38AD2B93ECA4E95A29CE40644D563', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1A477E323A8B84DB5B3A3C8053F65ED', X'5EA38AD2B93ECA4E95A29CE40644D563', X'5EA38AD2B93ECA4E95A29CE40644D563', 1, NULL, NULL, 2, NULL, 0, 'In a large stovetop espresso maker, brew coffee according to the manufacturer''s instructions using fine-ground coffee.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09F1B0655D364748B001398832988EAA', X'5EA38AD2B93ECA4E95A29CE40644D563', X'5EA38AD2B93ECA4E95A29CE40644D563', 2, NULL, NULL, 2, NULL, 0, 'In a glass measuring cup with at least 2 cup capacity, add the sugar. Once a little bit of coffee has brewed, add about 1 tablespoon to the sugar. Reserve a little extra coffee concentrate in a small dish to use if needed. Place the pot back on the stove to finish brewing.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FACA8469AD0D0419CD6819C69F4489D', X'5EA38AD2B93ECA4E95A29CE40644D563', X'5EA38AD2B93ECA4E95A29CE40644D563', 3, NULL, NULL, 2, NULL, 0, 'Make the espuma by using a small whisk or spoon to vigorously beat the sugar and espresso until it is a pale brown, almost foamy paste, about 2 to 3 minutes. You cannot beat it too much.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A60FC8663BB6E429D42A8B66288F25A', X'5EA38AD2B93ECA4E95A29CE40644D563', X'5EA38AD2B93ECA4E95A29CE40644D563', 4, NULL, NULL, 2, NULL, 0, 'When the coffee has finished brewing, pour it over the espuma while stirring to incorporate. Let the foamy crema rise to the top.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71AE599921571A48A03D1C894815122D', X'5EA38AD2B93ECA4E95A29CE40644D563', X'5EA38AD2B93ECA4E95A29CE40644D563', 5, NULL, NULL, 2, NULL, 0, 'Pour into individual demitasse espresso cups. Shake the measuring cup a little and use a spoon as you pour to get crema into each cup.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82E88EF3D1C5864AA7347396CA58681A', X'5EA38AD2B93ECA4E95A29CE40644D563', X'5EA38AD2B93ECA4E95A29CE40644D563', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05636A8BCB715844B32C2C2144168385', X'A834FC839620354B96F72E0E3C562D3E', X'A834FC839620354B96F72E0E3C562D3E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A867A905AED6344D8FE2C1DE40109BAE', X'A834FC839620354B96F72E0E3C562D3E', X'A834FC839620354B96F72E0E3C562D3E', 1, NULL, NULL, 2, NULL, 0, 'In a large microwave-safe bowl, combine the chocolate chips and coconut oil. Microwave in 30 second intervals, stirring after each interval until smooth.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58F2FD3BBE13AD4EBFA261F4B0AE0F77', X'A834FC839620354B96F72E0E3C562D3E', X'A834FC839620354B96F72E0E3C562D3E', 2, NULL, NULL, 2, NULL, 0, 'Dip the coffee beans in the melted chocolate. Mixing well to ensure each bean is evenly coated.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBCA9DF485331E4D898C599E45D03B89', X'A834FC839620354B96F72E0E3C562D3E', X'A834FC839620354B96F72E0E3C562D3E', 3, NULL, NULL, 2, NULL, 0, 'Using a fork, remove one bean at a time, letting excess chocolate drip off, and place it on a waxed paper or parchment lined baking sheet.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'958521895223214B91E8C91D8119DBA3', X'A834FC839620354B96F72E0E3C562D3E', X'A834FC839620354B96F72E0E3C562D3E', 4, NULL, NULL, 2, NULL, 0, 'If desired, the chocolate covered coffee beans can be rolled in unsweetened cocoa powder for a different appearance and texture.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD8EE7ED3D59744DB4296F6CC2E9454A', X'64F248E0FC93FA4B90E52092ECB0E1EE', X'64F248E0FC93FA4B90E52092ECB0E1EE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62115727DE71C841B0700450D7A6729B', X'64F248E0FC93FA4B90E52092ECB0E1EE', X'64F248E0FC93FA4B90E52092ECB0E1EE', 1, NULL, NULL, 2, NULL, 0, 'Heat the water to 200 F.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB2CE7FC513A124FA4AEBCAEE1C0F32A', X'64F248E0FC93FA4B90E52092ECB0E1EE', X'64F248E0FC93FA4B90E52092ECB0E1EE', 2, NULL, NULL, 2, NULL, 0, 'Place the coffee into your filter of choice. A French drip filter positioned over a cup or glass works best.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46F136119453954BB5C608CD4090A0EE', X'64F248E0FC93FA4B90E52092ECB0E1EE', X'64F248E0FC93FA4B90E52092ECB0E1EE', 3, NULL, NULL, 2, NULL, 0, 'Very slowly pour the hot water through the coffee grounds, letting the liquid slowly drip through to the glass or cup below. Don''t add the water too fast to ensure the filter stays positioned at the top of the glass.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C8174A31B91A04DB0640A99F44A498B', X'64F248E0FC93FA4B90E52092ECB0E1EE', X'64F248E0FC93FA4B90E52092ECB0E1EE', 4, NULL, NULL, 2, NULL, 0, 'Add condensed milk to taste, and enjoy hot or iced. Hot Vietnamese coffee is stronger and thicker, similar to espresso.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3D197CF93CA1C41B4D87D5EADBAF4A6', X'64F248E0FC93FA4B90E52092ECB0E1EE', X'64F248E0FC93FA4B90E52092ECB0E1EE', 5, NULL, NULL, 2, NULL, 0, 'To serve cold, pour the coffee and condensed milk into a tall glass filled with ice. Serve immediately.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F198E82EBAFAB24F895B4D6B3CCB2131', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and tools.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1294BC06C07ED40966526753AB0F7DB', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 1, NULL, NULL, 2, NULL, 0, 'Pour hot water into the serving cup to warm it.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82701F4C637A794EB487F08E9443E3E5', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 2, NULL, NULL, 2, NULL, 0, 'Add water to a gooseneck kettle and heat it on the stove until it reaches between 195 F and 205 F.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'055405F46307034AAE573F75DE6AC34F', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 3, NULL, NULL, 2, NULL, 0, 'Grind coffee beans to a medium to fine ground (about the size of kosher salt or coarse sand).');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42760E3C798B9F4083BFDDEC861848EC', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 4, NULL, NULL, 2, NULL, 0, 'Measure the ground coffee in the pour-over brewer.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1B23726F5A4DA4681266A584C3349E8', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 5, NULL, NULL, 2, NULL, 0, 'When the water is heated, dump the water from the cup. Set the drip cone on the cup''s rim. If using an all-in-one brewer, assemble the pieces, then measure the coffee.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCE77EE33C1A4942A07F577A5E196AE4', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 6, NULL, NULL, 2, NULL, 0, 'Zero out the scale and start the timer. Beginning in the center of the coffee grounds and working your way around, pour just enough water (about 1 3/4 ounces/50 mL) from the kettle to wet all of the coffee. Let it bloom and saturate the coffee for between 30 and 45 seconds.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D1EE7D09ADC7642921334C9F9529DCB', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 7, NULL, NULL, 2, NULL, 0, 'With the kettle spout as close to the cone as possible, continue to slowly pour water over the bed of coffee. Work in even circles, starting in the middle and moving outwards (when the water drains, the grounds should be flat, not concave). Do not pour water onto the cone or filter, only onto the coffee. Pause as needed to let it catch up until you have poured the full 10 ounces (300mL) of water.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC732E7C39B01D46A1C4A93BA2630268', X'610807CA97D8AA45B629C0942BF0C2FF', X'610807CA97D8AA45B629C0942BF0C2FF', 8, NULL, NULL, 2, NULL, 0, 'Tap the cone to release the last bits of coffee, then set it on a second cup or saucer to catch any additional drips. Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B02AED0BD8B22045B0B31F1AA872D561', X'CD860FCEF005324AAB811C7CFB3322AA', X'CD860FCEF005324AAB811C7CFB3322AA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1F3D991DE51F249BD59099E320B2730', X'CD860FCEF005324AAB811C7CFB3322AA', X'CD860FCEF005324AAB811C7CFB3322AA', 1, NULL, NULL, 2, NULL, 0, 'Add the prepared espresso to a small saucepan. The espresso can be prepared hours ahead of time or fresh. Whisk in the pumpkin puree, heavy cream, whole milk, pumpkin spice, and erythritol. Heat until steaming, then remove from the stove.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C5C989A56F94D40A412AE060CCBA4F5', X'CD860FCEF005324AAB811C7CFB3322AA', X'CD860FCEF005324AAB811C7CFB3322AA', 2, NULL, NULL, 2, NULL, 0, 'Froth the contents with a hand-held frother. You can also whisk vigorously. Just be careful not to splash yourself as you are stirring.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E15978217745344DB9B4A8571BCA5815', X'CD860FCEF005324AAB811C7CFB3322AA', X'CD860FCEF005324AAB811C7CFB3322AA', 3, NULL, NULL, 2, NULL, 0, 'Top with prepared whipped cream and a little cinnamon or pumpkin pie spice and serve immediately.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DA9F91351A12B45B3A957A539EEB803', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5569C014EAA35B45AB4EAE5F380C4C4D', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', 1, NULL, NULL, 2, NULL, 0, 'Brew the coffee according to your brewer instructions.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5255640E23F3EB4185BBE01EABE5F5B2', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', 2, NULL, NULL, 2, NULL, 0, 'Add the hot coffee to a warm mason jar that has a strong lid. Add the whole milk, heavy cream, cocoa powder, sugar, and vanilla extract to the jar. Put the lid back on and then cover the top with a clean towel in case the jar does leak. Shake the jar for 30 seconds or until the sugar has dissolved. It will also get nice and frothy. You can also use a milk frother instead of shaking.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45BDD0FD313262478FB8E61E9A432219', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', X'4C9F48A3C0DD0946A0E67D8FFF2C0FA2', 3, NULL, NULL, 2, NULL, 0, 'You can decide to heat the coffee back up if you wish, either before or after adding whipped cream. Just pop it in the microwave for 30 seconds to one minute or heat on the stove until steaming. Then serve with chocolate shavings and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86A5E67A0924994FB36C9309CC3DE259', X'8DBDDD711E67074AB6EF698BBDF93177', X'8DBDDD711E67074AB6EF698BBDF93177', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C54E01D2CDB76A40914C922B7B4CEF23', X'8DBDDD711E67074AB6EF698BBDF93177', X'8DBDDD711E67074AB6EF698BBDF93177', 1, NULL, NULL, 2, NULL, 0, 'Pour hot water into the serving cup to heat it while the coffee''s brewing.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4289296C0C9A624F93C249FB3212C2DA', X'8DBDDD711E67074AB6EF698BBDF93177', X'8DBDDD711E67074AB6EF698BBDF93177', 2, NULL, NULL, 2, NULL, 0, 'In a 3-cup stovetop espresso maker, add finely ground coffee and water according to the manufacturer''s instructions. Heat on the stove to brew.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'805D2C7AC68F2E4C8AAE46BBB5503103', X'8DBDDD711E67074AB6EF698BBDF93177', X'8DBDDD711E67074AB6EF698BBDF93177', 3, NULL, NULL, 2, NULL, 0, 'In a small saucepan over low heat, pour the milk and heat gently until it steams and there are small bubbles around the edges. Do not let it boil. Stir occasionally to prevent a film from forming on the top.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5B648879AAD7D489345B9D1291A2081', X'8DBDDD711E67074AB6EF698BBDF93177', X'8DBDDD711E67074AB6EF698BBDF93177', 4, NULL, NULL, 2, NULL, 0, 'When the espresso has brewed, dump the water from the cup. Fill it halfway with freshly brewed espresso.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'959283B8AE5FE24B9BA1E1BF86266053', X'8DBDDD711E67074AB6EF698BBDF93177', X'8DBDDD711E67074AB6EF698BBDF93177', 5, NULL, NULL, 2, NULL, 0, 'Slowly pour the steamed milk over the espresso and stir to incorporate. Add sugar to taste.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B379949AA076A4448844F891BCD70E3E', X'8DBDDD711E67074AB6EF698BBDF93177', X'8DBDDD711E67074AB6EF698BBDF93177', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65F479A28F89B24F8ECF6F63FC655EC2', X'E0126AF1A6F44448A73AD292A424AE5D', X'E0126AF1A6F44448A73AD292A424AE5D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC2522FB991D6B4E8FE5A47F3A1DF52D', X'E0126AF1A6F44448A73AD292A424AE5D', X'E0126AF1A6F44448A73AD292A424AE5D', 1, NULL, NULL, 2, NULL, 0, 'Add the milk and the white chocolate chips to a heavy bottom pot. Whisk together, on medium-low heat, until the white chocolate has been incorporated and the sauce is smooth.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56DA84BB71A8114CAD5B6090286836C3', X'E0126AF1A6F44448A73AD292A424AE5D', X'E0126AF1A6F44448A73AD292A424AE5D', 2, NULL, NULL, 2, NULL, 0, 'Brew a half cup of your favorite coffee or espresso and pour into a cup or mug. Pour the white chocolate milk sauce over the coffee and stir in any additional sugar, if desired.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B529BCF815CD6A43B37BFA42CCAC98F0', X'E0126AF1A6F44448A73AD292A424AE5D', X'E0126AF1A6F44448A73AD292A424AE5D', 3, NULL, NULL, 2, NULL, 0, 'Top with either homemade or store-bought whipped cream and sprinkle on a light dusting of ground nutmeg.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4061148BCA61BC4DBFCA58BF65EE294E', X'E0126AF1A6F44448A73AD292A424AE5D', X'E0126AF1A6F44448A73AD292A424AE5D', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABBC073DF6AC79439797C57521D4C504', X'BDDC50704FEAE64FB422DBE243341A83', X'BDDC50704FEAE64FB422DBE243341A83', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A742C513A6270942AF7549BC8995D096', X'BDDC50704FEAE64FB422DBE243341A83', X'BDDC50704FEAE64FB422DBE243341A83', 1, NULL, NULL, 2, NULL, 0, 'Add the unsweetened pumpkin puree, sugar, cinnamon, ginger, nutmeg, cloves, vanilla and espresso to a small pot.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF0FF3A84A3D3C4A9D9C62FCB6785F20', X'BDDC50704FEAE64FB422DBE243341A83', X'BDDC50704FEAE64FB422DBE243341A83', 2, NULL, NULL, 2, NULL, 0, 'Bring to a simmer on a medium low flame and whisk occasionally until the pumpkin puree has been incorporated and the sugar and spices dissolved.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B588252A817A5E4D9D710145850969FA', X'BDDC50704FEAE64FB422DBE243341A83', X'BDDC50704FEAE64FB422DBE243341A83', 3, NULL, NULL, 2, NULL, 0, 'Add the milk to a second pot or to a microwave safe jar and heat just enough to warm through.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'838C3244CF14E84A989E77A24C8230CD', X'BDDC50704FEAE64FB422DBE243341A83', X'BDDC50704FEAE64FB422DBE243341A83', 4, NULL, NULL, 2, NULL, 0, 'Whisk the milk vigorously until it begins to foam or froth. Alternately, if you have a milk frother wand, use this to create the foam desired.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BA4ACDDCA68C04C8F1E644CC755A9AD', X'BDDC50704FEAE64FB422DBE243341A83', X'BDDC50704FEAE64FB422DBE243341A83', 5, NULL, NULL, 2, NULL, 0, 'Pour the hot coffee mixture into a mug and then pour in the frothy milk.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9897FF47B3B0684F839C22AB16CC1140', X'BDDC50704FEAE64FB422DBE243341A83', X'BDDC50704FEAE64FB422DBE243341A83', 6, NULL, NULL, 2, NULL, 0, 'Top with whipped cream and a dash of cinnamon, if desired.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'179C52B83F350B4585F98D9543FBC71D', X'A741269B2480794FA6EDC5896ECF2106', X'A741269B2480794FA6EDC5896ECF2106', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
