INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6867F80AE5B6D44B25114652C67E712', X'A741269B2480794FA6EDC5896ECF2106', X'A741269B2480794FA6EDC5896ECF2106', 2, NULL, NULL, 2, NULL, 0, 'Fill with freshly-brewed hot coffee.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E938BCA444FCC4C8495EE6F60BA1917', X'A741269B2480794FA6EDC5896ECF2106', X'A741269B2480794FA6EDC5896ECF2106', 3, NULL, NULL, 2, NULL, 0, 'Garnish with whipped cream if desired.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0582E94A9B02064F9BD893C6AF379F37', X'A741269B2480794FA6EDC5896ECF2106', X'A741269B2480794FA6EDC5896ECF2106', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E27BC4F0E89EDE4087ACAD57180FE320', X'24BF728AD3409D4087F6D1464988E67E', X'24BF728AD3409D4087F6D1464988E67E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D603E0BFE15AD49BC4C0891D742FFA1', X'24BF728AD3409D4087F6D1464988E67E', X'24BF728AD3409D4087F6D1464988E67E', 1, NULL, NULL, 2, NULL, 0, 'Add hot water to a large measuring cup or medium-sized bowl and whisk in the espresso granules until they dissolve.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B90B977F5815540A3EB52AD8A361EEE', X'24BF728AD3409D4087F6D1464988E67E', X'24BF728AD3409D4087F6D1464988E67E', 2, NULL, NULL, 2, NULL, 0, 'Whisk in the sweetened condensed milk, vanilla extract, and salt until completely combined. Set it in the refrigerator to cool while you prepare the heavy cream.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5735596D69C63A489F76AF89B4AC0825', X'24BF728AD3409D4087F6D1464988E67E', X'24BF728AD3409D4087F6D1464988E67E', 3, NULL, NULL, 2, NULL, 0, 'Beat the heavy cream with a hand mixer until it becomes whipped cream and stiff peaks form.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD1C1A86482EBC4EAF0A5B945FB2059C', X'24BF728AD3409D4087F6D1464988E67E', X'24BF728AD3409D4087F6D1464988E67E', 4, NULL, NULL, 2, NULL, 0, 'Whisk in the sweetened condensed milk mixture until completely combined, but do not over mix.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7BC6331EAC6174099C2815124AA6D93', X'24BF728AD3409D4087F6D1464988E67E', X'24BF728AD3409D4087F6D1464988E67E', 5, NULL, NULL, 2, NULL, 0, 'Pour the ice cream into a loaf pan. Cover it in plastic wrap and place it in the freezer for least 6 hours, or until completely frozen. Making it and letting it freeze over night works the best.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6648284C09ECA64F9ACC679448614E7C', X'24BF728AD3409D4087F6D1464988E67E', X'24BF728AD3409D4087F6D1464988E67E', 6, NULL, NULL, 2, NULL, 0, 'When ready to serve, heat an ice cream scoop with hot water, scoop the ice cream, and serve with chocolate chips, amaretto cookies, or other sweet crumbled cookie toppings.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEA10C76CDBD124E9C699393EAAA30D4', X'9371799ADB3FB14C9087D0ACA88FA513', X'9371799ADB3FB14C9087D0ACA88FA513', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C181B3BC6F267F4CABDBC994DE0D7C35', X'9371799ADB3FB14C9087D0ACA88FA513', X'9371799ADB3FB14C9087D0ACA88FA513', 1, NULL, NULL, 2, NULL, 0, 'Bring water and sugar to a boil in an ibrik. If you do not have an ibrik, a small saucepan will work.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2AEBCEF17ED244782907107F2121B4D', X'9371799ADB3FB14C9087D0ACA88FA513', X'9371799ADB3FB14C9087D0ACA88FA513', 2, NULL, NULL, 2, NULL, 0, 'Remove from heat and add coffee and cardamom.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7361F474F18F6C43A06112F7DD96479A', X'9371799ADB3FB14C9087D0ACA88FA513', X'9371799ADB3FB14C9087D0ACA88FA513', 3, NULL, NULL, 2, NULL, 0, 'Return saucepan to heat and allow to come to a boil. Remove from heat when coffee foams.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDCC01D88599EC4C9177C8E71A95C8B5', X'9371799ADB3FB14C9087D0ACA88FA513', X'9371799ADB3FB14C9087D0ACA88FA513', 4, NULL, NULL, 2, NULL, 0, 'Again, return to heat, allowing to foam and remove from heat again.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEA528FB207D7B47A760EBDBEE0056A1', X'9371799ADB3FB14C9087D0ACA88FA513', X'9371799ADB3FB14C9087D0ACA88FA513', 5, NULL, NULL, 2, NULL, 0, 'Pour into 2 demitasse cups, and let them sit for a few minutes so the grounds can settle to the bottom of the cups. If using a cardamom pod, it can be served with the coffee for added flavor. Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'242815D5771B454596005CA44BE78A7B', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF0E864477FD324CAF984CFEFB4EDD69', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 1, NULL, NULL, 2, NULL, 0, 'Combine water and tea leaves in a small saucepan over medium heat.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A6644CF0E9904DB61EA80507DE9FC6', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 2, NULL, NULL, 2, NULL, 0, 'Bring to a low boil. Reduce heat and simmer for 3 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9C6BAB917796A4290AB918971B4360B', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 3, NULL, NULL, 2, NULL, 0, 'Remove from heat. Stir in sweetened, condensed milk. Return to heat.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F091FD57E32C6F4CB8ED77E11410EB44', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 4, NULL, NULL, 2, NULL, 0, 'Return to a boil. Simmer for 3 more minutes.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DFF9DF68EDFFE4C944C8CC7107115F9', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 5, NULL, NULL, 2, NULL, 0, 'Strain tea and condensed milk.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54DB57EC46D94B47988C0FC7AA8A5626', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 6, NULL, NULL, 2, NULL, 0, 'Serve hot or (optional) chill and serve over ice. Small glasses are ideal.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FADF7B65B9FD2F49BC3CE1087DAE5361', X'0C5ECA5DBBFE8F49B0194E02C12D2350', X'0C5ECA5DBBFE8F49B0194E02C12D2350', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C7A1AA6C2759C4381F5EDD57F77EF1D', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 0, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine the heavy cream, 1/3 cup of granulated sugar, and espresso powder. Blend well and bring the mixture to a simmer. Pour through a fine mesh strainer into a bowl and set aside for about 15 minutes or until it cools to room temperature.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5411D947C923C7469E8B731D9B2881AA', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 325 F (165 C/Gas 3).');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C96FD3DB2E5794499AD0255AF2AE29A9', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 2, NULL, NULL, 2, NULL, 0, 'Arrange six to eight ramekins in a 9-by-13-by-2-inch metal baking pan. If you are using wide shallow ramekins, you might need two pans.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1B9246697D4F844AF6695A8372E04E0', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk the egg yolks with the cinnamon. Whisk in the cream mixture until well blended.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5336603837B0D54AAA8BFEDB710C0743', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 4, NULL, NULL, 2, NULL, 0, 'Ladle the mixture into the ramekins and fill the outer pan with hot tap water. The water should come about halfway up the sides of the ramekins. You might want to put the pan in the oven before you add the hot water. Try to avoid sloshing when you move the pan.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A808A69A0CFAD04E9CD1403105342753', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 5, NULL, NULL, 2, NULL, 0, 'Bake for 30 to 40 minutes, depending on the depth of the creme brulee. They should be set with only a slight jiggle. Very carefully remove the pan(s) from the oven. Remove the ramekins from the panuse a spatula and oven mittto a rack to cool. Cover and refrigerate them for three to four hours or until thoroughly chilled. They can be made a day in advance.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'428AFCC5EEF8FB49B8B887C0E431A701', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 6, NULL, NULL, 2, NULL, 0, 'When you are ready to serve, take the ramekins out of the refrigerator.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CC5923F9C424346AF4E49AE3D236960', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle 1 1/2 to 2 teaspoons of superfine sugar over each creme brulee, depending on the surface area of the ramekins. Gently rotate the ramekin on the countertop to distribute the sugar evenly over the top.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'783A8D33A8B18F4AB60AD27AF9A83476', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 8, NULL, NULL, 2, NULL, 0, 'Use a kitchen torch to melt the sugar, using a protected hand to move the ramekin quarter-turns as you torch it. The ramekin will be hot, so take care.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF8983886FC22A43BB346555CA71CF3C', X'57D931305E60704B829BB16333F6CD95', X'57D931305E60704B829BB16333F6CD95', 9, NULL, NULL, 2, NULL, 0, 'Alternatively, arrange the ramekins on a baking sheet and place them under the broiler. Watch closely and rotate the pan to melt the sugar evenly.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12C907C097FAD643AF95E7CF7216D031', X'77C27A688944CA458C224FC74954BFC7', X'77C27A688944CA458C224FC74954BFC7', 0, NULL, NULL, 2, NULL, 0, 'In a traditional ibrik or small saucepan combine all ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEF445F4EF447146A3F09C2849CF4873', X'77C27A688944CA458C224FC74954BFC7', X'77C27A688944CA458C224FC74954BFC7', 1, NULL, NULL, 2, NULL, 0, 'Bring the mixture to a boil and remove from heat.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E8D370A1BCCDA44962E67E5A8519257', X'77C27A688944CA458C224FC74954BFC7', X'77C27A688944CA458C224FC74954BFC7', 2, NULL, NULL, 2, NULL, 0, 'Once the coffee stops bubbling, place it back onto stovetop and allow to come to a boil once more. Remove from heat and repeat.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A89B5CEE10228947B888F78D64820A57', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6119EBD56FD5549BA8073008F53A15D', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', 1, NULL, NULL, 2, NULL, 0, 'In a blender, add the banana, pear, almond milk, ginger, honey, vanilla extract, and ground cinnamon. Add the kale and then the ice on top to weigh it down.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26CF2E18E323A94897E76D172DC42E61', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', 2, NULL, NULL, 2, NULL, 0, 'Blend until perfectly smooth (two to three minutes, depending on your blender). You may need to push the ingredients down halfway through blending.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19BE7F633FECE14DA12474A574284AD6', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', X'C5C92E9CA7A2AE469EACEFAA6A085F2D', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2B6995414742B4E9DA6247AF16D2632', X'9AB476359C8778448203F4C53357A86C', X'9AB476359C8778448203F4C53357A86C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A54B5D9B7932FB49A74F097453151E92', X'9AB476359C8778448203F4C53357A86C', X'9AB476359C8778448203F4C53357A86C', 1, NULL, NULL, 2, NULL, 0, 'Add ice, pineapple juice, and cream of coconut to a blender');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'925756E6DD3E204093830750A962E5F9', X'9AB476359C8778448203F4C53357A86C', X'9AB476359C8778448203F4C53357A86C', 2, NULL, NULL, 2, NULL, 0, 'Blend until slushy.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D53E01EA709E34CA4DB39AFA4EED091', X'9AB476359C8778448203F4C53357A86C', X'9AB476359C8778448203F4C53357A86C', 3, NULL, NULL, 2, NULL, 0, 'Pour into a chilled hurricane glass and garnish with an orange slice.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7012DAD771A664293D2F3A5049859C0', X'665ED5FB5B0BD24D8C755DD21CB30D94', X'665ED5FB5B0BD24D8C755DD21CB30D94', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6FB674A0CF5B4242AD4BE34822DBCC01', X'665ED5FB5B0BD24D8C755DD21CB30D94', X'665ED5FB5B0BD24D8C755DD21CB30D94', 1, NULL, NULL, 2, NULL, 0, 'Build the liquid ingredients in a highball glass over ice cubes. Start by pouring the vodka, and then add the tomato juice, lemon juice, and the dash of Worcheshire sauce.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AF0FF0390C7AA449502EE14CDE26BE0', X'665ED5FB5B0BD24D8C755DD21CB30D94', X'665ED5FB5B0BD24D8C755DD21CB30D94', 2, NULL, NULL, 2, NULL, 0, 'Add the celery salt, pepper, and hot sauce to taste. If using, add the horseradish.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E34C221282CC84695804714DCCF370A', X'665ED5FB5B0BD24D8C755DD21CB30D94', X'665ED5FB5B0BD24D8C755DD21CB30D94', 3, NULL, NULL, 2, NULL, 0, 'Stir well, or try rolling the drink.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEFC8781E643A64DB788B853CFE824EA', X'665ED5FB5B0BD24D8C755DD21CB30D94', X'665ED5FB5B0BD24D8C755DD21CB30D94', 4, NULL, NULL, 2, NULL, 0, 'Taste and adjust the seasonings to your liking.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'874A839BC9DB13479000803EDDCDDF65', X'665ED5FB5B0BD24D8C755DD21CB30D94', X'665ED5FB5B0BD24D8C755DD21CB30D94', 5, NULL, NULL, 2, NULL, 0, 'Garnish with a lemon wedge and celery stalk or pickle.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7352476F4103B24D9F2CE408C24AA3FF', X'665ED5FB5B0BD24D8C755DD21CB30D94', X'665ED5FB5B0BD24D8C755DD21CB30D94', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA0E20BDB8824543AD801D93F105A470', X'4D24B2E89B352E48915756D738A01CC7', X'4D24B2E89B352E48915756D738A01CC7', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D1F77C676EDD84CAC488DB169AD2288', X'4D24B2E89B352E48915756D738A01CC7', X'4D24B2E89B352E48915756D738A01CC7', 1, NULL, NULL, 2, NULL, 0, 'Put the juice, coconut milk, pineapple then ice cream into your blender.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'262AC91B0029184A990156D2BDEB0D11', X'4D24B2E89B352E48915756D738A01CC7', X'4D24B2E89B352E48915756D738A01CC7', 2, NULL, NULL, 2, NULL, 0, 'Blend on high until completely blended, smooth and creamy, adding a little more juice to help it blend, if needed. It''s typically very thick, so you will need to stop and scrape down the sides.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B23AFF9B83C1F949BFD835FEACABC5AF', X'4D24B2E89B352E48915756D738A01CC7', X'4D24B2E89B352E48915756D738A01CC7', 3, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into glasses or ice cream cups and serve. If you''re feeling fancy, place the mixture into a piping bag with a star tip. Pipe it into the cups, add a little fresh pineapple garnish, and dig in.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27899DFF6D8E4549BBFAC5E80C990A33', X'BD7AA464149FC04CA0BBAF8B8D14204A', X'BD7AA464149FC04CA0BBAF8B8D14204A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FBACD75EB23B441BB497A379DD0E3B5', X'BD7AA464149FC04CA0BBAF8B8D14204A', X'BD7AA464149FC04CA0BBAF8B8D14204A', 1, NULL, NULL, 2, NULL, 0, 'Add the frozen raspberries, frozen pineapple chunks, banana, and orange pineapple juice to a quality blender. If you are adding yogurt, add it at this time as well. It is not necessary to cut up the banana if it is fresh. If it is frozen you may want to cut it in half. It''s also helpful if you are freezing bananas yourself to freeze them in chunks!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'573D2DCCADDF5541AEE73A9A9E17C990', X'BD7AA464149FC04CA0BBAF8B8D14204A', X'BD7AA464149FC04CA0BBAF8B8D14204A', 2, NULL, NULL, 2, NULL, 0, 'Blend the fruit and juice on high for a minute or so. Make sure the lid is securely placed on top of your blender so you don''t have an explosion! Then turn the blender speed down to a lower setting for a minute or so to help create a smooth texture.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD23821CAF74324C8AA8C4B4D09A1644', X'BD7AA464149FC04CA0BBAF8B8D14204A', X'BD7AA464149FC04CA0BBAF8B8D14204A', 3, NULL, NULL, 2, NULL, 0, 'Pour the smoothie into a large glass or to go container and use a smoothie straw to drink it up! You can probably share this smoothie with someone else, but we can drink this recipe in one sitting!');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31C7E0E3E6835A4B868D3011C4158CD7', X'E27533CF5FE8B042BD814FE2A4CB02AA', X'E27533CF5FE8B042BD814FE2A4CB02AA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
