INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A7A59079322D94AB012627EBD2A939A', X'F3170EBCA183CC47A8CA7FAAB5E9B58F', X'F3170EBCA183CC47A8CA7FAAB5E9B58F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
