INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4197EA96B42B304E91D7792755383A9D', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', 1, NULL, NULL, 2, NULL, 0, 'Put this mixture into a food processor and blend till smooth.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FB51401EE25FC42880035DE1DEE138D', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', 2, NULL, NULL, 2, NULL, 0, 'Put the blended mix back into the saucepan and on a medium flame.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'073F5F03DD21BC4BBBB9BCE58C71BE05', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining ingredients and cook for another 20 minutes.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33C55BF60CF65A409C9E8F30CAD87CC0', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', 4, NULL, NULL, 2, NULL, 0, 'Take off from the fire and allow to cool completely.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0151C5E43D2E3D4EB897DA836CAD5899', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', 5, NULL, NULL, 2, NULL, 0, 'Pour into glasses, dilute with a little chilled water if needed, mix well and add crushed ice.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C00066ECA169BE4FAFBD4C6E702FC92B', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', X'E8B39B1FFCC64B4EA9CCC3207352EBAE', 6, NULL, NULL, 2, NULL, 0, 'Garnish with a sprig of mint and serve.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E57914C4DBC4946B9E8B61791250778', X'F95226FACB8A424A8388E87FCFFD68C9', X'F95226FACB8A424A8388E87FCFFD68C9', 0, NULL, NULL, 2, NULL, 0, 'Blend all ingredients until smooth, adding more water if desired.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A91F9866AFBCB42B754EFDAE29BB59F', X'782E77101BFE5F40B6CA4686E676A62D', X'782E77101BFE5F40B6CA4686E676A62D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'827FD40C3377514F8D74AA0F257F3A3E', X'782E77101BFE5F40B6CA4686E676A62D', X'782E77101BFE5F40B6CA4686E676A62D', 1, NULL, NULL, 2, NULL, 0, 'Place the coconut milk, orange juice, honey, frozen raspberries, and ice in a large blender. Blend on high until the smoothie is totally smooth. If you use a frozen banana instead of the ice you don''t need to add the honey.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9484CA7D16AD3443B7FEB429737C2E95', X'782E77101BFE5F40B6CA4686E676A62D', X'782E77101BFE5F40B6CA4686E676A62D', 2, NULL, NULL, 2, NULL, 0, 'Split the smoothie between two large smoothie glasses. Use a smoothie straw as it will be nice and thick! Garnish with fresh raspberries and serve immediately (it really only tastes good when it is still cold!)');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCA6487ABBF1C647AC45B738F98D56C0', X'DE5A291D13A66042A618B91B4E3480C6', X'DE5A291D13A66042A618B91B4E3480C6', 0, NULL, NULL, 2, NULL, 0, 'Pour the peach nectar into a Champagne flute.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36CBFEA354351C44B471256376C55A70', X'DE5A291D13A66042A618B91B4E3480C6', X'DE5A291D13A66042A618B91B4E3480C6', 1, NULL, NULL, 2, NULL, 0, 'Slowly add the sparkling cider.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D010D3BF4EA93349AF6FA941D9C3E4A7', X'DE5A291D13A66042A618B91B4E3480C6', X'DE5A291D13A66042A618B91B4E3480C6', 2, NULL, NULL, 2, NULL, 0, 'Garnish with a peach slice, if available.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9B8DBCE1173154182C7F8011C7D818F', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', 0, NULL, NULL, 2, NULL, 0, 'Wearing rubber gloves, peel the turnip and beet and slice them. Peel the carrots and divide them into fours.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F8D47D03EE2AA40B9389CADA5457501', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', 1, NULL, NULL, 2, NULL, 0, 'Wrap the half a loaf of bread in the cheesecloth and tie off the end to make a hobo sack. Put the sack inside the glass jar. Add the sliced vegetables and all the other ingredients except the salt.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7C1E28997B2F64A9C58C8E406F6C950', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', 2, NULL, NULL, 2, NULL, 0, 'In a separate container, mix the salt with about 3 cups of purified water to dissolve it, then pour it into the jar. Add more water if needed to fill the jar to the top with liquid, ensuring that all vegetables are completely covered in liquid. This is key to ensuring a safe environment for the vegetables to ferment. Close the lid tightly.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEF928DF4983B9448DFEA0ACA73A1A8E', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', 3, NULL, NULL, 2, NULL, 0, 'Set the jar aside in a cool place to ferment for 15 days. Dont attempt to open the jar before 15 days pass.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81FB57D090374C44A7619093C596630A', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', X'2D8BBF3E3F8BBD4C98CC93E16898C17F', 4, NULL, NULL, 2, NULL, 0, 'After 15 days, open the jar and remove the cheesecloth and bread. The bread has acted as the fermenting agent. Like all fermented items, particularly turnips, the aroma may be pungent. This is normal, and a sign of good fermentation. Once you chill it, your salgam juice is ready to drink. You can serve the now pickled vegetables alongside to munch on.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC357F99523CA44DBD3D10C78B78EF25', X'000245226557E5469FBDCC4EF2E3F825', X'000245226557E5469FBDCC4EF2E3F825', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E96865722F690B409D2073E8703C756B', X'000245226557E5469FBDCC4EF2E3F825', X'000245226557E5469FBDCC4EF2E3F825', 1, NULL, NULL, 2, NULL, 0, 'Add the Royal Hawaiian Orchards Macadamia Nut Milk, honey, and almond butter to the bottom of a blender. Pulse a few times to combine.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B58BBB41B1496C419EC5441954EC6CBD', X'000245226557E5469FBDCC4EF2E3F825', X'000245226557E5469FBDCC4EF2E3F825', 2, NULL, NULL, 2, NULL, 0, 'Add the spinach on top of the liquid, followed by the banana slices and cored, sliced, and seeded Fuji apple. Pulse a few times to begin breaking up the ingredients. Increase speed to high to combine and liquefy the ingredients.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7714A59F9029E44BC2EC9D05EA0869F', X'000245226557E5469FBDCC4EF2E3F825', X'000245226557E5469FBDCC4EF2E3F825', 3, NULL, NULL, 2, NULL, 0, 'Stir in the flax seed or chia seeds until well combined.');
