INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D3FC528BD1B8438CDC7B1F29008380', X'D185D5B0C5D2F34EB098A8EA3608EDA1', X'D185D5B0C5D2F34EB098A8EA3608EDA1', 2, NULL, NULL, 2, NULL, 0, 'Stir in orange juice concentrate. This will help cool down the Jello. Add the ginger ale, pineapple juice, and sugar. Mix well. Transfer the Halloween punch to a large punch bowl. Unmold the brain ice cube (invert onto a plate), and place in the center of the Halloween punch.');
INSERT INTO `VWR INTERNAL RecipesbyDrinks2 recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5932F7820360844828AFDDFC48D29E5', X'734BE372063C494DB744D2F5A28CF53C', X'734BE372063C494DB744D2F5A28CF53C', 0, NULL, NULL, 2, NULL, 0, 'Soften sherbet into the mix of fruit juices and water.');
