INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C894260B3BB9A44F9DA5EC4F618F9247', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 0, NULL, NULL, 2, NULL, 0, 'Add about 1/2 inch grapeseed oil to a skillet or shallow pot. The pan should be large enough to hold all the sticks with plenty of room to turn them over and move them around without feeling crowded.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56C0A232C4921149B267DC32F49D3099', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 1, NULL, NULL, 2, NULL, 0, 'Add the flours, parmesan cheese, garlic powder, salt and black pepper to a shallow dish large enough to hold all the cheese at once and mix to thoroughly combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72BCDB8DD57B2C4296BAB5F2C97C5C5F', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 2, NULL, NULL, 2, NULL, 0, 'In another dish of similar size, add the beaten eggs.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2975146C89F73A429227F16AE9DBC5D3', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 3, NULL, NULL, 2, NULL, 0, 'Coat the frozen cheese in the dry mixture, then the egg, then the dry mixture again, then the egg a second time, and finally once again in the dry mixture. Take care to coat the entire surface of the sticks, including the ends.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3DFDC202997B445856216D496897AC4', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 4, NULL, NULL, 2, NULL, 0, 'Prepare a platter and a dish of marinara sauce (or your dip of choice.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBFA254C17510D4F801417DB41843732', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil in the pan to 360F. Youll want to fry the cheese around 350F, so make the oil a little hotter so when the frozen cheese is added, the temperature wont drop too much.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46589B68E8B84B489F0C216A0C87067D', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 6, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add all the cheese sticks. Flip them over after about 30 seconds, and continue turning them every 20-30 seconds until they''re golden in color. No matter how well you coated the sticks, some cheese may begin to leak. Excessively leaky ones are likely done cooking; remove them from the pan first.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'958AD4A64CF1C0459937CC1FAB8C8F2D', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 7, NULL, NULL, 2, NULL, 0, 'When all the sticks are golden, remove them to a plate lined with paper towels to drain excess oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9045A3E56090AD42BDF4EA3B85D7A426', X'E13C266FA5533A45AC2187E83F5C09CC', X'E13C266FA5533A45AC2187E83F5C09CC', 8, NULL, NULL, 2, NULL, 0, 'Transfer the mozzarella sticks to the platter, and serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C7D98E69BA9FA44BB711D23309772F1', X'B20318C4A31D174B98350AA4166885EE', X'B20318C4A31D174B98350AA4166885EE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C538C56D4A5C940AAC2501C67065FDB', X'B20318C4A31D174B98350AA4166885EE', X'B20318C4A31D174B98350AA4166885EE', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4). Butter a 9 x 13 x 2-inch baking pan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD3CB8559DFC454E8B36D90982249841', X'B20318C4A31D174B98350AA4166885EE', X'B20318C4A31D174B98350AA4166885EE', 2, NULL, NULL, 2, NULL, 0, 'Cook the ziti or similar pasta in boiling salted water following the package instructions. Drain well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FC108388F58794BA2D9A253D2A3BD09', X'B20318C4A31D174B98350AA4166885EE', X'B20318C4A31D174B98350AA4166885EE', 3, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ricotta cheese, seasoned salt, garlic powder, and half of the mozzarella cheese. Taste and adjust seasoning, as needed. Add the egg and blend well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'849C0C6510CEB54E85A3CC2047DD6542', X'B20318C4A31D174B98350AA4166885EE', X'B20318C4A31D174B98350AA4166885EE', 4, NULL, NULL, 2, NULL, 0, 'Stir in the drained pasta and transfer to the prepared baking dish. Pour the spaghetti sauce over all.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6582897B9B7564EBF5A922579618DCE', X'B20318C4A31D174B98350AA4166885EE', X'B20318C4A31D174B98350AA4166885EE', 5, NULL, NULL, 2, NULL, 0, 'Bake in the heated oven for 45 minutes. Sprinkle with the remaining cup of mozzarella cheese and the 1/4 cup of Parmesan cheese, and bake for 5 minutes longer, or until the mozzarella cheese has melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D9A96C4D9C2BD47B5CBD523866F4D6E', X'B20318C4A31D174B98350AA4166885EE', X'B20318C4A31D174B98350AA4166885EE', 6, NULL, NULL, 2, NULL, 0, 'Serve the ziti casserole with a fresh tossed salad and baked or grilled garlic bread.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A231EE07910114388CE1294DA1DF0DA', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'379D1A62A71EFA4894099D9A9198996F', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 1, NULL, NULL, 2, NULL, 0, 'Cook the ziti in boiling salted water following the package directions. Drain well and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4D4D9BD520229418A0DCC2DA64151DF', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 2, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4). Lightly butter a 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'425D554FCFAF36428367D1E4022BE2F6', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 3, NULL, NULL, 2, NULL, 0, 'While the ziti is cooking, drizzle the olive oil in a large skillet or sauté pan and place it over medium heat. When the oil is hot, add the ground beef, onion, and green bell pepper. Cook, stirring until the onion is tender and the beef is no longer pink.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF032BDA036D4F45ADF8AF1D8AFCB052', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 4, NULL, NULL, 2, NULL, 0, 'Drain off the excess fat and then add the tomatoes, tomato sauce, basil, oregano, garlic powder, salt, and pepper. Bring to a simmer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6194819C932B354780DF819BB6FCB3D0', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked pasta and half of the mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0827C145B963514187D53F4BD1FDA98D', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 6, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to the prepared baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96F053405D59CB42AA9AABCABFD4EB9B', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 7, NULL, NULL, 2, NULL, 0, 'Top the casserole with the remaining mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE9B6308256304EB40583FFE882BE30', X'347C7AB7FB54494C8B4F84BC13581496', X'347C7AB7FB54494C8B4F84BC13581496', 8, NULL, NULL, 2, NULL, 0, 'Bake the ziti casserole for 25 to 30 minutes or until hot and bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEDCA802686CE046B67BFE52971032B7', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3754B880DEA374A80F7275ABFE0C9A0', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 1, NULL, NULL, 2, NULL, 0, 'Place the eggs in a shallow bowl or pan, and whisk them together with the oregano and some salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D162816347A884E84339D1B6D7AA61F', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 2, NULL, NULL, 2, NULL, 0, 'Stir the parmesan cheese and garlic into the bread crumbs and place them in another shallow pan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C763CC1EFE91B459B87CE273F21608E', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 3, NULL, NULL, 2, NULL, 0, 'Dip the steaks first in the egg mixture, then in the bread crumbs, coating them well with the crumbs.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F3E1118A8F9DF45AEA84C23E9645F73', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a heavy skillet, and cook steaks for several minutes on each side, until golden brown and crispy. Drain steaks on paper towels.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BE446158A12D34CB95DAD88749E91BE', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 5, NULL, NULL, 2, NULL, 0, 'Place steaks on a baking sheet. Turn on oven broiler.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29CACD222E03FD4AA8B5E0CABB925D17', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 6, NULL, NULL, 2, NULL, 0, 'Top each steak with a slice of ham, 2 to 3 tablespoons tomato sauce, and 1/4 cup grated mozzarella cheese. Sprinkle Italian seasoning over the cheese and place steaks under ?the broiler until cheese melts.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DAE7AE4F67C504E91FFDAFE5FD95A4E', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 7, NULL, NULL, 2, NULL, 0, 'Serve warm, with fries.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFB54DB86E78E648A644B20A1BF5D6CD', X'124C0E68932C294EAC1B658F90AE2E08', X'124C0E68932C294EAC1B658F90AE2E08', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'661EA99454605041BB5370D2939A38F9', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CAC3B64D815734AA66350D625381FE2', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 1, NULL, NULL, 2, NULL, 0, 'In a large pot, combine the butter and olive oil heat on medium high until melted. Add the chopped onions and garlic and saute until translucent and fragrant.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4A3F12AFAC3E94F85952769D073E78E', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 2, NULL, NULL, 2, NULL, 0, 'Add the flour and whisk until it is all incorporated. Cook on medium heat for about 5 minutes, stirring frequently. This will cook off the flour taste and help the sauce to thicken later on.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6BD882E00905945AAB31C42ED1790AF', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 3, NULL, NULL, 2, NULL, 0, 'Add the half and half and heavy cream. Whisk until combined. Cook for 5 minutes, stirring frequently until the mixture has thickened.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'015D3F8EC21B5F4E994C7259DFFFDB56', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 4, NULL, NULL, 2, NULL, 0, 'Stir the Parmesan cheese and frozen spinach into the white sauce. Heat on low heat until the spinach is warmed through and completely defrosted. Add salt and pepper to taste. Add a little milk if the sauce is too thick.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82CB06C2895DBB46820395F2D88459BA', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 5, NULL, NULL, 2, NULL, 0, 'In a separate medium sized bowl, whisk together the ricotta cheese, egg, and salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B911834D1B25E8489DDE15B17CB3B30A', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 6, NULL, NULL, 2, NULL, 0, 'Add a layer of the white spinach sauce to the bottom of a 9 x 13-inch casserole dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A4009659B15D04AA03F1DEF07AC462B', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 7, NULL, NULL, 2, NULL, 0, 'Layer two matzo crackers on top of the sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'379ED4590B9AA74D8108680E5B54A53D', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 8, NULL, NULL, 2, NULL, 0, 'Add about a 1/2 cup to 3/4 cup of the ricotta cheese mixture to the top of the crackers. Spread it out evenly. Then repeat with the sauce, matzo, and ricotta cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4CDD06402139141A7FF079ED0722FDB', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 9, NULL, NULL, 2, NULL, 0, 'Once you''ve reached the top of the casserole dish, top with the whole milk and mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E0E3E55DC91174CADEB3372906ECB27', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 10, NULL, NULL, 2, NULL, 0, 'Cover the casserole dish with aluminum foil and place it on a sheet tray in case it overflows while baking. Bake for 45 minutes or until bubbly. Remove the foil and allow the top to brown in the oven for an additional 15 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8AAD0AB0AC1D342B588AFB89C9B1FCB', X'0D68DA6FB284824783B47F7D2D97863E', X'0D68DA6FB284824783B47F7D2D97863E', 11, NULL, NULL, 2, NULL, 0, 'Serve immediately and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED4235B8F644104C8ED0FB62F46D9A24', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'402CFCE636946443994BC4C3F5866008', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 1, NULL, NULL, 2, NULL, 0, 'In a small pot over medium heat, warm the milk to 100 to 105 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA6742D7DA74BC4CB472FBE00B1020C7', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 2, NULL, NULL, 2, NULL, 0, 'Place warmed milk in a small bowl and stir the sugar into the milk. Sprinkle the yeast over the sugar milk mixture, and set aside for 5 to 10 minutes, until mixture is bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21B43A777B97C240871EF40EA1B60010', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 3, NULL, NULL, 2, NULL, 0, 'Place the flour, 1 tablespoon olive oil, and salt in the bowl of a standing mixer and mix together briefly using the dough hook. Add the yeast/milk mixture and begin to knead, adding the water gradually. The mixture should come together as a soft, stretchy dough, pulling away from the sides of the bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DE5F0F8793BB1449E94A96A699588CF', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 4, NULL, NULL, 2, NULL, 0, 'Knead for 5 to 10 minutes, until dough is smooth, soft and elastic.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED0B1B3B3B99B94A8C41BE04ED1A2A95', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 5, NULL, NULL, 2, NULL, 0, 'Oil a bowl with olive oil and place the dough in the bowl. Cover with plastic wrap and let the dough rise on the counter until doubled in size.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C0F2C7FDC248D4D896B9507289BD45F', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 6, NULL, NULL, 2, NULL, 0, 'While the dough is rising, peel and slice the onion into very thin strips. Place them in a bowl of cold salt water and soak for 30 minutes. Drain onions well and dry them with paper towels.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AC2FF27ACE75D40A0ECAB0059C03C2C', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 7, NULL, NULL, 2, NULL, 0, 'Once the dough has risen, punch it down and divide dough into two pieces. Roll each half into a smooth ball.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B541CDD54810034382BA91C19812AAB3', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 8, NULL, NULL, 2, NULL, 0, 'Pour 3 tablespoons of olive oil into a 12-inch pizza pan or cast iron skillet. Place one ball of dough in the middle of the pan and flatten gently with your fingers. Let dough relax for 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49ECEE21665EBC4A89FEA88E2F2983C4', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 9, NULL, NULL, 2, NULL, 0, 'Continue to flatten dough into the pan, flattening it and pushing it toward the sides of the pan, letting it relax in between until dough covers the bottom of the pan. Oil the counter and roll the other piece of dough into a 12-inch circle, letting it relax in between until it holds its shape.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3878D37EC8685F469B2DAB12ABB9D47A', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 10, NULL, NULL, 2, NULL, 0, 'Preheat oven to 450 F. Place the slices of mozzarella cheese over the dough in the pan. Sprinkle the provolone over the mozzarella. Place the other round of dough over the cheese and seal the edges of the two dough circles together.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC3C1957D49D9E4E8AADCD9CBC57EE68', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 11, NULL, NULL, 2, NULL, 0, 'Top the pizza with the sliced onions. Drizzle a tablespoon of olive oil over the onions, and sprinkle with the dried oregano and some Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'733A8843EF33F243B317B1B90B90E15D', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 12, NULL, NULL, 2, NULL, 0, 'Place the fugazzeta in the oven. Bake for 20 to 25 minutes, or until the edges are golden brown and crispy. Brown the onions under the broiler for the last 3 minutes of cooking if desired.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30009933B7AF5C41A06433BF94B0BF70', X'8D933B9CD05694429311C5B2819F1153', X'8D933B9CD05694429311C5B2819F1153', 13, NULL, NULL, 2, NULL, 0, 'Remove from the oven. Let cool for 5 to 10 minutes before cutting into slices to serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDB3E2047F3F00428A523440B53D29A4', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2634B4ED7ED4634BB07F4DF7A9D08E04', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F / 180 C / Gas 4. Lightly grease or spray a 2 1/2 to 3-quart baking dish with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53BC13A4EB3134448B7CC2CB75996684', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Add the ground beef, sausage, and onion. Cook, stirring until the meat is no longer pink and the onion is translucent.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC0E86B89DCF75409C6875E82C968203', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic to the meat mixture and cook, stirring, for 1 minute longer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76C6C2E52A0B9F449351A70FA8F9C274', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 4, NULL, NULL, 2, NULL, 0, 'Add the crushed tomatoes, tomato paste, basil, oregano, and crushed red pepper. Cook, stirring occasionally, for about 10 to 12 minutes. Taste and add salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7040ACB0A30CE4080A348EA759FCCF6', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 5, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ricotta cheese, 1/2 cup of Parmesan cheese, the egg, and 1 cup of Mozzarella cheese. Stir until well blended.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB4876D1DADE6442ABAB4CFD3BDEF32C', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 6, NULL, NULL, 2, NULL, 0, 'While the sauce is simmering, cook the ziti in boiling salted water following directions on the package. Drain well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C0048DF848AA24CBD9F18D2A24FBF48', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 7, NULL, NULL, 2, NULL, 0, 'Add the hot drained pasta to the bowl with the ricotta cheese mixture. Toss lightly to blend.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEFE461A052A4847971512DE18CECE86', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 8, NULL, NULL, 2, NULL, 0, 'Add 2 cups of the meat sauce to the ziti mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'596CEB301A0DB0498E4A9F00339BA57D', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 9, NULL, NULL, 2, NULL, 0, 'Transfer half of the pasta mixture into the prepared baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9385F4E7D3386A4BA05D344ADBEB1C5C', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 10, NULL, NULL, 2, NULL, 0, 'Spread about half of the meat sauce over the pasta, and then top with the remaining Parmesan cheese and half of the remaining Mozzarella.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B1CD5353E5FDB42A42C79C4B74C749A', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 11, NULL, NULL, 2, NULL, 0, 'Top with the remaining pasta mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99350019B94C244B963B100F12378A07', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 12, NULL, NULL, 2, NULL, 0, 'Then finish with the remaining meat sauce and remaining Mozzarella cheese and sprinkle with the chopped parsley.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9836E89679799B46931775906452FB85', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 13, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes, or until hot and bubbly around the edges.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13FEC5C96E8E344E9F38F268EB3D4CE1', X'A0DCFAD534BD444184D42618527D7E2A', X'A0DCFAD534BD444184D42618527D7E2A', 14, NULL, NULL, 2, NULL, 0, 'Serve with garlic bread and a tossed green salad or Caesar salad.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D776BD3EAFBE9438D24C1FF4B61B4A9', X'F91B6047B6117B4DB7131E8DDFD9F219', X'F91B6047B6117B4DB7131E8DDFD9F219', 0, NULL, NULL, 2, NULL, 0, 'Pour oil to a depth of 2` in a 6 quart Dutch oven. Heat over medium-high heat until your deep-fry thermometer reads 350°.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C079C36A3225D4CB8C1AB7B49E89D5B', X'F91B6047B6117B4DB7131E8DDFD9F219', X'F91B6047B6117B4DB7131E8DDFD9F219', 1, NULL, NULL, 2, NULL, 0, 'Divide mozzarella slices among 4 slices of bread. Season with salt and pepper, and cover with the remaining cheese slices to make 4 sandwiches. Trim the crusts.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26192395E7FDD447A893193D4317B48F', X'F91B6047B6117B4DB7131E8DDFD9F219', X'F91B6047B6117B4DB7131E8DDFD9F219', 2, NULL, NULL, 2, NULL, 0, 'Take out 3 bowls and place the flour in one bowl, the eggs (cracked and whisked) in another and the breadcrumbs in the last bowl. Dredge each sandwich in flour, coat in eggs and coat in breadcrumbs before putting on the frying pan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F01C502B5C86FC4289C1BD9B35A5C35F', X'F91B6047B6117B4DB7131E8DDFD9F219', X'F91B6047B6117B4DB7131E8DDFD9F219', 3, NULL, NULL, 2, NULL, 0, 'Fry until golden brown, about 1-2 minutes on each side.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF8197CCA16804469F1F96A2F576CB8D', X'F91B6047B6117B4DB7131E8DDFD9F219', X'F91B6047B6117B4DB7131E8DDFD9F219', 4, NULL, NULL, 2, NULL, 0, 'Let each sandwich rest for about a minute after it''s been removed from the oil, then spoon some sauce over the top and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B574E12624FB694DABFC5A7861A20019', X'D027FC6FA98D334FAF0D6B3F896B72C8', X'D027FC6FA98D334FAF0D6B3F896B72C8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8CD945DD9B561439696AD0FACF6C081', X'D027FC6FA98D334FAF0D6B3F896B72C8', X'D027FC6FA98D334FAF0D6B3F896B72C8', 1, NULL, NULL, 2, NULL, 0, 'Cut round bread loaf in half horizontally.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B2D6AB5B0D0BB41870A3B12716C655D', X'D027FC6FA98D334FAF0D6B3F896B72C8', X'D027FC6FA98D334FAF0D6B3F896B72C8', 2, NULL, NULL, 2, NULL, 0, 'Spread half the bottom portion with olive salad mix, then layer on meats and cheeses.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66ADD79CA7AC7343A509C41D10739225', X'D027FC6FA98D334FAF0D6B3F896B72C8', X'D027FC6FA98D334FAF0D6B3F896B72C8', 3, NULL, NULL, 2, NULL, 0, 'Cover with top of the bread. Ideally, the sandwich should be made an hour or more in advance and then tightly wrapped in plastic wrap to enable the juices to soak into the bread.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DA6ABB517E2F343BFE5D08671CC3574', X'D027FC6FA98D334FAF0D6B3F896B72C8', X'D027FC6FA98D334FAF0D6B3F896B72C8', 4, NULL, NULL, 2, NULL, 0, 'Slice into quarters and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11A7A5D345E3F440A3394CC8FAA8FB78', X'D027FC6FA98D334FAF0D6B3F896B72C8', X'D027FC6FA98D334FAF0D6B3F896B72C8', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45024BAFFCE4BF439F0121D505C0F728', X'9E6C064F167EB94081C50BF3C3B26CC2', X'9E6C064F167EB94081C50BF3C3B26CC2', 0, NULL, NULL, 2, NULL, 0, 'In a large, deep skillet or Dutch oven, heat the olive oil and butter over medium heat. Add the carrot, celery, and onion. Cook, stirring, until onion is softened and beginning to brown. Add the ground beef and sausage and cook, stirring and breaking up, until no longer pink. Add the garlic and cook, stirring, for 1 minute longer. Stir in the crushed tomatoes, tomato sauce, tomato paste, and water. Add the bay leaf and simmer over low heat for about 1 hour. Stir in 1 tablespoon of chopped parsley. Remove the bay leaf and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D003F16D60104D4CA1EC91FE62E6DE29', X'9E6C064F167EB94081C50BF3C3B26CC2', X'9E6C064F167EB94081C50BF3C3B26CC2', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74751B34EEEE3645B18C2F4DF474DF9F', X'9E6C064F167EB94081C50BF3C3B26CC2', X'9E6C064F167EB94081C50BF3C3B26CC2', 2, NULL, NULL, 2, NULL, 0, 'Lightly grease a 9-by-13-by-2-inch baking pan. .');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86363F096A1E734F9FE10FCCDC91A6FA', X'9E6C064F167EB94081C50BF3C3B26CC2', X'9E6C064F167EB94081C50BF3C3B26CC2', 3, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the ricotta cheese, Parmesan cheese, 1 tablespoon of parsley, and the large egg. Stir until smooth and well blended.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7783B77BC16E74449F07FF2BE2094687', X'9E6C064F167EB94081C50BF3C3B26CC2', X'9E6C064F167EB94081C50BF3C3B26CC2', 4, NULL, NULL, 2, NULL, 0, 'Cover the bottom of the prepared pan with a thin layer of the sauce. Cover the sauce layer with lasagna noodles. Top with about one-third of the remaining sauce and half of the ricotta mixture. Sprinkle with a generous layer of mozzarella cheese. Repeat with another layer of the noodles and another one-third of the sauce. Top with the remaining ricotta mixture and more mozzarella. Cover with another layer of lasagna noodles, then top with the remaining sauce and a layer of mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECA3F2AF43B16949A9A8E537B7F4D333', X'9E6C064F167EB94081C50BF3C3B26CC2', X'9E6C064F167EB94081C50BF3C3B26CC2', 5, NULL, NULL, 2, NULL, 0, 'Cover the pan loosely with foil, tenting somewhat so the foil won''t stick to the cheese as it melts.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC0101F2164C7645858D6645174A098B', X'9E6C064F167EB94081C50BF3C3B26CC2', X'9E6C064F167EB94081C50BF3C3B26CC2', 6, NULL, NULL, 2, NULL, 0, 'Bake for 45 minutes. Remove the foil and bake for 10 to 15 minutes longer, or until hot and bubbly and the cheese has melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8BE38DBA6D7EE42924C70F1351B4E18', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED65BD3649A4D4409B3AA3B8001E5E7D', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', 1, NULL, NULL, 2, NULL, 0, 'First turn your broiler on to high. Next, begin by smearing the tomato paste onto one slice of bread.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05298CE2DCF8D84ABD76266099A96A5A', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', 2, NULL, NULL, 2, NULL, 0, 'Top with mozzarella and the remaining piece of bread and butter the outside. Sprinkle each side with garlic powder and salt and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4172E117513F64995350741D9894E81', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', 3, NULL, NULL, 2, NULL, 0, 'Heat a small frying pan over medium heat and add the sandwich. Let it cook for a few minutes on each side until the entire exterior is light golden brown and the cheese is mostly melted. At this point remove from the frying pan and carefully place the hot sandwich onto a baking sheet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59F127DC293410418DB6F20805C442C6', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', 4, NULL, NULL, 2, NULL, 0, 'On the lighter colored of the two sides, smear on the tomato paste and add torn basil leaves. Top with asiago and parmesan and pepperoni. Sprinkle with a little bit of red pepper flakes and put it in the oven and let it broil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42F62A06A9E9574E946EA04661DB5E09', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', X'2F1CADBBA745FD4EAC8D25B1B6E74F22', 5, NULL, NULL, 2, NULL, 0, 'Once the cheese starts to bubble and the top resembles pizza, it''s time to take it out of the oven. Be sure to let it sit for a few minutes before slicing into it - so that the cheese doesn''t ooze out all over the place! Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E379FB538A8C4C9501FCF1F4610E9A', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 0, NULL, NULL, 2, NULL, 0, 'Place rice, stock, and olive oil in a large pot and bring to a boil. Reduce heat to low and cover, cooking for 20 minutes. Remove from heat and add the butter and parmesan cheese. Stir and add more broth if it is too dry. Cover and let sit off of the heat for 10 minutes. Allow the rice to cool to room temperature before beginning to make the arancini. You can make the risotto ahead of time and store in the refrigerator until you are ready to assemble.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90CF8FBDB091254E8BA1F4B83C5F3AAC', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 1, NULL, NULL, 2, NULL, 0, 'Take a golf ball sized scoop of risotto rice, make a well in the center and place the cheese inside. Wrap the rice around the piece of cheese and place on a parchment lined baking tray. Repeat with remaining rice and cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B85277BD0B9C994BAEDA07DF9F9A5B20', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 2, NULL, NULL, 2, NULL, 0, 'Freeze the balls on the baking tray for 2-3 hours or overnight.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'705996F1A3CE0C4AAA418C1A7EB08C4F', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 3, NULL, NULL, 2, NULL, 0, 'Place the flour in a low dish, add 1/2 tsp of salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62DD220BC4CB8D4688AD9F6C869F830D', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 4, NULL, NULL, 2, NULL, 0, 'Place eggs in another low dish with the milk. Whisk together and add a 1/2 tsp of salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FA0EBCE70E71349A5EFE267E12D4E3A', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 5, NULL, NULL, 2, NULL, 0, 'Place the breadcrumbs in another low dish and add 1/2 tsp of salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFCA1911F7394A4F9480AB5FF8409E27', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 6, NULL, NULL, 2, NULL, 0, 'Dip the rice ball into the flour mixture and coat. Then dip into the egg mixture and coat. Then dip it into the Panko bread crumbs and roll to coat completely. Repeat with the remaining rice balls.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7280DA0FE339443899C3495CE7629BE', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 7, NULL, NULL, 2, NULL, 0, 'Heat the oil in a heavy bottomed, deep pot on high until it is about 375 F. Turn it down to medium heat and add in a few rice balls at a time to cook.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCB3E780B3A481498216663F93E491F0', X'A67B59774B74F24AAA53958F1059FC95', X'A67B59774B74F24AAA53958F1059FC95', 8, NULL, NULL, 2, NULL, 0, 'Rotate them in the oil as they are cooking to ensure browning on all sides. Remove the rice balls from the oil when they are golden brown. Place the rice balls on another parchment lined baking dish and keep in a 250 F oven while cooking the remaining arancini.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'826C9488AE8E284193739CC797C3F38E', X'9F45C3D7EB444B4AB4C86607C341A063', X'9F45C3D7EB444B4AB4C86607C341A063', 0, NULL, NULL, 2, NULL, 0, 'Peel the onion and chop it coarsely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F40A5B7D3C9E2F4BB80677A5F82476DF', X'9F45C3D7EB444B4AB4C86607C341A063', X'9F45C3D7EB444B4AB4C86607C341A063', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Add the onion to the skillet and cook for about 2 minutes, stirring constantly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53BECBB7FF2F4C4C8910311FDA48A962', X'9F45C3D7EB444B4AB4C86607C341A063', X'9F45C3D7EB444B4AB4C86607C341A063', 2, NULL, NULL, 2, NULL, 0, 'Crumble the ground beef into the skillet and cook until it is no longer pink, stirring frequently; drain well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38A68D8B321ACF4EBE27FF13D49A4137', X'9F45C3D7EB444B4AB4C86607C341A063', X'9F45C3D7EB444B4AB4C86607C341A063', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic, tomato sauce, tomato paste, salt and oregano and basil to the skillet. Continue cooking until the mixture begins to simmer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA15431F118FE84CB431D5A693693F46', X'9F45C3D7EB444B4AB4C86607C341A063', X'9F45C3D7EB444B4AB4C86607C341A063', 4, NULL, NULL, 2, NULL, 0, 'Spoon a layer of meat sauce onto the bottom of the slow cooker. Add a double layer of broken lasagna noodles and top with a layer of the cheeses. Repeat the layers with more sauce, noodles, and cheeses, ending with cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2006967D642C1547B8D745F221B1AB6E', X'9F45C3D7EB444B4AB4C86607C341A063', X'9F45C3D7EB444B4AB4C86607C341A063', 5, NULL, NULL, 2, NULL, 0, 'Cover the crock pot and cook on low for 6 to 8 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66E152C6A9062B489BD65F21A4B01082', X'090EAC86CAB66A499D503EC3F5BFA3AF', X'090EAC86CAB66A499D503EC3F5BFA3AF', 0, NULL, NULL, 2, NULL, 0, 'In a saucier or saucepan, heat 1 tablespoon extra virgin olive oil over medium heat. Add onions and saute, occasionally stirring with a wooden spoon, until onions are soft and translucent about 5 minutes. Add garlic and cook, continually stirring, for 30 seconds. Add tomatoes, tomato paste, and Italian herbs and bring to a simmer, stirring occasionally. Continue to simmer over medium-low heat while you prepare the rest of the lasagna ingredients  season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4475F21D6B057F458A688FDAA9CBD25C', X'090EAC86CAB66A499D503EC3F5BFA3AF', X'090EAC86CAB66A499D503EC3F5BFA3AF', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350°F. In a medium skillet, heat olive oil. Add mushrooms and saute over medium heat, occasionally stirring, until mushrooms are softened and brown, about 7 to 9 minutes. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48F51177C93EA544AC3780D16D984A42', X'090EAC86CAB66A499D503EC3F5BFA3AF', X'090EAC86CAB66A499D503EC3F5BFA3AF', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, combine ricotta cheese, eggs, parmesan cheese, 1/2 teaspoon salt and a generous pinch of black pepper. Set aside. In a separate bowl, combine the spinach and broccoli.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18BC973AACFBCB468F46758A15F535B7', X'090EAC86CAB66A499D503EC3F5BFA3AF', X'090EAC86CAB66A499D503EC3F5BFA3AF', 3, NULL, NULL, 2, NULL, 0, 'In a glass or ceramic baking dish measuring approximately 9x13 inches, spread a generous ladleful of tomato sauce along with the bottom with a spatula. Place a single layer of lasagna noodles on the sauce (you''ll probably need four, and they can overlap slightly to fit the pan). Spread about one-third of the ricotta mixture on the noodles, and then evenly spread the spinach-broccoli mixture over the ricotta. Sprinkle with about one-third of the shredded mozzarella cheese. Add another layer of noodles, then another third of the ricotta mixture, and evenly layer the zucchini slices over the ricotta. Top evenly with a thin layer of sauce and a sprinkling of mozzarella cheese. For the final layer, add one more row of lasagna noodles, the remaining ricotta cheese, the mushrooms, the remaining tomato sauce, and the remaining mozzarella cheese. Bake in a 350°F oven for 45 to 50 minutes, until cheese is melting and bubbling. Remove from oven and let stand for 15 minutes before cutting and serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54078FBB422F6246B8C8BEAE08949A03', X'090EAC86CAB66A499D503EC3F5BFA3AF', X'090EAC86CAB66A499D503EC3F5BFA3AF', 4, NULL, NULL, 2, NULL, 0, 'For the final layer, add one more row of lasagna noodles, the remaining ricotta cheese, the mushrooms, the remaining tomato sauce, and the remaining mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'299EBC9E59D4064C8E24AF28B7EC6065', X'090EAC86CAB66A499D503EC3F5BFA3AF', X'090EAC86CAB66A499D503EC3F5BFA3AF', 5, NULL, NULL, 2, NULL, 0, 'Bake in a 350°F oven for 45 to 50 minutes, until cheese is melting and bubbling. Remove from oven and let stand for 15 minutes before cutting and serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFCB02DDFF454849982AF0FE552FFEB0', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 375 F. Lightly oil a baking pan or spray with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E11D76CCDB38642A1208330C022A31E', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 1, NULL, NULL, 2, NULL, 0, 'Put the chicken, one piece at a time, in a food storage bag and pound gently with a meat mallet or other heavy object until about 1/4 inch thick. Repeat with all of the chicken breasts. Sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E40AFEB25A827C46B4B48BA0694532B5', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 2, NULL, NULL, 2, NULL, 0, 'Saute the garlic in butter for about 1 minute, just to soften. Combine the garlic with basil leaves. Spread basil mixture over each chicken breast; top with a slice of Mozzarella cheese or a little of the shredded Mozzarella cheese. Roll the chicken up and secure with toothpicks.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84F6F511701BD841BC982BB45DC54A21', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 3, NULL, NULL, 2, NULL, 0, 'Beat the egg in a shallow bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6E8C580D9461044B12C9F00BEB428B5', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 4, NULL, NULL, 2, NULL, 0, 'In a wide bowl, combine the breadcrumbs and parsley.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56C453F17CAD5D45AFC6E80898087569', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 5, NULL, NULL, 2, NULL, 0, 'Carefully dip the rolled chicken breasts in the egg; turning to coat well, then coat with breadcrumbs. Arrange the chicken breast rolls on the baking sheet. Bake for 30 to 40 minutes, or until the chicken is thoroughly cooked.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F273B4DE4C0A4BA7D0B36F13009137', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 6, NULL, NULL, 2, NULL, 0, 'Heat 3 tablespoons of butter over medium-low heat. Add the mushrooms and cook until tender and browned. Add garlic, green onions, 1/4 teaspoon salt, and a dash of pepper. Cook, stirring, for 1 minute longer; add the flour and stir until well blended. Add the chicken broth and simmer for about 2 minutes. Add cream and heat through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2F542FA30A7354E8730883447E0C879', X'02312723F1367041AC50FC6AA69D147B', X'02312723F1367041AC50FC6AA69D147B', 7, NULL, NULL, 2, NULL, 0, 'Serve the chicken with the sauce, along with noodles or rice and a side vegetable.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55F6FEB4F65F3148972C1768E0F2737F', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C5CB182C7F58D4EBE4065FC2DBBC3C9', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'167DA0C08AD530429075D9F089F215DD', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 2, NULL, NULL, 2, NULL, 0, 'Grease a 2- to 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C06FD29D76D0894593E2024204D590B9', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 3, NULL, NULL, 2, NULL, 0, 'Cook the penne pasta in boiling salted water following package directions. Drain in a colander and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83BA583E7D5E9740BF36728E2860414F', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Add the chicken, mushrooms, and Creole seasoning.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACBFF12611434342964AD8AD5592AE58', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 5, NULL, NULL, 2, NULL, 0, 'Cook until the chicken is thoroughly cooked and the mushrooms are lightly browned, stirring frequently.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B76D8C1588EF2544ABF244D373C283EE', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 6, NULL, NULL, 2, NULL, 0, 'Remove the chicken and mushroom mixture to a bowl and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C66FD2EABD0E194BA506AF4D704E67D9', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 7, NULL, NULL, 2, NULL, 0, 'Melt the butter in the same skillet and add the minced garlic. Cook, stirring, for about 1 minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6236D63E157BEB43943E863F454B6260', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 8, NULL, NULL, 2, NULL, 0, 'Blend the flour into the butter mixture until smooth and bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'168C0E2D6B74ED42B1086D33ADC63027', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 9, NULL, NULL, 2, NULL, 0, 'Continue to cook the flour and butter roux for 1 minute, stirring constantly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A0FDAEBA04ACF41A2D01ABF3A7F28C8', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 10, NULL, NULL, 2, NULL, 0, 'Add the milk and chicken broth and continue to cook until thickened, stirring constantly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13879F9CB4CCE74EA3C8D812FBECFB78', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 11, NULL, NULL, 2, NULL, 0, 'To the sauce, add 1 1/2 cups of the mozzarella cheese and about half of the Parmesan cheese; cook until the cheese has melted, stirring constantly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'236523F0728EBC4F847ADF51C9E1BB1A', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 12, NULL, NULL, 2, NULL, 0, 'Stir in the chicken and mushroom mixture and the artichokes. Taste and add salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF6D8F77C31E0042A0EFF51BFEE59121', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 13, NULL, NULL, 2, NULL, 0, 'Combine the sauce and pasta; transfer to the prepared baking dish. Sprinkle with the remaining cheeses and sprinkle lightly with paprika, if using.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4B8D371901E254386DE45923FE37E45', X'5872BB85E794FF4FA2FAA43A045F7741', X'5872BB85E794FF4FA2FAA43A045F7741', 14, NULL, NULL, 2, NULL, 0, 'Bake for about 20 to 30 minutes, until lightly browned and bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A94A75106549934291508F316A084D40', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6CFB3A2A1D18A468A16F9B36C5F3957', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 425 F. Line a baking sheet with nonstick foil or parchment paper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A69BA393588C14AA58A1863836DC3C2', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', 2, NULL, NULL, 2, NULL, 0, 'Brush cut sides of French bread with melted butter. Sprinkle lightly with onion powder and garlic powder. Top with blue cheese or Gorgonzola cheese, tomatoes, chives or green onions, mozzarella, and parsley.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'181ADE109221CD469151E5C5287EAF0F', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', 3, NULL, NULL, 2, NULL, 0, 'Bake about 10 to 15 minutes, until bread is crusty on the outside and the top is melted and just beginning to turn golden.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE262A102F627241A704FF55F5A35662', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', 4, NULL, NULL, 2, NULL, 0, 'Serve as is for an appetizer or serve it as an accompaniment for a classic spaghetti dinner or any other pasta dish like linguine with clam sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2B3FAEC6501C941A719A0E0B3EAFF63', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', X'0E207AB0EDC9FF40ADB5CFAD3595CE03', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE7A5F75B0566449B3C2AAA4B89E64FD', X'4510CCB719995B42BAB9FA253674B745', X'4510CCB719995B42BAB9FA253674B745', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ADD17ED0D71924A9ED6D115280863A8', X'4510CCB719995B42BAB9FA253674B745', X'4510CCB719995B42BAB9FA253674B745', 1, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, beat the eggs then add the cottage cheese and Parmesan cheese. Spray a 13 x 9-inch glass baking dish with non-stick cooking spray or a homemade olive oil spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9AEB6DDC1BCC14884989D4507CC1204', X'4510CCB719995B42BAB9FA253674B745', X'4510CCB719995B42BAB9FA253674B745', 2, NULL, NULL, 2, NULL, 0, 'Put one layer of the slightly cooked lasagna noodles flat across the bottom of the baking dish. Add about half of the spinach, pressing down lightly and evenly over the noodles. Top with another layer of lasagna noodles. Top this layer of noodles with the cottage cheese mixture. Add the remaining spinach then the last of the lasagna noodles, laying evenly on top of spinach.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1598DB71134B6240BFC8CCD44F8A469C', X'4510CCB719995B42BAB9FA253674B745', X'4510CCB719995B42BAB9FA253674B745', 3, NULL, NULL, 2, NULL, 0, 'Once you''ve layered everything, spread the pasta sauce evenly over the top and sprinkle the whole thing with mozzarella cheese. Press down lightly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8245D42CA6D92E45856E556AC903BD79', X'4510CCB719995B42BAB9FA253674B745', X'4510CCB719995B42BAB9FA253674B745', 4, NULL, NULL, 2, NULL, 0, 'Cover baking dish with foil, using foil sprayed with cooking spray and keeping foil off the center of the lasagna. Secure sides tightly over the baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A19150AF6F253A4E961E28AE3F31C437', X'4510CCB719995B42BAB9FA253674B745', X'4510CCB719995B42BAB9FA253674B745', 5, NULL, NULL, 2, NULL, 0, 'Bake about 1 hour 15 minutes in the preheated oven. To lightly brown the top, remove foil for a few minutes at the end of baking time.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'668EFC607C9CEB4D9E1B5E48C8F08D93', X'4510CCB719995B42BAB9FA253674B745', X'4510CCB719995B42BAB9FA253674B745', 6, NULL, NULL, 2, NULL, 0, 'Allow to rest for 10 to 15 minutes before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A055A2F65E39254B9C3A7E6BAE953BA8', X'7CC60851139F5946B0797B387B0F0076', X'7CC60851139F5946B0797B387B0F0076', 0, NULL, NULL, 2, NULL, 0, 'In a saucepan, combine tomatoes, garlic, bay leaf, thyme, cayenne pepper, olive oil, sugar, and 1/2 teaspoon salt. Bring to a boil; reduce heat, cover, and simmer for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1977BDCEDBBC904B80BE2E119B921321', X'7CC60851139F5946B0797B387B0F0076', X'7CC60851139F5946B0797B387B0F0076', 1, NULL, NULL, 2, NULL, 0, 'Cook lasagna noodles in boiling salted water for about 10 minutes, or until just tender. Drain and run cold water over the noodles to stop the cooking; pat dry.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62274E920BA1E645A3C1DB67A9CF2E9B', X'7CC60851139F5946B0797B387B0F0076', X'7CC60851139F5946B0797B387B0F0076', 2, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D34CE761C5A87468157FEAA9BCAC0D2', X'7CC60851139F5946B0797B387B0F0076', X'7CC60851139F5946B0797B387B0F0076', 3, NULL, NULL, 2, NULL, 0, 'In a cup, stir a few tablespoons of cold water into the 3 tablespoons flour. Stir until a smooth paste forms. Place chicken broth and milk in a saucepan and heat. Add flour mixture to the liquid; add salt and pepper to taste, then stir in parsley. Continue to cook, stirring until mixture is thickened and bubbling.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23218512D5228A43A6E71B225157111A', X'7CC60851139F5946B0797B387B0F0076', X'7CC60851139F5946B0797B387B0F0076', 4, NULL, NULL, 2, NULL, 0, 'Pour a thin layer of the tomato sauce mixture into a 9-by-13-by-2-inch baking pan. Top with a layer of lasagna noodles placed side-by-side. Add a layer of chicken; sprinkle some of the mozzarella cheese over chicken. Top with a layer of the white sauce. Add another layer of lasagna, more tomato sauce, another layer of chicken, mozzarella cheese, and white sauce; repeat until all ingredients are used or the baking dish is full, finishing with a layer of lasagna noodles. Sprinkle with the grated Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72E76E5B946D3F43A7E70851A7EE0806', X'7CC60851139F5946B0797B387B0F0076', X'7CC60851139F5946B0797B387B0F0076', 5, NULL, NULL, 2, NULL, 0, 'Bake at 375 F for 25 to 35 minutes, or until browned and bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A031577A738CF4ABFB2B4A201E985D6', X'7CC60851139F5946B0797B387B0F0076', X'7CC60851139F5946B0797B387B0F0076', 6, NULL, NULL, 2, NULL, 0, 'Makes about 6 servings.');
