INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56BEA21CB0F2E948BA40D4B8A0E47927', X'B0CEBF1955301B41AB48491B70BCF47D', X'B0CEBF1955301B41AB48491B70BCF47D', 2, NULL, NULL, 2, NULL, 0, 'Drizzle the inside with Italian extra virgin olive oil and balsamic glaze.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B56B6400BE84E449150A6C7E89F6A36', X'B0CEBF1955301B41AB48491B70BCF47D', X'B0CEBF1955301B41AB48491B70BCF47D', 3, NULL, NULL, 2, NULL, 0, 'Add basil and tomato to one side and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81DD050E430FBC489BB1465BE1880ADB', X'B0CEBF1955301B41AB48491B70BCF47D', X'B0CEBF1955301B41AB48491B70BCF47D', 4, NULL, NULL, 2, NULL, 0, 'Top with fresh mozzarella and close sandwich.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77BC5CEAC58F6B459F932F5B8BE79AF5', X'B0CEBF1955301B41AB48491B70BCF47D', X'B0CEBF1955301B41AB48491B70BCF47D', 5, NULL, NULL, 2, NULL, 0, 'Eat now or wrap tightly to enjoy later.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6509CBD71BD9E849A1F3FD5A114B92B4', X'3A07C850AC93AD45A115BD41403CB5AB', X'3A07C850AC93AD45A115BD41403CB5AB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2D9A57207BE624AA1984E86C1E94D7E', X'3A07C850AC93AD45A115BD41403CB5AB', X'3A07C850AC93AD45A115BD41403CB5AB', 1, NULL, NULL, 2, NULL, 0, 'Cook pasta according to package instructions, then drain well. Set aside.');
