INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB643A3C92978742BB9AD0A1EA632CAA', X'3A07C850AC93AD45A115BD41403CB5AB', X'3A07C850AC93AD45A115BD41403CB5AB', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
