INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0F1D4E8F55F2C44929E8F073C37AAB2', X'3A07C850AC93AD45A115BD41403CB5AB', X'3A07C850AC93AD45A115BD41403CB5AB', 5, NULL, NULL, 2, NULL, 0, 'Cover with foil and bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E0C5343F6F604478AE62EAB69FB8C57', X'3A07C850AC93AD45A115BD41403CB5AB', X'3A07C850AC93AD45A115BD41403CB5AB', 6, NULL, NULL, 2, NULL, 0, 'Remove cover and bake for an additional 10 minutes, until golden brown and hot throughout.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9D30E7B39823B43A9E5551D9A6B9C53', X'3A07C850AC93AD45A115BD41403CB5AB', X'3A07C850AC93AD45A115BD41403CB5AB', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E32E6872D1C77F46AC8C5AB0A9E6E9FA', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5F9D3350A5E004AAEE43C946557B827', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F. Spray a 9-by-13-inch glass baking dish with nonstick baking spray containing flour and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6769E559F95E6348B4651729A2EBC43B', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 2, NULL, NULL, 2, NULL, 0, 'Bring a large pot of salted water to a boil. Add the penne to the water and cook according to the package directions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7364C983C87BFD46AF3669FBFBCE83FA', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large saucepan, melt the butter over medium heat. Add the onion and garlic and cook until tender, about 5 to 7 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63D89F219AD3DE4C9F30DDE5BDC89DD3', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 4, NULL, NULL, 2, NULL, 0, 'When the pasta is done, drain well and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E289EA5CBBDB74B991D56905E4F912A', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 5, NULL, NULL, 2, NULL, 0, 'Add the Alfredo sauce and milk to the onion mixture and heat until steaming.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B5CF104DAE4EA4193FF313D282C9DCA', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 6, NULL, NULL, 2, NULL, 0, 'Add the Havarti, provolone, mozzarella, and 1/2 cup Parmesan cheese to this mixture and cook over low heat until cheese melts.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20FFD5E85F6AFB498978790DEC302F2F', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 7, NULL, NULL, 2, NULL, 0, 'Stir in drained pasta and the meatballs.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBF7A9C807DB7A43A5E9DA2DE88D10F8', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 8, NULL, NULL, 2, NULL, 0, 'Pour the mixture into the prepared dish. Top with the remaining 1/4 cup Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD27B494C9C2A74EA60B5BF676B95BD3', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 9, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes or until the casserole is bubbly around the edges and the cheese starts to brown on top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79E44A628C53994A805D98B73A9CB60B', X'E92A771E2F5DA64497A95215AD1ABF88', X'E92A771E2F5DA64497A95215AD1ABF88', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38734E76DF39C8468A5DF77118042D27', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC5644BA356DA242A2A58C7B91F2B5A2', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', 1, NULL, NULL, 2, NULL, 0, 'Combine the salt, seasoned salt, chili powder, and pepper in a small bowl. Trim the pork chops and discard any excess fat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'415AED9910E07946AC7EE52F1A6142E3', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in ?a skillet over medium-high heat; sear the pork chops for about 3 to 4 minutes on each side, seasoning both sides with 1 teaspoon of the seasoning mixture as they cook.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'474BEA9564285D4A81DDA7A0F16A5E18', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', 3, NULL, NULL, 2, NULL, 0, 'Drain off any excess fat, leaving the pork chops in the skillet. Add the rice to the skillet along with the tomatoes, green bell pepper, onion, olives, and sugar. Arrange sliced zucchini around the edge of the skillet and sprinkle with remaining seasoning mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36CC22659E825043A59A2FAE46FDB376', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook, stirring occasionally, for about 30 minutes, or until the pork chops are cooked through (at least 145 F/63 C) and the rice is tender. If you are using brown rice, allow extra time.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E66C81965AB21549A6CBA8E7C5773046', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', 5, NULL, NULL, 2, NULL, 0, 'Top with the mozzarella cheese and cover the pan. Cook for about 2 minutes longer, or until the cheese has melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55089A53E8E4B549BBC3076273A73339', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', X'B2F1CD6CA67497429FB7EC8BE2F9F26D', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3DDEA581548F0438F443B197B017CEB', X'B4825144C5ED7D408C27D448CBB48446', X'B4825144C5ED7D408C27D448CBB48446', 0, NULL, NULL, 2, NULL, 0, 'Use a serrated knife to cut the Italian sub roll in half. Place sub roll on butcher or wax paper and drizzle both sides with a really nice olive oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E187DC400B8A984DB62C0A629CE12870', X'B4825144C5ED7D408C27D448CBB48446', X'B4825144C5ED7D408C27D448CBB48446', 1, NULL, NULL, 2, NULL, 0, 'Add mozzarella to one-half and sprinkle with salt. Next, add roasted red peppers, genoa salami, and a handful of fresh washed basil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3865DA32D451E949A683C450F95C99D8', X'B4825144C5ED7D408C27D448CBB48446', X'B4825144C5ED7D408C27D448CBB48446', 2, NULL, NULL, 2, NULL, 0, 'Close the sandwich and tightly wrap the butcher or wax paper around it. Roll and cut down the middle. Serve immediately or wrap it up for later. ?The sandwich is good for 24 hours if refrigerated properly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F9EC0063A525F4784B090BF8FBCD33E', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB92129EEDCF2C4D841FB164197984FE', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F/200 C/Gas 6.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFFD19AEED154A46880292EF78F0BEC1', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 2, NULL, NULL, 2, NULL, 0, 'Line a baking sheet with foil; spray with olive oil spray or nonstick cooking spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EE22BDA0386444982BFB855973ABBF5', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 3, NULL, NULL, 2, NULL, 0, 'In a food processor, grind the breadcrumbs, Parmesan, basil, parsley, a dash of pepper, and 1/4 teaspoon salt until fine. Pour this mixture onto a plate or shallow bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'410DD939C2E57E4B865FAF30B610D204', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 4, NULL, NULL, 2, NULL, 0, 'Pound the chicken breasts halves lightly to flatten them to a uniform thickness. (You can avoid some kitchen mess and contaminating your surfaces if you place the breasts between sheets of plastic wrap before gently pounding them with a mallet or rolling pin.) If you are using thin cutlets, there should be no need to pound them.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0384C54EFFCF64AA653E81D63C10D73', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 5, NULL, NULL, 2, NULL, 0, 'Whisk the egg with the milk in a shallow bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8F1FB755B20544FBF4DE58484632CFD', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 6, NULL, NULL, 2, NULL, 0, 'Dip chicken pieces in egg mixture then coat them with the breadcrumb mixture. Arrange the chicken pieces on the baking sheet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79C607798AF5E147A04DAC32D92B5195', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 7, NULL, NULL, 2, NULL, 0, 'Bake the chicken for 15 minutes, then turn the chicken over and bake for 10 minutes longer. To ensure the chicken is done, check with an instant-read thermometer. According to the USDA, the minimum safe temperature for chicken is 165 F. Remove the chicken from the oven and reduce the oven temperature to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB9190EBB79A464086747909F9F228B9', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 8, NULL, NULL, 2, NULL, 0, 'In an 11-by-7-inch baking dish, spread about one-third of the spaghetti sauce. Arrange the cooked chicken in the sauce; pour remaining sauce over chicken. Sprinkle with Mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1949B57A2C76684C9B9E5DE15A356A83', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 9, NULL, NULL, 2, NULL, 0, 'Place the pan into the oven and bake for 15 to 20 minutes longer, or until the sauce is bubbly and cheese is melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A8FA92523A14B478D7977EECFB211EC', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 10, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the spaghetti in boiling salted water following the package directions. Drain the pasta thoroughly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'587158A202BEE740B2D31146141829FA', X'0868B11C97AFA240B80A1DF449E8D8BD', X'0868B11C97AFA240B80A1DF449E8D8BD', 11, NULL, NULL, 2, NULL, 0, 'Serve the chicken and sauce with the hot drained spaghetti. Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF21E55BC28C954EB3269FF53326CD27', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', 0, NULL, NULL, 2, NULL, 0, 'Fold a 30-inch long piece of foil in half lengthwise. Place in bottom of a slow cooker with both ends hanging over top edge of cooker. I also put a piece of crumpled foil in the bottom to keep the meatloaf out of the fat. The foil strip will make it much easier to remove the meatloaf when it''s done.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5B53F4E3F949E4B8AD4F26E1BA57BB2', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix ground beef with bread crumbs, 1/2 cup spaghetti sauce, egg, onion, salt, garlic powder, Italian herbs, and pepper until well blended. Shape into a loaf. Place in slow cooker. Spread 3 tablespoons spaghetti sauce over the loaf.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0EDB6FE9DC9794B8E444E9B21B1DBF4', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on HIGH for 2 1/2 to 3 hours or on LOW for 5 to 6 hours, or until juices run clear. Top loaf with cheese slices, overlapping, and continue to cook (turn to HIGH if necessary) for 15 to 25 minutes longer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93883264CB9E3444A9F02F93B5C410B4', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', X'CBA821A3CF2BFE41BE5B8CFE11A0F152', 3, NULL, NULL, 2, NULL, 0, 'Use ends of foil to lift out meat loaf and transfer to a serving platter.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'339CD6D0B58C5C4AA1106281406B6EC2', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C140B4731C88DC4C90BD0B78AEDDDF17', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 1, NULL, NULL, 2, NULL, 0, 'Cut the large tomatoes into approximately 1/4-inch slices. Use a serrated knife for the cleanest cuts.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C6B27610EDDF0488B451157730A3F73', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 2, NULL, NULL, 2, NULL, 0, 'Cut the fresh mozzarella cheese into 1/4 inch slices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7589276BACCA04DBE71BC740088C79E', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 3, NULL, NULL, 2, NULL, 0, 'Salt the sliced tomatoes generously with kosher salt and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10D47192B1752B48903C0424C80E8047', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the balsamic vinegar and sugar in a small saucepan. Cook on medium low heat and simmer for 5 to 10 minutes or until thickened. Stir occasionally as it cooks and don''t leave it unattended because it can easily scorch.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80F90628FA8AC14BA60167545F7BF0E9', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 5, NULL, NULL, 2, NULL, 0, 'Layer a salted tomato, then one to two basil leaves, followed by a slice of mozzarella.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07D11783FEE5294E88D8C3606A0F427F', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 6, NULL, NULL, 2, NULL, 0, 'Keep layering these ingredients until you have filled up your platter. Drizzle with the olive oil, and more salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FD639FFC62A3A4D9D161E67ADD41634', X'9EFA947A1F5C24419ECEE1975F13B068', X'9EFA947A1F5C24419ECEE1975F13B068', 7, NULL, NULL, 2, NULL, 0, 'Drizzle the balsamic vinegar glaze over the top of the salad and serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA712AC5F6B3BD4BB0CE95319E92304B', X'8D941742A314C048B20450F7F2C87971', X'8D941742A314C048B20450F7F2C87971', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23A21206AC707C47ACFFA13895B50CD8', X'8D941742A314C048B20450F7F2C87971', X'8D941742A314C048B20450F7F2C87971', 1, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a heavy skillet over medium heat. When the oil is hot, add the veal. Cook the veal for about 4 to 6 minutes, turning to brown both sides. Remove the veal and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A136B203ECB2E84E9CD7925ACFBD8836', X'8D941742A314C048B20450F7F2C87971', X'8D941742A314C048B20450F7F2C87971', 2, NULL, NULL, 2, NULL, 0, 'Add the onion and green bell pepper to the skillet and cook until tender. Stir in the wine, tomatoes, tomato sauce, tomato paste, parsley, and oregano; blend well. Add the veal back to the pan and bring to a simmer. Reduce the heat to low, cover the pan, and simmer for 30 minutes, stirring occasionally. Add the Parmesan cheese and stir to blend.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96C32B7955230B47B0EE22780E4338E2', X'8D941742A314C048B20450F7F2C87971', X'8D941742A314C048B20450F7F2C87971', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the spaghetti in boiling salted water following the package directions; drain thoroughly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79AB4CDF06B6384A9293819DBE808015', X'8D941742A314C048B20450F7F2C87971', X'8D941742A314C048B20450F7F2C87971', 4, NULL, NULL, 2, NULL, 0, 'Mix about half of the sauce into the hot drained spaghetti. Put the spaghetti into a baking pan (approximately 13 x 9 x 2 inches). Top the spaghetti with veal cutlets and the remaining sauce. Top with the cheese slices or an equal amount of shredded mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'607BD1F7BEDABE4B97296036C9662E35', X'8D941742A314C048B20450F7F2C87971', X'8D941742A314C048B20450F7F2C87971', 5, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 10 to 15 minutes, until cheese is melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F14801A97BEB9F4589C88D814CFABA95', X'8D941742A314C048B20450F7F2C87971', X'8D941742A314C048B20450F7F2C87971', 6, NULL, NULL, 2, NULL, 0, 'Serve the dish with a tossed green salad and garlic bread, and pass the Parmesan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36C0E4C4A7400A46906E36DB3A77BC18', X'7D5E284CAC31D84389026D7E04A24506', X'7D5E284CAC31D84389026D7E04A24506', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Warm the oil over medium-high heat in a ?chef''s pan or large, deep skillet. Add the garlic and saute for 30 seconds or until fragrant. Add the spinach and saute until it is thawed and heated through, about 5 to 7 minutes. Remove from the heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AA62B42B9B3B84F9F5D0C90A7DE5BA8', X'7D5E284CAC31D84389026D7E04A24506', X'7D5E284CAC31D84389026D7E04A24506', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the spinach, ricotta, and a generous pinch of nutmeg. Season with a few grinds of black pepper and a little salt if desired. If your marinara sauce is salty, you may wish to skip the salt in the cheese mixture. Stir in 1/4 cup of the shredded mozzarella.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F81DD312DB38594B85FF3C0304B03420', X'7D5E284CAC31D84389026D7E04A24506', X'7D5E284CAC31D84389026D7E04A24506', 2, NULL, NULL, 2, NULL, 0, 'Place about 1/2 cup of marinara sauce in the bottom of an 8x10x2-inch (or similarly sized) baking dish. Spread the sauce over the bottom of the dish and top with 3 noodles, overlapping slightly. Top the noodles with half of the spinach-ricotta mixture, spreading to cover them evenly. Sprinkle with 1/2 cup of mozzarella. Spoon 1/2 to 3/4 cup sauce over the cheeses, spreading gently with the back of the spoon.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE821AF55BF47E46A9B9470C277EF506', X'7D5E284CAC31D84389026D7E04A24506', X'7D5E284CAC31D84389026D7E04A24506', 3, NULL, NULL, 2, NULL, 0, 'Repeat the layering with 3 more noodles, the rest of the ricotta mixture, 1/2 cup of mozzarella, and 1/2 to 3/4 cup of sauce. Top with 3 more noodles. Spoon over enough of the remaining sauce to completely cover the noodles, spreading to the edges to make sure they are coated. Sprinkle evenly with the rest of the mozzarella.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F66787D2DA405344AEE308A3BB2204CD', X'7D5E284CAC31D84389026D7E04A24506', X'7D5E284CAC31D84389026D7E04A24506', 4, NULL, NULL, 2, NULL, 0, 'Cover the dish with foil and bake in the preheated oven for 40 minutes, or until the sauce is bubbly and the noodles are easily pierced with a knife. Remove the foil and bake for 10 minutes more. Remove the lasagna from the oven and allow to rest for 5 to 10 minutes before serving. Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'782203854FB3864F9B490C1DABC2FD73', X'108F4811B80C0143BEC6F977C1BBBC3D', X'108F4811B80C0143BEC6F977C1BBBC3D', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Add the ground beef and cook, stirring, until no longer pink. Add the pepperoni and cook, stirring, for about 2 minutes longer. With a slotted spoon remove the beef and pepperoni to the slow cooker.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC54C5772A398B43848530E64C9AF5BE', X'108F4811B80C0143BEC6F977C1BBBC3D', X'108F4811B80C0143BEC6F977C1BBBC3D', 1, NULL, NULL, 2, NULL, 0, 'Add the onion and bell pepper to the skillet and cook, stirring, until the onion is translucent. Add the garlic and cook, stirring, for 2 minutes longer. Transfer to the slow cooker. Add the pizza sauce, tomatoes, oregano, basil, crushed pepper, and sugar to the slow cooker. Stir to blend ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E4E812B1CEAAF46A378E7F1C696D0B8', X'108F4811B80C0143BEC6F977C1BBBC3D', X'108F4811B80C0143BEC6F977C1BBBC3D', 2, NULL, NULL, 2, NULL, 0, 'Cover the slow cooker and cook on low for 4 to 6 hours or cook the sauce on high for 3 to 4 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39669D5DE7D2B64D89BD1577D081E91B', X'108F4811B80C0143BEC6F977C1BBBC3D', X'108F4811B80C0143BEC6F977C1BBBC3D', 3, NULL, NULL, 2, NULL, 0, 'About 15 minutes before the sauce is done, cook the pasta in boiling salted water until `al dente` as directed on the package. Drain well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F7EC2827AB3E64C83F4BA607B5D7579', X'108F4811B80C0143BEC6F977C1BBBC3D', X'108F4811B80C0143BEC6F977C1BBBC3D', 4, NULL, NULL, 2, NULL, 0, 'Stir the drained pasta into the sauce mixture. Stir in half of the Parmesan cheese and half of the mozzarella cheese. Sprinkle the sliced olives over the top and then top with the remaining Parmesan and mozzarella cheese. Cover and continue cooking on low for 15 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C920D306EA299D4D985B87326885679A', X'108F4811B80C0143BEC6F977C1BBBC3D', X'108F4811B80C0143BEC6F977C1BBBC3D', 5, NULL, NULL, 2, NULL, 0, 'Alternatively, combine the drained hot pasta with the sauce and half of the Parmesan and mozzarella cheeses and transfer to a 3-quart baking dish. Top with the sliced olives and remaining Parmesan and mozzarella cheese. Bake at 350 F for about 15 to 20 minutes, or until the cheese has melted and the sauce is hot and bubbling.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21DF26E9AB71884783337C34B04B2054', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', 0, NULL, NULL, 2, NULL, 0, 'Butter an 11-by-7-inch baking dish. Heat oven to 375 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55BB3911013E3443873A01EC9FD16227', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', 1, NULL, NULL, 2, NULL, 0, 'Cook the green beans in a small amount of water until just tender. Drain and arrange in the prepared baking dish. Sprinkle with the diced tomatoes and shredded cheese. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05A08DE495F9DE4A8F403B890259340B', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', 2, NULL, NULL, 2, NULL, 0, 'In a saucepan over medium-low heat, melt butter. Add onion and saute until tender. Stir in flour until well blended. Add the salt, pepper, cream, and sour cream. Heat through, stirring constantly. Spread the cream mixture over the green beans then sprinkle the bread crumbs or French fried onions over all.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEA265DCA6DCAE41B62F06185C4F0A7F', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', X'A8EF42BEA3E9D745A5A1560C40F5AE8D', 3, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes, until browned and bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F09495C2DCC4094FAAE88BAD88740019', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 0, NULL, NULL, 2, NULL, 0, 'Make sure you''ve pre-cooked the octopus and cut it into bite-sized pieces.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15604166EB1DBA46B105F7801ED0D559', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, make the takoyaki batter by combining flour, dashi broth, and eggs. Mix until well incorporated. Note, the thickness of the batter should be similar to a cream soup and is on the thinner side.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C43734F9D458843A44F615590D9FF05', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 2, NULL, NULL, 2, NULL, 0, 'Preheat a takoyaki pan and coat each of the round molds in the pan with canola oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DDEA2E3176A974A882FB865188446C2', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 3, NULL, NULL, 2, NULL, 0, 'Pour batter into the molds so that it is about 3/4 full.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36D5AFDBA73F5447BF9614F85F3E3DF4', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 4, NULL, NULL, 2, NULL, 0, 'Put octopus, red ginger, green onion, and dried red shrimp in each mold.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'518E587841E013449C49BBAA8CBD52FD', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 5, NULL, NULL, 2, NULL, 0, 'Grill takoyaki balls, allowing it to cook about 3 to 4 minutes before flipping the ball over using a takoyaki pick.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'560A13E6DAD6C549BA1C1F24986E71AE', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 6, NULL, NULL, 2, NULL, 0, 'Grill the other side of the takoyaki another 3 minutes until golden brown.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37DB713D6483424BB0CD03203B269D9F', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 7, NULL, NULL, 2, NULL, 0, 'When browned, remove takoyaki from the pan and place on a plate.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D57F13B41E23E64791EAD62B3C300765', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 8, NULL, NULL, 2, NULL, 0, 'Garnish the takoyaki with okonomiyaki sauce (or substitute sauce). Then add mayonnaise, and sprinkle with dried bonito flakes and green aonori seaweed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E89882D09E508488A49C10F8DD2E9A2', X'2A526EF065E60C46A9922D14C56CE3C9', X'2A526EF065E60C46A9922D14C56CE3C9', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18422D647C8D4D42B0CFDB84C4C636AC', X'66709BF23CF41C4C945871EBF85159B6', X'66709BF23CF41C4C945871EBF85159B6', 0, NULL, NULL, 2, NULL, 0, 'Place three of the tortillas on a work surface. Top the tortillas with half of the cheeses, then the chicken, then the remaining cheeses. Top the filling with the remaining tortillas. Spread outsides of the quesadillas with the butter.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D1F3D2A3543394EA9F7E8A0605AD350', X'66709BF23CF41C4C945871EBF85159B6', X'66709BF23CF41C4C945871EBF85159B6', 1, NULL, NULL, 2, NULL, 0, 'Heat a flat pan, large skillet, or griddle over medium heat. Add the quesadillas, one at a time, and cook, pressing down with a spatula, until bottoms are browned, about 2 to 4 minutes. Turn quesadillas and cook second side 2 to 3 minutes or until the tortillas are browned and crisp and the cheeses melt.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9CAB5A0C7589D4FBA2BF33C6B70DC75', X'66709BF23CF41C4C945871EBF85159B6', X'66709BF23CF41C4C945871EBF85159B6', 2, NULL, NULL, 2, NULL, 0, 'Remove the quesadilla with a spatula to the work surface and cut into quarters. Repeat with remaining quesadillas. Serve immediately with salsa for dipping.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30E6946F11610C49A02895E4BBDE85FB', X'1081EBEEAB704544BDC87EF2C72AFE1B', X'1081EBEEAB704544BDC87EF2C72AFE1B', 0, NULL, NULL, 2, NULL, 0, 'In a large nonstick skillet sprayed with vegetable spray, quickly brown eggplant slices and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A30B6498F8E474ABB96F4CEEEAD6329', X'1081EBEEAB704544BDC87EF2C72AFE1B', X'1081EBEEAB704544BDC87EF2C72AFE1B', 1, NULL, NULL, 2, NULL, 0, 'In the same skillet in hot olive oil, cook onion for about 3 to 4 minutes, stirring occasionally, or until tender.');
