INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'330CA6ED706A9C44A2456FC4D889C5A1', X'1081EBEEAB704544BDC87EF2C72AFE1B', X'1081EBEEAB704544BDC87EF2C72AFE1B', 3, NULL, NULL, 2, NULL, 0, 'Cook lasagna noodles in boiling salted water according to package directions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD1B7B74E8EF554EB66340D88B94F901', X'1081EBEEAB704544BDC87EF2C72AFE1B', X'1081EBEEAB704544BDC87EF2C72AFE1B', 4, NULL, NULL, 2, NULL, 0, 'Spoon about one-quarter of the sauce Into an 11x7-inch baking dish. Arrange three alternate layers of lasagna noodles, ricotta, mushroom mixture, mozzarella cheese, eggplant slices, sauce, and Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45CA933A79157C439A9CA239D299618E', X'1081EBEEAB704544BDC87EF2C72AFE1B', X'1081EBEEAB704544BDC87EF2C72AFE1B', 5, NULL, NULL, 2, NULL, 0, 'Bake eggplant lasagna at 350 F for 30 to 40 minutes.');
