INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11DE69E5C84F044E979D33BFEBDFCFE1', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', 3, NULL, NULL, 2, NULL, 0, 'In a bowl, beat the egg with the water.');
