INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39741CEFBD8E0C4B815DE850117BFB96', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', 7, NULL, NULL, 2, NULL, 0, 'Transfer the browned steak to a 2 1/2- to 3-quart baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF2DF40603003C498B9F256AB0244B93', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', 8, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the tomatoes, tomato sauce, Parmesan cheese, wine (if using), sugar, oregano, and remaining 1/4 teaspoon salt. Pour the sauce over the steaks.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85549E8A354CA94F907F898D3B444196', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', 9, NULL, NULL, 2, NULL, 0, 'Cover the baking dish tightly with foil and bake for about 1 hour and 10 minutes or until the meat is tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B843409F59E18748BE788FE950A112A9', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle mozzarella cheese over the meat and continue baking, uncovered, for about 5 minutes or until the cheese has melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CB87E1026D236468D59FBFDE6A19329', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', X'55DDA9EE6ADCE64C97078B3D8CDE1A4A', 11, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81F29DB47557584A89220075709B3B08', X'8529BA5DB0528C4985FC64940AA0247F', X'8529BA5DB0528C4985FC64940AA0247F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C646F78423582246BD2B0B00896682BB', X'8529BA5DB0528C4985FC64940AA0247F', X'8529BA5DB0528C4985FC64940AA0247F', 1, NULL, NULL, 2, NULL, 0, 'Spray the slow cooker crockery insert with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'653580006EFE4E4D83DC5136EB2D09D3', X'8529BA5DB0528C4985FC64940AA0247F', X'8529BA5DB0528C4985FC64940AA0247F', 2, NULL, NULL, 2, NULL, 0, 'Spread the cream cheese in bottom of the slow cooker. Then mix up the pizza sauce, onion, olives and pepperoni and spread on top of the cream cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D87A8139993939439AB83282E7C22CEB', X'8529BA5DB0528C4985FC64940AA0247F', X'8529BA5DB0528C4985FC64940AA0247F', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle with the pizza cheese and cook on low until the cheese on top melts.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A628BD477F7FE42B8EC187BEB76A4DC', X'8529BA5DB0528C4985FC64940AA0247F', X'8529BA5DB0528C4985FC64940AA0247F', 4, NULL, NULL, 2, NULL, 0, 'Serve with tortilla chips and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8E54887EAB83248ABB37905F18D78DF', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'426290C943284344865BB44F8BEDB336', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 425°F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86A59576B808F14385B18F87B3C7AA54', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 2, NULL, NULL, 2, NULL, 0, 'To prepare the dippers, combine the Parmesan, Italian seasoning, and garlic powder in a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D35F30E1E5EBA448A720C337E115EB17', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 3, NULL, NULL, 2, NULL, 0, 'Roll out one pizza dough on a lightly floured work surface and cut into 18 squares. Then cut each square in half diagonally to form two triangles. Arrange the triangles on a baking sheet and brush each one with olive oil. Sprinkle the Parmesan mixture over the triangles. Repeat with the remaining pizza dough. There will be a total of 72 triangles. Bake for 1012 minutes, until golden brown. Remove to a wire rack to cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA2D90A390B2144C8503622B26FD98BB', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 4, NULL, NULL, 2, NULL, 0, 'To prepare the dip, whisk together the cream cheese, sour cream, oregano, basil, red pepper flakes, garlic, pizza sauce, pepperoni, bell pepper, scallions, cheddar, and 3/4 cup mozzarella in a bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'873936FC562BB14BACADC5AF92F99CBF', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 5, NULL, NULL, 2, NULL, 0, 'Evenly spread out the mixture in a shallow 2-quart ovenproof dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DC31179C8CB9F4EAC33FE9A931D485B', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 6, NULL, NULL, 2, NULL, 0, 'Bake for 15 minutes, until bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C754DD63656EE3458D3F1A6EF772B432', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 7, NULL, NULL, 2, NULL, 0, 'Remove from the oven and sprinkle with the remaining 1/4 cup mozzarella.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F7347EB350E8F4985D724A123BA42DD', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 8, NULL, NULL, 2, NULL, 0, 'Bake for 5 minutes more.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16B648E73B7E23459A93C2AA52D3BFD1', X'D63CD1595F22C749B2B7A2941B3B1DCF', X'D63CD1595F22C749B2B7A2941B3B1DCF', 9, NULL, NULL, 2, NULL, 0, 'Serve hot with the dippers.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D485503A19CEDC42B6DC58FC1A7ED035', X'787916E33CFEF44FB941A65D9F4A70E3', X'787916E33CFEF44FB941A65D9F4A70E3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF4B339977F005429894513BD4931D5C', X'787916E33CFEF44FB941A65D9F4A70E3', X'787916E33CFEF44FB941A65D9F4A70E3', 1, NULL, NULL, 2, NULL, 0, 'Place parsley, basil, oregano, olive oil, lemon juice, and salt in a zip-top bag large enough to hold the mozzarella. Massage to combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51C79431E27E404C9EFE39F3B5C390C3', X'787916E33CFEF44FB941A65D9F4A70E3', X'787916E33CFEF44FB941A65D9F4A70E3', 2, NULL, NULL, 2, NULL, 0, 'Add the drained baby mozzarella cheese balls, seal, and toss bag to coat the cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5588EFCB170432409317D8F64E171F49', X'787916E33CFEF44FB941A65D9F4A70E3', X'787916E33CFEF44FB941A65D9F4A70E3', 3, NULL, NULL, 2, NULL, 0, 'Open the bag, squeeze out all the air, and re-seal.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D42C8B72301954FB8A26E313CFC65EA', X'787916E33CFEF44FB941A65D9F4A70E3', X'787916E33CFEF44FB941A65D9F4A70E3', 4, NULL, NULL, 2, NULL, 0, 'Let mozzarella balls sit in the marinade at room temperature for 4 hours before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'952601279CC6394ABCF1DA778A9112F7', X'787916E33CFEF44FB941A65D9F4A70E3', X'787916E33CFEF44FB941A65D9F4A70E3', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF58D2746096E048A661F5641ECC795D', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4). Grease a 12-by-8-by-2-inch baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A69B6BD4C38E243A82C462654F1E36A', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 1, NULL, NULL, 2, NULL, 0, 'Put the beaten eggs in a wide, shallow bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6FD9EA1884DC2478BB9DBA0B47F3C3E', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 2, NULL, NULL, 2, NULL, 0, 'Put the seasoned bread crumb mixture in another wide, shallow bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E899A6BA52D341B030A1C9C78F6D2D', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 3, NULL, NULL, 2, NULL, 0, 'Wash eggplant and cut crosswise into 1/2-inch thick slices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A0F67D32F0DE04994B073C7FEA0C210', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 4, NULL, NULL, 2, NULL, 0, 'Dip the eggplant slices in beaten eggs then dredge with seasoned bread crumbs.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAF1022E8AF45B4689DABAF2EE80424B', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 5, NULL, NULL, 2, NULL, 0, 'Place the breaded slices on a plate and chill for 30 to 45 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE9DFF40D4152A478A99C117430E9DA8', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 6, NULL, NULL, 2, NULL, 0, 'Heat about 1/8-inch of olive oil in a heavy skillet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14F5EEC14F59DF4E8DC729869940F3C1', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 7, NULL, NULL, 2, NULL, 0, 'Fry the breaded eggplant on both sides until golden brown and crispy. Drain well on paper towels.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98833F39883F3A449BFE8B2854F81FDB', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 8, NULL, NULL, 2, NULL, 0, 'In a saucepan, heat tomato sauce, basil, and oregano.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36536F59FDC756468E1A4FAFBD9A1C9A', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 9, NULL, NULL, 2, NULL, 0, 'Spread about one-third of the sauce in the prepared baking dish. Layer half of the eggplant, half of the mozzarella cheese slices, another one-third of the sauce, and half the Parmesan cheese. Repeat layers, ending with the remaining sauce and Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C4F7190631836478C450C7457B95AA9', X'96C8F61F5A6B154F819F048D1B928CB6', X'96C8F61F5A6B154F819F048D1B928CB6', 10, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes, or until the dish is hot and bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2EC8B7330F2324AB4250AE714EFD8DE', X'9809563395872F49B26EA1C4C669E0E4', X'9809563395872F49B26EA1C4C669E0E4', 0, NULL, NULL, 2, NULL, 0, 'Prepare and heat an outdoor gas or charcoal grill for medium heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F4A7F0B0A6C8A45A7126E8F75DE7BE5', X'9809563395872F49B26EA1C4C669E0E4', X'9809563395872F49B26EA1C4C669E0E4', 1, NULL, NULL, 2, NULL, 0, 'Combine the ground beef, Italian seasonings and salt in a large bowl and mix gently. Form into 12 thin patties and place on waxed paper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CB19BEB27DE8D4ABB6348BD02B0E731', X'9809563395872F49B26EA1C4C669E0E4', X'9809563395872F49B26EA1C4C669E0E4', 2, NULL, NULL, 2, NULL, 0, 'Combine the pepperoni, 1/3 cup pizza sauce, mustard, and Parmesan cheese in a small bowl. Divide this mixture among 6 patties within 1/2` of pattie edges. Top the filling with the remaining patties and press edges together to seal.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6690296AB1022D439FA5144936BDAC82', X'9809563395872F49B26EA1C4C669E0E4', X'9809563395872F49B26EA1C4C669E0E4', 3, NULL, NULL, 2, NULL, 0, 'Grill the patties 4-6` from medium coals for 10 to 15 minutes until meat is thoroughly cooked to 165 F, turning carefully just once. Top each burger with a slice of the mozzarella cheese and the remaining pizza sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43BE21D18B85F448B6E7736D552A5A6A', X'9809563395872F49B26EA1C4C669E0E4', X'9809563395872F49B26EA1C4C669E0E4', 4, NULL, NULL, 2, NULL, 0, 'Grill the buns and, cut side down, and let the cheese melt on the burgers for 1 to 2 minutes, then serve burgers in buns.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1852A9A3D0F4EB4092E62A3AC2062BF9', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. Then, spray a 9 x 13 baking dish with nonstick spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDF6D894FA3D5B48B5BF0B48D7033E66', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 1, NULL, NULL, 2, NULL, 0, 'Cook pasta according to package directions for `al dente.` Drain. Pour into prepared baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0961573971D917478A89110CBE06E7B3', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in a large saucepan or Dutch oven over medium-high heat. While oil is heating, cut chicken breasts into bite-sized pieces.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65CE8AB507C18645A1718E13180EE059', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 3, NULL, NULL, 2, NULL, 0, 'Brown chicken in oil. Add Italian seasoning, half of the spaghetti sauce and the drained tomatoes. Let simmer 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2FE37E4D7C828478A3DA5E95CAB132F', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 4, NULL, NULL, 2, NULL, 0, 'Cut cream cheese into small chunks. Add to chicken and tomato sauce mixture. Simmer until cream cheese is melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E014DAFB11F6540847D2A57038A23B6', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 5, NULL, NULL, 2, NULL, 0, 'Stir in spinach. Cook another minute, until spinach wilts slightly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C23DD8682193B148913B9A55191BE493', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 6, NULL, NULL, 2, NULL, 0, 'Pour chicken mixture over pasta. Top with one cup of the shredded mozzarella. Top with the remaining spaghetti sauce, remaining mozzarella and Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BE5127E4B1BBF4EB245F900E258DF6B', X'80D59082DF8FCE4F9AAA447D21196A53', X'80D59082DF8FCE4F9AAA447D21196A53', 7, NULL, NULL, 2, NULL, 0, 'Bake, uncovered, 20 minutes, or until bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3EF7EAE1D485645B2F774B877342247', X'A6F25162E34AEA428E03272C8189BC4B', X'A6F25162E34AEA428E03272C8189BC4B', 0, NULL, NULL, 2, NULL, 0, 'Cut the eggplant in 1/2-inch slices; place in bowl in layers, sprinkling each layer with salt. Let stand 30 minutes to drain excess moisture; dry on paper towels.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19151C0A56295F489FDCA2726BF3967B', X'A6F25162E34AEA428E03272C8189BC4B', X'A6F25162E34AEA428E03272C8189BC4B', 1, NULL, NULL, 2, NULL, 0, 'In a shallow bowl beat the eggs with 1/3 cup water and the flour. Dip the eggplant slices in batter, letting excess drip off.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'011707170EA6E64EA8DCB77E79269A69', X'A6F25162E34AEA428E03272C8189BC4B', X'A6F25162E34AEA428E03272C8189BC4B', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium heat. Sauté the eggplant slices in batches just until lightly browned, turning to brown both sides.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27262D9D2D45CD4BBF933F3B0D03AE59', X'A6F25162E34AEA428E03272C8189BC4B', X'A6F25162E34AEA428E03272C8189BC4B', 3, NULL, NULL, 2, NULL, 0, 'Combine seasoned bread crumbs with the Parmesan cheese in a small bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9356125EE7769748AFB1803E78FAB632', X'A6F25162E34AEA428E03272C8189BC4B', X'A6F25162E34AEA428E03272C8189BC4B', 4, NULL, NULL, 2, NULL, 0, 'In the crock pot, layer one-quarter of the eggplant, top with one-quarter of the crumbs, one-quarter of the marinara sauce and one-quarter of the Mozzarella cheese. Repeat to make four layers of eggplant, crumbs, sauce and mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8B41416CC807A54DB18B9744A3151572', X'A6F25162E34AEA428E03272C8189BC4B', X'A6F25162E34AEA428E03272C8189BC4B', 5, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 4 to 5 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'677E306101D53D42A3DCF9CC7C80CA86', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC264EE2CB8BAB4E96D7A384D6D764F5', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F/180 C/Gas 4.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEAA9B73AE4F6C4085A24BAACB6193C2', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 2, NULL, NULL, 2, NULL, 0, 'Butter a 2 1/2 to 3-quart shallow baking dish or casserole.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AC58F34C4CECF459FF4E0461C40A3CD', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 3, NULL, NULL, 2, NULL, 0, 'Cook the penne pasta in boiling salted water as directed on package; drain well and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9850D94CFCD881428874E24CB384FE2A', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in a medium saucepan over medium heat. Add the onion to the pan and cook for about 4 minutes, or until it is translucent. Add the garlic to the onion and continue cooking for 1 minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'365E236B0520864CA945F717B5C98718', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 5, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, basil, and oregano to the onion and garlic. Cook until heated through, stirring frequently.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5141077D762F7C448DD0066A1BD83DFF', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 6, NULL, NULL, 2, NULL, 0, 'Taste and add salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5B88341356E7944BB92449ADDE8CB15', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 7, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the cooked, drained penne, the tomato sauce mixture, and 1 cup of the shredded mozzarella cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24311FD2B2D50443932DB4A8BD02D2D2', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 8, NULL, NULL, 2, NULL, 0, 'Stir to blend the ingredients thoroughly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17D3C84637C5E4419C0D5921BF69D71A', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 9, NULL, NULL, 2, NULL, 0, 'Spoon the pasta and sauce mixture into the prepared casserole.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B7878E111367841A9C980E2C34B9A8E', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle the remaining 1 cup of shredded mozzarella cheese over the top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE5B7D10389FEE4ABB594BBD094C56A2', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 11, NULL, NULL, 2, NULL, 0, 'Bake the casserole for about 20 to 25 minutes, or until the pasta is hot and bubbly and the cheese has melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CAC4C05CE07E743B6978C61D04A3588', X'FCCA8E66D3290345B4F36E74CC7F06BC', X'FCCA8E66D3290345B4F36E74CC7F06BC', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B21B49B0DE85545BC6B33DA76578AC6', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4BE871CE014944F9C514B9654738E13', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F/200 C/Gas 6.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58A45C0568E3454BB638468AF9798F0F', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 2, NULL, NULL, 2, NULL, 0, 'Line two baking sheets with foil. Brush foil with a little of the olive oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAD39526FAD13A4F9C2003A0213357A1', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 3, NULL, NULL, 2, NULL, 0, 'Arrange slices of eggplant over the bottom of one of the baking sheets; brush slices with olive oil and sprinkle lightly with salt and pepper. Arrange bell pepper and mushrooms on the other baking sheet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'578F297689CD7846891CABDC306DBAF9', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 4, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 8 minutes. Turn the vegetables and bake for 7 to 8 minutes longer. Remove from oven and let cool slightly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D84A26F080D7F459194813B2D4FEF4A', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 5, NULL, NULL, 2, NULL, 0, 'Grease a 3-quart baking dish. Arrange a layer of eggplant slices in the bottom of the baking dish. Top with a few slices of bell pepper, some mushrooms, and about 1/3 of the sauce. Sprinkle with 1/3 of the mozzarella cheese. Repeat layers two more times, then sprinkle with the Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'777EAE234EFD67459D33C4C7862FE5F2', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 6, NULL, NULL, 2, NULL, 0, 'Bake the casserole for 25 to 30 minutes, until vegetables are tender and cheese is melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F83CE0AB62430E47AB9E7B37430BD7FC', X'9ECD0C7D19FBBA459A6EE0628759468E', X'9ECD0C7D19FBBA459A6EE0628759468E', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'820FFEF2029BBB4BA0603A40CC84FEA2', X'D31E30484984B84C8E7BC7FE7D9C0726', X'D31E30484984B84C8E7BC7FE7D9C0726', 0, NULL, NULL, 2, NULL, 0, 'Pound chicken breast halves until evenly thin. Dredge chicken with flour.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C206CE921A6CD64FA14917D6AF1650DA', X'D31E30484984B84C8E7BC7FE7D9C0726', X'D31E30484984B84C8E7BC7FE7D9C0726', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat 2 tablespoons of butter over medium heat. Cook chicken in butter for about 4 to 6 minutes on each side, until tender and cooked through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41CB1D8B15105346A412BBD535A3625E', X'D31E30484984B84C8E7BC7FE7D9C0726', X'D31E30484984B84C8E7BC7FE7D9C0726', 2, NULL, NULL, 2, NULL, 0, 'Remove chicken to a shallow baking dish; sprinkle lightly with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E50611076C52734E897C898C8693539A', X'D31E30484984B84C8E7BC7FE7D9C0726', X'D31E30484984B84C8E7BC7FE7D9C0726', 3, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon butter to the skillet and sauté mushrooms until tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7C43342C330834D94A06BD1E15E6073', X'D31E30484984B84C8E7BC7FE7D9C0726', X'D31E30484984B84C8E7BC7FE7D9C0726', 4, NULL, NULL, 2, NULL, 0, 'Arrange mushroom slices on top of each piece of chicken; cover with a slice of cheese. Place the remaining mushrooms around the chicken.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37CC64B29452D94499B1272F07C066D1', X'D31E30484984B84C8E7BC7FE7D9C0726', X'D31E30484984B84C8E7BC7FE7D9C0726', 5, NULL, NULL, 2, NULL, 0, 'Place chicken breasts under a hot broiler until cheese is melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78B5EE5CF9E2F6458EC9F7960447E738', X'D31E30484984B84C8E7BC7FE7D9C0726', X'D31E30484984B84C8E7BC7FE7D9C0726', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately with hot buttered pasta or rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1853F6212561C649BE205713108559EF', X'2ECA7DB4B4DFC742964165A4891C8DD2', X'2ECA7DB4B4DFC742964165A4891C8DD2', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. In ?a medium saucepan, melt butter over medium heat. Add onion and garlic; cook and stir 2 minutes. Add red bell pepper; cook and stir 3 to 5 minutes longer until vegetables are crisp-tender. Remove from heat. Chop artichoke hearts into small pieces.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8650E59AB778146B49321A15F076DA6', X'2ECA7DB4B4DFC742964165A4891C8DD2', X'2ECA7DB4B4DFC742964165A4891C8DD2', 1, NULL, NULL, 2, NULL, 0, 'In ?a large bowl, beat cream cheese until softened. Gradually beat in mayonnaise until smooth. Add sautéed vegetables and artichoke hearts.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65417D55EA1AB9479C5EAF1A32B1A971', X'2ECA7DB4B4DFC742964165A4891C8DD2', X'2ECA7DB4B4DFC742964165A4891C8DD2', 2, NULL, NULL, 2, NULL, 0, 'Stir in half of the Parmesan cheese and all of the Monterey Jack and mozzarella cheese. Place in 8` square baking dish that has been sprayed with nonstick cooking spray. Sprinkle with remaining Parmesan cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'888F36A8B4BD664CA8F63344036677A9', X'2ECA7DB4B4DFC742964165A4891C8DD2', X'2ECA7DB4B4DFC742964165A4891C8DD2', 3, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 35 minutes until dip begins to bubble around the edges. Serve warm with lightly toasted baguette slices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88294B016249E349A1827C8C31C3F23A', X'2ECA7DB4B4DFC742964165A4891C8DD2', X'2ECA7DB4B4DFC742964165A4891C8DD2', 4, NULL, NULL, 2, NULL, 0, 'You can make this dip ahead of time; just cover and refrigerate up to 24 hours. When it''s time to eat, bake for 5 to 10 minutes longer until the dip is hot and bubbling around the edges. Don''t overbake or the dip will separate.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64639BA1C2357044BFEA90672D9D9B25', X'7550F96BED5BD34EA636A757BBEDA528', X'7550F96BED5BD34EA636A757BBEDA528', 0, NULL, NULL, 2, NULL, 0, 'Grease a 13x9x2-inch baking pan. Heat oven to 350 F.');
