INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACB562E5FC505544B9DC329198F4A739', X'8900F95EE209C849B382B9D88B79046A', X'8900F95EE209C849B382B9D88B79046A', 7, NULL, NULL, 2, NULL, 0, 'When butter foams, add the egg mixture and use a rubber spatula to rapidly stir eggs in pan for a few seconds.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB748705717B3D4F8E27D002CFECC21F', X'8900F95EE209C849B382B9D88B79046A', X'8900F95EE209C849B382B9D88B79046A', 8, NULL, NULL, 2, NULL, 0, 'Then shake pan to evenly distribute eggs, and let cook over medium-high heat until eggs are nearly set.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9712AFF98C8CC8488A78F0E391B88CD6', X'8900F95EE209C849B382B9D88B79046A', X'8900F95EE209C849B382B9D88B79046A', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle about 1/4 cup mozzarella, 1 tablespoon basil, 1/4 cup tomatoes, and 1 teaspoon Parmesan cheese over one-half of the omelet. Season omelet lightly with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEC9BD9D95E2A240B5E5ACBE35E22AB0', X'8900F95EE209C849B382B9D88B79046A', X'8900F95EE209C849B382B9D88B79046A', 10, NULL, NULL, 2, NULL, 0, 'Run the rubber spatula lightly under the other half to make sure it''s not stuck, then gently fold the egg mixture over the filling.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D72406DDC93547889F83983174B165', X'8900F95EE209C849B382B9D88B79046A', X'8900F95EE209C849B382B9D88B79046A', 11, NULL, NULL, 2, NULL, 0, 'Let cook another minute or two to let cheese melt, then slide the omelet out of the skillet onto a plate.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31F90F416ABAA441BEBA0D56C7E6FB37', X'8900F95EE209C849B382B9D88B79046A', X'8900F95EE209C849B382B9D88B79046A', 12, NULL, NULL, 2, NULL, 0, 'Keep warm in a 200 F oven while you repeat the process to create additional omelets with the remaining ingredients. Serve omelets warm.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C4E5ABF2703C940924C1F355CA758CA', X'22FFCAB4B7D5D04CBEC2164BB71781D6', X'22FFCAB4B7D5D04CBEC2164BB71781D6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF5E68CFB1F3064493689A0E7689A763', X'22FFCAB4B7D5D04CBEC2164BB71781D6', X'22FFCAB4B7D5D04CBEC2164BB71781D6', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 400 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'533EEA68744B2944B5D786B55443AA88', X'22FFCAB4B7D5D04CBEC2164BB71781D6', X'22FFCAB4B7D5D04CBEC2164BB71781D6', 2, NULL, NULL, 2, NULL, 0, 'Add yeast to the water and stir until dissolved. Add sugar, oil, flour, and salt. Set mixing bowl filled with dough in warm water for 5 minutes to rise. Pat dough out on a well-greased pizza pan or cookie sheet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DD7B8C4FEF3B646898C7FD2025C40FB', X'22FFCAB4B7D5D04CBEC2164BB71781D6', X'22FFCAB4B7D5D04CBEC2164BB71781D6', 3, NULL, NULL, 2, NULL, 0, 'Layer toppings on dough in the order listed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D828736C99CB2D40BB306249A4BD3005', X'22FFCAB4B7D5D04CBEC2164BB71781D6', X'22FFCAB4B7D5D04CBEC2164BB71781D6', 4, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 20 minutes, or until the crust is browned.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'798E7BE3B5844A4E9D1BB5E7195A3F98', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F. Lightly grease an 8-inch square baking dish, or spray with cooking spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'899EBFEE7962734A9B10639C852AD964', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 1, NULL, NULL, 2, NULL, 0, 'Cook pasta in boiling salted water following package directions; drain, and rinse with hot water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93AE995C224A7346AFF736F9B8FD21E0', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large saucepan, heat butter over medium-low heat. Stir in flour until smooth and bubbly. Add the milk, and continue cooking, stirring, until thickened.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B334CCBAA909DD4BB09BC45B09D59DBF', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 3, NULL, NULL, 2, NULL, 0, 'Add the basil and the salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA427F3158B0C742A20B229DFD7B1C67', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 4, NULL, NULL, 2, NULL, 0, 'Stir in the spinach leaves until wilted; stir in the hot drained pasta.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E889D401636EC44BCD8EE4CEC99FB73', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 5, NULL, NULL, 2, NULL, 0, 'Stir in the mozzarella cheese until well blended. Spoon the pasta mixture into the prepared baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AC11D944E090904BB3B4496B3FE2E500', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 6, NULL, NULL, 2, NULL, 0, 'In a bowl, toss bread crumbs with the melted butter; sprinkle over the pasta mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18DD77627970214AAE49B8F6854D7485', X'4D55DF6B400D0E44BD19F64CE79210FD', X'4D55DF6B400D0E44BD19F64CE79210FD', 7, NULL, NULL, 2, NULL, 0, 'Bake for 20 to 30 minutes, or until nicely browned and hot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F778EB08F341DD4A9B1DC57ACAE2A6D4', X'3D3F4C4F3FE70B4B953851C99A71BCDF', X'3D3F4C4F3FE70B4B953851C99A71BCDF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD6295264093724FBBE13EF7DF02E9F2', X'3D3F4C4F3FE70B4B953851C99A71BCDF', X'3D3F4C4F3FE70B4B953851C99A71BCDF', 1, NULL, NULL, 2, NULL, 0, 'In a saucepan combine the blackberries, blueberries, cornstarch, vanilla, sugar, and water. Cover and cook over a medium-low heat. Once simmering, mash and mix well. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F475447AABE2314AB7DB36B464F45C13', X'3D3F4C4F3FE70B4B953851C99A71BCDF', X'3D3F4C4F3FE70B4B953851C99A71BCDF', 2, NULL, NULL, 2, NULL, 0, 'Toast the bread.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC6EC22F4DA3BF49B403D0B2DDE2037F', X'3D3F4C4F3FE70B4B953851C99A71BCDF', X'3D3F4C4F3FE70B4B953851C99A71BCDF', 3, NULL, NULL, 2, NULL, 0, 'Slather each slice with goat cheese and top with a spoonful of the warm berry mixture. Sprinkle with some chopped fresh mint if you have it.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71F909F0A28F4145B4F73C38C7C4C29F', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'639FDE6EE437834FA001E916200B867C', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 1, NULL, NULL, 2, NULL, 0, 'Preheat an oven to 425 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'274BDD329FB819488A3EE5EE1155DBDE', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 2, NULL, NULL, 2, NULL, 0, 'Roll out the puff pastry into a 10-inch tart pan and bake it for 18 to 22 minutes until it puffs and turns golden brown. Or, for individual tarts roll out the pastry and cut into squares and with a small, sharp knife score a border inside each one taking care not to cut right through. Bake as above.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60DB9FCF4AD09B45823293C154F47A6C', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 3, NULL, NULL, 2, NULL, 0, 'Once baked and golden brown, remove the pastry from the oven and leave to cool completely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A38A0D59BA2CAB48B97E48C5805D0B5F', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 4, NULL, NULL, 2, NULL, 0, 'Spread the Dijon mustard in an even layer over the browned pastry, leaving 1/2-inch of the edges empty.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60CF5AD4EA82C040BFB6BB72CCE7E2E4', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 5, NULL, NULL, 2, NULL, 0, 'Dry the tomato slices with kitchen paper. Arrange the tomato and goats cheese slices neatly over the mustard.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D61271BBB43D741889F37132C7694DE', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle the tomato slices with the remaining sea salt, pepper, goat cheese, and fresh herbs. Serve the tart at room temperature or chilled.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF679AFC1F82F4EA391E3A6CCDC8A5A', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', X'A6DC09FFEEFA2B409BEF8F20A0B4D7DC', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B86922911DB7E049A22F68FBA91F72FF', X'7EA85AA13845EB479BF29E2426436ACA', X'7EA85AA13845EB479BF29E2426436ACA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D85A3F6BD7EA6468DAEAD04B705C44E', X'7EA85AA13845EB479BF29E2426436ACA', X'7EA85AA13845EB479BF29E2426436ACA', 1, NULL, NULL, 2, NULL, 0, 'Slice the croissant in half and heat in the oven.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'141FDEDBD8390A408DAAD52A4B984DA8', X'7EA85AA13845EB479BF29E2426436ACA', X'7EA85AA13845EB479BF29E2426436ACA', 2, NULL, NULL, 2, NULL, 0, 'Take a small non-stick frying pan and melt the pat of salted butter. Add the egg and cook, sunny-side up, over medium-low heat until the white is cooked through and the yolk is still runny. Sprinkle with a little bit of salt, remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E9B2CBDDB0B2540948D579E457D5CAC', X'7EA85AA13845EB479BF29E2426436ACA', X'7EA85AA13845EB479BF29E2426436ACA', 3, NULL, NULL, 2, NULL, 0, 'Take the croissant out of the oven and crumble half of the blueberry goat cheese onto the bottom half.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66425BF859888F438581DDEB85D6B2F4', X'7EA85AA13845EB479BF29E2426436ACA', X'7EA85AA13845EB479BF29E2426436ACA', 4, NULL, NULL, 2, NULL, 0, 'Add the turkey slices, bacon, fried egg, and the rest of the blueberry goat cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EC3C7F96590644D9E8D14E55FB8F38B', X'7EA85AA13845EB479BF29E2426436ACA', X'7EA85AA13845EB479BF29E2426436ACA', 5, NULL, NULL, 2, NULL, 0, 'Top with the remaining half of the croissant.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BEF07003DB69742926FC61A4253BB85', X'7EA85AA13845EB479BF29E2426436ACA', X'7EA85AA13845EB479BF29E2426436ACA', 6, NULL, NULL, 2, NULL, 0, 'Serve immediately and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'933A780ED52D24479F402553C16184F9', X'7CECA92E2F527C4B9FE0B60CCFB7F313', X'7CECA92E2F527C4B9FE0B60CCFB7F313', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 375 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC51E3C48E48A245B0DFD285E8F7501C', X'7CECA92E2F527C4B9FE0B60CCFB7F313', X'7CECA92E2F527C4B9FE0B60CCFB7F313', 1, NULL, NULL, 2, NULL, 0, 'Wrap the beets in foil, place in a small baking dish, and bake for 1 hour, or until tender (test with a small knife). Allow to cool completely. When cool, scrape off the skin with a knife, and slice the beets as thin as possible; reserve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DA49D8604CD5E47B21F382DD831DF40', X'7CECA92E2F527C4B9FE0B60CCFB7F313', X'7CECA92E2F527C4B9FE0B60CCFB7F313', 2, NULL, NULL, 2, NULL, 0, 'Fan the beet slices on four chilled salad plates. Top with the sliced pear and walnuts. In a small bowl whisk together the vinegar, olive oil, and walnut oil. Season with salt and fresh ground black pepper to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AA15A6275035E499926E44A52B60F0C', X'7CECA92E2F527C4B9FE0B60CCFB7F313', X'7CECA92E2F527C4B9FE0B60CCFB7F313', 3, NULL, NULL, 2, NULL, 0, 'Toss the arugula with a couple of tablespoons of the dressing, and divide on top of each salad. Top each plate with the goat cheese (grate finely if using firm, aged goat cheese, or simply crumble if using soft goat cheese).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA775CF0E4321E4D82BD9036C6447E2E', X'7CECA92E2F527C4B9FE0B60CCFB7F313', X'7CECA92E2F527C4B9FE0B60CCFB7F313', 4, NULL, NULL, 2, NULL, 0, 'Drizzle each beet and pear salad with more of the dressing, serving an extra on the side. Serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0EA1DEC5AB76A42B16C9FE511BAFC42', X'DACBB7B47A60AA41A2661B9037F65C41', X'DACBB7B47A60AA41A2661B9037F65C41', 0, NULL, NULL, 2, NULL, 0, 'Using a serrated knife, cut the small French baguette in half.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29AA225A8C0E74448B391DCA06DFF219', X'DACBB7B47A60AA41A2661B9037F65C41', X'DACBB7B47A60AA41A2661B9037F65C41', 1, NULL, NULL, 2, NULL, 0, 'Place the sandwich on parchment paper or butcher paper and drizzle the inside of the bread with olive oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7743BAD4A79AEC45AF64B9C0744E6DB5', X'DACBB7B47A60AA41A2661B9037F65C41', X'DACBB7B47A60AA41A2661B9037F65C41', 2, NULL, NULL, 2, NULL, 0, 'Add rosemary ham, the apple, and a drizzle of honey to one side.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E2F3EAD8F10CB43AD1FFD1B8323CB1D', X'DACBB7B47A60AA41A2661B9037F65C41', X'DACBB7B47A60AA41A2661B9037F65C41', 3, NULL, NULL, 2, NULL, 0, 'Add the arugula, the goat cheese and the juice from one lemon wedge to the other half and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29763267D57EAA4A8E41C7BED1F25F02', X'DACBB7B47A60AA41A2661B9037F65C41', X'DACBB7B47A60AA41A2661B9037F65C41', 4, NULL, NULL, 2, NULL, 0, 'Wrap the sandwich in butcher paper and either refrigerate until ready to eat or cut the sandwich down the middle and serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0C8D29B9CBE1A41B75FC2B3B9512575', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'043E230E8AA9214D95FB22DF55F79AF7', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 1, NULL, NULL, 2, NULL, 0, 'With a spiralizer kitchen tool, spiralize two large zucchini into spaghetti-size noodles. If you don''t have a spiralizer, you can use a julienne peeler instead.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'994A0EFC02041848A879A4DE1CB84CF6', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 2, NULL, NULL, 2, NULL, 0, 'In a medium-sized skillet, heat the olive oil over medium heat. Add the shallot and cook until softened, about 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67E23A3B677FE94DA3B1BC889FDB43B6', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic and cook until fragrant, about 1 min.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'742241578A752640AC2D3E1EA35D8669', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 4, NULL, NULL, 2, NULL, 0, 'Add the corn, grape tomatoes, and basil. Season to taste with salt and pepper, stirring occasionally as you cook until heated through. When hot, turn off heat and sprinkle 1 tablespoon of crumbled goat cheese over top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13F539C5B2A077459FF3CC5391ABB447', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 5, NULL, NULL, 2, NULL, 0, 'In a separate large skillet, heat the remaining oil over medium-high heat. Add the zucchini noodles and toss as you cook (using tongs makes this easier). The zucchini noodles don''t take long to cooktoss for about 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92D71CACA604CA49B1C0E53FF1E73FF9', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 6, NULL, NULL, 2, NULL, 0, 'Then add in the corn, tomato, shallot, and cheese mixture. Toss to combine then remove the pan from heat. (Note: If you julienned the noodles, only cook for 2 to 3 minutes as these noodles are thinner and cook faster than spiralized noodles).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30866D341262F34A82CBCD69AFC777D7', X'2080F281C131C54AB22FB420C5541949', X'2080F281C131C54AB22FB420C5541949', 7, NULL, NULL, 2, NULL, 0, 'Divide noodle dish into two bowls, garnishing with remaining goat cheese and additional basil, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8D2BF0E69C413469D25BB16CD53322D', X'326CC4743A780948A63BBDB9F1EC6315', X'326CC4743A780948A63BBDB9F1EC6315', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A67A3115525B6141BAF465176DE79756', X'326CC4743A780948A63BBDB9F1EC6315', X'326CC4743A780948A63BBDB9F1EC6315', 1, NULL, NULL, 2, NULL, 0, 'If you are using store bought flatbread, brush the flatbread with olive oil and place on cookie sheets. Bake for five minutes, so it gets a little crispy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4F98D662A35D645AEA56F03E5BBA8EB', X'326CC4743A780948A63BBDB9F1EC6315', X'326CC4743A780948A63BBDB9F1EC6315', 2, NULL, NULL, 2, NULL, 0, 'Remove from the oven and evenly cover with the sundried tomatoes, olives and dollops of the goat cheese. Bake for 5 minutes more, or until the flatbread is crisp around the edges and the cheese is warm and soft.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAE0044DC4FE494BB5AC9E9CAA67303C', X'326CC4743A780948A63BBDB9F1EC6315', X'326CC4743A780948A63BBDB9F1EC6315', 3, NULL, NULL, 2, NULL, 0, 'If you are making your own flatbread, follow the flatbread recipe to make the dough then lightly oil two rimless baking sheets. Set one ball of dough on top of each baking sheet and roll the dough out as thin as possible (put a towel under the baking sheet to keep it from sliding while you roll the dough). The thinner the dough, the crispier the crust will be.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A630FDA055FF9440BBE110AE6E790FC7', X'326CC4743A780948A63BBDB9F1EC6315', X'326CC4743A780948A63BBDB9F1EC6315', 4, NULL, NULL, 2, NULL, 0, 'Brush a tablespoon of olive oil on each piece of dough. Bake the dough for 15 minutes until it is fairly crispy and lightly browned. Remove from the oven and spread sundried tomatoes,olives and dollops of the goat cheese on top. Bake for another 10 minutes or so, until the crust is brown and crispy and the cheese is soft.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65FF1634BA0F8E4EA433C0C71E0BDFBD', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 0, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. Set aside a deep lasagna oven safe dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D97C5794B0D7E4499CC83797E1AB1FB1', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 1, NULL, NULL, 2, NULL, 0, 'Fill a large stockpot 2/3 full with cold water. Add 1 teaspoon of salt, cover with a lid, and bring to a rolling boil. Add 14 gluten-free lasagna noodles and boil for 13 to 14 minutes. (Do not overcook the noodles. They will continue to cook once the lasagna is baked in the oven).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8067561B329633479136AE5B8431263D', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 2, NULL, NULL, 2, NULL, 0, 'Drain and rinse the noodles in cold water. Pour 1 tablespoon of olive oil on the noodles and coat. This will prevent the noodles from sticking together as they cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ACF274F9DF8C3479A82C1E01D98CCB6', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 3, NULL, NULL, 2, NULL, 0, 'Add onions, garlic and 3 tablespoons of olive oil to a large skillet. Sauté for two minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'969C3C2072B4AA4CA5853CDF61FC1299', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 4, NULL, NULL, 2, NULL, 0, 'Add the ground beef and 1 teaspoon salt. Brown meat over medium heat, stirring occasionally, until thoroughly cooked.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0FFBCE51CE24684C9D611269A7D7CA78', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 5, NULL, NULL, 2, NULL, 0, 'Pour the canned sauce over the beef mixture, add 1 tablespoon of the Italian seasonings, and all of the fennel. Stir to combine and simmer for 10 minutes. Stir occasionally to prevent burning.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08DDBBB60B44A542AD8B4B928881CDCD', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 6, NULL, NULL, 2, NULL, 0, 'In a medium-sized mixing bowl, blend goat cheese, ricotta cheese, Parmesan cheese, eggs, fresh basil, Italian parsley, fresh oregano, 1 teaspoon salt, and fresh cracked pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D20B736A0A09114E9FF52DB95676E6BC', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 7, NULL, NULL, 2, NULL, 0, 'Assemble lasagna: Spread 1 cup of the meat sauce mixture in the bottom of the baking dish. Overlap the edges of 4 lasagna noodles on top of the sauce. Evenly spread 1 cup of meat sauce over the noodles. Spread 1 cup of the cheese/herb mixture over meat sauce. Evenly sprinkle about 2 cups of shredded cheeses over this layer. Repeat layers (lasagna noodles/meat sauce/ cheese herb mixture/shredded cheese). Overlap 4 lasagna noodles on top, spread evenly with remaining meat sauce and top with remaining shredded cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5154A03E2EB24E42875764DC9B6B9520', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle remaining 1 tablespoon of Italian herb blend evenly over the shredded cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'113E6EDA89F6BE4DB9D72A17C8C58D86', X'18E4489E43A45A4A94B8E0D5A21CD7C7', X'18E4489E43A45A4A94B8E0D5A21CD7C7', 9, NULL, NULL, 2, NULL, 0, 'Bake for 1 hour or until golden brown on top. Let rest for 15 minutes before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7C5F3E4E124244C8ADE9148AF522829', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 0, NULL, NULL, 2, NULL, 0, 'Gently heat the milk in a stainless steel pot until it reaches 86 F then turn off the heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15464A7C7BE1C14AA4E2C9E53ECEF0D3', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the starter culture packet into the milk and stir a few times with the wooden spoon.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA8C5CF73D78DF4F90FFE830933BB505', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 2, NULL, NULL, 2, NULL, 0, 'Cover the pot and let the milk culture for 12-18 hours. Cultures for health recommends culturing the milk in an environment that''s as close to 73 degrees as possible. The milk will not culture properly in an environment that is too cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53C9E4B7B9DFCB4092335D67911AAE25', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 3, NULL, NULL, 2, NULL, 0, 'When the milk has properly cultured, it will have the consistency of yogurt. There will probably still be some liquid in the pot as well. This is the whey.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78DB8ED4C759EE4197841A02A3DDC1FF', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 4, NULL, NULL, 2, NULL, 0, 'Drape the butter muslin or cheesecloth (double layers) over a colander. Gently pour or scoop the cheese into the colander. Pull the cloth up around the cheese and tie it into a little sack.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D95A8BEA7585E4BB0BA3F0A9D0B0D75', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 5, NULL, NULL, 2, NULL, 0, 'Hang the cheese up so it can drain. You can tie it to a wooden spoon or ladle and hang it over a deep bowl or pitcher. Cultures for Health recommends tying it to the handle of a cupboard and setting a bowl underneath.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD5FF93797FDC4DA9CAC187CA516FD3', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 6, NULL, NULL, 2, NULL, 0, 'The cheese needs to drain for at least 6 hours, but the longer you let it drain the richer and denser the cheese will be. Twelve hours is often the right amount of time. Do not squeeze the cheese to get ?the moisture out; let it drain on its own.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E26D80FDF6484B4B90C7BFA3D33A52CD', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 7, NULL, NULL, 2, NULL, 0, 'Scrape the cheese out of the cloth and into a bowl. Add salt if desired.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B08A4F48CA2C8439E95B2AD58A7DAAE', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 8, NULL, NULL, 2, NULL, 0, 'The goat cheese can also be flavored with chopped fresh herbs, dried spices or anything else you can think of.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10EB51E6490BC94A8C8208F2427880EA', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 9, NULL, NULL, 2, NULL, 0, 'If you want to shape your goat cheese, try using a biscuit or cookie cutter with deep sides.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F269C290880B624A90F086AA23BBC3D1', X'AAD6CCEC6F88604C9DB57EDB47A498C5', X'AAD6CCEC6F88604C9DB57EDB47A498C5', 10, NULL, NULL, 2, NULL, 0, 'Kept refrigerated in an airtight container the goat cheese will keep up to one week.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA315DD5B4035F4DBDB2536A01A10B36', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients and preheat grill for high heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'997E80BCDD64FB4E8BAD9FBB2AEA3A03', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 1, NULL, NULL, 2, NULL, 0, 'Place a sheet of plastic wrap under and over the turkey, so that it''s covered.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21225981C941FF49B8F4EFF8807B25D5', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 2, NULL, NULL, 2, NULL, 0, 'With heavy pan or kitchen mallet, flatten turkey breast evenly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1FDE906AA804A4090E7F7666589714C', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 3, NULL, NULL, 2, NULL, 0, 'Squeeze excess fluid from thawed spinach.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C9FF6B587AED04C92BF60B7CB64B2FC', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 4, NULL, NULL, 2, NULL, 0, 'Coarsely chop and add to mushrooms, sunflower seeds, goat cheese, pepper, nutmeg, and eggs.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE3A5CB171C36F47868E40A3D972FAC2', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 5, NULL, NULL, 2, NULL, 0, 'Remove turkey from plastic wrap and season with minced garlic and black pepper. Fill breast with stuffing and fold making sure to keep intact. Use twine or toothpick to keep breast closed. Top with bacon slices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DCED73A2C7B424BB6A68F0A76FDD4B7', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 6, NULL, NULL, 2, NULL, 0, 'Place turkey breast on a roasting rack. Set over the burners that are not turned on. This will cook the turkey indirectly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1C2DAC2CCCDDA42AB7BC5C291194890', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 7, NULL, NULL, 2, NULL, 0, 'Close lid and let cook until internal temperatures reach between 160 to 165 F/70 to 75 C, usually about 20 minutes per pound.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E8C541D28DA28948850C4AA6C9339C65', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 8, NULL, NULL, 2, NULL, 0, 'Remove turkey breast from the grill, cover, and let stand for 10 to 15 minutes. Discard bacon, twine, or toothpicks.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43EBACDD2B286B4D9F826578F9F9CE98', X'CEAAECB44D0329478F5DBC1F30A24880', X'CEAAECB44D0329478F5DBC1F30A24880', 9, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEEA826DC31D0F4184683B9A62B4897F', X'E3440696C8C0DD4A9D43FD7D34A18865', X'E3440696C8C0DD4A9D43FD7D34A18865', 0, NULL, NULL, 2, NULL, 0, 'Trim the lettuce leaves from the stalk, then wash and dry each leaf. If you use a salad spinner to dry your lettuce, you might find it easier to do so after you chop it.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B20C0D277524743A0FAA0627F43BBC3', X'E3440696C8C0DD4A9D43FD7D34A18865', X'E3440696C8C0DD4A9D43FD7D34A18865', 1, NULL, NULL, 2, NULL, 0, 'Chop the lettuce into 1-inch pieces and transfer to a large salad bowl.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FF4103D8EF5B446A882C38ECA01E08B', X'E3440696C8C0DD4A9D43FD7D34A18865', X'E3440696C8C0DD4A9D43FD7D34A18865', 2, NULL, NULL, 2, NULL, 0, 'Slice the tomato into eight sections, then cut each section in half crossways, so that you have 16 chunks of tomato. Add them to the lettuce.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E3381CB9CC52342823D39D525510921', X'E3440696C8C0DD4A9D43FD7D34A18865', X'E3440696C8C0DD4A9D43FD7D34A18865', 3, NULL, NULL, 2, NULL, 0, 'Halve the avocado and remove the pit. Then section the avocado much as you did the tomato. Add the avocado chunks to the lettuce and tomato.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4BE950B364E81409266630B533EBD08', X'E3440696C8C0DD4A9D43FD7D34A18865', X'E3440696C8C0DD4A9D43FD7D34A18865', 4, NULL, NULL, 2, NULL, 0, 'Roughly crumble the goat cheese over the top.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80E34582582EB647A5EEAD13D6072B88', X'E3440696C8C0DD4A9D43FD7D34A18865', X'E3440696C8C0DD4A9D43FD7D34A18865', 5, NULL, NULL, 2, NULL, 0, 'Spoon a couple of tablespoons of dressing over the salad and gently toss. You want the dressing to lightly coat everything. Add more if you need to, and toss again.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C88055EC9D59924F9D090F9769FDE6E2', X'E3440696C8C0DD4A9D43FD7D34A18865', X'E3440696C8C0DD4A9D43FD7D34A18865', 6, NULL, NULL, 2, NULL, 0, 'Once the salad is dressed, serve it right away.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7D39D68A618BB459734C970B85DE7E9', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C179B80602BCD24F84424D476B8FDD42', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', 1, NULL, NULL, 2, NULL, 0, 'Using either a stand or hand mixer, whip together the cream and sugar.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0121634CE050649A356EDF11B62B201', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', 2, NULL, NULL, 2, NULL, 0, 'Beat in the yogurt, goat cheese, vanilla, and salt.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09702CF08FC70D4CAA11CFA4CB1B3167', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', 3, NULL, NULL, 2, NULL, 0, 'Pour equally into 4 jars or dessert glasses and refrigerate for about an hour to firm up.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67EA90C65164A04881A4DB392A31846F', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', 4, NULL, NULL, 2, NULL, 0, 'Add the sugar, honey, salt, and water to a saucepan and heat on medium just until the sugar dissolves.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D4268662BFC2B439BE6156305DA61AD', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', X'71B99EDFBA18E646ACEDE6B7AD69C7AD', 5, NULL, NULL, 2, NULL, 0, 'Distribute the halved figs evenly into the 4 servings and pour the honey glaze over the figs.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB6A9BB57CEA2B41ABA8F242EE3890CA', X'ED0680F52C690B41AAF00F1C27487D62', X'ED0680F52C690B41AAF00F1C27487D62', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9A6CF02459E1B4192CA9C34AC6BD4B7', X'ED0680F52C690B41AAF00F1C27487D62', X'ED0680F52C690B41AAF00F1C27487D62', 1, NULL, NULL, 2, NULL, 0, 'Either by hand or using an electric mixer, whisk the cream until it has soft peaks and a texture that is similar to whipped cream. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F17028CC054E04EAB05C43D119B7862', X'ED0680F52C690B41AAF00F1C27487D62', X'ED0680F52C690B41AAF00F1C27487D62', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, whisk together goat cheese and milk until the goat cheese has softened. Mix in chives. Gently fold in whipped cream.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D24373E621C7C247B1F8A6FDEF603248', X'ED0680F52C690B41AAF00F1C27487D62', X'ED0680F52C690B41AAF00F1C27487D62', 3, NULL, NULL, 2, NULL, 0, 'Serve chilled with bread and/or crackers on the side.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C95D2A301D5DE449639F15031CB726F', X'71A7E521499B314C96A63A9FAE23B65A', X'71A7E521499B314C96A63A9FAE23B65A', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat together cheese and butter until smooth. Mix in remaining ingredients, adjusting the seasonings, if necessary, and refrigerate, covered, for at least 1 hour for the flavors to marry.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D01EC553DB093A4889BDB93007EB71B6', X'71A7E521499B314C96A63A9FAE23B65A', X'71A7E521499B314C96A63A9FAE23B65A', 1, NULL, NULL, 2, NULL, 0, 'When ready to serve, let korozott come to room temperature before spreading on toasted or untoasted bread, or instead of mayo in a sandwich.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDEAE0514292854EA92037ABA773D154', X'2A54362AD729294493CD82C57EB0AFCB', X'2A54362AD729294493CD82C57EB0AFCB', 0, NULL, NULL, 2, NULL, 0, 'Prepare the dipping sauce: Place the guava paste in a small saucepan. Add the remaining ingredients (vinegar, soy sauce, sugar, and juice) and whisk together over low heat until the guava paste has completely melted. Remove from heat and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'305135DC87C7AC409E311FAEB6375181', X'2A54362AD729294493CD82C57EB0AFCB', X'2A54362AD729294493CD82C57EB0AFCB', 1, NULL, NULL, 2, NULL, 0, 'Prepare the cheese fritters: coarsely chop the green onions and place them in the bowl of a food processor. Pulse a couple of times for a few seconds each time, until the onions are more finely chopped. Add the goat cheese, the softened cream cheese, and the ricotta cheese to the food processor bowl. Add 2 eggs and the flour and process the mixture until it is well mixed. Add salt and pepper to taste, and pulse to mix. If desired, add one or more optional ingredients to the cheese mixture at this point, such as crumbled cooked bacon, crumbled blue cheese, grated manchego cheese, finely chopped sun-dried tomatoes, finely chopped olives, etc.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2F0C062D16E41419889BD147C7119CB', X'2A54362AD729294493CD82C57EB0AFCB', X'2A54362AD729294493CD82C57EB0AFCB', 2, NULL, NULL, 2, NULL, 0, 'Chill the cheese mixture for at least an hour.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EFFC0ECFBE33B48B63DB1DB1DDAB4B4', X'2A54362AD729294493CD82C57EB0AFCB', X'2A54362AD729294493CD82C57EB0AFCB', 3, NULL, NULL, 2, NULL, 0, 'Once the cheese mixture is cold, shape the mixture into 1-inch diameter balls. Roll each ball in the breadcrumbs and thoroughly coat the cheese with the crumbs. Place the cheese balls on a baking sheet and keep them chilled until you are ready to fry them.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB3E78A8EE55D14090CA6AD5DB86F432', X'2A54362AD729294493CD82C57EB0AFCB', X'2A54362AD729294493CD82C57EB0AFCB', 4, NULL, NULL, 2, NULL, 0, 'Heat 2 inches of vegetable oil in a deep skillet or saucepan to 350 F. Fry the cheese balls, in batches, until they are very golden brown. Carefully remove them from the oil with a slotted spoon and place them on a plate lined with paper towels. Serve cheese fritters warm, with the dipping sauce. If you are not going to serve the fried fritters right away, keep them warm in a 200 F oven until ready to serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D4863E6BA8771479C96E5F712D722EF', X'7E838DDC24F7CD45A2AC00785ED8027E', X'7E838DDC24F7CD45A2AC00785ED8027E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B997AFFB5841947A8F88CEAC638531A', X'7E838DDC24F7CD45A2AC00785ED8027E', X'7E838DDC24F7CD45A2AC00785ED8027E', 1, NULL, NULL, 2, NULL, 0, 'Drain tomatoes, reserving 2 teaspoons of the oil. Chop tomatoes and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2020500CC5E2CA4D86DC378B446F80E4', X'7E838DDC24F7CD45A2AC00785ED8027E', X'7E838DDC24F7CD45A2AC00785ED8027E', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl stir reserved oil, goat cheese, sesame seeds, and garlic until well combined. Stir in beans, tomatoes, and parsley. Season to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D154CF1FAB69E4CAD242E158FE174CA', X'7E838DDC24F7CD45A2AC00785ED8027E', X'7E838DDC24F7CD45A2AC00785ED8027E', 3, NULL, NULL, 2, NULL, 0, 'Serve with warm or cold pita wedges.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F932C5E6AAF734CBAB17D1F50A452DA', X'556D1353E450F6459A2C9473708D08A0', X'556D1353E450F6459A2C9473708D08A0', 0, NULL, NULL, 2, NULL, 0, 'Cut off the greens from the beets, and then trim the beets. Very young and tender beets can be scrubbed clean, but you will want to peel most beets. Cut the beets in half lengthwise, lay them cut-side flat and cut them into 1/4-inch-thick slices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0750493EAAE5F142B3D597111F30E843', X'556D1353E450F6459A2C9473708D08A0', X'556D1353E450F6459A2C9473708D08A0', 1, NULL, NULL, 2, NULL, 0, 'Heat a large frying pan over medium heat. Add 1 teaspoon of the olive oil, 1/4 cup of water, and the beets. Cover and cook until the beets are just barely tender, 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4E9A6CEDE1E5D4A942F165DBF69693D', X'556D1353E450F6459A2C9473708D08A0', X'556D1353E450F6459A2C9473708D08A0', 2, NULL, NULL, 2, NULL, 0, 'While the beets start to cook, rinse the beet leaves and cut out their thick stems (cut a `v` into the leaf to remove the thick stem while saving most of the tender leaf around it. Trim the stems and cut them into about 2-inch lengths. When the beets are just starting to seem tender, add the stems to the pan. Cover and let cook until the beets and beet stems are tender, about 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'296D21177E462B4FB04172020C61DB9C', X'556D1353E450F6459A2C9473708D08A0', X'556D1353E450F6459A2C9473708D08A0', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, chop the leaves and set them aside. Peel and thinly slice the garlic.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1925F3464A147A4BAA47C82422816F92', X'556D1353E450F6459A2C9473708D08A0', X'556D1353E450F6459A2C9473708D08A0', 4, NULL, NULL, 2, NULL, 0, 'Add the garlic to the pan, cook, stirring, until fragrant, about 1 minute. Add the chopped beet greens, cover, and cook until the leaves are wilted about 1 minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF2C4FC3B3F3F046A264088F602BD088', X'556D1353E450F6459A2C9473708D08A0', X'556D1353E450F6459A2C9473708D08A0', 5, NULL, NULL, 2, NULL, 0, 'Divide the warm salad between 4 plates. Spoon any pan liquids evenly over each serving. Drizzle each salad with the remaining olive oil and sprinkle with salt. Add any optional garnishes (lemon zest, orange zest, hazelnuts, feta, goat cheese) that you like.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D114FA953D6A74C92CEB18B7AB705CE', X'B3A58EB82614BD4196F9104B32208125', X'B3A58EB82614BD4196F9104B32208125', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F and butter a 13` x 9` baking dish. Bring a large pot of water to boil for the pasta.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D84F9FC4439CF047ABE8EB17EB779284', X'B3A58EB82614BD4196F9104B32208125', X'B3A58EB82614BD4196F9104B32208125', 1, NULL, NULL, 2, NULL, 0, 'Bring the chicken broth to a boil in a medium saucepan and reduce to 3/4 cup over high heat. Bring the cream to a boil in another large heavy saucepan and reduce it to 1-1/2 cups. Both of these processes should take about 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F34A7F1A14FD0E4D85A1A4DE21680249', X'B3A58EB82614BD4196F9104B32208125', X'B3A58EB82614BD4196F9104B32208125', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter and olive oil in another large heavy saucepan and cook the onions and garlic until translucent and tender. Cook the pasta until al dente and drain.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA0A672BF9D22547B519CCFA99BF9421', X'B3A58EB82614BD4196F9104B32208125', X'B3A58EB82614BD4196F9104B32208125', 3, NULL, NULL, 2, NULL, 0, 'Add the reduced cream and reduced broth to the onions, then add the mustard, cheddar, Gouda, Gruyere, and Brie cheeses and whisk until melted and blended. Add the seasonings. Add the pasta and mix well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF65F56A7D605F4B9B4775BAB278A2E7', X'B3A58EB82614BD4196F9104B32208125', X'B3A58EB82614BD4196F9104B32208125', 4, NULL, NULL, 2, NULL, 0, 'Spoon into the prepared baking dish. In a small bowl, mix bread crumbs, 3 tablespoons melted butter and Parmesan cheese; sprinkle over casserole. Bake for 20 to 30 minutes until the top browns and casserole are bubbly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D6F290FC2EEEA419559640D71D035FD', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Heat the oven to 350 F / 175 C / Gas Mark 4.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF00FE3785B15845A54AE42E99BB3A82', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 1, NULL, NULL, 2, NULL, 0, 'Peel, rinse, and dry the potatoes and slice as thinly as possible (a mandoline is good for this, but a sharp knife works too).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5C366B24C7DF24D855FB5EF5431EB73', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 2, NULL, NULL, 2, NULL, 0, 'In a large saucepan, place the potatoes, milk, cream, 1 of the crushed garlic cloves, salt to taste, along with a dash pepper and nutmeg (or mace).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25091D7D48006841A4E84F4D1B245244', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 3, NULL, NULL, 2, NULL, 0, 'Bring to a very gentle boil and simmer for a few minutes until the cream thickens.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5481DDB3530E844C89D8D3620317D018', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 4, NULL, NULL, 2, NULL, 0, 'Remove from the heat and add the grated Gruyère. Stir until the cheese has melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4D32437577CC94CBA0ED97004E43A11', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 5, NULL, NULL, 2, NULL, 0, 'Generously grease an ovenproof dish with 1 teaspoon of the butter, then rub the dish all over with the remaining lightly crushed clove of garlic.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'112D7F8108D79D4E8A3B91B03312F241', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 6, NULL, NULL, 2, NULL, 0, 'Pour the potato mixture into the dish, using a spoon to gently move the potatoes in the cream mixture to make sure they are all covered.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD00E95DAA216641B7091214BFE5F285', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 7, NULL, NULL, 2, NULL, 0, 'Finely slice the brie into long strips and lay across the top of the gratin. Dot the surface with the remaining 1 tablespoon butter. Season with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EB6048F255C964CB9BFC52AD05186D9', X'992BC7252DB49140B01BBF864028BED8', X'992BC7252DB49140B01BBF864028BED8', 8, NULL, NULL, 2, NULL, 0, 'Bake for about 40 minutes or until the top is bubbly and golden brown and the potatoes are soft when pierced with a knife. If the top is browning too quickly before the potatoes are cooked, cover with aluminum foil. Serve immediately. Enjoy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'915F0C4CB264824FB231E50434DF3C8E', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F10752CBEC23574A864ED1EAD5351EBD', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 1, NULL, NULL, 2, NULL, 0, 'Smear each slice of bread with cranberry jam.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'283EE5EC133CCE4EB46F5C057C913872', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 2, NULL, NULL, 2, NULL, 0, 'Add the slices of roasted turkey breast, spinach (trying to pack it down as much as possible), and brie to one half of the bread.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA19060DFF60C449AC464CC3E8617F2C', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 3, NULL, NULL, 2, NULL, 0, 'Top the sandwich with the other piece of bread.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BB1A123EEBCAF4F960A5B6BCEA46B50', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 4, NULL, NULL, 2, NULL, 0, 'Take a rectangle-sized sheet of wax paper or butcher paper and put the sandwich in the middle.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CDC85E30429074B91AFD813D920F272', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 5, NULL, NULL, 2, NULL, 0, 'Pull up two sides above the middle of the sandwich and roll down towards the sandwich until it''s snug.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB2821F99D627748A85B10F533EC7D94', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 6, NULL, NULL, 2, NULL, 0, 'Take the other two sides of the paper and fold them under the sandwich.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E740E73A43118E4CAA7C8385CB29014E', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 7, NULL, NULL, 2, NULL, 0, 'With a very sharp serrated knife, slice down the middle of the sandwich to separate the two halves.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A0CB0798D2FBF45B0913B8DC5DDA458', X'50BF73CD443178409AACA527261A7E23', X'50BF73CD443178409AACA527261A7E23', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3967787B22EA724F987245DE7310F1EE', X'8975A06E781FEC4DAD658CE152EF4C9E', X'8975A06E781FEC4DAD658CE152EF4C9E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20912EAF8A41B04488043CEB00F73CF1', X'8975A06E781FEC4DAD658CE152EF4C9E', X'8975A06E781FEC4DAD658CE152EF4C9E', 1, NULL, NULL, 2, NULL, 0, 'Spread the butter evenly on the cut sides of the ciabatta bread, or drizzle with the same amount of olive oil if you aren''t going to toast the sandwich.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FF407AD813DF6D4EB636640AB988264A', X'8975A06E781FEC4DAD658CE152EF4C9E', X'8975A06E781FEC4DAD658CE152EF4C9E', 2, NULL, NULL, 2, NULL, 0, 'Toast the bread halves in a toaster oven or bake them in a 375 F oven for 7 to 11 minutes or until the butter melts and starts to sizzle.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCD3FAA843F0AF40AE1B45BD9B38ABCA', X'8975A06E781FEC4DAD658CE152EF4C9E', X'8975A06E781FEC4DAD658CE152EF4C9E', 3, NULL, NULL, 2, NULL, 0, 'Arrange the Brie cheese slices on the top half of the ciabatta bread; toast again until the Brie melts, or bake for another 4 to 6 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9111217D5C308F42B7850E7CD9C298A3', X'8975A06E781FEC4DAD658CE152EF4C9E', X'8975A06E781FEC4DAD658CE152EF4C9E', 4, NULL, NULL, 2, NULL, 0, 'Make sandwiches with the basil leaves, tomato slices, and the toasted bread. Put the sandwich together, then cut into 3-inch segments. Press down lightly with your hand and serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33588BDF918EAA439D3C31D830870212', X'B4504F7915191348BF804D6A9CB6F685', X'B4504F7915191348BF804D6A9CB6F685', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40D9C58B1E1B3D4099E02998B91F871F', X'B4504F7915191348BF804D6A9CB6F685', X'B4504F7915191348BF804D6A9CB6F685', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61713C1598B0B242A90BA528E3A2D9AC', X'B4504F7915191348BF804D6A9CB6F685', X'B4504F7915191348BF804D6A9CB6F685', 2, NULL, NULL, 2, NULL, 0, 'In a heavy saucepan, heat brown sugar and butter over medium heat until smooth and thickened. Remove from heat. Add cinnamon, nutmeg, and amaretto. Mix well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D29CEC187D86164C9E7FD0EE86C812A5', X'B4504F7915191348BF804D6A9CB6F685', X'B4504F7915191348BF804D6A9CB6F685', 3, NULL, NULL, 2, NULL, 0, 'Place Brie in an oven-safe casserole dish. Top with sauce and sprinkle with almonds or walnuts. Bake 10 to 15 minutes until Brie is soft. (Instead of baking, you may microwave Brie at 30-second intervals until soft and warm.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62CBA3D03963904890D6A2C002B94B05', X'B4504F7915191348BF804D6A9CB6F685', X'B4504F7915191348BF804D6A9CB6F685', 4, NULL, NULL, 2, NULL, 0, 'Serve with baguette slices, toast rounds, crostini, crackers, or sliced apples.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46D7295740C9764C86D864892AC2B6A4', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93143BC61032EB45904BFA45E88BE962', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 1, NULL, NULL, 2, NULL, 0, 'Put the goat meat with the garlic and ginger pastes in a large bowl and mix well to coat the meat with the pastes. Set aside for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BDB538B4E167B4EBB62FF5AB13B43B5', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 2, NULL, NULL, 2, NULL, 0, 'While the meat is marinating, heat 3 tablespoons cooking oil in a large, deep pot or pan over medium heat. Add the finely chopped onions and fry until translucent.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF7B55DAB5FBC440B030CB405D9E7255', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 3, NULL, NULL, 2, NULL, 0, 'Add the curry leaves and green chilies, if using, and fry for 1 minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1324016ADD573E49AD3FE3E248D82B77', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 4, NULL, NULL, 2, NULL, 0, 'Add the powdered spices (coriander, cumin, turmeric, and garam masala) and salt to taste and mix well and cook for 2 to 3 minutes. Stir often to prevent burning.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF5E529AB6680E4AA35260C355E00F80', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 5, NULL, NULL, 2, NULL, 0, 'Add the marinated meat. Stir well and often and cook until the meat is browned.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43D19CADB046DF4497E6DCADBB67BDE2', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 6, NULL, NULL, 2, NULL, 0, 'Add 1 1/2 cups of hot water, stir, cover, and simmer until meat is tender. Keep checking at this stage as you do not want the meat overcooked and soft.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09A99409C11DE246AC39F3D1154B61F9', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 7, NULL, NULL, 2, NULL, 0, 'While the meat is cooking, make the tamarind puree. Put the tamarind in a plastic or glass bowl and pour 1/2 cup of hot water over it. Allow the mixture to stand for 5 to 10 minutes. Strain the tamarind and water mixture through a sieve (do not use a very fine sieve) into a bowl to get tamarind puree. Add this to the curry when you feel the meat is almost done. Stir well. Once the meat is cooked, set it aside and prepare the rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CEE39CE48A3AE4F897C92318669A1FF', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 8, NULL, NULL, 2, NULL, 0, 'Put the rice in a colander and wash under running water until the water runs clear. Place in a large, deep cooking pot (preferably one with handles).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7D53D30266F804D887FB1406391C0FE', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 9, NULL, NULL, 2, NULL, 0, 'Add enough water to fully cover the rice, usually at least 4 inches over the surface of the rice. Add 1 teaspoon of salt (or to taste). Bring the rice to a boil. Cook until almost done. (To determine when it has reached that stage, remove a few grains from the pot and press between your thumb and forefinger. The rice should mostly mash but will have a firm, whitish core.) Turn off the heat and strain through a colander and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1177550D2E0DEB49AE1C4BE52A697C4A', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 10, NULL, NULL, 2, NULL, 0, 'Heat 3 tablespoons of oil in a pan and fry the thinly sliced onions until caramelized and golden brown. Drain and set aside on paper towels for later use.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B41E9F8244800348951DC9349A0466E6', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 11, NULL, NULL, 2, NULL, 0, 'If you are using the food coloring, divide the rice into 3 equal portions and put into separate dishes. Add the orange food coloring to one portion of the rice and the green food coloring to another portion of the rice. Leave the third portion white. With each portion, mix the rice until all the grains are well colored. Set aside for 10 minutes and then mix all 3 portions of rice together in a bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAC886800D48AA45879D0FFF565B6E17', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 12, NULL, NULL, 2, NULL, 0, 'Preheat the oven or grill to 350 F/180 C and grease a deep dish or pot (which has a well-fitting cover).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'188E5CF4A7A49B4A8934E01B3FF5C150', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 13, NULL, NULL, 2, NULL, 0, 'Evenly layer the cooked rice and the meat (with its gravy) in the dish to form at least two sets of layers (rice-meat-rice-meat-rice). Garnish with the caramelized onions. Cover the dish tightly. If your dish does not have a cover use two layers of aluminum foil (shiny side of both layers facing down toward the rice) and secure onto a dish with baking string. If you are using a handi (a deep pot with a nicely fitting lid) which has a flat rim, you can seal it by making a firm dough with flour and water and pressing this over the joint of the handi''s rim and cover.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F9CDC6F59AF6145A554419D40D0E6F6', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 14, NULL, NULL, 2, NULL, 0, 'Place the dish in the oven or grill and cook for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92DDD3C781BAA545BD7936C59E33AD75', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 15, NULL, NULL, 2, NULL, 0, 'Turn off the oven or barbecue and let the dish sit in the oven or barbecue until you are ready to eat. It''s important that you only open when you are ready to serve. The way to serve biryani is to gently dig in with a spoon so you get through the layers.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75DEFFA8AC49B5488CA375098B139814', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', X'48EDAFBF8CA8DD41A26FF77D7E3BCB60', 16, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D78307A0E5AAA46A2D606D4E9EFBD27', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9530D9B0682EB4584B5D97C3FBDB55A', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 1, NULL, NULL, 2, NULL, 0, 'Bring the water to boil in a medium saucepan. Add the salt and butter and allow the butter to melt.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A6875A982E7C146B3D1EE3F8AD65A69', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 2, NULL, NULL, 2, NULL, 0, 'When the water has returned to a boil, stir in the rice. Let the water return to a light simmer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60657CB8D1F19645A80EDA2A2650F7A1', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 3, NULL, NULL, 2, NULL, 0, 'Stir again, cover the pot, and turn the heat down to low. Keep the rice simmering slightly, and keep the pot covered (you may have to peek after a few minutes to make sure the heat is at the correct temperature, but then let it cook, covered).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F58E9FA5A05FA438B00BE3CC4B0B8CB', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 4, NULL, NULL, 2, NULL, 0, 'Start checking to see if the rice is tender and all of the liquid is absorbed in about 17 minutes. It may take up to 25, especially if you are making a larger quantity of rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD8F81D3DD3C274999DFF323A537348C', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 5, NULL, NULL, 2, NULL, 0, 'When the rice is cooked, turn off the heat and let it sit for another couple of minutes to finish absorbing any liquid.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F9738DA90125A4BA2C04692F2E6B448', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 6, NULL, NULL, 2, NULL, 0, 'Take off the lid, fluff the rice with a fork, and let it sit for another 2 minutes so that some of the excess moisture in the rice dries off.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD0171F1576BAB499A780DD2BC51144D', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', X'89B2D8D2CBE5E3438BF7CAC5C25CFC77', 7, NULL, NULL, 2, NULL, 0, 'Serve with your side of protein and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDA738B1DE890143BDFFCECC89246FF3', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D329A418CB28E448964D47003E9F50BD', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 1, NULL, NULL, 2, NULL, 0, 'Cut tops off peppers; remove seeds and membranes. Chop edible part of tops and set aside. Rinse peppers under cold water. Place peppers in a large pot; cover with salted water. Bring to a boil; reduce heat, cover, and simmer for 5 minutes. Drain peppers and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD3DB022C2C80043B96758C8451D8B6A', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 2, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large skillet or sauté pan over medium heat until hot. Sauté chopped green pepper (from tops), chopped onion, and chopped celery for about 5 minutes, or until vegetables are tender. Add tomatoes, tomato paste, water, crushed garlic, basil, 1 teaspoon salt, and 1/4 teaspoon of pepper. Simmer for about 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E6C64D1D469BB4284309B67253774CC', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 3, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8830D7BDAEBD24A978A120146741074', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 4, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the beaten egg with remaining 1 teaspoon salt and 1/4 teaspoon pepper, and Worcestershire sauce. Gently stir to blend; add ground beef, cooked rice, and 1 cup of the tomato mixture. Mix well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4A693F1F378D748A99F3CB12CF6489A', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 5, NULL, NULL, 2, NULL, 0, 'Stuff peppers with meat mixture and place in a 3-quart baking dish. Pour remaining tomato mixture over the stuffed peppers.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'542B82E43236FF40AF221B4E688DE9FF', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 6, NULL, NULL, 2, NULL, 0, 'Bake in preheated oven for about 45 minutes, or until the meat mixture is fully cooked. The temperature of the meat mixture should be cooked to a minimum of 160 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58924F934B87864A911AC36AAA4225D9', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 7, NULL, NULL, 2, NULL, 0, 'Top stuffed peppers with a little shredded cheddar cheese just before they are done; bake until cheese is melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45BAFA172C45A444B1DE29F8729F8EE6', X'D6A89C95C47EDF43805172DF806CC6D8', X'D6A89C95C47EDF43805172DF806CC6D8', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A4913698353584381B5905A8709B933', X'9CB6B8EC8E761E46859E2BF549A1B1C1', X'9CB6B8EC8E761E46859E2BF549A1B1C1', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68F5AF792F5C45459BC1BA98C96EB73D', X'9CB6B8EC8E761E46859E2BF549A1B1C1', X'9CB6B8EC8E761E46859E2BF549A1B1C1', 1, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, combine all of the marinade ingredients until completely smooth.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BB1514FF31BD04AA43AD017EBA25831', X'9CB6B8EC8E761E46859E2BF549A1B1C1', X'9CB6B8EC8E761E46859E2BF549A1B1C1', 2, NULL, NULL, 2, NULL, 0, 'Add both the marinade and chicken to a bowl with a lid. Refrigerate for at least an hour or for a maximum of four hours. If you marinate for too long, the lime juice will actually start to cook the chicken.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D2DCBCCC41F124E9AA332D8BCED6C36', X'9CB6B8EC8E761E46859E2BF549A1B1C1', X'9CB6B8EC8E761E46859E2BF549A1B1C1', 3, NULL, NULL, 2, NULL, 0, 'When you''re ready to cook the chicken, heat your grill pan on high heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21587CBBA2DE114CB605B6E93D696397', X'9CB6B8EC8E761E46859E2BF549A1B1C1', X'9CB6B8EC8E761E46859E2BF549A1B1C1', 4, NULL, NULL, 2, NULL, 0, 'Sear the chicken on both sides, then turn the heat to medium-low and cook until the juices run clear, or the internal temperature reaches 165 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80D4ABCAFE12C14D98D4CF2474721119', X'9CB6B8EC8E761E46859E2BF549A1B1C1', X'9CB6B8EC8E761E46859E2BF549A1B1C1', 5, NULL, NULL, 2, NULL, 0, 'Remove the chicken from the grill pan and turn off the heat. Allow the chicken to rest for at least 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C81167E1DDEAC542A8D9D4B4665E58DC', X'9CB6B8EC8E761E46859E2BF549A1B1C1', X'9CB6B8EC8E761E46859E2BF549A1B1C1', 6, NULL, NULL, 2, NULL, 0, 'Then chop the chicken to your desired size and serve it on top of the rice along with any of the other toppings, like salsa, guacamole, beans, and shredded cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'404BA97E7DE3CB4A81A0B361BCE190F7', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'352D9AE3DBA73E4C960743D64E566A12', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 1, NULL, NULL, 2, NULL, 0, 'Put the almonds in a bowl of hot water (enough to cover them) and set aside for 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8368C7E7700E38489D6E20149AE65E06', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 2, NULL, NULL, 2, NULL, 0, 'After 10 minutes, remove the skins from all the almonds by pressing each one between your thumb and forefinger. The almonds will slip out of their skins.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15356FC3E1212C4E94316944BC5400B7', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 3, NULL, NULL, 2, NULL, 0, 'Mix the garlic and ginger pastes and the peeled almonds and grind the mixture into a smooth paste in a food processor.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEBD985271BD1745A6B69ECEEE370C6D', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 4, NULL, NULL, 2, NULL, 0, 'Wash the rice in a sieve and add enough water to fully cover the riceat least 4 inches over the surface of the rice. Add salt to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E465669CBD50234C890BE910E378A06A', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 5, NULL, NULL, 2, NULL, 0, 'Boil the rice until it is almost done. To determine when the rice has reached that stage, remove a few grains from the pot and press them between your thumb and forefinger. The rice should mostly mash but will have a firm, white core. Turn off the burner.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9ADCE63AF6A0B743BEC9789A32856AEA', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 6, NULL, NULL, 2, NULL, 0, 'Strain the rice through a colander and set it aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15E963C098AC9B4C9A17825E0B868F35', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 7, NULL, NULL, 2, NULL, 0, 'Heat 3 tablespoons of oil in a pan and fry two of the onions until they are caramelized and golden brown. Drain and set the onions aside on paper towels.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B4B954E7720E249B2C7795C680AB702', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 8, NULL, NULL, 2, NULL, 0, 'Heat 3 tablespoons of oil in another pan and add the whole spicescinnamon, cardamom, cloves, and peppercorns. Fry the mixture until the spices turn a little darker.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF855AD0CC3C5D4FAE8C89FF9F7A3ACD', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 9, NULL, NULL, 2, NULL, 0, 'Add the two remaining onions and fry them until they are translucent.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB84B1D029C412468815FD57A75666C3', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 10, NULL, NULL, 2, NULL, 0, 'Add the ginger-garlic-almond paste and fry for two to three minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8512CE9C7EB2B84299C05F4B4A7194A4', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 11, NULL, NULL, 2, NULL, 0, 'Add all the spice powderscoriander, cumin, and garam masala and mix well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46412336E13B894487D7A576C9E191F7', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 12, NULL, NULL, 2, NULL, 0, 'Fry the mixture until the oil begins to separate from the masala and then add the lamb or chicken. Continue frying until the meat is fully sealed; it will become opaque and lose its pink color.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'351B16D8E13DAD4F9F10E15CB09086D2', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 13, NULL, NULL, 2, NULL, 0, 'Add the yogurt, lime juice, stock, coriander and mint leaves and salt to taste (if needed). Mix well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2DA5E40B66B83499702B4ED44804EC1', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 14, NULL, NULL, 2, NULL, 0, 'Cover the pot and allow the dish to cook until the meat is tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A73D98F3CD4D645ABFB5E44F349BEEE', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 15, NULL, NULL, 2, NULL, 0, 'If you are using food coloring, divide the rice into three equal portions and put each portion into a separate dish. Add the orange food coloring to one portion of the rice and the green food coloring to another portion. Leave the third portion white.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAFAAA6D069B9641BAD6591F54A2581D', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 16, NULL, NULL, 2, NULL, 0, 'With each portion, mix the rice until all the grains are well colored.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54182A7D3D36EC448003B74F575E510D', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 17, NULL, NULL, 2, NULL, 0, 'Set the rice aside for 10 minutes, and then mix the three portions in a bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B87369AA45F5041B4E85A3AA7F2A643', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 18, NULL, NULL, 2, NULL, 0, 'Grease a deep baking dish and evenly layer the cooked rice and meat (and its gravy) to form at least two sets of layersrice-meat-rice-meat-rice. Garnish with the previously caramelized onions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'227AA8F0D409BE43B69973C6A8109E99', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 19, NULL, NULL, 2, NULL, 0, 'Cover the dish tightly. If the dish does not have a cover, use two layers of aluminum foil with the shiny side of both layers pointing down toward the rice, and secure the foil to the dish with baking string.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'395063BD95B0B44895F5406108CDC0D7', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 20, NULL, NULL, 2, NULL, 0, 'Put the dish in a preheated oven set at 350 F. Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B17628A6EA664CBDBDD8696154DF27', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 21, NULL, NULL, 2, NULL, 0, 'Turn off the oven and let the dish sit in the oven until you are ready to eat. Remove the foil only when you are ready to eat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9B0B383A296414FA96F8EB582F5017A', X'BE78558A34C90C4DBE7730A08EC237F6', X'BE78558A34C90C4DBE7730A08EC237F6', 22, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FEF6D1EF2D551419DAEFBBB36A018D6', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F89680C2C765348B0E13C05475F5F6D', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 1, NULL, NULL, 2, NULL, 0, 'Put the dried beans in a bowl of cold water and swish them around to rinse. Discard floating beans.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B64B91B3BD7E9645A24131F2CB75DC40', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 2, NULL, NULL, 2, NULL, 0, 'Drain the beans in a colander and pick them over, looking for any small stones or malformed beans.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85E6FCAF1E39524BB5FED5334AC99BA5', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 3, NULL, NULL, 2, NULL, 0, 'Put the beans in the crockery insert of a slow cooker.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94FE60CC8B6B3C4693CD8EA3A1E92B97', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 4, NULL, NULL, 2, NULL, 0, 'Add 1 1/2 quarts of water to the beans along with the celery, onion, ?garlic, bay leaves, and crushed red pepper flakes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78E0C3BE515E1E4E8041D65D43DAE831', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 5, NULL, NULL, 2, NULL, 0, 'Cover the slow cooker and cook the beans on LOW for about 6 to 7 hours, or until the beans are tender. Or cook them for about 3 1/2 to 5 hours on HIGH.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67843E099943914CA923E0467455D824', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 6, NULL, NULL, 2, NULL, 0, 'Heat the vegetable oil in a skillet over medium heat. Add the diced andouille sausage and cook, stirring, until the sausage is nicely browned.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1121DB2846C05408125A64D682A5D86', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 7, NULL, NULL, 2, NULL, 0, 'Add the sausage and bell pepper to the beans. Taste and add 2 to 3 teaspoons of Kosher salt, or to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88040BE8F3A35C44A45326C923FB0F74', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 8, NULL, NULL, 2, NULL, 0, 'Cover and continue cooking on LOW for about 1 hour longer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A45FFEB1401B7740937C2F86D1B0A4D0', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 9, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the rice in a rice cooker or on the stovetop following package directions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09F9B3BE308ADC4F81E074563B90702B', X'6177E91D474E7441AE5EDEB729E251C5', X'6177E91D474E7441AE5EDEB729E251C5', 10, NULL, NULL, 2, NULL, 0, 'Mound the rice on a plate or in a bowl, ladle the beans over the rice, and serve with freshly baked cornbread.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38F822FCCF0CBD449426CDF28825D8A2', X'6922C49D5F2A064AAC513A0E368FC144', X'6922C49D5F2A064AAC513A0E368FC144', 0, NULL, NULL, 2, NULL, 0, 'Place a 2/3 cup of cooked rice in a small rice bowl. If using leftover rice, reheat the rice in the microwave first.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47FC8B3910D32442A24CA9C45CBC04B8', X'6922C49D5F2A064AAC513A0E368FC144', X'6922C49D5F2A064AAC513A0E368FC144', 1, NULL, NULL, 2, NULL, 0, 'Add a 1/4 cup of grilled salmon and desired garnishes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A89DE978B311340A19D2467B5E1E8DA', X'6922C49D5F2A064AAC513A0E368FC144', X'6922C49D5F2A064AAC513A0E368FC144', 2, NULL, NULL, 2, NULL, 0, 'Pour a 1/2 to 2/3 cup of hot brewed green tea over the rice, salmon, and garnishes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FE71B4EF2969B498AD300AEC9A865DE', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B083F4E7B99DA7478CDABED527BE6BC7', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 1, NULL, NULL, 2, NULL, 0, 'Bring 8 cups of water to a boil in a large pot, and add the juice of 1/2 lemon and the salt. Carefully unroll the leaves (do not separate them). Turn off the heat and place the leaves in the hot water for 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFF596144DF7F44095A8A397D164898E', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 2, NULL, NULL, 2, NULL, 0, 'Remove the leaves and place them in a bowl and cover with cold water. When cooled, drain in a colander. It is not unusual for many of the outer leaves in the jar to be damaged or to tear while using. Set these aside to use later in the recipe.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D9D1F674B875C47B6B6D8F6287A96B1', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 3, NULL, NULL, 2, NULL, 0, 'To prepare the filling, start by soaking the rice for 10 minutes in hot water and drain. (Alternatively, sauté the rice with the onion.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82BEFDEE9815FF448A42AF49EAA64FD0', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 4, NULL, NULL, 2, NULL, 0, 'Sauté the onions in 1 tablespoon of olive oil until translucent, not browned.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96F01CBA812E4C4AA2B81032262A7F54', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 5, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the onion, ground beef, rice, remaining olive oil, dill, mint, juice of 1 lemon, and pepper. Mix well by hand.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9184EBB28D5634CB18E1F2861A77608', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 6, NULL, NULL, 2, NULL, 0, 'To fill and roll the leaves, gently separate one leaf and place it shiny-side down on a work surface. Place a pinch (up to a teaspoon) of the filling on the leaf at the point where the stem joins the leaf.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA56BC7C48377F42BEFC05A765B0623C', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 7, NULL, NULL, 2, NULL, 0, 'Fold up the bottom of the leaf over the filling, then each side inward in parallel folds, and roll up the leaf. The roll should be firm, not tight, as the filling will expand during cooking. Repeat until all the filling has been used.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F54C1F6AB749A4492718DCEAD7DC4A9', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 8, NULL, NULL, 2, NULL, 0, 'Because the leaves on the bottom can burn while the filling cooks, put a plate or wooden souvlaki skewers in the bottom of a heavy-bottomed pot (see tip below). The plate should fit snugly in the pot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A3112142BBFCB4F8C4EB15EC92DD0A3', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 9, NULL, NULL, 2, NULL, 0, 'If there are unused leaves or leaves that were torn and not used during the filling process, put them on the plate or on top of the skewers. Place the dolmathakia on top, packing them closely together (not squashed), seam side down, so they don''t unroll during cooking. Layer them until all are in the pot (two to three layers are best, but no more than four layers). Place several unused leaves over the top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24A34B8067B97B46A1ACF9CC05A59EE7', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 10, NULL, NULL, 2, NULL, 0, 'Take another plate and place it upside down on top of the dolmathakia, using something to weigh it down (a second plate works well). Add the 2 cups of water to the pot and cover. Bring the water to a gentle boil, add the remaining juice from the 1 1/2 lemons, reduce heat to low and simmer for approximately 50 to 70 minutes. Check to see if doneif the rice has cooked, they are done. If not, continue cooking for another 10 minutes and check again. Cooking time depends both on the type of pot used and the particular stovetop heating element.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33E4086493D4894C80171EF15B5BCF6D', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 11, NULL, NULL, 2, NULL, 0, 'If preferred, you can use a pressure cooker. No plates are needed but do use the skewers in the bottom. Pack the dolmathakia into the pressure cooker, add the 2 cups of water, close and cook for 15 to 20 minutes at the first pressure mark.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96EC985858A6AC48A16E0A7710DFD5CB', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 12, NULL, NULL, 2, NULL, 0, 'If you don''t have skewers or a plate that fits the bottom of the pot, line the bottom of the pan with unused or torn leaves.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E75BF439C4E9D4EA4697714291433DC', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 13, NULL, NULL, 2, NULL, 0, 'Leftover filling can be used to make stuffed vegetables like tomatoes, peppers, zucchini, and eggplant.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'413BC632A37C004CBFCB1C130F22A897', X'66154D53D1B15C4D93694F8ECFFF2D22', X'66154D53D1B15C4D93694F8ECFFF2D22', 14, NULL, NULL, 2, NULL, 0, 'To make stuffed grape leaves as a main course, use larger grape leaves and increase the amount of filling in each leaf to 1 tablespoon.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8225152D3DBBF46A279D723E4478750', X'6588ABBD567CFC4CB64CE723172499CD', X'6588ABBD567CFC4CB64CE723172499CD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA37A8137AA6594CA914994CC91118ED', X'6588ABBD567CFC4CB64CE723172499CD', X'6588ABBD567CFC4CB64CE723172499CD', 1, NULL, NULL, 2, NULL, 0, 'Spray the slow cooker stoneware with cooking spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80B96D9A9780EF4094EFDF98B6D97E55', X'6588ABBD567CFC4CB64CE723172499CD', X'6588ABBD567CFC4CB64CE723172499CD', 2, NULL, NULL, 2, NULL, 0, 'Combine all ingredients in the slow cooker.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A88008E9D52C1C488DCA7F504D7286BB', X'6588ABBD567CFC4CB64CE723172499CD', X'6588ABBD567CFC4CB64CE723172499CD', 3, NULL, NULL, 2, NULL, 0, 'Cook on HIGH 2 to 3 hours or LOW 4 to 5 hours (follow the directions for your slow cooker, as they may vary depending on the model)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'356B68FEC1E0B4419B8BDCE6A1E835E1', X'6588ABBD567CFC4CB64CE723172499CD', X'6588ABBD567CFC4CB64CE723172499CD', 4, NULL, NULL, 2, NULL, 0, 'Serve warm and add toppings if desired.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2E363068F8587478E39B9781B14973B', X'6588ABBD567CFC4CB64CE723172499CD', X'6588ABBD567CFC4CB64CE723172499CD', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1D5F0D105A29D479267F95C31F39A9B', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 0, NULL, NULL, 2, NULL, 0, 'Place rice, stock, and olive oil in a large pot and bring to a boil. Reduce heat to low and cover, cooking for 20 minutes. Remove from heat and add the butter and parmesan cheese. Stir and add more broth if it is too dry. Cover and let sit off of the heat for 10 minutes. Allow the rice to cool to room temperature before beginning to make the arancini. You can make the risotto ahead of time and store in the refrigerator until you are ready to assemble.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAA5036067EC4F45810B37EFD04B5EA6', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 1, NULL, NULL, 2, NULL, 0, 'Take a golf ball sized scoop of risotto rice, make a well in the center and place the cheese inside. Wrap the rice around the piece of cheese and place on a parchment lined baking tray. Repeat with remaining rice and cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23891ED42D6F5548BFAB052BAF6B029A', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 2, NULL, NULL, 2, NULL, 0, 'Freeze the balls on the baking tray for 2-3 hours or overnight.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E17261971BE034C8A1E51573DCCA2C1', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 3, NULL, NULL, 2, NULL, 0, 'Place the flour in a low dish, add 1/2 tsp of salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'019DF97DF53CA644B1A3095F1E84627D', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 4, NULL, NULL, 2, NULL, 0, 'Place eggs in another low dish with the milk. Whisk together and add a 1/2 tsp of salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF63BFD7D0EA264D8518E298FE61D534', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 5, NULL, NULL, 2, NULL, 0, 'Place the breadcrumbs in another low dish and add 1/2 tsp of salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57948A6AFA5E474280A5F5CEABE4EE86', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 6, NULL, NULL, 2, NULL, 0, 'Dip the rice ball into the flour mixture and coat. Then dip into the egg mixture and coat. Then dip it into the Panko bread crumbs and roll to coat completely. Repeat with the remaining rice balls.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'717D0F352E031F4D88AE927EBF672A18', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 7, NULL, NULL, 2, NULL, 0, 'Heat the oil in a heavy bottomed, deep pot on high until it is about 375 F. Turn it down to medium heat and add in a few rice balls at a time to cook.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95A597F966DD744589DC81B8CD3CA4C7', X'90C54B4CC2599E46970311B00B3AF442', X'90C54B4CC2599E46970311B00B3AF442', 8, NULL, NULL, 2, NULL, 0, 'Rotate them in the oil as they are cooking to ensure browning on all sides. Remove the rice balls from the oil when they are golden brown. Place the rice balls on another parchment lined baking dish and keep in a 250 F oven while cooking the remaining arancini.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'514E3393925DE54ABD74A24B613980D3', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 0, NULL, NULL, 2, NULL, 0, 'Put the whole squash in a large stock pot and fill it with water to cover by at least an inch. Bring it to a boil. Reduce the heat just enough to maintain a moderate boil without overflow and cook the squash until they feel fork tender, about 15 to 20 minutes, depending on size.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9723632A2DC094EB392D43517E13E91', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F. Line a 9-by-13-inch baking dish with foil and spray it with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75F0E3DBA679E54A8FB0F0BBE2E41D95', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a medium sauce pot or large sauté pan over medium heat. Add the green onions and garlic, and cook, stirring, for 1 minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06946DD1F436D749BA8CB388988E20E5', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken broth and bring it to a boil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB6BDA1CF7B1E242923AE05C56A45786', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 4, NULL, NULL, 2, NULL, 0, 'Add the rice, 1/4 teaspoon salt, and the pepper. Reduce the heat, cover the pot, and simmer the rice for 20 minutes, or until the grains absorb the liquid and turn tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'098D15D0BCC323499A87DD008E1873DF', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 5, NULL, NULL, 2, NULL, 0, 'Add the 1/2 cup Parmesan cheese and spinach, stirring until the spinach wilts. Set it aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32C2526EC271BB4AB51A0E6AF0645B76', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 6, NULL, NULL, 2, NULL, 0, 'Cut the tops off the squash and scoop out some of the insides to make room for the rice, being careful not to break through the sides or bottom of the squash.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4EC94F3CD816540B3AF6D747B8F44D2', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 7, NULL, NULL, 2, NULL, 0, 'Mound some of the rice mixture into each squash using a spoon or cookie scoop. Arrange them in the prepared baking pan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88538A47D4F82F4FBB580292162BBA43', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle each stuffed squash lightly with salt and pepper and sprinkle a little more Parmesan cheese over the top of each one.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'225EC5991D797745AFD0224FAA9C532B', X'E13CEF6BC6A6AC498340C7A9B332B137', X'E13CEF6BC6A6AC498340C7A9B332B137', 9, NULL, NULL, 2, NULL, 0, 'Bake the squash in the preheated oven for 15 to 20 minutes until heated through and the cheese turns lightly brown.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'727D9D21FAA7C54494B17C7A708D426D', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE27E66912C0CF4A9F22656E8B9A6BE2', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 1, NULL, NULL, 2, NULL, 0, 'Cut the tops off of the bell peppers and rinse them under cold water; remove seeds and cut away the white ribs, which might be bitter. Chop the edible part of tops and set aside. Place the peppers in a large pot and cover with salted water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF01C54D94359D4AB07F658B613B7DA6', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 2, NULL, NULL, 2, NULL, 0, 'Bring to a boil. Reduce heat, cover, and simmer for 5 minutes. Drain and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2941BBE7E702546AD281BCF7C70E6B2', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 3, NULL, NULL, 2, NULL, 0, 'Heat the olive oil and butter in a large skillet over medium heat until oil is hot and the butter is foamy. Sauté the chopped bell pepper (from the tops), chopped onion, and chopped celery for about 5 minutes, or until vegetables are tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7654D1ABE08C6408E8933745993AA4F', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 4, NULL, NULL, 2, NULL, 0, 'Add the (undrained) canned diced tomatoes, tomato sauce, crushed garlic, oregano, basil, 1 teaspoon salt, and 1/4 teaspoon pepper. Bring to a simmer and cook for about 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'276BDA64363C594BA140507BD0966170', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 5, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, combine the egg with the remaining 1 teaspoon of salt, 1/4 teaspoon pepper, and Worcestershire sauce. Gently stir to blend; add ground beef, cooked rice, and 1 cup of the tomato sauce mixture. Mix well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C72998876C484D4FB4FB18455FADE13D', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 6, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A73A6B5A309245B3CBF23C2DE93E35', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 7, NULL, NULL, 2, NULL, 0, 'Stuff the peppers loosely with the ground beef mixture and place them in a 13 x 9 x 2-inch baking pan. Pour the remaining tomato mixture over the stuffed peppers.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97CFC76B21D1774796DC0B2E3C2853B4', X'A4B714F869586445AC232E6C74DB8FFE', X'A4B714F869586445AC232E6C74DB8FFE', 8, NULL, NULL, 2, NULL, 0, 'Bake the peppers for about 45 minutes, or until the meat mixture is thoroughly cooked.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14009959CA92754BB920358C76473B8F', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E49C0577E3A884ABF2C93115738BD6A', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 1, NULL, NULL, 2, NULL, 0, 'In a medium-sized saucepan, heat oil over medium heat. Add the fresh onion and sauté for 1 or 2 minutes until softened and translucent.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36EB36C6863EF943866015A1BA00F5AC', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 2, NULL, NULL, 2, NULL, 0, 'Add the dry rice and continue sautéeing, stirring frequently, for about 5 minutes or until rice becomes a golden brown color. Do not allow the rice to burn.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'928F7E05535DA641832E96FFB0DAF1E7', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 3, NULL, NULL, 2, NULL, 0, 'Add the garlic to the rice and sauté for one more minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68A5F7FF40CC394CBE05BD31B01AC275', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 4, NULL, NULL, 2, NULL, 0, 'Take the pan off the heat; add the chicken broth and tomato sauce, plus the parsley and/or vegetables, if using. Stir, return pan to heat and bring to a boil. Turn the heat to low and cover.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D6857F4BA326745AFE5ACA6FFD794A9', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 5, NULL, NULL, 2, NULL, 0, 'Let simmer for 20 minutes without taking the lid off. After 20 minutes, remove the lid and carefully insert a spoon into the cooked rice. (If the bottom of the pan is dry, your rice is done; if there is still some broth visible, allow it to cook for a few more minutes.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50895325C9A51244929F48DF3BA38D02', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 6, NULL, NULL, 2, NULL, 0, 'Once the rice is done cooking, remove it from the heat and let it sit, covered and undisturbed, for another 10 minutes or so.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26E8C99809A4A34D93A16344B21C6BC5', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 7, NULL, NULL, 2, NULL, 0, 'Fluff your rice with a fork before serving. Use your delicious homemade rice as a side for almost any savory Mexican dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D8069EC3497B4488B065AFC7E7662C7', X'B91CAA183A5CE9439E1DA2C3BD5335D0', X'B91CAA183A5CE9439E1DA2C3BD5335D0', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E54EFA377EF2AC408C76D167631E0138', X'7A86F0ADC8177546AE6E411CF152033F', X'7A86F0ADC8177546AE6E411CF152033F', 0, NULL, NULL, 2, NULL, 0, 'In a large Dutch oven or large saucepan, combine the black-eyed peas, ham bone or ham hocks, and 6 cups water. Cut 1 of the onions in half and add it to the pot along with the garlic halves and bay leaf. Bring to a boil over high heat, reduce the heat to medium-low, and simmer gently until the beans are tender but not too soft and mushy, 2 to 2 1/2 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6656FEC9DFA5DB44B9071426324CE4E7', X'7A86F0ADC8177546AE6E411CF152033F', X'7A86F0ADC8177546AE6E411CF152033F', 1, NULL, NULL, 2, NULL, 0, 'Remove the ham bone or hocks, cut off the meat; dice and set aside. Drain the peas and set aside. Remove and discard the bay leaf, onion pieces, and garlic.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'127F19A4865520459509FAD5686E1B94', X'7A86F0ADC8177546AE6E411CF152033F', X'7A86F0ADC8177546AE6E411CF152033F', 2, NULL, NULL, 2, NULL, 0, 'Add 2 1/2 cups of water to the pot and bring to a boil. Add the rice, cover, and simmer until the rice is almost tender about 10 to 12 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A36BFBC571C88B42AFCF544E94B16AB8', X'7A86F0ADC8177546AE6E411CF152033F', X'7A86F0ADC8177546AE6E411CF152033F', 3, NULL, NULL, 2, NULL, 0, 'Mince the remaining onion then add to the rice along with the peas, tomatoes, and their juices, red and green bell pepper, celery, jalapeno pepper, Creole seasoning, thyme, cumin, and salt. Cook until the rice is tender, 5 to 8 minutes. Stir in the sliced green onions and the reserved diced ham.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E7B083A3D75BE4ABE70F47DD1311653', X'E196DA9628864449AB3776DF113CDD9F', X'E196DA9628864449AB3776DF113CDD9F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2808C0B245F4794CA850825B02660E85', X'E196DA9628864449AB3776DF113CDD9F', X'E196DA9628864449AB3776DF113CDD9F', 1, NULL, NULL, 2, NULL, 0, 'Rinse the rice until the water runs clear. Drain in a colander and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6222A261951D0744B2766D73EA689E03', X'E196DA9628864449AB3776DF113CDD9F', X'E196DA9628864449AB3776DF113CDD9F', 2, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, bring the 2 cups of water to a boil. Add the salt, stir, and then add the rinsed and drained rice. Stir with a fork.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'506B25A9777DEE4F8F0BCD3652FD21D6', X'E196DA9628864449AB3776DF113CDD9F', X'E196DA9628864449AB3776DF113CDD9F', 3, NULL, NULL, 2, NULL, 0, 'Reduce the heat, cover the rice, and let it simmer for 20 minutes. Check it after 15 minutes to see if all the water has evaporated. If it has, the rice is ready. If not, replace the lid and let the rice simmer an additional 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E309E2DE7A0BD044B578CDFBAADCED12', X'E196DA9628864449AB3776DF113CDD9F', X'E196DA9628864449AB3776DF113CDD9F', 4, NULL, NULL, 2, NULL, 0, 'Remove from the heat, fluff with a fork, and transfer into a serving dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D1F2E3BC4D81540B7D0D3A1AD92D9A7', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D002D3E71C373A4A9DA24C439F4DC146', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 1, NULL, NULL, 2, NULL, 0, 'Gently roast and then coarsely grind the coriander seeds into powder. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03C2779F50E41E48A30172571C6EAC06', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a pan and add the mustard seeds, curry leaves, and green chilis. Fry the mixture until the splattering stops.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2332DF4E70CBF642B2276CC35A0BC65C', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 3, NULL, NULL, 2, NULL, 0, 'Add the ginger and peanuts. Fry the mixture for another minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3451E490CD132742B840F686A92490D9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 4, NULL, NULL, 2, NULL, 0, 'Add the turmeric powder and turn off the burner.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'250D53B28DE7A44BABA49071EA817438', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 5, NULL, NULL, 2, NULL, 0, 'Add the lemon juice and mix well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B50983CA1ABE94E82C76B344F46256F', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 6, NULL, NULL, 2, NULL, 0, 'Add the rice, roasted coriander powder, and salt to taste and mix thoroughly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF77C735B9BEC14A81A96BEEDB423199', X'BFA1433497FC6A4FBD5AB353C1F19FC9', X'BFA1433497FC6A4FBD5AB353C1F19FC9', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7EA3E430122ED4CA766D33E233E67ED', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 0, NULL, NULL, 2, NULL, 0, 'Put the rich in a fine sieve and wash under running water till the water runs clear. Now put the rice into a deep bowl and fill the bowl with enough warm water to fully cover the rice. Soak for 30 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'593D854F79E8624ABC2655DE383D57BC', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 1, NULL, NULL, 2, NULL, 0, 'After soaking, put the rice in a deep pot and add enough water to cover the rice and 2` above it. Cook the rice on medium heat until almost done - to test, squeeze a few grains between thumb and index finger. They will be squishy but will not fully mash. Drain the almost cooked rice through a sieve and keep aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B50FB0642512904592DD419E29CB1C75', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 2, NULL, NULL, 2, NULL, 0, 'Marinate the prawns in a deep, non-metallic bowl, by adding the yogurt, lemon juice, turmeric powder, ginger and garlic pastes and salt to taste. Mix well and set aside for at least 1 hour.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F639A5C3A1F3DD439F7A3239D9DD3275', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 3, NULL, NULL, 2, NULL, 0, 'Heat a griddle or flat pan on a medium flame and gently roast all ingredients mentioned under ''For dry masala/ spice mix'' till they start to become slightly darker and give off a faint aroma.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'270F52A87FF80548AA98D8C52EB8F0D2', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 4, NULL, NULL, 2, NULL, 0, 'Peel the cardamom pods and remove seeds. Throw skins away. Now grind all the roasted ingredients to a fine powder in a clean, dry coffee grinder.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7228CE9C2165714B9F503E9BBA6611F6', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 5, NULL, NULL, 2, NULL, 0, 'Grind the ingredients mentioned under `For wet masala` to a smooth paste in a food processor.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E68874997F37F4098B1FDEE6896F17F', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 6, NULL, NULL, 2, NULL, 0, 'Heat the cooking oil in a deep pan till hot and then fry 1 of the 3 portions of sliced onions until crispy and golden. When done, drain and remove from the oil and keep aside on paper towels to garnish biryani later.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8570749E90CD4F459C70D095CE507A41', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 7, NULL, NULL, 2, NULL, 0, 'In the same oil, add the remaining sliced onions and fry till soft. Now add the tomatoes and fry till soft.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAC115A90F27994E9C60D53844616C38', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 8, NULL, NULL, 2, NULL, 0, 'Add all the dry and wet masala ingredients prepared earlier and fry till the oil begins to separate from the whole mix.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'854FC53D1A03A14091773F029C991736', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 9, NULL, NULL, 2, NULL, 0, 'Now add the prawns and all the marinade to this spice mix. Stir and allow to cook till the prawns are done. Do not overcook as the prawns will get rubbery in texture. Season with salt if required. Remove from fire.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67F780F295FC2046B5E37708F44D71EA', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 10, NULL, NULL, 2, NULL, 0, 'Soak the saffron strands in 2 to 3 tbsp. of warm milk.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41B5A9B5AFAEAF468F6996B1387B8D66', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 11, NULL, NULL, 2, NULL, 0, 'Take a large, deep ovenproof baking dish (it MUST have its own oven-proof cover) and grease lightly with cooking oil. Layer the rice and prawn as follows: rice - prawn - rice. End with a layer of rice. Keep a few prawns aside to garnish at the end.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E65809CD665646A5F9AFFB6CBACE70', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 12, NULL, NULL, 2, NULL, 0, 'Pour the saffron infused milk all over the top of the last layer of rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'749A72F7578D5646B5F37A2AACB1A323', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 13, NULL, NULL, 2, NULL, 0, 'Garnish with the crispy fried onions, chopped coriander leaves. Cover the dish and seal tightly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B040230330A98C4597B42B0BB4691348', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 14, NULL, NULL, 2, NULL, 0, 'Bake in a 350 F oven for 30 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0579910EE1F7B499329BC7B9DA0DF08', X'390664E3DA006643A91D8BF87CB4A77C', X'390664E3DA006643A91D8BF87CB4A77C', 15, NULL, NULL, 2, NULL, 0, 'Serve hot with a raita and green salad of your choice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21496B819068AA4BBD5E2BD7ACD6C9F5', X'97DBA97F9796924287C67EC139D136F0', X'97DBA97F9796924287C67EC139D136F0', 0, NULL, NULL, 2, NULL, 0, 'Select an amount of rice depending on whether you are cooking for two or more. Wash brown rice well and drain until water runs almost clear. Allow rice to soak for 30 minutes, time permitting. Cook rice according to your rice cooker''s instructions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82A35C319631B64EBC2BCE160BDC1138', X'97DBA97F9796924287C67EC139D136F0', X'97DBA97F9796924287C67EC139D136F0', 1, NULL, NULL, 2, NULL, 0, 'While the rice is cooking, cook frozen shelled edamame in boiling water for about 5 minutes until the beans are tender. Drain and rinse with cold water. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAED545B4271C045B78E41D48DB18893', X'97DBA97F9796924287C67EC139D136F0', X'97DBA97F9796924287C67EC139D136F0', 2, NULL, NULL, 2, NULL, 0, 'After the rice is done cooking, allow it to rest in the rice cooker for about 15 minutes. Transfer the rice to a large shallow wooden sushi dish (or Japanese `oke`).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9FA45C9374A684491107278DFC3E9B6', X'97DBA97F9796924287C67EC139D136F0', X'97DBA97F9796924287C67EC139D136F0', 3, NULL, NULL, 2, NULL, 0, 'Gently incorporate the Wakame-Chazuke (Green Tea) Furikake seasoning with the rice. If available, use a Japanese `shamoji` or rice paddle for ease of use.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B80D1DC19321741B08797A41F9FDEC0', X'97DBA97F9796924287C67EC139D136F0', X'97DBA97F9796924287C67EC139D136F0', 4, NULL, NULL, 2, NULL, 0, 'Add the bottled Nametake (seasoned mushrooms) to the rice and gently mix.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CA547984741B540A6655661CB082553', X'97DBA97F9796924287C67EC139D136F0', X'97DBA97F9796924287C67EC139D136F0', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked shelled edamame. Serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1196BA5189B03D47B56235A3C7857D42', X'B62A54ABA8202B498E14EAAD3A38BE3B', X'B62A54ABA8202B498E14EAAD3A38BE3B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17AEB6B8C827E54DB3385BFE96EF91D8', X'B62A54ABA8202B498E14EAAD3A38BE3B', X'B62A54ABA8202B498E14EAAD3A38BE3B', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD711FF413722D4C96E6D8C025728F1A', X'B62A54ABA8202B498E14EAAD3A38BE3B', X'B62A54ABA8202B498E14EAAD3A38BE3B', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the ground beef with the rice, the water, chopped onion, seasoned salt, garlic powder, and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBCADFA979B85C44BDE9F5F52C991ADA', X'B62A54ABA8202B498E14EAAD3A38BE3B', X'B62A54ABA8202B498E14EAAD3A38BE3B', 3, NULL, NULL, 2, NULL, 0, 'Shape the ground beef mixture into 1 1/2-inch balls. Use a tablespoon or small cookie scoop to help make them uniform in size. (Or you can weigh them as you shape them.) Place them in an ungreased 2-quart shallow baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A42DA5B4BB16564FB8DB400D95F41964', X'B62A54ABA8202B498E14EAAD3A38BE3B', X'B62A54ABA8202B498E14EAAD3A38BE3B', 4, NULL, NULL, 2, NULL, 0, 'Make the sauce by mixing together the tomato sauce, the water, and Worcestershire sauce in a measuring cup or bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73C365C478E7D5469DB1D126497505D5', X'B62A54ABA8202B498E14EAAD3A38BE3B', X'B62A54ABA8202B498E14EAAD3A38BE3B', 5, NULL, NULL, 2, NULL, 0, 'Now it''s time to cook the meatballs. Continue reading for the oven method, or scroll down to the stovetop method.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5FC214BCE161443BBE1DD713EA0A458', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'146216AE4C13404B8AACC51FAB3A0DA8', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 1, NULL, NULL, 2, NULL, 0, 'Heat the extra virgin olive oil (or annatto/achiote oil if using) in a pot over medium heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C56B233C9F43B44F9331CD506C56D945', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 2, NULL, NULL, 2, NULL, 0, 'When hot enough, add the sofrito and saute lightly until fragrant, about 1 or 2 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'618870584C32FD4FAD2949AB59321017', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 3, NULL, NULL, 2, NULL, 0, 'Add the sazón packet (if using), Spanish Manzanilla olives, bay leaf, salt, and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0916E9420ACE69428551A17FC35B5DF9', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 4, NULL, NULL, 2, NULL, 0, 'Stir the mixture to combine the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D5B2DC4535FE241A4080184D29B6A9D', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 5, NULL, NULL, 2, NULL, 0, 'When the mixture starts to bubble, add the corn kernels and rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3009493C2DC4B747ACEB0A9366C60B47', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 6, NULL, NULL, 2, NULL, 0, 'Thoroughly stir the mixture again to coat the rice and corn with color.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74CD8E3D210EDE418940E6F7711094A6', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 7, NULL, NULL, 2, NULL, 0, 'Slowly pour in the water (or chicken stock), while stirring.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76A2E8C6EF1A65428006A2D4DD4AD4AD', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 8, NULL, NULL, 2, NULL, 0, 'Bring the ingredients to a rolling boil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7100C5430978344BFEC0D558E33F2B7', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 9, NULL, NULL, 2, NULL, 0, 'Allow it to boil for 1 to 2 minutes, then stir the rice mixture again.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24F5291CEC1CAB468C3BD3A05C429692', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 10, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and cover with a tight-fitting lid.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32CAB8266304F64486ED08DCF0E6D2D6', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 11, NULL, NULL, 2, NULL, 0, 'Continue to cook over low heat for 30 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4D34F7EB07EF04496582D08A3C94757', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 12, NULL, NULL, 2, NULL, 0, 'Remove the lid and fluff the rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B75C71E81E5ED14B9FAAEA7AEB324FC2', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 13, NULL, NULL, 2, NULL, 0, 'Then cover again, remove from the heat, and let it sit a few minutes more before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38DC3E38BC1EE24D8DFE86A8FDD453A3', X'8864DA6D7A22B84D920F8C26A3BA170C', X'8864DA6D7A22B84D920F8C26A3BA170C', 14, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20220EC4A42C15429CA7217C3F04CC62', X'6E65C12A86B2D247B010BD46A32DD4B5', X'6E65C12A86B2D247B010BD46A32DD4B5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94999EC0CAFA3C488296DBC20A72B8ED', X'6E65C12A86B2D247B010BD46A32DD4B5', X'6E65C12A86B2D247B010BD46A32DD4B5', 1, NULL, NULL, 2, NULL, 0, 'Heat the broth to nearly boiling, then hold warm over low heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75CA59A2EBCDF04CA61DC21DDC8A8020', X'6E65C12A86B2D247B010BD46A32DD4B5', X'6E65C12A86B2D247B010BD46A32DD4B5', 2, NULL, NULL, 2, NULL, 0, 'In a deep skillet or heavy bottomed pot placed over medium heat, saute the rice, onions and peppers in the oil. When the rice begins to brown, add the garlic and tomatoes. Cook for several minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A21F83C5E71E343BB1880CEC4B0C61A', X'6E65C12A86B2D247B010BD46A32DD4B5', X'6E65C12A86B2D247B010BD46A32DD4B5', 3, NULL, NULL, 2, NULL, 0, 'Add the broth, tomato paste, cilantro and spices. Bring the liquids to a boil then reduce the heat, cover the pan, and simmer gently for 25 to 30 minutes without stirring, until the rice is tender and has absorbed most of the liquids.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC56CB07BA6CDF419B9CD4F667572089', X'6E65C12A86B2D247B010BD46A32DD4B5', X'6E65C12A86B2D247B010BD46A32DD4B5', 4, NULL, NULL, 2, NULL, 0, 'Remove the rice from the heat and lightly stir with a fork to fluff the rice and mix the vegetables throughout. It should still be a little saucy; if not, gently mix in a few tablespoons of hot water. Cover and leave the rice sit for at least five minutes before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'591F44A3AAEF4A40AAF836ADB444A619', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 0, NULL, NULL, 2, NULL, 0, 'Steam rice according to your rice cooker''s instructions or on the stovetop.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE64A6E38F85584A9223F5AF7303D230', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 1, NULL, NULL, 2, NULL, 0, 'While the rice is cooking, boil green beans and slice on the diagonal into bite-sized pieces. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B217A7ECF87BF947994AEA237604BEE8', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl scramble eggs and add a pinch of salt. Heat medium size pan with olive oil and cook a thin layer of egg to make kinshi tamago (thin omelet). Remove from pan and let cool on cutting board. Cut into thin strips and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'443AA9CA09599E4AA0A94634CC471BB2', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 3, NULL, NULL, 2, NULL, 0, 'Boil a small pot of water to cook the baby broccolini.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33D03DCDD0F990458792F6DCBFAD22F3', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 4, NULL, NULL, 2, NULL, 0, 'Thaw cooked frozen shrimp, remove tails if desired, and then slice in half vertically to create 6 halved pieces of shrimp. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF1BDCBA50260E4B88F82702CF750567', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 5, NULL, NULL, 2, NULL, 0, 'Mix in sushi rice vinegar powder with cooked rice. You may or may not use all of the cooked rice, as it will depend on how thick you make your rice layers, as well as the diameter of the cake pan. Be sure to use diagonal cutting motions with the side edge of the rice paddle (pre-moistened with water) to mix with the warm rice. This helps to prevent the rice from getting mashed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D7A07E7A74C7E4986DCA5F59CF5346E', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 6, NULL, NULL, 2, NULL, 0, 'Line cake pan with a large sheet of plastic wrap. It will help you to remove the sushi with ease. If you are using a springform pan (with removable bottom), there''s no need to line the pan with plastic wrap, but instead, make sure the pan is wet with water, to prevent the rice from sticking too much from the side of the pan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2837C173A92AE4BA8C930D9BEFB7322', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 7, NULL, NULL, 2, NULL, 0, 'Add a layer of rice on the bottom of the pan. Top with pink sakura denbu shredded codfish. Layer more rice on top and then garnish with sliced green beans. Layer with the last layer of rice. Garnish top with thinly sliced egg, baby broccolini, shrimp, ikura (salmon roe caviar), and any optional garnishes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16AF0F865DD56E4CBF6DE714FC1D98F6', X'11EA285E9F08E34BACB6F05957E9D6D4', X'11EA285E9F08E34BACB6F05957E9D6D4', 8, NULL, NULL, 2, NULL, 0, 'Remove from pan onto a plate and serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCEC62371E604A4E8B25E03F80AB4CEA', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 0, NULL, NULL, 2, NULL, 0, 'One hour ahead of time, prepare the rice and lotus leaves. Soak the lotus leaves in hot water for 1 hour. Pat dry. Cover the rice with water and let soak for 1 hour. Drain.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B642276847B52242A39610CFEC991243', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 1, NULL, NULL, 2, NULL, 0, 'Next, steam the rice. Line a bamboo steamer with parchment paper or cabbage leaf. Fill a wok approximately to the halfway point with water so that the steamer will be sitting above the water without touching. Bring the water to a boil cover the rice and steam for about 20 minutes. Remove the rice, cover and keep warm while preparing the remainder of the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235EE21BBDA7674DAEF75F0E5A43F642', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 2, NULL, NULL, 2, NULL, 0, 'Soften the dried mushrooms by soaking in hot water for 20 to 30 minutes. Squeeze out any excess water, remove the stems and finely chop.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBA50F95423B4A43BE1898AA8B08846F', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 3, NULL, NULL, 2, NULL, 0, 'Cut the chicken into small cubes about the size of a postage stamp. Add the salt, 1 tablespoon rice wine and 1 teaspoon cornstarch. Marinate the chicken for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF388D585A04FF4D95FE0A56D798F117', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 4, NULL, NULL, 2, NULL, 0, 'Finely chop the sausages. Peel and chop the garlic. In a small bowl, combine the rice wine, light soy, and dark soy sauce. In a separate small bowl, dissolve the cornstarch in the water, and whisk into the sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB898358B7B8F1469E0734B922E9E1FA', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 5, NULL, NULL, 2, NULL, 0, 'Heat a wok and add 2 tablespoons oil. When the oil is hot, add the garlic and stir-fry until aromatic (about 30 seconds). Add the chicken cubes. Stir-fry until they turn white and are 80 percent cooked through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F03834470997024D9790B76889574D4C', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 6, NULL, NULL, 2, NULL, 0, 'Add the sausages and the mushrooms. Stir-fry for a minute. Give the sauce mixture a quick re-stir then add in the middle, stirring quickly to thicken. Season with pepper to taste. Cook for 1 to 2 more minutes to mix everything together and heat through. Remove from the heat and stir in the sesame oil. Cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14F37D5E130C0A4488487AA1E23BA762', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 7, NULL, NULL, 2, NULL, 0, 'To make the wraps, separate the rice and the filling into 8 equal sections, 1 section for each wrap. Lay a lotus leaf in front of you. Place a portion of the rice mixture into the center of a lotus leaf. Add the meat and vegetable mixture over top, shaping the rice with your hands so that it forms a ring around the filling. Add more rice to cover.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75D2C60CE1B73245A68300626C85C69F', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 8, NULL, NULL, 2, NULL, 0, 'Form a square parcel with the lotus leaf and tie it up with twine. Repeat with the remaining lotus leaves.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE6FE4CB0F44864F8F15D907933EC10E', X'2CAF2563DE6D724BAEE27E4E6D3CC628', X'2CAF2563DE6D724BAEE27E4E6D3CC628', 9, NULL, NULL, 2, NULL, 0, 'Steam the lotus leaf parcels, covered, on a heatproof plate in a bamboo steamer for 15 minutes, or until they are done.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C2F5EDA0A2F984A9C2C87A770682BC8', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2487C4B6A60D84BAC28782B52DA2A53', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 1, NULL, NULL, 2, NULL, 0, 'Put all the ingredients (including garam masala powder) for the marinade into a large non-metallic bowl and mix until a smooth marinade is formed and all the ingredients are well blended.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0F2FCBB30627A4F89C238A4B7C27C15', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 2, NULL, NULL, 2, NULL, 0, 'Add the goat meat pieces and stir to coat every piece well. Cling warp the bowl and keep in the fridge to marinate for an hour.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC45ADB7783560428BF717B708BAEC0B', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 3, NULL, NULL, 2, NULL, 0, 'Heat the cooking oil in a deep pan/ pot and add when hot, add the onions. Sauté until the onions begin to turn a pale golden color. Now add the ginger and garlic pastes and sauté for a minute. This will splutter at first so be careful while you stir.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'521F7EBCE224CD408A929E3331832844', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 4, NULL, NULL, 2, NULL, 0, 'Add the khus-khus (poppy seed), coconut, cashew powders and sauté for another 1-2 minutes, stirring continuously to avoid burning.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD5A5E14F29F1446A4DA30E535A70626', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 5, NULL, NULL, 2, NULL, 0, 'Add the tomato paste you made earlier and sauté for a minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B0C9BF9720209489DF89F31479E1929', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 6, NULL, NULL, 2, NULL, 0, 'Now add the marinated goat meat and the marinade to the pot and stir well to mix everything.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82EC8057E1F2254FACDCC07B19B440AD', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 7, NULL, NULL, 2, NULL, 0, 'Cover and cook until the goat meat is three-quarters cooked.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'861C7608EC1450489E56247C7FCFA787', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 8, NULL, NULL, 2, NULL, 0, 'While the meat is cooking, prepare the rice. To do this, wash the rice thoroughly under running water till the water runs clear. Now put into a deep pot and add enough water to submerge the rice and go 2-inch over its surface. Cook the rice on medium heat until it is almost done. To test, just squeeze a grain between your thumb and index finger. There should still be a uncooked white core at the center when you do this. When the rid dis done, drain it in a colander and keep aside for later.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD699014FE25004E82F1CF35AAC0B9E8', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 9, NULL, NULL, 2, NULL, 0, 'If you are using the food coloring, divide the cooled rice into 3 equal portions and put into separate dishes. Add the orange food coloring to one portion of the rice and the green food coloring to another portion of the rice. Leave the third portion white. With each portion, mix the rice till all the grains are well colored. Keep aside for 10 minutes after which you can mix all 3 portions of rice together in a bowl for use in the next step.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFBE2FB45AB65E439E8E1D9B1E2B9484', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 10, NULL, NULL, 2, NULL, 0, 'Soak the strands of saffron in the warm milk.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49FA20613BFFA742ABE8C5E7A1D57991', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 11, NULL, NULL, 2, NULL, 0, 'Pre-heat your oven to 350 F/ 180 C/ Gas mark 4.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4D5277C940EF9468E7BE06A5063436D', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 12, NULL, NULL, 2, NULL, 0, 'Grease a deep baking dish and add a layer of the cooked rice to the bottom. Now add half the goat meat (and its gravy) on top of the rice. Add another layer of rice, another layer of meat and then the last layer of rice (rice-meat-rice-meat-rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CB3A5B3E711AF40AB33F7720DBE842C', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 13, NULL, NULL, 2, NULL, 0, 'Garnish with the ghee and the saffron and milk mixture by drizzling all over the top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE0C9671A70D0344921D3F978664B879', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 14, NULL, NULL, 2, NULL, 0, 'Cover the dish tightly. If your dish does not have a cover tightly with foil and place another same-sized baking pan over it.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3825631E1B190E419B5DF06A813452AC', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 15, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes in preheated oven.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95F0DD58EB89B348880827E0DA9C17B4', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 16, NULL, NULL, 2, NULL, 0, 'Turn off the oven and let the dish sit in the oven till you are ready to eat. Only open when you are ready to eat. The way to serve Biryani is to gently dig in with a spoon so you get through the layers.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8652D394839C2D47903FA624D44AE214', X'F7D5EDE27772334E885EBEF7AEA94B7B', X'F7D5EDE27772334E885EBEF7AEA94B7B', 17, NULL, NULL, 2, NULL, 0, 'Serve with Raita and Kachumbar salad.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9F6F461548A9C4ABE5E3766A65A4A3F', X'9DFF34AD69627447A85BA5F8F696BBC7', X'9DFF34AD69627447A85BA5F8F696BBC7', 0, NULL, NULL, 2, NULL, 0, 'Cut the tops off peppers, dice tops only, and set aside. Remove seeds and membranes from peppers. Try not to pierce pepper flesh as this will prevent juices from leaking out during baking.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCE1D6FA90D3DB4BAB1B98001230A124', X'9DFF34AD69627447A85BA5F8F696BBC7', X'9DFF34AD69627447A85BA5F8F696BBC7', 1, NULL, NULL, 2, NULL, 0, 'Blanche peppers and fill a large pot half full with water. Add 1 teaspoon of salt and bring to a boil. Add peppers, reduce heat, cover, and simmer for 5 minutes. Drain peppers and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A3658A4EB999D4DB2D33E0589019031', X'9DFF34AD69627447A85BA5F8F696BBC7', X'9DFF34AD69627447A85BA5F8F696BBC7', 2, NULL, NULL, 2, NULL, 0, 'Cook one cup of basmati rice according to package directions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31CE60E76D9A6A4AB41BAA8098081A81', X'9DFF34AD69627447A85BA5F8F696BBC7', X'9DFF34AD69627447A85BA5F8F696BBC7', 3, NULL, NULL, 2, NULL, 0, 'Add olive oil to a large skillet, then heat to medium-high and brown the ground beef. Add diced vegetables and cook the mixture until the vegetables are tender but not overcooked. Add tomatoes, tomato paste, crushed garlic, oregano, basil, 1 teaspoon salt, and 1/2 teaspoon of pepper. Simmer for about 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CAE907A23C6A547829FE4B01884F262', X'9DFF34AD69627447A85BA5F8F696BBC7', X'9DFF34AD69627447A85BA5F8F696BBC7', 4, NULL, NULL, 2, NULL, 0, 'Add rice and cooked ground beef mixture in a large bowl, then stir to combine. Add half of the shredded cheddar cheese and stir to blend. Stuff peppers with meat mixture and place in a 3-quart baking dish. Top each pepper with remaining cheese.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA97F9918DDC944F831E6C4AD3287099', X'9DFF34AD69627447A85BA5F8F696BBC7', X'9DFF34AD69627447A85BA5F8F696BBC7', 5, NULL, NULL, 2, NULL, 0, 'Bake at 350 F for about 30 minutes or until cheese just begins to bubble and brown.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB8F10AF17BDEE4FAA097E5B80058768', X'0564322A6A96404B9D47587B83BAA16B', X'0564322A6A96404B9D47587B83BAA16B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D836BE3C7F7D94AA44A97C89B65547C', X'0564322A6A96404B9D47587B83BAA16B', X'0564322A6A96404B9D47587B83BAA16B', 1, NULL, NULL, 2, NULL, 0, 'Mix the rice, sugar, and salt in a large saucepan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1248B8FDF1D05A429F78ADBB3282BFD1', X'0564322A6A96404B9D47587B83BAA16B', X'0564322A6A96404B9D47587B83BAA16B', 2, NULL, NULL, 2, NULL, 0, 'Stir in the milk and add the piece of vanilla bean or the vanilla extract.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44094E3DEEB3BF439B05896F6B3A230D', X'0564322A6A96404B9D47587B83BAA16B', X'0564322A6A96404B9D47587B83BAA16B', 3, NULL, NULL, 2, NULL, 0, 'Place the mixture over medium heat and bring to a boil, stirring often.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDA22A03346F864E9FED4E7DCE51E77F', X'0564322A6A96404B9D47587B83BAA16B', X'0564322A6A96404B9D47587B83BAA16B', 4, NULL, NULL, 2, NULL, 0, 'Reduce the heat and simmer for 30 minutes, or until the rice is soft and the mixture becomes thick. Stir often.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7234EFAEA285264D87369E069FBF3AD6', X'0564322A6A96404B9D47587B83BAA16B', X'0564322A6A96404B9D47587B83BAA16B', 5, NULL, NULL, 2, NULL, 0, 'Serve warm with cinnamon and sugar or fruit compote or both. Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEFC876C8DFB2449BA8FAB5F7E44BF2F', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50CE45BE4ADA584B9922D517FACECA4A', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 1, NULL, NULL, 2, NULL, 0, 'Cover rice with water and soak at least 1 hour or up to 4 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF8E08EC5BEB3241A5AF1BE713A4AC50', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 2, NULL, NULL, 2, NULL, 0, 'Grease 6 to 8 ramekins OR an 8-inch square cake pan with a little vegetable oil (if you happen to have coconut oil, use that).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74E05315E9DC3E4A8D073A8621F6DDDD', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 3, NULL, NULL, 2, NULL, 0, 'Drain rice and distribute among ramekins or in cake pan. Rice should be at least 1/2 to 3/4 inch deep in each ramekin. Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD75609FFFFD374CB1A93CCEA2840F82', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 4, NULL, NULL, 2, NULL, 0, 'Place coconut milk, brown sugar, vanilla and salt in a mixing bowl. Crack in the eggs and stir everything together until sugar dissolves. Taste for sweetness, adding more sugar if desired. Pour over rice in the pan/ramekins (if using ramekins, distribute it as evenly as you can), stirring to combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE3B85E22D67B40A056ED17560DE4B3', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 5, NULL, NULL, 2, NULL, 0, 'Bake 35 to 40 minutes (an inserted fork should come out clean or nearly clean). If you''re adding the topping, the cake(s) can remain a little undercooked at this stage, as they will be going back in the oven. While baking, prepare topping (if using).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BA04755C034534292A6CC851EB091E5', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 6, NULL, NULL, 2, NULL, 0, 'For the topping (optional): Place topping ingredients in a pot or saucepan. Stir with a whisk over medium heat until mixture begins to lightly bubble. Reduce heat to low, whisking continuously for 2 minutes. Remove from heat and continue whisking until it thickens and most of the lumps are gone. Transfer to a bowl to cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70CC63C217449441B914D9BD5EC94A3F', X'E8E6F0D1E1648A4088395B0FC1C05297', X'E8E6F0D1E1648A4088395B0FC1C05297', 7, NULL, NULL, 2, NULL, 0, 'When rice layer is done baking, spread on the topping. Take another baking dish and fill at least 1 inch deep with hot water from the tap. Place this on a lower rack and set cake(s) on a higher rack. Bake 15 minutes, until topping feels firm when lightly touched. Serve either warm or at room temperature. This cake is best stored outside the refrigerator and eaten within 3 days.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B08B6CC5AA5F0418EF0B1BC29F02FF2', X'5091876D53321541B547D8134AEEE8DA', X'5091876D53321541B547D8134AEEE8DA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5CA516991B87240AEC8070D707D2548', X'5091876D53321541B547D8134AEEE8DA', X'5091876D53321541B547D8134AEEE8DA', 1, NULL, NULL, 2, NULL, 0, 'Brown sausage in a skillet or sauté pan until no longer pink, breaking up with a fork.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F272E8277A77704A92BC3C7F0382AAA1', X'5091876D53321541B547D8134AEEE8DA', X'5091876D53321541B547D8134AEEE8DA', 2, NULL, NULL, 2, NULL, 0, 'Add minced onion and cook 2 to 3 minutes longer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF5ADDADA2D92442BA994EE15929A69B', X'5091876D53321541B547D8134AEEE8DA', X'5091876D53321541B547D8134AEEE8DA', 3, NULL, NULL, 2, NULL, 0, 'Pour off most of the excess fat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A88A548F15C63A479C3CE48F6DC0DD25', X'5091876D53321541B547D8134AEEE8DA', X'5091876D53321541B547D8134AEEE8DA', 4, NULL, NULL, 2, NULL, 0, 'Stir in broth and rice. Cover and simmer gently 18 to 20 minutes or until rice is tender and most of the broth is absorbed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B5009288BF042489DA8953A90423E60', X'5091876D53321541B547D8134AEEE8DA', X'5091876D53321541B547D8134AEEE8DA', 5, NULL, NULL, 2, NULL, 0, 'Add the minced fresh parsley and toss to combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4072C0DC92AC9B43A143140D56FE73E3', X'5091876D53321541B547D8134AEEE8DA', X'5091876D53321541B547D8134AEEE8DA', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C367CAF493919B428D134F8E6B97AD5B', X'5B906CBEB1F74246BA37FC42202CF86D', X'5B906CBEB1F74246BA37FC42202CF86D', 0, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large saucepan over medium heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C210CF45AE2904EB9D74FE15565BE65', X'5B906CBEB1F74246BA37FC42202CF86D', X'5B906CBEB1F74246BA37FC42202CF86D', 1, NULL, NULL, 2, NULL, 0, 'Add the sofrito and lightly fry for about 1 minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69EA8962BE900943BDCB6FEEA7C5EFFA', X'5B906CBEB1F74246BA37FC42202CF86D', X'5B906CBEB1F74246BA37FC42202CF86D', 2, NULL, NULL, 2, NULL, 0, 'Add the sazón, salt, beans, and rice. Stir to combine the ingredients and coat the rice with color.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82CB0C3281CFE440B7483403320F66EF', X'5B906CBEB1F74246BA37FC42202CF86D', X'5B906CBEB1F74246BA37FC42202CF86D', 3, NULL, NULL, 2, NULL, 0, 'Pour in the water (or chicken broth) and bring to a rolling boil. Allow to boil for 1 to 2 minutes and then stir the rice mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8154737A9102574CAC0109396719BDEA', X'5B906CBEB1F74246BA37FC42202CF86D', X'5B906CBEB1F74246BA37FC42202CF86D', 4, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and cover with a tight-fitting lid that will not allow steam to escape. Do not lift the lid during the cooking time.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96DE1373CB6E1042B4B9BD557E1ACB99', X'5B906CBEB1F74246BA37FC42202CF86D', X'5B906CBEB1F74246BA37FC42202CF86D', 5, NULL, NULL, 2, NULL, 0, 'Cook on low for 30 minutes. Remove the lid, fluff the rice with a fork, and put the lid back on. Let sit a few minutes before serving as a side dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88C712DD93F55646AE52814DBA349A90', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'079A5E73725F9947853BE77056A26E72', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 1, NULL, NULL, 2, NULL, 0, 'If you don''t have steamed rice on hand, prepare 4 servings of cooked rice per your rice cooker instruction. To prepare cooked rice on the stove, follow these instructions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88FA140B9EB38D4C9521E38F365B75DF', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 2, NULL, NULL, 2, NULL, 0, 'Make the sushi rice. Using warm rice, gently mix in the Sushinoko brand powdered sushi vinegar mix using the edge of a rice paddle with diagonal slashing motions. Note, a 2.6-ounce package of Sushinoko powdered sushi vinegar may be used to season 4 cooked servings of rice (about 1 cup cooked rice per serving).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DABA07AC25BBC3488625F9379A12B2AA', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 3, NULL, NULL, 2, NULL, 0, 'In a small pot, heat water over medium high heat and hard boil egg. It will take about 8 to 10 minutes for the egg to cook. Remove the egg from the water and let it cool. Peel the shell, save the egg white to enjoy later, and crumble the egg yolk into small bits with a fork.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F4DE7FBD388D142ABA1E880138CC2BD', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 4, NULL, NULL, 2, NULL, 0, 'While the egg is cooking, prepare the balsamic soy sauce. In a small pot heat balsamic vinegar and soy sauce over medium heat until warmed. Add a dash of black pepper and set aside to cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE23F7FD9597CF42A215258D83020ADC', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 5, NULL, NULL, 2, NULL, 0, 'Chop mitsuba herb (or watercress or cilantro). Chop ginger into thin matchstick pieces and chop the matchstick pieces into tiny cubes. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D79F007A55301428F4BB831DF2D45DF', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 6, NULL, NULL, 2, NULL, 0, 'In a medium frying pan, heat olive oil over medium-high heat and cook the steak until medium. Cook about 6 to 7 minutes on the first side, and flip the steak over and cook for another 4 to 5 minutes. Let the steak rest for about 3 to 5 minutes before slicing and chopping up the steak. By allowing the steak to rest you let the juices circulate back into the meat for tender, juicy meat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'962E16F819DAB04FB8E6F4280AC2127E', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 7, NULL, NULL, 2, NULL, 0, 'If you prefer, drizzle some of the balsamic soy sauce over the chopped steak to allow the flavor to absorb into the meat. Otherwise, the balsamic soy sauce can be drizzled over the chirashi sushi before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3D7AC3E8F6060488F2A901DF10C96D5', X'9D595A268416534A8C6E9B3143A73053', X'9D595A268416534A8C6E9B3143A73053', 8, NULL, NULL, 2, NULL, 0, 'Assemble the chirashi sushi. On individual plates, place a serving of sushi rice on the plate and spread out. Top the sushi rice with cubed beef. Garnish with chopped mitsuba and ginger. Garnish with crumbled egg yolk. Drizzle balsamic soy sauce over the beef and chirashi sushi. Enjoy immediately!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C903C94584847499B0486EAA102519D', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C94B0A45A363FF43B86368D51FFF3332', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 1, NULL, NULL, 2, NULL, 0, 'Butter a large baking dish (about 2 1/2 to 3 quarts in size).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2F59E6A91C30E4CAF41E1DF3B5C981D', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 2, NULL, NULL, 2, NULL, 0, 'Prepare the vegetables. Peel the onion and chop it finely. Chop the celery. Remove the stem and seeds from the bell pepper and chop it finely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'458C2AB7BD23934C9D900D419EFD135C', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 3, NULL, NULL, 2, NULL, 0, 'In a large heavy skillet over medium heat, fry the bacon until crisp; remove the bacon to paper towels to drain. Crumble bacon and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16FCA8C16287154F807B0AA4F087A272', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 4, NULL, NULL, 2, NULL, 0, 'In the same skillet with the bacon drippings, saute the chopped onion, celery, and bell pepper until just softened.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65302510D757F14ABAC64C6C0453D7E4', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 5, NULL, NULL, 2, NULL, 0, 'To the skillet, add the rice, the pureed tomatoes, the drained crumbled bacon, half of the salt, pepper, sugar, and hot sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7A17B708AE7504994388574B5104C7E', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 6, NULL, NULL, 2, NULL, 0, 'Bring the rice and vegetables to a boil; reduce the heat to low and simmer for about 5 minutes. Taste and add more salt, if needed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B564702419F3341990A923B7FD9C826', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 7, NULL, NULL, 2, NULL, 0, 'Transfer the rice mixture to the prepared baking dish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EADCEF551C3634181A81CB7E1F73D01', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 8, NULL, NULL, 2, NULL, 0, 'Cover the baking dish tightly with foil and bake the rice in the preheated oven for about 40 to 50 minutes or until the rice is tender. Check the rice after about 30 minutes and add water or stock, if necessary, to keep the rice moist.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03E000C156EDD745936B11C61D83410E', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', X'78BEDE35A2F3EA43B4D6CDAF4556CC89', 9, NULL, NULL, 2, NULL, 0, 'Fluff with a fork before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'473FEDD99AFD0848A20CB6FA155F5F8B', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', 0, NULL, NULL, 2, NULL, 0, 'Trim the stem end off the onion. Leave the root end in tact to hold the layers of the onion together. Cut the onion in half lengthwise and remove the peel. Working with one-??half of the onion at a time, make horizontal cuts into the onion before making vertical and then crosswise cuts to finely chop the onion, using the root end as a sort of a handle. Set the onion aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC15AE9176BB6244A6619DBEAD67A6FE', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', 1, NULL, NULL, 2, NULL, 0, 'Peel and mince the garlic. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B662CA6A9FE594DB35194A60091C128', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', 2, NULL, NULL, 2, NULL, 0, 'Pick over and trim the spinach. Rinse it clean, spin or pat it dry. You really want the spinach dried off to avoid having to cook off the water later. Cut the leaves into thin ribbons. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5FEECED7D0C0448A14E6E0E650A20AF', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', 3, NULL, NULL, 2, NULL, 0, 'In a medium pot over medium-high heat, melt the butter. Add the onion and salt and cook, stirring occasionally, until the onion is soft, about 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3975B6C18465124F8094D8A6B2F76FC9', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', 4, NULL, NULL, 2, NULL, 0, 'Add the garlic to the onion and cook, stirring, until fragrant, about 1 minute. Stir in the spinach. Cook, stirring occasionally, until the leaves are wilted and any liquid that has cooked out of the spinach has evaporated, about 4 minutes. If there''s a lot of liquid, turn up the heat and boil it off.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE2538D5495F5D43AD52DCCEAD81C868', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', 5, NULL, NULL, 2, NULL, 0, 'Add the rice and stir to coat it and make sure it''s as well-combined with the spinach and onions as possible. Add the broth and bring to a boil. Add the dill, if you like. Cover and reduce the heat to low. Cook, undisturbed, for 15 minutes. Without removing the cover, remove the pot from the heat and let sit 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20F62BCE30857C43988EFF86D236F76F', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', X'56F70C4FA4D8A9409CEA7DF1DE7A2F4A', 6, NULL, NULL, 2, NULL, 0, 'Remove the cover. Fluff the pilaf with a fork. Stir in the pepper, if you like. Serve the pilaf hot or warm.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C7B171523E6934EB40EE2364AE3A622', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', 0, NULL, NULL, 2, NULL, 0, 'Rinse the beans and rice and soak in filtered water for a few hours or overnight. Drain well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BD62ECDA44A5B4682FBE1DF32BCF248', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', 1, NULL, NULL, 2, NULL, 0, 'Blend the ginger root and coconut together with 1 cup of water and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FD191B8F700A54DB8907F85251722DC', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil over medium flame in a heavy-bottomed pot. Grind up the cumin, coriander, and fennel and add to the pot along with the cardamom and turmeric. Stir briefly, and then add the ginger-coconut mixture, bay leaves and cinnamon.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'383F323B282EC047AEDF20DF67CC1A20', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', 3, NULL, NULL, 2, NULL, 0, 'Stir well, add the beans and grains and salt to taste, along with 6 cups filtered water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39E3C3D96B778E4493D7043D74BC1805', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', 4, NULL, NULL, 2, NULL, 0, 'Bring to a boil, reduce heat, cover and cook 30 to 35 minutes, until the dal and rice are tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F182DA8EEF1B1E46BEDDCD49675E9E90', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', X'EDA15FFEB3EC7C4A87C1E71CF20F452B', 5, NULL, NULL, 2, NULL, 0, 'Add the chopped cilantro, stir well and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E251DFA2D1A4BB4D82DB73417379773E', X'70643C774CB1EA41AB063357C2161D13', X'70643C774CB1EA41AB063357C2161D13', 0, NULL, NULL, 2, NULL, 0, 'Grease a 2-quart baking dish. Heat the oven to 375 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5646B3D5AE8B4F43A5DD3581C256AA52', X'70643C774CB1EA41AB063357C2161D13', X'70643C774CB1EA41AB063357C2161D13', 1, NULL, NULL, 2, NULL, 0, 'Put the uncooked rice in the baking dish. Combine the condensed soup, water, and onion soup mix; pour over the rice and stir to blend ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F474644C129E7244B07A38E987294CAF', X'70643C774CB1EA41AB063357C2161D13', X'70643C774CB1EA41AB063357C2161D13', 2, NULL, NULL, 2, NULL, 0, 'Place the chicken in the rice mixture and sprinkle lightly with pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0ACDB17D85F794CA8AA17692E12E1EB', X'70643C774CB1EA41AB063357C2161D13', X'70643C774CB1EA41AB063357C2161D13', 3, NULL, NULL, 2, NULL, 0, 'Cover tightly with foil and bake in the preheated oven for about 1 hour, or until rice is tender and chicken is cooked through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'838D85708B1CE3439163607C86939D0A', X'70643C774CB1EA41AB063357C2161D13', X'70643C774CB1EA41AB063357C2161D13', 4, NULL, NULL, 2, NULL, 0, 'Remove the foil and top with the toasted almonds, if desired. To toast almonds for garnish, spread them out in a dry skillet and cook over medium heat, stirring constantly. When they start to brown and smell toasty, remove them to a plate to cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6736ABBCF1819A40A1A7E68EE75DB53C', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'457F1B16E79D78498D386B2CDA867E47', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 1, NULL, NULL, 2, NULL, 0, 'Season pork chops on both sides with the poultry seasoning, salt, and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9761438F098A70478822634430E13C94', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a large frying pan over medium-high heat. When the oil is hot, brown the pork chops well, about 5 minutes per side. A good sear helps to keep the moisture inside and the chops tender and juicy; don''t cover the pan when searing as it will make the chops sweat and soggy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06B10935CFB6E547A7F0ACC28B6A491A', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 3, NULL, NULL, 2, NULL, 0, 'Remove from the pan and reserve on a plate.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B403F5918304E540AE7C19EE8A88B43A', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 4, NULL, NULL, 2, NULL, 0, 'Pour off the excess oil and place the pan back on the stove over medium heat. Add the butter and the onion, along with a big pinch of salt.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BF37A0A71B64C45B958639E32C1F09C', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 5, NULL, NULL, 2, NULL, 0, 'Sauté for about 10 minutes, or until the onions are well browned. The onions need to caramelize for the best results.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9070C70A368E6647A1EB4367697B3567', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 6, NULL, NULL, 2, NULL, 0, 'Stir in the garlic and cook for 1 minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7D7BE89DFB3A840A2C0550E91121A1F', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 7, NULL, NULL, 2, NULL, 0, 'Stir in the flour and cook for 2 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89DFD2A01EB8AD44893C69120FA9BACF', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 8, NULL, NULL, 2, NULL, 0, 'Add the chicken broth, buttermilk, and water. As the mixture comes to a simmer, use a wooden spoon to scrape any browned bits from the bottom of the pan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E200B3D17EEBDB4BB9D969F6F784E32A', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 9, NULL, NULL, 2, NULL, 0, 'Turn the heat to low and let the onion gravy gently simmer for 15 minutes. Add a splash of water if it seems to be getting too thick.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C800179AF72BAB4182AF4DB2594EA108', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 10, NULL, NULL, 2, NULL, 0, 'Add the pork chops and any juices back into the pan and coat with the gravy. Cook for about 10 to 15 minutes, or until the pork is done. Remember that the internal temperature must reach at least 145 F for a safe level of doneness. Taste for seasoning and adjust if needed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'222D5EFA3C95CD4F8327CA3862314373', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 11, NULL, NULL, 2, NULL, 0, 'Serve pork chops over white rice and topped with the onion gravy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0BFA414DD0836478935D66A81F0C32D', X'9A8A0B1B3471894DB367B792B9AD907C', X'9A8A0B1B3471894DB367B792B9AD907C', 12, NULL, NULL, 2, NULL, 0, 'Enjoy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E2A78CE2401ED499E50F4C09E2541DB', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9F3BCAD6A97E144AC294C4B8B97D8C7', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 1, NULL, NULL, 2, NULL, 0, 'In a heavy oven-proof saucepan with a tight-fitting lid, heat the olive oil over medium heat. Add the onion and garlic; sauté and stir for 4 to 5 minutes until translucent.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2443FD65BE7DE340A8BAE9B565605700', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 2, NULL, NULL, 2, NULL, 0, 'Add the rice; cook and stir for 4 to 5 minutes longer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'109C997329A5804BAFBB2B41D7DFC264', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 3, NULL, NULL, 2, NULL, 0, 'Add the broth, basil, salt, and pepper and bring to a simmer, stirring frequently.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C4C9D26CE94154CB9CB4C9E59A6B271', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 4, NULL, NULL, 2, NULL, 0, 'Cover the pan with foil, then add the lid. Place in the oven.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8636D296C42A2F4B8C3C792E3EDCA76D', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 5, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes, then carefully remove from the oven, remove the lid and foil, and stir thoroughly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE6D456D6E6F014EBABD3CEFA3343ECC', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 6, NULL, NULL, 2, NULL, 0, 'Return the foil and lid and place back in ?the oven.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E911FF3E7F69344C976BB1C1EA571F6E', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 7, NULL, NULL, 2, NULL, 0, 'Bake 5 minutes longer. Test the rice to see if it''s done; it should have a slight firmness in the center. If the rice isn''t done, cover and return to the oven and bake 5 to 10 minutes longer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9D473AC4777D14793F1E62730E319DA', X'1208B228A832E743A286DB1C4E7DB2A5', X'1208B228A832E743A286DB1C4E7DB2A5', 8, NULL, NULL, 2, NULL, 0, 'When the rice is done, stir in the cheese and butter. Cover and let stand for 5 minutes before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F21B75DEEB977E4AAD9D8CE2A804B557', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCBA2F48EC04324EB5C4D944F7515FBE', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 1, NULL, NULL, 2, NULL, 0, 'With the tip of a sharp knife, make a shallow cut down the back of each shrimp. Peel the shrimp, remove the dark veins.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C585CC0D86319479A70F640405E19E6', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 2, NULL, NULL, 2, NULL, 0, 'Rinse the shrimp under cold running water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAAC2F27B58C054292A43B295AA21A06', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 3, NULL, NULL, 2, NULL, 0, 'Bring a small saucepan of lightly salted water to a boil. Add the shrimp and cook for about 2 minutes, or until they are pink and opaque.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BB610BC571D384D89E39503C5A0918A', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 4, NULL, NULL, 2, NULL, 0, 'Drain and transfer the shrimp to a bowl. Cover and refrigerate.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAD4D4940427B54FB53AD36D3732341A', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 5, NULL, NULL, 2, NULL, 0, 'To make the roux, wipe the saucepan out with a paper towel and add the flour and oil, mixing well. Place the saucepan over medium-high heat and cook for 5 minutes, stirring constantly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'880CA490529587458C98CA2B16F5BA47', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 6, NULL, NULL, 2, NULL, 0, 'Reduce the heat to medium-low and continue cooking for 8 to 12 minutes, or until the mixture turns a light reddish-brown. Stir constantly to prevent scorching.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'619B620C07311F439E363F5B272AD583', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 7, NULL, NULL, 2, NULL, 0, 'Transfer the roux to the crock pot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'651EA638A559D44EBC55CA83DEFB3C87', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 8, NULL, NULL, 2, NULL, 0, 'Add all but the shrimp to the crockpot. Cover and cook on low for 7 to 9 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B042DA7DAA468459B509BE64A6223EB', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 9, NULL, NULL, 2, NULL, 0, 'Add the cooked shrimp to the gumbo and mix well. Cover and continue to cook on low for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B59456687284BB43B070A153E095B6E9', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 10, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the rice in the chicken stock or water, following the package directions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F1715854E5BD34EA8D54806306D345F', X'7D654DA689F88747BCF1ECF27269CB9B', X'7D654DA689F88747BCF1ECF27269CB9B', 11, NULL, NULL, 2, NULL, 0, 'Serve the gumbo over the hot cooked rice along with crusty French baguettes, cornbread, or biscuits.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD95A1E853C1764F8CC5FA52615B3481', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 0, NULL, NULL, 2, NULL, 0, 'Heat a large cast-iron pot or Dutch oven over medium heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A7D7D731085B46B2666BED8DE1046E', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 1, NULL, NULL, 2, NULL, 0, 'When the pot has heated, add the oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DC82D3355D2D243B7CBDE21E53AB84D', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 2, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add the flour and cook, whisking almost constantly (that is, about every 15 seconds), for 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEC6CF4DC532784CA52C50F137CEA69A', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 3, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and continue to cook, stirring almost constantly, for 5 to 10 minutes longer until roux is medium color  the color of peanut butter.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB8D8A1BD81CC349AF9F9FBF831BD5F9', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 4, NULL, NULL, 2, NULL, 0, 'Add the bell pepper, onion, and celery and cook, stirring often, for another 10 minutes. Add the salt, the three types of pepper and bay leaves and stir to combine seasonings with the roux.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E195F3100929AD44B3C2246B6C6DA6A5', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 5, NULL, NULL, 2, NULL, 0, 'Add the stock and water. Raise heat and bring to a boil, whisking constantly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'396AC75084531444BD37A690C82509F0', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 6, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium, add the Andouille slices, and simmer for 30 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C2905D84CFB21C4081FB080C9525C627', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 7, NULL, NULL, 2, NULL, 0, 'Add the shrimp and simmer 5 minutes longer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13DA6E653B60084296061A65564DB358', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', X'D7DB05B77F378F4A9A6CB7B4C79DDEDC', 8, NULL, NULL, 2, NULL, 0, 'Serve a scoop of rice on top of each bowl of gumbo, then sprinkle with the green onions and parsley. (Cook the rice while the gumbo is simmering for the final 30 minutes.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DC632FC7E99344A81F9007161696160', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17974E821D80D14989F7DBBC8B86B811', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan, combine the ground beef with the onion and 2 cloves of the garlic; cook and stir to break up the meat until the beef is browned and the onion and garlic are tender; drain well and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1E195EBE0FCB846BCC1D07B397BFCA1', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 2, NULL, NULL, 2, NULL, 0, 'In a large bowl, mix together the beef mixture, the cooked rice, mustard, egg, and salt and pepper until combined.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F404E211C98E3440B7C4BD7333F5EAFE', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 3, NULL, NULL, 2, NULL, 0, 'Cut out the core end of the cabbage and remove and discard some of the exterior leaves. Then carefully remove 8 whole perfect leaves. Soak the leaves in hot water in a large bowl until they are limp and pliable.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15D5143A4E9D0A4DBF0B5E4648D047DD', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 4, NULL, NULL, 2, NULL, 0, 'Chop half of the remaining cabbage and add this to the beef and rice mixture. Roll up about 1/4 cup of the meat mixture in each cabbage leaf. Reserve any remaining meat mixture. At this point, preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5800C757B6B81A4A9FA425849112F875', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 5, NULL, NULL, 2, NULL, 0, 'Then, in the same large skillet, saute two cloves of garlic in the olive oil until tender, about 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F559CCA4B3828143A4C9046CAA46DE2E', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 6, NULL, NULL, 2, NULL, 0, 'Add the tomato puree, diced tomatoes, bay leaves, salt, and pepper. Simmer this sauce for 15 minutes, stirring frequently. Then remove and discard the bay leaves.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4720CA381B395344AD1E38DABB8B0F01', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 7, NULL, NULL, 2, NULL, 0, 'Place the stuffed cabbage rolls in a Dutch oven or large casserole. Top the rolls with any remaining meat mixture. Pour the tomato sauce mixture over all.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD7D49F1F8F03A4FA1E5155407D64741', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 8, NULL, NULL, 2, NULL, 0, 'Cover the casserole and bake for 80 to 90 minutes or until the cabbage rolls are tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'788E1972C29D6D498315922525C98B5F', X'18EC9D054EF45A489B675997D6D0CCC8', X'18EC9D054EF45A489B675997D6D0CCC8', 9, NULL, NULL, 2, NULL, 0, 'Serve the stuffed cabbage with the sauce. Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A63F1EA56972941A3C7DF220151D8EF', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8339846E0A2CB5478F0D591A0C80D0B1', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 1, NULL, NULL, 2, NULL, 0, 'Heat a heavy-bottomed, oven-proof saucepan over medium heat, then add the butter.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90926AF4F03F6D4F8151DD1FACDE2D7B', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 2, NULL, NULL, 2, NULL, 0, 'In a separate small saucepan, bring the stock or broth to a boil. It''s a good idea to check the seasoning of the cooking liquid and make any necessary adjustments with the salt before it gets too hot to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'641D56057C4CA6499CF08E8E285B5597', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 3, NULL, NULL, 2, NULL, 0, 'When the butter gets foamy, add the diced onion and celery, and sauté until the onion is slightly translucent, about 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1724EF02E8B0214DBCA203F27253DBDE', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 4, NULL, NULL, 2, NULL, 0, 'Add the uncooked rice and sauté, stirring constantly, for about a minute, or until the rice is fully coated with the melted butter.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B44AFC00CBB42468CF49A82B94C94ED', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 5, NULL, NULL, 2, NULL, 0, 'Add the hot stock or broth, bring the pot back to a boil, cover, and place the entire pot in the oven.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6094D129ED54E2489C754815BA7EE34D', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 6, NULL, NULL, 2, NULL, 0, 'Cook the rice pilaf in the oven for 18 minutes or until all the liquid is absorbed. If after 18 minutes, there''s still liquid in the pot or the rice is still too moist, re-cover, and return the pot to the oven for another 2 to 4 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B11C1FA0C3C4E049BE7F8172687AB31B', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', X'27E1610FD7C5CB43AAFA1F6DACD57CF4', 7, NULL, NULL, 2, NULL, 0, 'Remove the pot from the oven, and gently fluff the rice pilaf with a wooden fork. Then place a paper towel across the top of the pot, replace the lid, and let the rice pilaf stand for 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1016E73297A7394E9375338BD48404EB', X'E83350DB52E3C74CA6029674A62D8E86', X'E83350DB52E3C74CA6029674A62D8E86', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38EEA8B750582844B4660CBA41E6AF5B', X'E83350DB52E3C74CA6029674A62D8E86', X'E83350DB52E3C74CA6029674A62D8E86', 1, NULL, NULL, 2, NULL, 0, 'Toast the rice over medium heat in a dry skillet until lightly golden. Shake the pan often to prevent burning. This will take about 5 minutes. Transfer the rice to a plate or platter to cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63354068F1215043ABACDDDD2D9D7763', X'E83350DB52E3C74CA6029674A62D8E86', X'E83350DB52E3C74CA6029674A62D8E86', 2, NULL, NULL, 2, NULL, 0, 'Place the remaining spices, except the turmeric, into the skillet and cook over medium heat until fragrant and toasty. Shake the pan often to prevent burning. This will take about 3 minutes. Transfer the mixture to a plate or platter to cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEC5334674B2B64D853D23F94FCA2F7F', X'E83350DB52E3C74CA6029674A62D8E86', X'E83350DB52E3C74CA6029674A62D8E86', 3, NULL, NULL, 2, NULL, 0, 'Place the cooled rice and spices into a spice mill, blender, or food processor. Grind into a fine powder. Place the powder in a bowl and thoroughly stir in the turmeric.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0879244AB683041B133E44A3CA46A30', X'E83350DB52E3C74CA6029674A62D8E86', X'E83350DB52E3C74CA6029674A62D8E86', 4, NULL, NULL, 2, NULL, 0, 'Store the seasoning in an airtight jar, container, or sealed baggie. Be sure to label it and record the date you made it. The powder will keep for several months.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12D290FE10F90F4F83C2FBFE5509FEEC', X'E83350DB52E3C74CA6029674A62D8E86', X'E83350DB52E3C74CA6029674A62D8E86', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5524B4D09C78684DB4B698414FB7F98A', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCC05C13FC978449A2F0686D283B200C', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 1, NULL, NULL, 2, NULL, 0, 'Khichdi can be cooked in standard cooking pot but is prepared fastest in a pressure cooker. Heat the cooking oil in the pot/ pressure cooker on medium heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FA1D46FE6871A428B3870739A31FFF9', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 2, NULL, NULL, 2, NULL, 0, 'When hot, add the cumin seeds and dry red chilli to it. Fry till the sizzling stops, then add the ginger and garlic pastes. Fry for 1 minute, stirring frequently.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B11EE72204FA843A47DE37D6CC220CF', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 3, NULL, NULL, 2, NULL, 0, 'Add the chopped tomatoes and fry till soft and pulpy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5ADA9127904E1C4FB9045CD95C996EA9', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 4, NULL, NULL, 2, NULL, 0, 'Add the spinach and stir. Now add the rice, Toor daal, salt to taste and the 3 cups of water. Stir well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C4093248DCE0A42B6D47001B6F2F0C1', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 5, NULL, NULL, 2, NULL, 0, 'Allow the water to come to a boil and then put on the cover of the pressure cooker. Cook till you have heard 3 whistles/ pressure releases. Turn off the heat and release the pressure from the cooker. Open the cover. The Khichdi should be cooked now.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'748CDD6CEE2EED4EA3542B7EEEAA9D89', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 6, NULL, NULL, 2, NULL, 0, 'If you are using a standard pot, cook on medium heat. When the water comes to a boil, reduce the heat a little and cover. Check occasionally and stir to prevent burning at the bottom and to ensure even cooking. Cook till the rice and daal are soft and most of the water has dried up. Add more, as required, to maintain the required consistency.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B5B7FC06600B946A0AC95C21CD7CFAF', X'B70220938ACDB74D93C92F60C8B7B210', X'B70220938ACDB74D93C92F60C8B7B210', 7, NULL, NULL, 2, NULL, 0, 'When cooked, serve with yogurt and your favorite chutney or pickle!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D7942C7DC2CAF4EAB98D926D6779D7D', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7C4869DB5D83346A1354C7C35F3C9E6', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 1, NULL, NULL, 2, NULL, 0, 'Combine soup and water in the slow cooker. Stir to blend well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99CCCEE68D816E4C9AB1ABE00C8A3CF9', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 2, NULL, NULL, 2, NULL, 0, 'Stir in the uncooked rice, celery, diced turkey, poultry seasoning, and dried onion.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DBCE810118F3114983462C2D1F848891', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on high for about 2 1/2 to 3 hours or on low for about 5 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B5B8A6B69B15940BE595167F39140DB', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 4, NULL, NULL, 2, NULL, 0, 'About 20 to 30 minutes before the dish is done and the rice is just tenderit will be mushy if cooked too longthaw the vegetables in a colander under cold running water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFE9CF6F1A4B0148BB9C684AA673BE9B', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 5, NULL, NULL, 2, NULL, 0, 'Add the vegetables to the rice and turkey mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFA4F449F5E6E846BBD47D6524412F5D', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 6, NULL, NULL, 2, NULL, 0, 'If desired, top with some shredded cheese and continue cooking for 20 to 30 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EBEA1528321BA44B4221A5E0E42194D', X'6D028E1BE91370408448F7C986666A3F', X'6D028E1BE91370408448F7C986666A3F', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52ECFAD8673C264FA6A512149D35AD75', X'991A58AF48C3804088A37BC30FC44DBC', X'991A58AF48C3804088A37BC30FC44DBC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'540F87576467E440B86E9E53AFEF1850', X'991A58AF48C3804088A37BC30FC44DBC', X'991A58AF48C3804088A37BC30FC44DBC', 1, NULL, NULL, 2, NULL, 0, 'Combine the salt, seasoned salt, chili powder, and pepper in a small bowl. Trim the pork chops and discard any excess fat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AA80C29AAE82F4B93D31482D3BDE773', X'991A58AF48C3804088A37BC30FC44DBC', X'991A58AF48C3804088A37BC30FC44DBC', 2, NULL, NULL, 2, NULL, 0, 'Heat oil in ?a skillet over medium-high heat; sear the pork chops for about 3 to 4 minutes on each side, seasoning both sides with 1 teaspoon of the seasoning mixture as they cook.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'574A0C707F58C149BC50909C737B5FA2', X'991A58AF48C3804088A37BC30FC44DBC', X'991A58AF48C3804088A37BC30FC44DBC', 3, NULL, NULL, 2, NULL, 0, 'Drain off any excess fat, leaving the pork chops in the skillet. Add the rice to the skillet along with the tomatoes, green bell pepper, onion, olives, and sugar. Arrange sliced zucchini around the edge of the skillet and sprinkle with remaining seasoning mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55F69817465CBA4F98E0360B456CEC63', X'991A58AF48C3804088A37BC30FC44DBC', X'991A58AF48C3804088A37BC30FC44DBC', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook, stirring occasionally, for about 30 minutes, or until the pork chops are cooked through (at least 145 F/63 C) and the rice is tender. If you are using brown rice, allow extra time.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1FBD43CC07236418DA67A7B89542559', X'991A58AF48C3804088A37BC30FC44DBC', X'991A58AF48C3804088A37BC30FC44DBC', 5, NULL, NULL, 2, NULL, 0, 'Top with the mozzarella cheese and cover the pan. Cook for about 2 minutes longer, or until the cheese has melted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77DC7E5FA020BD4FA2185651C2C43BF4', X'991A58AF48C3804088A37BC30FC44DBC', X'991A58AF48C3804088A37BC30FC44DBC', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'391A39C80193EF41A8E9DAD142AB2BFE', X'03C6B6BBFD82A549886E8821B1E0DE86', X'03C6B6BBFD82A549886E8821B1E0DE86', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D293D320C7A21842B81892002AA9F9C7', X'03C6B6BBFD82A549886E8821B1E0DE86', X'03C6B6BBFD82A549886E8821B1E0DE86', 1, NULL, NULL, 2, NULL, 0, 'In a heavy skillet over medium heat, cook the onion, bell pepper, and garlic in the olive oil until tender, stirring frequently.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'511B5960B4EA524DB1155B606FCAB767', X'03C6B6BBFD82A549886E8821B1E0DE86', X'03C6B6BBFD82A549886E8821B1E0DE86', 2, NULL, NULL, 2, NULL, 0, 'Place this mixture in large bowl along with the uncooked rice, salt, pepper, egg, basil pesto, and milk, and mix well using a spoon.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2FA73E560959A4E90C8EA93EA3A53BD', X'03C6B6BBFD82A549886E8821B1E0DE86', X'03C6B6BBFD82A549886E8821B1E0DE86', 3, NULL, NULL, 2, NULL, 0, 'Add the ground beef and cheese to the ingredients in the bowl and mix gently with your hands until combined. Form the beef mixture into a loaf shape and place on a broiler pan with slits for the fat to drain off.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68746F1C62BDD342BA20A3C5EE53B566', X'03C6B6BBFD82A549886E8821B1E0DE86', X'03C6B6BBFD82A549886E8821B1E0DE86', 4, NULL, NULL, 2, NULL, 0, 'Bake the meatloaf at 350 F for 55 to 65 minutes or until the internal temperature registers 160 F as measured with a reliable meat thermometer. Cover the meatloaf with foil and let the loaf stand for 10 minutes before slicing to serve.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5113BD486AB5D4CBEABAC7F514CCEFF', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5EEFEECFF73C804FA846E36FE08B9E10', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 1, NULL, NULL, 2, NULL, 0, 'Fill a large saucepan with 2 1/2 cups of water or unsalted chicken or vegetable broth.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B522F5DC115F74A848260B7168A0C69', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 2, NULL, NULL, 2, NULL, 0, 'Add the rice and place the pan over high heat. Bring the rice to a boil. Reduce the heat to low, cover the pan, and simmer for about 45 minutes, or until the liquid has been absorbed and the rice is tender. (If the liquid has been absorbed and the rice isn''t quite tender yet, add more water.) Let the rice stand for 5 minutes and then fluff with a fork. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00BF4A8B04A4B243B207C3F4C8C6E180', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 3, NULL, NULL, 2, NULL, 0, 'In a skillet over medium-low heat, melt the butter.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F686A6E54B6FB4DA18D8D48A6BDD765', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 4, NULL, NULL, 2, NULL, 0, 'Add the chopped onion and green bell pepper and cook, stirring for 4 to 6 minutes, or until the onion is softened and lightly browned.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'051EAA4E4A990744912CFAD6B923D7F0', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 5, NULL, NULL, 2, NULL, 0, 'Add the canned or fresh cooked mushrooms (see below), rice, seasoned salt, and about 1/8 teaspoon of black pepper to the onions and peppers. Cook for 2 to 4 minutes, or until the rice is hot, stirring and turning frequently.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'633F7212DA53D3469F8D615E47B9730F', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 6, NULL, NULL, 2, NULL, 0, 'Taste and adjust the seasonings.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2CFEB0CC3175B45AED3AB7FE9A20A19', X'D0B682671A393549BA0F902042F23A19', X'D0B682671A393549BA0F902042F23A19', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD3D5EA1F8F0644ABFEE83834E3D6C30', X'F1982EECCAC4B545BAB27D2004231992', X'F1982EECCAC4B545BAB27D2004231992', 0, NULL, NULL, 2, NULL, 0, 'Cook the rice as directed on package; fluff it and keep warm in the rice cooker or cover and keep warm in a 170 F to 200 F oven or warming drawer, or transfer ?the rice to a slow cooker with a warm setting.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE2B32ED0B98CF4FA4C4AC73483F5CC0', X'F1982EECCAC4B545BAB27D2004231992', X'F1982EECCAC4B545BAB27D2004231992', 1, NULL, NULL, 2, NULL, 0, 'Cut the chicken breasts into 3/4-inch pieces. Remove the seeds from the chile peppers and mince.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0AAA661025D4E445B87C423883A60C6E', X'F1982EECCAC4B545BAB27D2004231992', X'F1982EECCAC4B545BAB27D2004231992', 2, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a large skillet over medium-high heat. Add the diced chicken, jalapeño peppers, bell peppers, kosher salt, pepper, and crushed red pepper. Cook for 4 to 5 minutes, or until the chicken is cooked through and no longer pink, stirring constantly. Add the shrimp and continue cooking for about 2 to 3 minutes, or until the shrimp are opaque and pink, stirring constantly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD1E961F0ED0B0479A52F3B81B8FD34C', X'F1982EECCAC4B545BAB27D2004231992', X'F1982EECCAC4B545BAB27D2004231992', 3, NULL, NULL, 2, NULL, 0, 'Add the fresh chopped tomatoes or canned tomatoes and the tomato paste, if using. Cook for about 1 minute longer, or until hot and bubbling, stirring occasionally.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23B5E57E99E0A144813F867E1AFBBB83', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 0, NULL, NULL, 2, NULL, 0, 'Sprinkle the ground beef, chopped chicken livers, and gizzards with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C19776593403084DAD2E157C7F61F3CA', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet or sauté pan over medium-high heat, brown the meats in the vegetable oil or shortening.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D93FC20E26F14890359D72DAECA250', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 2, NULL, NULL, 2, NULL, 0, 'Cook, stirring constantly until the meat is browned and no longer pink.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51881574D0F1ED46ABC278B50B70BFF1', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 3, NULL, NULL, 2, NULL, 0, 'Add chopped celery, onion, bell pepper, and garlic to the meat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD3646CFC56493488F546E9BC3B64D0A', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 4, NULL, NULL, 2, NULL, 0, 'Cook, stirring, until the onions are translucent.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AAE3039B97B0040B7C90BFA9A515B0C', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 5, NULL, NULL, 2, NULL, 0, 'Add a small amount of water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D1D1CF8BEE2DB40A5789728CB107360', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 6, NULL, NULL, 2, NULL, 0, 'Cover and simmer on low heat until vegetables are tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7D61D451C537F41B011AAB821AAF3C1', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 7, NULL, NULL, 2, NULL, 0, 'Add cooked rice and stir gently to combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F90611BFA24354FADE4618B34AA68DC', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 8, NULL, NULL, 2, NULL, 0, 'Taste and adjust seasonings with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E30054878C2F64EA5EED851C1BEDF83', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 9, NULL, NULL, 2, NULL, 0, 'Cover and let stand until ready to serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78BC4508A319C649BA0A0B6BF321375A', X'FA41749DAA0E73438E7B179575C89788', X'FA41749DAA0E73438E7B179575C89788', 10, NULL, NULL, 2, NULL, 0, 'If desired, garnish with green onions or parsley and fluff with a fork just before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6ABEDED8FDCEA40995BCCD353D774C0', X'5FACCD1482015D4FAEA9449936F0E4A2', X'5FACCD1482015D4FAEA9449936F0E4A2', 0, NULL, NULL, 2, NULL, 0, 'Rinse the rice well in a colander.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52CB4F4474927B4FBAE2989BE96BE97A', X'5FACCD1482015D4FAEA9449936F0E4A2', X'5FACCD1482015D4FAEA9449936F0E4A2', 1, NULL, NULL, 2, NULL, 0, 'Bring the water to boil in a saucepan. Add the rice and the salt.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D6198DD88D8354986E466E546BB94FF', X'5FACCD1482015D4FAEA9449936F0E4A2', X'5FACCD1482015D4FAEA9449936F0E4A2', 2, NULL, NULL, 2, NULL, 0, 'Allow the rice to boil for about 1 minute. Stir at least once to prevent sticking, but don''t overdo it.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'201F8B4EBD4429468E339179884AE586', X'5FACCD1482015D4FAEA9449936F0E4A2', X'5FACCD1482015D4FAEA9449936F0E4A2', 3, NULL, NULL, 2, NULL, 0, 'Cover the saucepan with an appropriate tight-fitting lid that does not have an exhaust hole.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EF93F0356972A4381B940437128DC6B', X'5FACCD1482015D4FAEA9449936F0E4A2', X'5FACCD1482015D4FAEA9449936F0E4A2', 4, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and allow the rice to simmer for about 30 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74483581A5F3FB4BBAAD9A82EC12BFD9', X'5FACCD1482015D4FAEA9449936F0E4A2', X'5FACCD1482015D4FAEA9449936F0E4A2', 5, NULL, NULL, 2, NULL, 0, 'Fluff the rice with a fork and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8358E93A6A937C4C9035AAE406675A4F', X'21D6E3B3A21D274C90DE556011A46672', X'21D6E3B3A21D274C90DE556011A46672', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a large skillet and add rice and garlic. Cook and stir for 2 to 3 minutes until rice is coated with oil and starts to turn a bit golden in spots.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97692A95EB252D48A22F90DD93F0EA0C', X'21D6E3B3A21D274C90DE556011A46672', X'21D6E3B3A21D274C90DE556011A46672', 1, NULL, NULL, 2, NULL, 0, 'Add wine, pepper, and 1 cup chicken broth to the skillet. Bring to a boil, then reduce heat to medium and cook mixture for 8 to 10 minutes, stirring frequently, until liquid is absorbed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F1029F590FE0545B92B131AD44B978F', X'21D6E3B3A21D274C90DE556011A46672', X'21D6E3B3A21D274C90DE556011A46672', 2, NULL, NULL, 2, NULL, 0, 'Add about 1/2 cup of the remaining broth, stirring the rice until liquid is absorbed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B7F71C89F4E8B4790BEDDAA622459A4', X'21D6E3B3A21D274C90DE556011A46672', X'21D6E3B3A21D274C90DE556011A46672', 3, NULL, NULL, 2, NULL, 0, 'Continue until the rice is tender, and the risotto is the consistency you want.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'996097ABA37CF4499D10E1FA13B19FD7', X'21D6E3B3A21D274C90DE556011A46672', X'21D6E3B3A21D274C90DE556011A46672', 4, NULL, NULL, 2, NULL, 0, 'Add lemon juice, spinach, lemon peel, and tuna. Cook and stir gently until spinach is wilted.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EC67543F1FD6B4BAB0B73F55885D293', X'21D6E3B3A21D274C90DE556011A46672', X'21D6E3B3A21D274C90DE556011A46672', 5, NULL, NULL, 2, NULL, 0, 'Stir in butter until melted along with 1/2 cup cheese. Sprinkle with remaining cheese and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BB8916B5366E6498797476B4743C006', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03B50E81F60FE04C8A330DBE6FA9CE6B', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A72A1B7CA63584483CC5330A19668BF', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 2, NULL, NULL, 2, NULL, 0, 'To drain the tofu: Wrap the tofu in a clean dish towel and place a plate on top. Place a heavy can or similar item on top of the plate to weigh it down, and let drain for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'366CF307973D9C4994346C838CE520DC', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 3, NULL, NULL, 2, NULL, 0, 'Add 2 cubes of vegetable bouillon to the 5 cups of water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA4CC042C928264193729FF6EC042EBE', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 4, NULL, NULL, 2, NULL, 0, 'Cook rice according to package directions, using the vegetable bouillon.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AB718B83722274E91DA7A123F2CBEFA', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 5, NULL, NULL, 2, NULL, 0, 'Heat a tablespoon of olive oil in a large frying pan over medium-low heat, then add the vegetables.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CD902D23B4DB844BDB76FAF19DAF21E', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 6, NULL, NULL, 2, NULL, 0, 'Cover and cook until carrots are soft, salt to taste, then set aside until ready to use.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8A38691749AFA4CB3DBF7FAE4ED21E4', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 7, NULL, NULL, 2, NULL, 0, 'Slice the tofu about 1/2 inch thick. Mix olive oil and sage together and brush onto tofu slices, then transfer to a lightly greased baking sheet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F73252AC248C434580CF64CADE6867DB', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 8, NULL, NULL, 2, NULL, 0, 'Roast the tofu for about 30 minutes or until firm and browned on both sides. Remove from the oven, let cool, then cut into bite-sized pieces.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA288E210C13004BA695517A4DF99F87', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 9, NULL, NULL, 2, NULL, 0, 'Make the sauce by combining all sauce ingredients into a food processor. Blend until very smooth. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64DC863D0493424A8E84F48485AD5093', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 10, NULL, NULL, 2, NULL, 0, 'Once the rice is finished cooking, combine everything together with the rice in the pot. Warm on low heat if needed. Serve hot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43CBB495C6210348AAB172E4E51498EF', X'5740B914CAFBD642B110D2956A2E0AFE', X'5740B914CAFBD642B110D2956A2E0AFE', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CB09CDBDA876B4C9135F59FE817769C', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DCDDDED05147A4CA2C986E0601E517C', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 1, NULL, NULL, 2, NULL, 0, 'First, press your tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D24CF54C1C87F94190241214DDA0B597', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 2, NULL, NULL, 2, NULL, 0, 'Once your tofu is well-pressed, cut it into your desired shapes. Thin slabs, triangles or even small bite-sized shapes will work.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C6753FABADADF478790A4BAAE730908', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 3, NULL, NULL, 2, NULL, 0, 'Once your tofu is ready to go, pre-heat the oven to 400 F. I highly recommend lining a baking tray with aluminum foil if you can, since baked tofu sometimes gets a bit sticky, and the foil makes clean up super easy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF1932811EFE5242A710BFFE43D8FDB1', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 4, NULL, NULL, 2, NULL, 0, 'Next, prepare the sauce. Whisk together the peanut butter (I like to melt it slightly in the microwave for just a few seconds to make this easier) with the ginger, lime juice or orange juice, soy sauce, sesame oil, maple syrup and sweet chili sauce. Before you bake your tofu, give the sauce a quick taste. It should be delicious! If not, adjust the seasonings to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE91CA4803B304468CFA2B80DB1DC5FB', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 5, NULL, NULL, 2, NULL, 0, 'Coat the tofu with the peanut sauce on all sides. You can transfer the sauce to a wide shallow bowl or dish and dip it in, or use a pastry brush to coat each piece by hand if you''re working with larger pieces of tofu. I like to dip it, then brush on extra sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BFBAA94CB594749AF6D1BF5D288A68A', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 6, NULL, NULL, 2, NULL, 0, 'Bake the tofu in the oven for about 15 to 17 minutes, keeping an eye on them so they don''t burn. Turn pieces over, and bake another 10 to 15 minutes. (Note: The sauce on the aluminum foil will burn a bit, don''t worry. You just don''t want to see the coated tofu burn.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CE26A0AFBED6B4281E2E93D8B247E5B', X'CBD3722A88BF2F46A38E1EFCC067CA8D', X'CBD3722A88BF2F46A38E1EFCC067CA8D', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE0EEB78C68B4246971162736BB85382', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48CFA2A77A7DDB47AE773A4720BC8FE1', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 1, NULL, NULL, 2, NULL, 0, 'Wrap the tofu with paper towels, and place it on a flat tray. Put a cutting board or a flat plate on top of the tofu, and let sit for about 15 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'617729625CF52F42A2EABC6FF8438D66', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, put dashi, soy sauce, and mirin in a saucepan, and bring to a boil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'263916CE63FE8E40AFCF9911180C48F8', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 3, NULL, NULL, 2, NULL, 0, 'Mix 1 tablespoon katakuriko potato starch with 1 tablespoon water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B102028C84A75142884F8C60F51434DF', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 4, NULL, NULL, 2, NULL, 0, 'Add the katakuriko mixture to the sauce, and stir quickly. Stop the heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19FFFBC2B94F604E8BDAC2CBB050E5D4', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 5, NULL, NULL, 2, NULL, 0, 'Dry the tofu with paper towels, and cut each tofu piece in half.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01A25F8C5E00BF4A8C64B3F3DFEED295', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 6, NULL, NULL, 2, NULL, 0, 'Dust the tofu with 4 tablespoons of katakuriko to coat the pieces completely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F952121D6C42844A8E6B68B2595040F', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 7, NULL, NULL, 2, NULL, 0, 'Heat oil to 350 F in a deep pot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C027FA9D90FF8344BCD589A8596B9205', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 8, NULL, NULL, 2, NULL, 0, 'Deep-fry the tofu pieces until they turn light brown. Drain on paper towels.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F21D37884EDD9C48BBE90CB2A224F320', X'07F82DAE40DB064EB6652BD8D79EF57C', X'07F82DAE40DB064EB6652BD8D79EF57C', 9, NULL, NULL, 2, NULL, 0, 'Place each fried tofu piece in a small dish and pour the sauce over it. Garnish with grated ginger and bonito flakes, if using.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4702C2F5E9AB943BB06390200A6F12E', X'F42824F5D0ADE64389D74716B6170D71', X'F42824F5D0ADE64389D74716B6170D71', 0, NULL, NULL, 2, NULL, 0, 'Warm flour tortillas over stove or in microwave until heated and soft.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E81C30DDC0CEC4C93BBBC17A49C6686', X'F42824F5D0ADE64389D74716B6170D71', X'F42824F5D0ADE64389D74716B6170D71', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet or frying pan, sautee the garlic and onion in the vegetable oil for a minute or two, then add the diced tofu, mushrooms and tomatoes and cook, stirring frequently, for about 4-6 minutes, or until the mushrooms are soft and cooked.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'367CBC26D160654A8871E191E7F85593', X'F42824F5D0ADE64389D74716B6170D71', X'F42824F5D0ADE64389D74716B6170D71', 2, NULL, NULL, 2, NULL, 0, 'Remove the pan from heat. Mix in the turmeric and a few dashes of Tabasco sauce, then season generously with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FFB1C1018E9BDE42B31FF46A6198196A', X'F42824F5D0ADE64389D74716B6170D71', X'F42824F5D0ADE64389D74716B6170D71', 3, NULL, NULL, 2, NULL, 0, 'Place a couple of spoonfuls of the tofu and vegetable mixture in each flour tortilla. Top with vegan sour cream and vegan cheese if desired, then wrap up your burritos and serve hot. You may find that you have enough tofu and veggies to make more than four burritos worth, depending on how full you make them.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7030261815E76541A9C83E279E45CDE7', X'F42824F5D0ADE64389D74716B6170D71', X'F42824F5D0ADE64389D74716B6170D71', 4, NULL, NULL, 2, NULL, 0, 'To wrap your burritos, place a layer of filling in a horizontal row just below the center of the tortilla, leaving about 2 inches on the ends. Then, fold those ends (the right and left sides) in, and then roll the burrito up away from you.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CDA609AD8C504439BD86B1F25F9E542', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8349F5FB27B9DB46AC9B47CB18C8BD75', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 1, NULL, NULL, 2, NULL, 0, 'Working in batches as needed, process the tofu in a blender or food processor until smooth and creamy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5E9862D9ABB68408371A74DA2841306', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 2, NULL, NULL, 2, NULL, 0, 'Add the tofu to a large mixing bowl, and stir in the sage, thyme, rosemary, vegetable broth powder, and poultry seasoning, stirring well to combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9F3E03EC2B0024F971EF29DBBB7A672', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 3, NULL, NULL, 2, NULL, 0, 'Line a colander with cheesecloth or a thin towel.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16D4660DBB66F54E87CCF4589F30C6ED', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 4, NULL, NULL, 2, NULL, 0, 'Place the tofu in the colander and a layer of cheesecloth or a thin towel on top. Place a weight on top of the towel to gently press the tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7962B0FF5E4B144AFAE3722BC59395B', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 5, NULL, NULL, 2, NULL, 0, 'Place on a plate or towel and chill for at least 3 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74858654ED0FD641A4E52D5B48B7CB89', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 6, NULL, NULL, 2, NULL, 0, 'Once the tofu is chilled and firm, scoop out the tofu in the middle, creating a hole or a well, and leaving about 1 1/2 inches of a tofu `shell` in the colander.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D25D7171A982A4794B013364CE56B9D', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 7, NULL, NULL, 2, NULL, 0, 'Gently pat your stuffing into the `well,` then gently press the extra tofu on top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49DA722FEC264146895DEA56B4B86FAE', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 8, NULL, NULL, 2, NULL, 0, 'Carefully invert your tofu turkey onto a lightly greased baking sheet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E96F848FDD1F54798DF256D1E9BC6DD', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 9, NULL, NULL, 2, NULL, 0, 'Whisk together the balsamic vinegar, red wine, Dijon mustard, and soy sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3EA9B7274610A4E944C2872608B6331', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 10, NULL, NULL, 2, NULL, 0, 'Generously baste or brush the mixture on tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9EFF2BCC59DC84D83F229B28B64E02E', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 11, NULL, NULL, 2, NULL, 0, 'Bake for about 90 minutes at 350 F, basting again every 15 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9B20BF7011EE334DA72E565F0E21D937', X'E150B1ACC4210742AB549ED15ED9CF74', X'E150B1ACC4210742AB549ED15ED9CF74', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A15333A74F0D424EB99CE0933BD2DA7B', X'93F3EA5B0861FB4887EF9DA61E5AABA6', X'93F3EA5B0861FB4887EF9DA61E5AABA6', 0, NULL, NULL, 2, NULL, 0, 'Place cubed tofu in a bowl and pour marinade over. Toss well to coat and set aside to marinate while you prepare your other ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE5746AB72CDE944B8E108EA377A6102', X'93F3EA5B0861FB4887EF9DA61E5AABA6', X'93F3EA5B0861FB4887EF9DA61E5AABA6', 1, NULL, NULL, 2, NULL, 0, 'Combine all ''Stir-Fry Sauce'' ingredients, stirring well to dissolve sugar. Taste-test the sauce, keeping in mind that the first taste should be spicy-salty, followed by sweet and finally the rich taste of the coconut milk. Adjust these flavors to suit your taste, adding more lime juice if too sweet or salty (note: it will be less salty when combined with the stir-fry).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF2E54CDBFAFEE4DA3FE5BD7A9C19E4F', X'93F3EA5B0861FB4887EF9DA61E5AABA6', X'93F3EA5B0861FB4887EF9DA61E5AABA6', 2, NULL, NULL, 2, NULL, 0, 'Warm a wok or large frying pan over medium-high heat. Add 2 tablespoons oil and swirl around, then add the marinated tofu. Stir-fry until lightly browned (2 to 3 minutes). If pan becomes dry, add a little stir-fry sauce, 1 to 2 tablespoons at a time. Remove fried tofu from pan and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30894D341620DE4799356694CC693A85', X'93F3EA5B0861FB4887EF9DA61E5AABA6', X'93F3EA5B0861FB4887EF9DA61E5AABA6', 3, NULL, NULL, 2, NULL, 0, 'Add another 1 tablespoon oil then add the shallot/onion, garlic, ginger, and chili. Stir-fry 2 minutes, then add the carrot and mushrooms plus 3 to 4 tablespoons of the stir-fry sauce. Stir-fry 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88C177ECA517484FB84651861E3E907E', X'93F3EA5B0861FB4887EF9DA61E5AABA6', X'93F3EA5B0861FB4887EF9DA61E5AABA6', 4, NULL, NULL, 2, NULL, 0, 'Add the broccoli and red pepper plus up to 1/2 of remaining stir-fry sauce, enough to gently simmer vegetables in the sauce. (Note: this is a ''saucy'' stir-fry that is never dry - the sauce is meant to flavor the rice or noodles it is served with). Simmer until all the vegetables are cooked but still bright green with some crispness (2 to 3 minutes).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C960DBFDBE10C149BEBD8C3BE48BB2D9', X'93F3EA5B0861FB4887EF9DA61E5AABA6', X'93F3EA5B0861FB4887EF9DA61E5AABA6', 5, NULL, NULL, 2, NULL, 0, 'Add the tofu back in. Add more of the stir-fry sauce as desired.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0E452C645331841B9D5CAD57F29C1D8', X'93F3EA5B0861FB4887EF9DA61E5AABA6', X'93F3EA5B0861FB4887EF9DA61E5AABA6', 6, NULL, NULL, 2, NULL, 0, 'Remove from heat and do one last taste-test. If not salty enough, add a little more fish or soy sauce. If too salty or sweet, add another squeeze of lime juice. Add more sugar or chili if desired. Top with fresh basil and serve over Thai jasmine-scented rice (also good with noodles!). Also delicious with our easy Thai coconut rice OR easy Thai sticky rice. ENJOY!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A0AD24946581349A695CD7F1068F809', X'CE9128AFDBBD0D43A95CE717B0898A93', X'CE9128AFDBBD0D43A95CE717B0898A93', 0, NULL, NULL, 2, NULL, 0, 'First, prepare your tofu. Like most tofu recipes, tofu scramble will taste best if you press the tofu first. This allows the tofu to absorb more of the flavorings and seasonings which you add to it. It''s an extra step, but it just takes a few minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50D0AE799D7BC44FBF01D8818F49AA4C', X'CE9128AFDBBD0D43A95CE717B0898A93', X'CE9128AFDBBD0D43A95CE717B0898A93', 1, NULL, NULL, 2, NULL, 0, 'Slice your pressed tofu into 1/2-inch thick strips.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70C1279BC7666F43A3BB1B75FB354A35', X'CE9128AFDBBD0D43A95CE717B0898A93', X'CE9128AFDBBD0D43A95CE717B0898A93', 2, NULL, NULL, 2, NULL, 0, 'Whisk together all ingredients except for tofu. Transfer to a shallow pan or zipped plastic bag then the tofu, coating well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6C5203A8A58574BA8FDBCAA7AA99633', X'CE9128AFDBBD0D43A95CE717B0898A93', X'CE9128AFDBBD0D43A95CE717B0898A93', 3, NULL, NULL, 2, NULL, 0, 'Allow tofu to marinate for at least one hour (the longer the better, turning to coat well with marinade).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D908F0EA8AB3DF44BAD25BA8E1D30236', X'CE9128AFDBBD0D43A95CE717B0898A93', X'CE9128AFDBBD0D43A95CE717B0898A93', 4, NULL, NULL, 2, NULL, 0, 'Heat oven to 375 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBF9906C06B12747AA18F9C7BD3025CD', X'CE9128AFDBBD0D43A95CE717B0898A93', X'CE9128AFDBBD0D43A95CE717B0898A93', 5, NULL, NULL, 2, NULL, 0, 'Transfer tofu and marinade to baking dish and bake for 20 to 25 minutes, turning halfway through and pouring extra marinade over the tofu as needed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55767B3F045A224A8D6892DE86A42047', X'15328559102D0C45BD812D97F2E3897E', X'15328559102D0C45BD812D97F2E3897E', 0, NULL, NULL, 2, NULL, 0, 'Gather all the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED87028D5517C64D84B41430651B89C6', X'15328559102D0C45BD812D97F2E3897E', X'15328559102D0C45BD812D97F2E3897E', 1, NULL, NULL, 2, NULL, 0, 'Sauté the garlic and onion in olive or canola oil in a large skillet. Allow to cook for 3 to 5 minutes, or until the onion starts to get soft.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6EF4012440A794468CC724A0777A21BE', X'15328559102D0C45BD812D97F2E3897E', X'15328559102D0C45BD812D97F2E3897E', 2, NULL, NULL, 2, NULL, 0, 'Add the crumbled tofu and give it a quick stir.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E1455AFEDF72C4AADB09AD4F6151E69', X'15328559102D0C45BD812D97F2E3897E', X'15328559102D0C45BD812D97F2E3897E', 3, NULL, NULL, 2, NULL, 0, 'Add the curry powder, turmeric, cumin and salt and pepper to the skillet, stirring well to make sure the spices coat the tofu well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E745698537CD542AB723DE861ECA6C4', X'15328559102D0C45BD812D97F2E3897E', X'15328559102D0C45BD812D97F2E3897E', 4, NULL, NULL, 2, NULL, 0, 'Cook for 2 to 3 minutes, then add the diced tomatoes, and allow to heat, stirring frequently for another 3 minutes or so, until tofu is hot and slightly crispy on the outside. (You may need to add a bit more oil if needed during the cooking process.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BA375F8C54B8F4BAFB354D04C5B3880', X'15328559102D0C45BD812D97F2E3897E', X'15328559102D0C45BD812D97F2E3897E', 5, NULL, NULL, 2, NULL, 0, 'Add the spinach, cover the pan and cook for 1 to 2 minutes, just until the spinach is wilted, stirring well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2E70D5145EB0B41AF46250CC5840A04', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'714227D9530BE94E8871B9CA749D347E', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', 1, NULL, NULL, 2, NULL, 0, 'Press the tofu by placing paper towels underneath and on top of the block and weighing it down for several minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89555043EE688F4FB5AD152E9A4C9CB9', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', 2, NULL, NULL, 2, NULL, 0, 'Slice the pressed tofu into 1/2-inch cubes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A8CAE5F845F1544AA0104810C853C0D', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the tofu with the nutritional yeast, flour, garlic powder, salt, and pepper. Toss gently to coat the tofu well. Or, place all ingredients in a zip-lock bag or covered container and shake well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D92AD94609AF346893A66FD11E368CB', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', X'C7C8B6F1D892604CAF7EFFB4D1C7798D', 4, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the oil over medium heat until hot. Add the tofu. Cook for 4 to 6 minutes, turning occasionally, until golden brown and lightly crispy on most sides.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FBB4F81FE916040A94894F46F8B5EC3', X'B2D5890CC5EBDC4C8BEA7ED3B52AB79F', X'B2D5890CC5EBDC4C8BEA7ED3B52AB79F', 0, NULL, NULL, 2, NULL, 0, 'Bring broth up to a boil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9F6EAA61C8D3942A0D70D5989A01A0B', X'B2D5890CC5EBDC4C8BEA7ED3B52AB79F', X'B2D5890CC5EBDC4C8BEA7ED3B52AB79F', 1, NULL, NULL, 2, NULL, 0, 'Add garlic and ginger to the broth and reduce to simmer.');
