INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'140865368085D349BFAEAE56FAA66CA0', X'B44ED7F3BC48D247BB26EC31F85416DB', X'B44ED7F3BC48D247BB26EC31F85416DB', 1, NULL, NULL, 2, NULL, 0, 'Prepare your tofu. Like most vegan tofu recipes, this one will taste best if you press the tofu.');
