INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A922531DB55B5245A206F6F7A42A708F', X'B44ED7F3BC48D247BB26EC31F85416DB', X'B44ED7F3BC48D247BB26EC31F85416DB', 3, NULL, NULL, 2, NULL, 0, 'Chop the broccoli, dice the onion, and mince the garlic.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEB7962C2BEED4429B716A1BDE383C90', X'B44ED7F3BC48D247BB26EC31F85416DB', X'B44ED7F3BC48D247BB26EC31F85416DB', 4, NULL, NULL, 2, NULL, 0, 'In a large skillet, saute onions and garlic in olive oil until the onions turn clear, about 3 to 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2596057F3E30445AE2A650279204B8E', X'B44ED7F3BC48D247BB26EC31F85416DB', X'B44ED7F3BC48D247BB26EC31F85416DB', 5, NULL, NULL, 2, NULL, 0, 'Add the tofu, ginger, cayenne, and broccoli to the pan and continue to cook until broccoli is done, another 6 to 8 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'534CE0CD1AE8014E8D06FF3F4BB04DAF', X'B44ED7F3BC48D247BB26EC31F85416DB', X'B44ED7F3BC48D247BB26EC31F85416DB', 6, NULL, NULL, 2, NULL, 0, 'In a separate small bowl, mix together the cornstarch, soy sauce, and water, then add this mixture to the broccoli and tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C197D544ED5F6948B318E22C615A6E9B', X'B44ED7F3BC48D247BB26EC31F85416DB', X'B44ED7F3BC48D247BB26EC31F85416DB', 7, NULL, NULL, 2, NULL, 0, 'Cook until sauce thickens, then remove from heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB5BE21B163D004DB343B79CEF6B1012', X'B44ED7F3BC48D247BB26EC31F85416DB', X'B44ED7F3BC48D247BB26EC31F85416DB', 8, NULL, NULL, 2, NULL, 0, 'Serve the broccoli and tofu in garlic sauce over rice or whole grains and enjoy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F26DB9F5411F246BD8B7284101DB3C6', X'F4EEF3D9CAFEBC478EE5C8F07583A085', X'F4EEF3D9CAFEBC478EE5C8F07583A085', 0, NULL, NULL, 2, NULL, 0, 'Prepare the dairy-free pie dough according to the instructions, plate in a lightly oiled 9` tart or pie pan, cover with plastic wrap, and place in the refrigerator for at least 30 minutes before filling with the quiche filling and baking.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1DF508CB648F742B7ACDCA191BC2108', X'F4EEF3D9CAFEBC478EE5C8F07583A085', X'F4EEF3D9CAFEBC478EE5C8F07583A085', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'443814D2A3ED1744B280B55A730492A3', X'F4EEF3D9CAFEBC478EE5C8F07583A085', X'F4EEF3D9CAFEBC478EE5C8F07583A085', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make the filling. In a large skillet over medium heat, heat 2 tablespoons of the olive oil, adding the onions once hot. Cook, stirring occasionally until the onions are lightly caramelized, about 6-8 minutes. Add the remaining olive oil, garlic, vegan bacon, 1/2 teaspoon salt and red pepper flakes and cook until the garlic smells fragrant, about 4-5 minutes more. Remove from heat and transfer the mixture to a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45AE7E8D1452ED469453FF7A18B7B1BA', X'F4EEF3D9CAFEBC478EE5C8F07583A085', X'F4EEF3D9CAFEBC478EE5C8F07583A085', 3, NULL, NULL, 2, NULL, 0, 'In a steam basket set over boiling water, steam the broccoli for 3-5 minutes, or until bright green and lightly crisp. Toss with the onion-vegan bacon mixture and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A1D0CB781666941BF32D892DB6E6F3A', X'F4EEF3D9CAFEBC478EE5C8F07583A085', X'F4EEF3D9CAFEBC478EE5C8F07583A085', 4, NULL, NULL, 2, NULL, 0, 'In the bowl of a food processor, process the tofu, lemon juice, tahini, rice vinegar, and remaining 1/2 teaspoon salt until smooth. Fold in the broccoli-onion mixture, stirring until evenly mixed. Using your hands, crumble the vegan cheese evenly into the bottom of the pie crust. Pour the filling on top of the vegan cheese in the prepared pie crust and bake for 45-55 minutes, or until golden brown and set. Serve hot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'570E95E9A05C814CA611D863E1216A39', X'228661F60B4B0341B6678BEA5DC09377', X'228661F60B4B0341B6678BEA5DC09377', 0, NULL, NULL, 2, NULL, 0, 'First, in a large soup or stockpot, saute the onion, garlic, sliced carrots, and shiitake mushrooms in oil for just a few minutes until lightly cooked; do not overcook.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A6C344AA294074091C7C04BAB893C25', X'228661F60B4B0341B6678BEA5DC09377', X'228661F60B4B0341B6678BEA5DC09377', 1, NULL, NULL, 2, NULL, 0, 'Reduce heat to low and add the vegetable broth, coconut milk, minced chili peppers, sliced ginger, lemongrass, tofu, and juice from one lime.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1430AC43E73C644EA7CCDA75B212D2F3', X'228661F60B4B0341B6678BEA5DC09377', X'228661F60B4B0341B6678BEA5DC09377', 2, NULL, NULL, 2, NULL, 0, 'Simmer over low heat for at least 15 minutes. Taste test and add salt as needed.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8130D1DB31BE14592462AE222CD3624', X'228661F60B4B0341B6678BEA5DC09377', X'228661F60B4B0341B6678BEA5DC09377', 3, NULL, NULL, 2, NULL, 0, 'Stir in the fresh chopped cilantro just before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D3E107617C13448A984E434A86D12A1', X'228661F60B4B0341B6678BEA5DC09377', X'228661F60B4B0341B6678BEA5DC09377', 4, NULL, NULL, 2, NULL, 0, 'Enjoy your vegetarian Thai coconut soup!');
