INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3B7CC19E3568C4B8E2196AF6CC9A37C', X'5B4AC14815F2E049B18BB61BE308986A', X'5B4AC14815F2E049B18BB61BE308986A', 1, NULL, NULL, 2, NULL, 0, 'Press your tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4248315E366B945BA6E8B162BF4224D', X'5B4AC14815F2E049B18BB61BE308986A', X'5B4AC14815F2E049B18BB61BE308986A', 2, NULL, NULL, 2, NULL, 0, 'Once your tofu is well-pressed, chop it into chunks, and combine your tofu with the remaining ingredients in a crock pot or slow cooker.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2C9BB6B4C584E469C8B615D8948C3E8', X'5B4AC14815F2E049B18BB61BE308986A', X'5B4AC14815F2E049B18BB61BE308986A', 3, NULL, NULL, 2, NULL, 0, 'Cover and cook on LOW for 5 to 6 hours.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69CCED30D334CE4DA64A7D9E1E1A2D48', X'5B4AC14815F2E049B18BB61BE308986A', X'5B4AC14815F2E049B18BB61BE308986A', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F96FBA4C78FDA41BC4C29E3592382FB', X'829224B717B3A34BA4D84ABCFF31334A', X'829224B717B3A34BA4D84ABCFF31334A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B98019FEBD9CF44EB0CCCE42B0BDEC7A', X'829224B717B3A34BA4D84ABCFF31334A', X'829224B717B3A34BA4D84ABCFF31334A', 1, NULL, NULL, 2, NULL, 0, 'Prepare the dairy-free graham cracker crust, baking the crust in a 9-inch pie plate. Allow the crust to cool completely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0A5E3C435B56C4E9C3782EDD93DE35D', X'829224B717B3A34BA4D84ABCFF31334A', X'829224B717B3A34BA4D84ABCFF31334A', 2, NULL, NULL, 2, NULL, 0, 'In a blender, combine the tofu, lime juice, zest (if using), sugar, salt, and cornstarch.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'883651472F76E64D8D0AF40A0E7734E6', X'829224B717B3A34BA4D84ABCFF31334A', X'829224B717B3A34BA4D84ABCFF31334A', 3, NULL, NULL, 2, NULL, 0, 'Process for several minutes, or until smooth and thick.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0C332D2C122AE4A8A5AD377A4C6F15A', X'829224B717B3A34BA4D84ABCFF31334A', X'829224B717B3A34BA4D84ABCFF31334A', 4, NULL, NULL, 2, NULL, 0, 'Pour into the prepared pie crust and place in the freezer for at least 2 hours, or until firm.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D28C35737433F647B8DADF149CC54020', X'829224B717B3A34BA4D84ABCFF31334A', X'829224B717B3A34BA4D84ABCFF31334A', 5, NULL, NULL, 2, NULL, 0, 'Serve from the freezer, garnished with lime wedges if desired.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14D4DFF6A5EBFB4A8E60FDB11F12A6B2', X'829224B717B3A34BA4D84ABCFF31334A', X'829224B717B3A34BA4D84ABCFF31334A', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79C83F912CBDD94D88EF4EE2E4A9ECBF', X'432033F57266AE48866CDFF49B01C7B8', X'432033F57266AE48866CDFF49B01C7B8', 0, NULL, NULL, 2, NULL, 0, 'Melt the chocolate chips over low heat, stirring frequently to ensure the chocolate doesn''t burn.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20878CCC6E9FE34EA319252E54D84FA0', X'432033F57266AE48866CDFF49B01C7B8', X'432033F57266AE48866CDFF49B01C7B8', 1, NULL, NULL, 2, NULL, 0, 'You can use a double boiler if you have one.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69ED17CE0DE6AA4B9B918CC29A7EC80F', X'432033F57266AE48866CDFF49B01C7B8', X'432033F57266AE48866CDFF49B01C7B8', 2, NULL, NULL, 2, NULL, 0, 'Once the chocolate is melted, blend together the peanut butter, silken tofu, chocolate and soy milk in the blender until smooth and creamy, adding a bit more soy milk if necessary.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CA9CCA27B51954D84627C082E773BE4', X'432033F57266AE48866CDFF49B01C7B8', X'432033F57266AE48866CDFF49B01C7B8', 3, NULL, NULL, 2, NULL, 0, 'Pour into pie crust and refrigerate until firm, at least one hour.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84975F977DB69B42900F5C0E678ABAF1', X'0BE78E66AA4DAC4882062EF50B542730', X'0BE78E66AA4DAC4882062EF50B542730', 0, NULL, NULL, 2, NULL, 0, 'Once your tofu has been well-pressed, you can slice it into thin `finger` shapes, slabs, cubes, or whatever shape you like best.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68DF1D4F9EE83B49AF0B5FE3F23FB7C7', X'0BE78E66AA4DAC4882062EF50B542730', X'0BE78E66AA4DAC4882062EF50B542730', 1, NULL, NULL, 2, NULL, 0, 'In a shallow pan or a zip-closing plastic bag, combine the cornstarch, garlic salt, mustard powder and black pepper. Carefully add the tofu, gently tossing to make sure that all sides of the tofu are well coated with the cornstarch mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A19D96D98CB2154CB04E41FE1F3F0601', X'0BE78E66AA4DAC4882062EF50B542730', X'0BE78E66AA4DAC4882062EF50B542730', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil in a sauté pan or skillet over medium heat, using a non-stick pan if you have it to reduce the amount of oil needed. Add tofu and pan-fry on all sides just until it is just lightly golden brown. Once the tofu has cooked, remove it from the pan and set it aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CC241F29FCB304188620D78ED82F8F2', X'0BE78E66AA4DAC4882062EF50B542730', X'0BE78E66AA4DAC4882062EF50B542730', 3, NULL, NULL, 2, NULL, 0, 'Using the same pan (or a different one, your choice), heat the vegan margarine or butter substitute over very low heat. Once it is melted, add in the wing sauce, stirring just until combined well. Add the cooked tofu, gently turning to coat well on all sides with the wing sauce mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4111C69535D0A948865859C3EEC75460', X'0BE78E66AA4DAC4882062EF50B542730', X'0BE78E66AA4DAC4882062EF50B542730', 4, NULL, NULL, 2, NULL, 0, 'Once that is completely heated, you can take the tofu off of the stovetop heat and serve it while it is warm.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79876BDD40D2384AB1985C3AEA0BBE57', X'0BE78E66AA4DAC4882062EF50B542730', X'0BE78E66AA4DAC4882062EF50B542730', 5, NULL, NULL, 2, NULL, 0, 'Serve with some vegan ranch dressing if you''d like.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA029EC698DB2A44A850DA80F76C7236', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'140C11F20DA40243A0741BA7FB726824', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 1, NULL, NULL, 2, NULL, 0, 'Drain the tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F424B22E94FD743A9A52F02C7BACCE6', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 2, NULL, NULL, 2, NULL, 0, 'While the tofu is draining, prepare the marinade. In a bowl, whisk together the rice vinegar, light and dark soy sauce, Asian sesame oil, sugar, chili paste, garlic, and olive oil. Cover and refrigerate while preparing and dry frying the tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C14BD605DAD667408FB42B6EA733BF10', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 3, NULL, NULL, 2, NULL, 0, 'Cut the drained tofu into triangles about 1/2 inch thick. Dry-fry the tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA4921C29BF9F147AD7B10CF89711EE9', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 4, NULL, NULL, 2, NULL, 0, 'Remove the marinade from the refrigerator and pour into a large resealable plastic bag. Add the dry-fried tofu. Refrigerate overnight, turning the bag occasionally so that all the tofu is covered.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4818A125938047499A583CDC1DCD3625', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 5, NULL, NULL, 2, NULL, 0, 'Next day, prepare the vegetables for stir-frying. Remove the tofu from the bag. Reserve the marinade.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'428172D729578249BBE6C29A94CFE340', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 6, NULL, NULL, 2, NULL, 0, 'Preheat a wok on medium-high heat. Add 1 tablespoon oil. When the oil is hot, add the ginger slices and cook for a few seconds until fragrant. Add the onion and the mushrooms. Stir-fry for 2 minutes, adding water, vegetable broth, or Chinese rice wine or dry sherry if the vegetables begin to dry out.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D87572A99EC3340AD9056F5DBAD2DE3', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 7, NULL, NULL, 2, NULL, 0, 'Add the bell pepper. Stir briefly, then add the baby corn. Stir-fry for a minute, then add the tofu slices. Cook briefly, turning gently, to heat the tofu through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFD38945348C2A4BB9593F2318C29B0E', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 8, NULL, NULL, 2, NULL, 0, 'Push the vegetables and tofu to the sides of the wok. Add the reserved marinade in the middle of the wok and bring to a boil. Re-stir the cornstarch/water mixture and add to the boiling liquid, stirring quickly to thicken.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECBFEF8CD6F21949B6149445222E49AF', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 9, NULL, NULL, 2, NULL, 0, 'Stir to combine the sauce with the other ingredients. Taste and adjust seasoning as desired.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35FE4E37B17D5842B671823F758947E6', X'D100841B66E5EA459E2C9EA2D4CE856E', X'D100841B66E5EA459E2C9EA2D4CE856E', 10, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BECAAF448AED6F4FAFD6641731837261', X'D81ECA8F92AE814FBDBAA03ABFB1E073', X'D81ECA8F92AE814FBDBAA03ABFB1E073', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84A02444351CA54A8662B51124F4951B', X'D81ECA8F92AE814FBDBAA03ABFB1E073', X'D81ECA8F92AE814FBDBAA03ABFB1E073', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together ?the rice vinegar, light and dark soy sauces, Asian sesame oil, sugar, chili paste, garlic, and olive oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45A063205470C54BB98E17EC0C68EAB3', X'D81ECA8F92AE814FBDBAA03ABFB1E073', X'D81ECA8F92AE814FBDBAA03ABFB1E073', 2, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate while preparing and dry frying the tofu if you haven''t done so already.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F99235476834A4419FE7E73D7F262B83', X'D81ECA8F92AE814FBDBAA03ABFB1E073', X'D81ECA8F92AE814FBDBAA03ABFB1E073', 3, NULL, NULL, 2, NULL, 0, 'Put the marinade in a large bowl or large resealable plastic bag. Add the dry-fried tofu pieces.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F52A9E3B1AD0C4F878736A20047AE4F', X'D81ECA8F92AE814FBDBAA03ABFB1E073', X'D81ECA8F92AE814FBDBAA03ABFB1E073', 4, NULL, NULL, 2, NULL, 0, 'Cover the bowl or seal the bag and refrigerate overnight, stirring occasionally to make sure all the tofu pieces are covered.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'055071037065C84FA5F532D7E0C0D53B', X'D81ECA8F92AE814FBDBAA03ABFB1E073', X'D81ECA8F92AE814FBDBAA03ABFB1E073', 5, NULL, NULL, 2, NULL, 0, 'Drain the tofu, reserving the tofu marinade to use as a sauce at the end of stir-frying. Thicken with a cornstarch slurry if desired.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'731742CFD6AE724A8C14B7D4785E08D7', X'D81ECA8F92AE814FBDBAA03ABFB1E073', X'D81ECA8F92AE814FBDBAA03ABFB1E073', 6, NULL, NULL, 2, NULL, 0, 'Stir-fry and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34FE49DBF77BD44B8490B895A09F8F39', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74C26C7DC2C9F640AA5B3C5046039767', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, gently combine ground beef or ground pork, onion, cabbage, tofu, and noodles.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20A1AB3572F74D40BA2668753D784C4C', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 2, NULL, NULL, 2, NULL, 0, 'In a separate small bowl, combine garlic, sesame oil, soy sauce, salt, and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24F310EE87DFDB48A751FDF94B3EB7F6', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 3, NULL, NULL, 2, NULL, 0, 'Pour seasoning mixture over meat and vegetables and mix with hands to combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'715A2EBD4E6BA84A8BE8E1C0FFE358E5', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 4, NULL, NULL, 2, NULL, 0, 'Place about 1 tablespoon of filling in the center of dumpling wrapper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32AA5CA61FA79B4FBD995A1579EF32A2', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 5, NULL, NULL, 2, NULL, 0, 'Dip your finger in water and wet the perimeter of half of the wrapper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49CB62327A1BD14A83317BD140AFF5AD', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 6, NULL, NULL, 2, NULL, 0, 'Fold the wrapper in half, pressing to seal and then crimp the edges. Repeat until the filling is gone.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC9AC1180C84AE44900203ED0D128BB1', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 7, NULL, NULL, 2, NULL, 0, 'You can steam, boil, fry, or sauté the dumplings as you wish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5217A8717C32B0428D797AF6BB2ADCB9', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 8, NULL, NULL, 2, NULL, 0, 'Serve with basic dipping sauce or a spicy sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACC5E51DBFE4944F84D56766B6EB14BE', X'E131509F6BFC4B44BE0777315D6B5239', X'E131509F6BFC4B44BE0777315D6B5239', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D968D56D72DD3A438A7DF22540833B29', X'E78A2E51009B05458EF836A573B1AEE7', X'E78A2E51009B05458EF836A573B1AEE7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFEC16853F4FC44AA6AE658E3E5FB1DC', X'E78A2E51009B05458EF836A573B1AEE7', X'E78A2E51009B05458EF836A573B1AEE7', 1, NULL, NULL, 2, NULL, 0, 'Drain the tofu well, pressing out the excess moisture gently. If you leave too much moisture in the tofu, you''ll have trouble forming patties; they just won''t stay together.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCAEA6F250A1F54180C06FE1B0E25ADC', X'E78A2E51009B05458EF836A573B1AEE7', X'E78A2E51009B05458EF836A573B1AEE7', 2, NULL, NULL, 2, NULL, 0, 'Once your tofu is well pressed and drained, gently mash into small crumbles in a large bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'056BFBA5DD1D284486E2AD19511398A3', X'E78A2E51009B05458EF836A573B1AEE7', X'E78A2E51009B05458EF836A573B1AEE7', 3, NULL, NULL, 2, NULL, 0, 'Add the diced onions, diced green onions, wheat germ, flour, garlic powder, soy sauce, and a dash of pepper into the bowl along with the tofu. Mix to combine well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'413E5DF0202A2648B34C7A8B6948BA13', X'E78A2E51009B05458EF836A573B1AEE7', X'E78A2E51009B05458EF836A573B1AEE7', 4, NULL, NULL, 2, NULL, 0, 'Gently press the mixture together to form patties about 1-inch thick. You''ll probably want to use your (clean!) hands for this.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A4DFE99EDDB2F49A5BB596031D49107', X'E78A2E51009B05458EF836A573B1AEE7', X'E78A2E51009B05458EF836A573B1AEE7', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil on medium-high heat in a large skillet and fry the veggie burgers until lightly golden brown and crisp, about 5 to 6 minutes on each side.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E88C9830936A4184BFD5C94A40FBD6', X'E78A2E51009B05458EF836A573B1AEE7', X'E78A2E51009B05458EF836A573B1AEE7', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1613D996E7949541A8805201642A85AC', X'F84950167F5AA84B9F098BC15869D70F', X'F84950167F5AA84B9F098BC15869D70F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57212590089E1C46A995B5AFBE8C5348', X'F84950167F5AA84B9F098BC15869D70F', X'F84950167F5AA84B9F098BC15869D70F', 1, NULL, NULL, 2, NULL, 0, 'Prepare the broth. In a large stockpot, heat the oil over medium-high heat. Add the garlic and cook, stirring often, for about 2 minutes, or until fragrant. Add 1 1/2 cups of the chopped scallions and the vegetable broth. Bring the broth to a boil, then reduce the heat and allow the broth to simmer while you prepare the wontons.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C00D68037F8AD54E9DD2C05AA6A85C52', X'F84950167F5AA84B9F098BC15869D70F', X'F84950167F5AA84B9F098BC15869D70F', 2, NULL, NULL, 2, NULL, 0, 'Prepare the filling. In a saucepan over medium heat, heat the canola oil, adding the finely chopped mushrooms and garlic powder once hot. Cook, stirring often, until the mushrooms are soft, about 3 to 4 minutes. Remove the pan from heat and stir in the crumbled tofu, lemon juice, salt and remaining 1/2 cup chopped scallions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61D5CBA0002D244095E159C88213EC1E', X'F84950167F5AA84B9F098BC15869D70F', X'F84950167F5AA84B9F098BC15869D70F', 3, NULL, NULL, 2, NULL, 0, 'Fill the wontons. Set out the wonton wrappers on a dry surface, such as a counter, cutting board, or baking sheet. Place about 1 T. of the filling into the center of the wontons. Moisten the edges of the wrappers with water, using a pastry brush or your fingers, and fold: pull one corner to it''s diagonal and pinch, then pull the remaining two corners to the same point and pinch the edges closed. (To see this done step-by-step, check out my chocolate wontons recipe tutorial.) Repeat until all of the wontons or filling is used.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0394A36C437E1E49BF9EA3821CA84055', X'F84950167F5AA84B9F098BC15869D70F', X'F84950167F5AA84B9F098BC15869D70F', 4, NULL, NULL, 2, NULL, 0, 'Bring a pot of salted water to simmering. Just before serving, cook the wontons you intend to serve with the meal (leftover wontons can be frozen until a later date) in the salted water until they become translucent, about 2 to 3 minutes. Use a slotted spoon to remove the wontons from the boiling water. Transfer 3 to 4 wontons to each bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C1568DEBAD7D741B489E49274A79405', X'F84950167F5AA84B9F098BC15869D70F', X'F84950167F5AA84B9F098BC15869D70F', 5, NULL, NULL, 2, NULL, 0, 'Add the bok choy and broccoli florets to the simmering broth. Cook for about 2 to 3 minutes, or until the vegetables are bright green in color. Ladle the broth and greens over the wontons in the serving bowls and serve immediately with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'209B965B1DB96D47AED2AAE992B12C69', X'F84950167F5AA84B9F098BC15869D70F', X'F84950167F5AA84B9F098BC15869D70F', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3ECC1C02708A041A09E26EC543D2291', X'4C73D71D729C4E44A4D13E4B461EE6E5', X'4C73D71D729C4E44A4D13E4B461EE6E5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23097C916639C74B83239219D0BC4527', X'4C73D71D729C4E44A4D13E4B461EE6E5', X'4C73D71D729C4E44A4D13E4B461EE6E5', 1, NULL, NULL, 2, NULL, 0, 'Prepare the tofu by pressing it to remove excess moisture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B67554252E344C4FB4FD3264A096B45B', X'4C73D71D729C4E44A4D13E4B461EE6E5', X'4C73D71D729C4E44A4D13E4B461EE6E5', 2, NULL, NULL, 2, NULL, 0, 'Once your tofu is well pressed, crumble or chop it into small bite-sized pieces.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11FF95C66DC3D14D86CBD49934D06AEC', X'4C73D71D729C4E44A4D13E4B461EE6E5', X'4C73D71D729C4E44A4D13E4B461EE6E5', 3, NULL, NULL, 2, NULL, 0, 'Add the oil to a large skillet over medium meat. Saute the crumbled or diced tofu with the bell pepper and scallions until the tofu is lightly browned.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D876C98E3991D445B71C07B01B0BE40E', X'4C73D71D729C4E44A4D13E4B461EE6E5', X'4C73D71D729C4E44A4D13E4B461EE6E5', 4, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, garlic powder, onion powder, and turmeric, stirring to combine well. Cook, tossing, for another 6 to 8 minutes until hot and cooked.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E052EC5F175AE4F9D6168B7F5A78233', X'4C73D71D729C4E44A4D13E4B461EE6E5', X'4C73D71D729C4E44A4D13E4B461EE6E5', 5, NULL, NULL, 2, NULL, 0, 'Season generously with salt and pepper and top with the cheese (or vegan cheese) just before serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'053AA3CCC4EB534DA582D51CC8D5DF82', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8AC296019CD3240A66B0A61AC518608', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 1, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, gently combine egg, onion, cabbage, bean sprouts, tofu, and noodles.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9521493E9267DE40B4C9E0C265628BF7', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine garlic, sesame oil, soy sauce, salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1D4980166123040994F3642D3D42EB1', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 3, NULL, NULL, 2, NULL, 0, 'Pour seasoning mixture over tofu and vegetables and use your hands to mix the ingredients together.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BED945EA8BACEB468596524D5DC70D1F', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 4, NULL, NULL, 2, NULL, 0, 'Place about 1 tablespoon of filling in the center of the dumpling wrapper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C3446A319475B48B9F9A333BBD218CB', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 5, NULL, NULL, 2, NULL, 0, 'Dip your finger in the water and wet the outside edge of the top half of the wrapper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9B918447ADBFD4E979DBC83928A810E', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 6, NULL, NULL, 2, NULL, 0, 'Fold the wrapper up to close it and then crimp the edges.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8249566098E7944CA4F5553F7944EA71', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 7, NULL, NULL, 2, NULL, 0, 'Repeat until the filling is gone.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4BB750AF8CF1394A8A593460ED29E54E', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 8, NULL, NULL, 2, NULL, 0, 'Then steam, boil, fry, or sauté the dumplings as you wish.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB0EF217825154449AABC0E31CBF056B', X'47D0ED9206BD474A8C08A49EE4FE4416', X'47D0ED9206BD474A8C08A49EE4FE4416', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3CD87727EC915B48A9A923ACC4705FEE', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 0, NULL, NULL, 2, NULL, 0, 'Cut the goya in half lengthwise.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4259ED299EE7A84A9BB2C412ACC00B83', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 1, NULL, NULL, 2, NULL, 0, 'Remove the seeds with a spoon.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BFF370DE8E66D4F879F1EBBEAF0F64E', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 2, NULL, NULL, 2, NULL, 0, 'Slice the goya thinly and place goya slices in a bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5A92505B6B6AC4FAEC1E59F22493355', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 3, NULL, NULL, 2, NULL, 0, 'Sprinkle some salt over them. Let them sit for about 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C4CD9654E99E745B77BCC10E5B976B3', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 4, NULL, NULL, 2, NULL, 0, 'Wash goya slices and drain well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83B62EFE5F68A548AA9B05EB3F661484', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 5, NULL, NULL, 2, NULL, 0, 'Squeeze them to remove excess water.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACF12E00E7288345B6D26B92C597BF49', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 6, NULL, NULL, 2, NULL, 0, 'Heat about 1 tbsp. of vegetable oil in a large skillet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5EA5C92A3DA8642A245A91E7A6C8395', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 7, NULL, NULL, 2, NULL, 0, 'Stir-fry pork and season with salt and pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79B0ABC51F53DD4F9CD7DEBE6CBA010E', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 8, NULL, NULL, 2, NULL, 0, 'Add goya slices and cook until softened.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9820BAC7C025A146A4BD5A5D9C2CED5C', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 9, NULL, NULL, 2, NULL, 0, 'Crumble tofu into large pieces and add in the skillet.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A154881C324A064D9769AEE79DD029F2', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 10, NULL, NULL, 2, NULL, 0, 'Lightly stir-fry with pork and goya. Season with sake.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F335615441A96458931D3F002E7D615', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 11, NULL, NULL, 2, NULL, 0, 'Pour beaten eggs over and stir quickly. Season with soy sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9DF3E6211E40A4BA424EC7E4E33583F', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 12, NULL, NULL, 2, NULL, 0, 'Stop the heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3436EB5C9FA5DB479D539F331B39F649', X'971473A838646B419B7587BC78BBF9DF', X'971473A838646B419B7587BC78BBF9DF', 13, NULL, NULL, 2, NULL, 0, 'Adjust the flavor with salt.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'084F70A0E23D7C4CB0777AD833021389', X'2067E7719D705A438F7C23A77F28DD8B', X'2067E7719D705A438F7C23A77F28DD8B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E380AD37FE7CB14FAD18F28C6F811844', X'2067E7719D705A438F7C23A77F28DD8B', X'2067E7719D705A438F7C23A77F28DD8B', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan or stock pot, bring the dashi or stock to a simmer over medium-high heat but do not boil. Combine the white and red miso in a small dish, then add 1 1/2 cups of the hot dashi and whisk to combine. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'466008C7A3CB034988A1BCC807240779', X'2067E7719D705A438F7C23A77F28DD8B', X'2067E7719D705A438F7C23A77F28DD8B', 2, NULL, NULL, 2, NULL, 0, 'Simmer the dashi for 5 minutes more. Gently stir in the tofu and simmer for another 5 minutes. Again - do not boil! Add the baby bok choy, scallions, and miso mixture, and cook for another 5 minutes, or until the bok choy is slightly wilted but still bright green.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7BE89CA8BB93440B25B4EE94EBD7A83', X'2067E7719D705A438F7C23A77F28DD8B', X'2067E7719D705A438F7C23A77F28DD8B', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately while hot, taking care to stir before serving as the miso will settle.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAE55E98D51D464DB89367CC2A41CC22', X'2067E7719D705A438F7C23A77F28DD8B', X'2067E7719D705A438F7C23A77F28DD8B', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA3103CCED25364A9BD3D8A795C9E465', X'6508F93C73D5BD428DDA841E68CC3D9C', X'6508F93C73D5BD428DDA841E68CC3D9C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65BECD5023C5034DA21261496477592D', X'6508F93C73D5BD428DDA841E68CC3D9C', X'6508F93C73D5BD428DDA841E68CC3D9C', 1, NULL, NULL, 2, NULL, 0, 'First, prepare your tofu. Like most vegan tofu recipes, this one will taste best if you press the tofu first. Not sure how to do that? See this easy step-by-step char(36)e on how to press tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB540A767AE28948A0CDA1703D5E6F78', X'6508F93C73D5BD428DDA841E68CC3D9C', X'6508F93C73D5BD428DDA841E68CC3D9C', 2, NULL, NULL, 2, NULL, 0, 'After your tofu is well pressed, slice it into your desired shapes. Fingers, cubes, triangles or slabs will all work just fine, but the finger shape will give you the best coating-to-tofu ratio for this recipe.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'784303C4F102B647B706AAE48434D79D', X'6508F93C73D5BD428DDA841E68CC3D9C', X'6508F93C73D5BD428DDA841E68CC3D9C', 3, NULL, NULL, 2, NULL, 0, 'Once your tofu is ready to go, whisk together the egg replacer and water then add the Dijon mustard into it.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'452EECE411F6964F802B0F9D185617C1', X'6508F93C73D5BD428DDA841E68CC3D9C', X'6508F93C73D5BD428DDA841E68CC3D9C', 4, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine the panko with a bit of salt and pepper and preheat a skillet with a bit of oil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89271095A9B48641BC1B0395140EA759', X'6508F93C73D5BD428DDA841E68CC3D9C', X'6508F93C73D5BD428DDA841E68CC3D9C', 5, NULL, NULL, 2, NULL, 0, 'Dip each piece of tofu into the mustard and egg replacer batter mixture, then coat well with panko. Fry your tofu in the oil, turning as needed, for a few minutes on each side.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D4E6DBDED8E3A4FB7FF18448EB34F19', X'6508F93C73D5BD428DDA841E68CC3D9C', X'6508F93C73D5BD428DDA841E68CC3D9C', 6, NULL, NULL, 2, NULL, 0, 'Enjoy your panko-coated tofu fingers! You''ll probably want to serve them with a dipping sauce. Just about anything will do; a sweet chili sauce is a good option!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'497E12F28E60194D9CC7273944A3799B', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0463A6E5F2A3C41BDDDE5AE28A92853', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 1, NULL, NULL, 2, NULL, 0, 'Drain the tofu and cut into 1-inch cubes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A2D2EC69C737F4B979B36B9EC8A467E', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 2, NULL, NULL, 2, NULL, 0, 'Soften the dried mushrooms by soaking in hot water for 20 to 30 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFFFCD0D98F51C4B9ACB1AA2D25A90E4', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 3, NULL, NULL, 2, NULL, 0, 'Squeeze out any excess water and slice. If desired, strain and reserve a bit of the mushroom soaking water to add to the sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA6444563D88FC4AA36C72585087125F', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 4, NULL, NULL, 2, NULL, 0, 'Rinse the bamboo shoots under warm running water to remove any `tinny` taste. Drain thoroughly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E68ED9900BC14A479736637FD21CED96', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl or measuring cup, mix together the chicken broth, dark soy sauce, oyster sauce, and sugar. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'561D6D7F69946B4E91A675EE5A146E2F', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 6, NULL, NULL, 2, NULL, 0, 'In another small bowl, dissolve the cornstarch in the water. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CBB637763F38A4386A0E5F889C66AB3', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 7, NULL, NULL, 2, NULL, 0, 'Heat the wok and add 2 tablespoons oil, drizzling it around the sides of the wok. When the oil is hot, add the bean curd cubes. Stir-fry until browned. Remove the bean curd from the wok.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'212A75E2BF737A47951048632C7988E1', X'AD9AB0ACF4EFD041A5CB7DE934034539', X'AD9AB0ACF4EFD041A5CB7DE934034539', 8, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons oil to the wok. When the oil is hot, add the garlic, and stir-fry until aromatic.');
