INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B9BFAD2EFFF1249BB91BE40CEFAE750', X'F5EE8E8722B6574987585F7A475C28D4', X'F5EE8E8722B6574987585F7A475C28D4', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77B18647A515154AABA70A4B972AB61D', X'3F3239502A07364595CA4CC535EB8FB3', X'3F3239502A07364595CA4CC535EB8FB3', 0, NULL, NULL, 2, NULL, 0, 'Drain the tofu and cut into cubes. Marinate the ground pork in the soy sauce and cornstarch for 20 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BAFE6D855C212347BAC40F7A5BE7C731', X'3F3239502A07364595CA4CC535EB8FB3', X'3F3239502A07364595CA4CC535EB8FB3', 1, NULL, NULL, 2, NULL, 0, 'Wash the green onions and cut on the diagonal into 1-inch pieces. Peel and finely chop the garlic.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A97BAA1EEE0294BB9CB28F8BF07B1B6', X'3F3239502A07364595CA4CC535EB8FB3', X'3F3239502A07364595CA4CC535EB8FB3', 2, NULL, NULL, 2, NULL, 0, 'Heat the wok over medium-high to high heat. Add 2 tablespoons oil. When the oil is hot, add the chile paste and the chopped garlic. Stir-fry until aromatic (about 30 seconds). Add the ground pork. Stir-fry until the pork is nearly cooked through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5708BFF44090EE4789893AC517816523', X'3F3239502A07364595CA4CC535EB8FB3', X'3F3239502A07364595CA4CC535EB8FB3', 3, NULL, NULL, 2, NULL, 0, 'Lower the heat to medium, and add the black bean sauce and the tofu cubes. Cook over medium heat until the tofu is browned (8 to 10 minutes). Add water or chicken broth as needed. Stir in the green onion. Just before serving, sprinkle with the Szechuan peppercorn or ground coriander.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'048BD3DF50B54D4BB358240BC7E5149D', X'471970F72F66FD459C750782C39B7A74', X'471970F72F66FD459C750782C39B7A74', 0, NULL, NULL, 2, NULL, 0, 'In a large skillet or wok, heat the oil and add garlic and ginger for just a minute or two, then add the tofu, carefully stirring to mix up the ginger and garlic. Heat for 3 to 4 minutes, until tofu is lightly golden.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EC70E14F7CAEB41A78FCDAFD9FA0912', X'471970F72F66FD459C750782C39B7A74', X'471970F72F66FD459C750782C39B7A74', 1, NULL, NULL, 2, NULL, 0, 'Add the red bell pepper and celery and heat, stirring, for about a minute, then add the mushrooms and bamboo shoots.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A447F2B1A642040BCAC342A0F01846A', X'471970F72F66FD459C750782C39B7A74', X'471970F72F66FD459C750782C39B7A74', 2, NULL, NULL, 2, NULL, 0, 'Add the vegetable broth or water and soy sauce and allow to simmer for another minute or two, until vegetables are tender but not yet done.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48DC6AD6B0515E43A1143F6C8AE2114B', X'471970F72F66FD459C750782C39B7A74', X'471970F72F66FD459C750782C39B7A74', 3, NULL, NULL, 2, NULL, 0, 'Stir in water-cornstarch mixture, heat until thickened and vegetables are done cooking; then stir in cashews and green onions. Combine well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0860FD29037E6443AB7D9B28D68E1D66', X'471970F72F66FD459C750782C39B7A74', X'471970F72F66FD459C750782C39B7A74', 4, NULL, NULL, 2, NULL, 0, 'Serve this vegetable stir fry immediately with steamed rice, cooked noodles, or your favorite whole grain.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB402C7FE5D2E948B4769EEEDDA5A782', X'F1DF0E206444454586340DF768559182', X'F1DF0E206444454586340DF768559182', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99C62D70A1FA3C4B91ABF528E2038042', X'F1DF0E206444454586340DF768559182', X'F1DF0E206444454586340DF768559182', 1, NULL, NULL, 2, NULL, 0, 'Drain and mash the tofu. Wash and prepare all the vegetables. Lightly beat the egg with the salt and pepper. Combine the mashed tofu and vegetables with the egg and seasonings.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40904305A02E3E498C640AD66AF2E962', X'F1DF0E206444454586340DF768559182', X'F1DF0E206444454586340DF768559182', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil for deep-frying while you are wrapping the wontons. The oil should be heated to between 360 and 375 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E68FB4D731B9F44951B33570D3ABDEA', X'F1DF0E206444454586340DF768559182', X'F1DF0E206444454586340DF768559182', 3, NULL, NULL, 2, NULL, 0, 'To fill the wontons, lay a wrapper in front of you so that it forms a diamond shape. Wet all the edges of the wrapper with water. Place a teaspoon of filling in the middle. Bring the top half of the wrapper over the filling and seal the edges.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6FDC7FEC6676E40A49885EC7C6B657B', X'F1DF0E206444454586340DF768559182', X'F1DF0E206444454586340DF768559182', 4, NULL, NULL, 2, NULL, 0, 'Deep-fry the wontons until they are golden and crispy (about 2 minutes). Serve with hoisin sauce or soy sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4A37FC5D5BEA5458300ED6B33D78E09', X'F1DF0E206444454586340DF768559182', X'F1DF0E206444454586340DF768559182', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A0BD2F4488784CB4D7CC73514D3303', X'18234AC13B4D8A458CE61A13C1A2EEF3', X'18234AC13B4D8A458CE61A13C1A2EEF3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79A0813F2992144BA611D6B5DDB0B9B6', X'18234AC13B4D8A458CE61A13C1A2EEF3', X'18234AC13B4D8A458CE61A13C1A2EEF3', 1, NULL, NULL, 2, NULL, 0, 'Prepare the tofu. Most tofu will taste best if you press it first. This is because tofu is like a spongethe more water you gently press out of it, the more delicious marinade it can soak up. To press tofu, place it on a layer of paper towels or a clean dish cloth, and place another layer of paper towels on top of the tofu. Place a weight on it, such as a heavy can inside a glass or ceramic bowl. Let it sit for 30 minutes to press.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E884AE974EC2F54294ABDBA5E77A420A', X'18234AC13B4D8A458CE61A13C1A2EEF3', X'18234AC13B4D8A458CE61A13C1A2EEF3', 2, NULL, NULL, 2, NULL, 0, 'Slice the tofu into four thick slabs. Or, if you want to grill the tofu on skewers, chop it into cubes and place them on skewers.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD562CC96AFC774EBA5C012B57894E49', X'18234AC13B4D8A458CE61A13C1A2EEF3', X'18234AC13B4D8A458CE61A13C1A2EEF3', 3, NULL, NULL, 2, NULL, 0, 'Whisk together remaining ingredients and add tofu, coating well. Let it marinate in the refrigerator for at least two hours, and up to overnight.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9452060CE11DB84C922331E362761BC0', X'18234AC13B4D8A458CE61A13C1A2EEF3', X'18234AC13B4D8A458CE61A13C1A2EEF3', 4, NULL, NULL, 2, NULL, 0, 'Preheat an outdoor grill to medium heat. Oil the grate lightly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'112BAE403881254CAD1C2705CBF22DA4', X'18234AC13B4D8A458CE61A13C1A2EEF3', X'18234AC13B4D8A458CE61A13C1A2EEF3', 5, NULL, NULL, 2, NULL, 0, 'Place tofu or bamboo skewers on the grill and cook until lightly browned on all sides (about five minutes), basting occasionally with extra marinade. Serve hot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0162CF57661080409F19F205048035B7', X'56AF9E2C7F0C0345A0BBB462E25A69FA', X'56AF9E2C7F0C0345A0BBB462E25A69FA', 0, NULL, NULL, 2, NULL, 0, 'Sauté the onion and garlic in olive oil until soft, about 4 to 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B24D4048288CB946A9AA4E465201746E', X'56AF9E2C7F0C0345A0BBB462E25A69FA', X'56AF9E2C7F0C0345A0BBB462E25A69FA', 1, NULL, NULL, 2, NULL, 0, 'Add spinach and salt, stirring to combine well and cook just until spinach is heated through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B66EB92CE7E6146831239B8C32CD47B', X'56AF9E2C7F0C0345A0BBB462E25A69FA', X'56AF9E2C7F0C0345A0BBB462E25A69FA', 2, NULL, NULL, 2, NULL, 0, 'Add crumbled tofu and mix well. Allow to cool completely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5012F9507AE214687E160EE768DC9AE', X'56AF9E2C7F0C0345A0BBB462E25A69FA', X'56AF9E2C7F0C0345A0BBB462E25A69FA', 3, NULL, NULL, 2, NULL, 0, 'In a small saucepan over low heat, combine the cashew or almond butter, soy milk, miso, soy sauce, lemon juice, nutritional yeast, and onion powder until smooth and creamy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'406779CFC340044A8FD7DCF902B50191', X'56AF9E2C7F0C0345A0BBB462E25A69FA', X'56AF9E2C7F0C0345A0BBB462E25A69FA', 4, NULL, NULL, 2, NULL, 0, 'Prepare the lasagna noodles according to package instructions, if needed, and preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FD2DC658E9C09498F26347DDBDB49BB', X'56AF9E2C7F0C0345A0BBB462E25A69FA', X'56AF9E2C7F0C0345A0BBB462E25A69FA', 5, NULL, NULL, 2, NULL, 0, 'In a lightly greased lasagna pan, place a thin layer of the white cashew sauce and then a layer of noodles. Next, add spinach and more cashew white sauce, then a layer of noodles, continuing until all ingredients are used up. The top layer should be spinach and then sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4F33683914AAE4BADA8FC252B678B42', X'56AF9E2C7F0C0345A0BBB462E25A69FA', X'56AF9E2C7F0C0345A0BBB462E25A69FA', 6, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes, or until done. Allow to cool for at least 10 minutes before serving, to allow lasagna to set.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C2F35667FE6C140AE78E0BB58446EE3', X'3A169D6381EFE140893415B8C50D1CE4', X'3A169D6381EFE140893415B8C50D1CE4', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62777BB27C67F74990A726BB4A7AAE9E', X'3A169D6381EFE140893415B8C50D1CE4', X'3A169D6381EFE140893415B8C50D1CE4', 1, NULL, NULL, 2, NULL, 0, 'In a large pan, crumble the tofu and sauté in olive oil with taco seasoning or chili powder until lightly golden brown and coated with spices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'405F86E433B6EC449329AB71A100125A', X'3A169D6381EFE140893415B8C50D1CE4', X'3A169D6381EFE140893415B8C50D1CE4', 2, NULL, NULL, 2, NULL, 0, 'Transfer the sautéed tofu to a large bowl and add approximately 1/2 cup enchilada sauce and half of the cheese. Combine well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DD192A08634D947B465FBA70D28879C', X'3A169D6381EFE140893415B8C50D1CE4', X'3A169D6381EFE140893415B8C50D1CE4', 3, NULL, NULL, 2, NULL, 0, 'In the bottom of a baking pan or casserole dish, spread a thin layer (about 1/4 cup) of enchilada sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC6C53ACC4185B4585DB51C85E019940', X'3A169D6381EFE140893415B8C50D1CE4', X'3A169D6381EFE140893415B8C50D1CE4', 4, NULL, NULL, 2, NULL, 0, 'Place a few spoonfuls of the tofu mixture in each flour tortilla. Roll up and place in baking dish, stacking each enchilada closely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36F4F77323F4D6428D476F764B6F6CDB', X'3A169D6381EFE140893415B8C50D1CE4', X'3A169D6381EFE140893415B8C50D1CE4', 5, NULL, NULL, 2, NULL, 0, 'Pour the remaining enchilada sauce over the tortillas in the pan and sprinkle the rest of the cheese on top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F039D719113A7946889B44A738B038E7', X'3A169D6381EFE140893415B8C50D1CE4', X'3A169D6381EFE140893415B8C50D1CE4', 6, NULL, NULL, 2, NULL, 0, 'Bake your tofu enchiladas for 20 to 25 minutes, or until done.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F25491ED80094EB3C16F211AA62C4B', X'9D7AE00826EEDF45BB633ABA0C99088F', X'9D7AE00826EEDF45BB633ABA0C99088F', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB3C93C21E049B4FAE3844DBDA053CC1', X'9D7AE00826EEDF45BB633ABA0C99088F', X'9D7AE00826EEDF45BB633ABA0C99088F', 1, NULL, NULL, 2, NULL, 0, 'Sift the flour, baking soda and salt together in a medium-sized bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AB536E0D9105346ACA393AB5E47D3C7', X'9D7AE00826EEDF45BB633ABA0C99088F', X'9D7AE00826EEDF45BB633ABA0C99088F', 2, NULL, NULL, 2, NULL, 0, 'In a separate large bowl, beat together the 2/3 cup sugar, vegan margarine, and lemon zest until smooth and fluffy. Add the tofu and lemon juice and whip until well mixed, then add the flour, baking soda, and salt.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7710E761732FD44E84BF3829D89FBBE8', X'9D7AE00826EEDF45BB633ABA0C99088F', X'9D7AE00826EEDF45BB633ABA0C99088F', 3, NULL, NULL, 2, NULL, 0, 'Add the flour mixture to the margarine mixture and stir until well combined.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74D9A14F90B922458C7DA3198674E884', X'9D7AE00826EEDF45BB633ABA0C99088F', X'9D7AE00826EEDF45BB633ABA0C99088F', 4, NULL, NULL, 2, NULL, 0, 'Form dough into one-inch balls and roll into the 1/4 cup sugar until well coated. Flatten slightly and bake for 8-10 minutes until lightly golden brown.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90C6212600E2764FB1A3A536EE906284', X'9D7AE00826EEDF45BB633ABA0C99088F', X'9D7AE00826EEDF45BB633ABA0C99088F', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1B4771D6BB13B46AAB0B79B614C8446', X'438BD0DF777CCE4D922FC69BEEEAB8D7', X'438BD0DF777CCE4D922FC69BEEEAB8D7', 0, NULL, NULL, 2, NULL, 0, 'Drain the tofu, cut into cubes and mash. Wash and prepare the vegetables. Combine the tofu with the remainder of the ingredients and seasonings.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2ECF11D945BB164E95C85362125AA9DB', X'438BD0DF777CCE4D922FC69BEEEAB8D7', X'438BD0DF777CCE4D922FC69BEEEAB8D7', 1, NULL, NULL, 2, NULL, 0, 'Lay out one of the gyoza wrappers in front of you. Dip your finger in the water and moisten the edges of the wrapper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'608996196E3AE44FBA11BAFBA1BACFEC', X'438BD0DF777CCE4D922FC69BEEEAB8D7', X'438BD0DF777CCE4D922FC69BEEEAB8D7', 2, NULL, NULL, 2, NULL, 0, 'Place a heaping teaspoon of filling in the middle of the wrapper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C77DB0F3F66554F895A3541CFA50D3F', X'438BD0DF777CCE4D922FC69BEEEAB8D7', X'438BD0DF777CCE4D922FC69BEEEAB8D7', 3, NULL, NULL, 2, NULL, 0, 'Fold the gyoza wrapper over the filling and pinch the edges to seal it shut. (You may want to use a cornstarch/water mixture to make this easier).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'047D2B9EE2F6BF468B71B71DE94EB34A', X'438BD0DF777CCE4D922FC69BEEEAB8D7', X'438BD0DF777CCE4D922FC69BEEEAB8D7', 4, NULL, NULL, 2, NULL, 0, 'Heat 2 tablespoons oil in a large skillet or wok.When oil is ready, carefully add the dumplings and cook on high heat until golden brown (about 1 minute). Without turning the dumplings over, add 1/2 cup of water and cover. Cook for about 1 minute to cook the raw filling and then uncover and continue cooking until most of the liquid is absorbed.');
