INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C89BE0D8446C9346BFE729D65F48E206', X'AB19A6D77895A640885E0A1492D4F3A3', X'AB19A6D77895A640885E0A1492D4F3A3', 0, NULL, NULL, 2, NULL, 0, 'In a soup pot or a large wok, stir-fry marinated bulgogi and onion(s) for a couple of minutes. Put ALL the marinade into the pot, do not discard any liquid.');
