INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13E7632FD545034880ACB7710D4E453E', X'B29314BE487C2E43B9FC759F9A3AC296', X'B29314BE487C2E43B9FC759F9A3AC296', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
