INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7E1D6DFE241554D9BCDDDF5CC521B14', X'B29314BE487C2E43B9FC759F9A3AC296', X'B29314BE487C2E43B9FC759F9A3AC296', 2, NULL, NULL, 2, NULL, 0, 'Add the garlic and heat for just a minute or two, until lightly browned.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'236CF52347CD424FB47C5D4A4D7E8E8C', X'B29314BE487C2E43B9FC759F9A3AC296', X'B29314BE487C2E43B9FC759F9A3AC296', 3, NULL, NULL, 2, NULL, 0, 'Add the cubed tofu and cook until lightly golden brown on all sides, stirring occasionally as needed.');
