INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE2C68368A69484CBFC8EEBA18BE56D8', X'B29314BE487C2E43B9FC759F9A3AC296', X'B29314BE487C2E43B9FC759F9A3AC296', 5, NULL, NULL, 2, NULL, 0, 'Add the mushrooms and cook with the tofu for another minute.');
