INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'865EE6859C26EC4A952BEC862CC3FA6E', X'B29314BE487C2E43B9FC759F9A3AC296', X'B29314BE487C2E43B9FC759F9A3AC296', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle with a bit of sea salt and a dash of cayenne pepper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2EAFA5CE8B050C40AE637D43EC831175', X'B29314BE487C2E43B9FC759F9A3AC296', X'B29314BE487C2E43B9FC759F9A3AC296', 8, NULL, NULL, 2, NULL, 0, 'Allow the Swiss chard to cook down just a bit, until slightly wilted but still tender, rather than soft.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CA797468B20394790FCF10748D1D1BB', X'B29314BE487C2E43B9FC759F9A3AC296', X'B29314BE487C2E43B9FC759F9A3AC296', 9, NULL, NULL, 2, NULL, 0, 'Serve immediately over brown rice and enjoy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50A8E8F182C52F43BFD8CD6BA8C2612F', X'6CA196B7AB323B429D85B35D620F78B7', X'6CA196B7AB323B429D85B35D620F78B7', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C84F266BE192AD43A52A1FE735D860FE', X'6CA196B7AB323B429D85B35D620F78B7', X'6CA196B7AB323B429D85B35D620F78B7', 1, NULL, NULL, 2, NULL, 0, 'Prepare the manicotti according to package directions. Drain and rinse the noodles; gently pat dry.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B054B0148A95042A1B4AB15C4DD2181', X'6CA196B7AB323B429D85B35D620F78B7', X'6CA196B7AB323B429D85B35D620F78B7', 2, NULL, NULL, 2, NULL, 0, 'In a large mixing bowl, stir together the mashed tofu, garlic, soy milk, olive oil, lemon juice, sugar, salt, pepper, parsley, and spinach, if using.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'947C54E7E1C51C499F47F6167D710559', X'6CA196B7AB323B429D85B35D620F78B7', X'6CA196B7AB323B429D85B35D620F78B7', 3, NULL, NULL, 2, NULL, 0, 'Spread a 9x13-inch baking pan with 2 cups of the spaghetti sauce. Gently spoon the tofu mixture into each manicotti until they are all full. Place the filled manicotti noodles in one layer on top of the spaghetti sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFEA4AE27234424DA2EB7526C8C447A5', X'6CA196B7AB323B429D85B35D620F78B7', X'6CA196B7AB323B429D85B35D620F78B7', 4, NULL, NULL, 2, NULL, 0, 'Pour the remaining 2 cups spaghetti sauce over the stuffed noodles. Cover the pan tightly with aluminum foil and bake for 30 minutes, or until the sauce bubbles.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA536C627FCA20408FA9D15439A96B3F', X'28AE3D3FF0F8684B93D6B70D13178C27', X'28AE3D3FF0F8684B93D6B70D13178C27', 0, NULL, NULL, 2, NULL, 0, 'Combine the tofu, peanut butter, sugar, soy milk and vanilla in a food processor or blender and blend until smooth.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDC27C74B438C149A90E8B9461BB11C2', X'28AE3D3FF0F8684B93D6B70D13178C27', X'28AE3D3FF0F8684B93D6B70D13178C27', 1, NULL, NULL, 2, NULL, 0, 'Spoon into the pie shell. Refrigerate at least 2 hours and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE6E508C41F6144DAEE05333980B53D3', X'E072DC03E160FD48A487A6EED4AB42BF', X'E072DC03E160FD48A487A6EED4AB42BF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9FFC230CF2FB64CA536079BCA795AD4', X'E072DC03E160FD48A487A6EED4AB42BF', X'E072DC03E160FD48A487A6EED4AB42BF', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, mash the tofu with a fork or crumble with your hands.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85E22B095E59214A8531E5EBDDD5F76E', X'E072DC03E160FD48A487A6EED4AB42BF', X'E072DC03E160FD48A487A6EED4AB42BF', 2, NULL, NULL, 2, NULL, 0, 'Mix in the remaining ingredients until well combined.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B61AD723DDE334D97C1866CD6454864', X'AFC3430DD79DA7439D1A637FE50ABD05', X'AFC3430DD79DA7439D1A637FE50ABD05', 0, NULL, NULL, 2, NULL, 0, 'If using beef, then saute beef until slightly browned in a soup pot. If using shrimp, then skip this step and combine shrimp with vegetables.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0F4C8FAEA408D4B820D4B78FFA2315B', X'AFC3430DD79DA7439D1A637FE50ABD05', X'AFC3430DD79DA7439D1A637FE50ABD05', 1, NULL, NULL, 2, NULL, 0, 'Add stock, onion, garlic, zucchini, potato, and pepper and bring to a boil over high heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5C64D4A0762154D9CDB6F33C81AA04B', X'AFC3430DD79DA7439D1A637FE50ABD05', X'AFC3430DD79DA7439D1A637FE50ABD05', 2, NULL, NULL, 2, NULL, 0, 'Add soybean paste, chili pepper paste, pepper flakes (if using), and tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD8AE20C8E24114B8C244036B4A267C6', X'AFC3430DD79DA7439D1A637FE50ABD05', X'AFC3430DD79DA7439D1A637FE50ABD05', 3, NULL, NULL, 2, NULL, 0, 'When the stew begins boiling again, turn off heat and add chopped scallions.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B43AD7DAAB39045B8641CEB2D790B63', X'0798E83F6A56564091B75BDB820C056B', X'0798E83F6A56564091B75BDB820C056B', 0, NULL, NULL, 2, NULL, 0, 'Grill the asparagus. Heat the grill to high heat. Brush the asparagus spears with olive oil. Lightly salt and pepper, and grill for about 5 minutes, or until just lightly browned and still crisp. Remove from the grill, cut into pieces and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'451F9F795AA5B345B97BAC5C27B31315', X'0798E83F6A56564091B75BDB820C056B', X'0798E83F6A56564091B75BDB820C056B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Lightly oil a 9` pie plate and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE2AC3608B8B114CBB4D76C8F44A05E7', X'0798E83F6A56564091B75BDB820C056B', X'0798E83F6A56564091B75BDB820C056B', 2, NULL, NULL, 2, NULL, 0, 'Prepare the quiche. Sprinkle about 1/2 of the crumbled dairy-free cheese in the bottom of the pie plate. Spread the asparagus on top of the crumbled dairy-free cheese. Set aside. In a blender, process the tofu, soy milk, nutritional yeast, ground cashews, cornstarch, dairy-free cream cheese, salt, freshly ground pepper, garlic powder, onion powder, and turmeric until smooth. Stir in the remaining crumble dairy-free cheese using a wooden spoon. Pour the mixture into the prepared pie plate.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABBFB9D680FBDF4DAC7663F1B6A2298F', X'0798E83F6A56564091B75BDB820C056B', X'0798E83F6A56564091B75BDB820C056B', 3, NULL, NULL, 2, NULL, 0, 'Bake for 35 to 40 minutes until golden brown, or until a toothpick inserted into the center emerges clean. Serve warm or cold.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5D290C2F059A749ACFCCACF04FCCFF2', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F5BBF45C44FC8469DD1F935F78C49CE', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', 1, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, pulse together all ingredients except pecans until apples are minced and ingredients are blended. Alternatively, you can finely chop the apples by hand and mix together the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37E5C08DAA2495459A4D4B33A5820D98', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', 2, NULL, NULL, 2, NULL, 0, 'Gently fold in the pecans.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05B84355A10F2D47B354722BEDBB648D', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', 3, NULL, NULL, 2, NULL, 0, 'Drop by large spoonfuls onto a lightly oiled skillet or griddle and cook for a few minutes, until bubbles appear. Flip and cook until both sides are lightly golden brown.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E07E0511B1A454C90932A948260CDC2', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', X'D4AC2430AFABDF4ABEAE6E5EEC2FFEF5', 4, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'742F130A6185E34985B8EBEF89CF7C8A', X'D9865ACB46096C4D8C45F38C419281AF', X'D9865ACB46096C4D8C45F38C419281AF', 0, NULL, NULL, 2, NULL, 0, 'Make sure your tofu is well pressed before beginning. Not sure what that means or how to do that? See this simple step-by-step char(36)e for how to press tofu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECD6122356AF2947BB024CBC2103D53A', X'D9865ACB46096C4D8C45F38C419281AF', X'D9865ACB46096C4D8C45F38C419281AF', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together all the ingredients except for the tofu until smooth and creamy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DB610135B1BBE4FB0443476E577AFCD', X'D9865ACB46096C4D8C45F38C419281AF', X'D9865ACB46096C4D8C45F38C419281AF', 2, NULL, NULL, 2, NULL, 0, 'Add the tofu, and coat well with the sauce. Allow marinating in the refrigerator for at least 20 minutes.?');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D08834060D64B409BFC0CC8DE193F55', X'D9865ACB46096C4D8C45F38C419281AF', X'D9865ACB46096C4D8C45F38C419281AF', 3, NULL, NULL, 2, NULL, 0, 'Heat a bit of oil in a saute or frying pan. Add tofu, coating with any extra sauce, to make sure all the sauce is used, and fry for 3 to 4 minutes on each side until golden.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'931004FEB96B794F9663DF147447816A', X'D9865ACB46096C4D8C45F38C419281AF', X'D9865ACB46096C4D8C45F38C419281AF', 4, NULL, NULL, 2, NULL, 0, 'Use your tofu paneer `cheese` as a substitute in any Indian recipe that calls for paneer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'295552122FF00747BB8E32BD6A17AE48', X'FE8A0D1538189C429254BCA7B3B0A9C9', X'FE8A0D1538189C429254BCA7B3B0A9C9', 0, NULL, NULL, 2, NULL, 0, 'Put the mushrooms in a small bowl or measuring cup and pour 1/2 cup boiling water over them. Let soak 15 minutes. Lift mushrooms out of the water, rubbing them clean of any grit as you do. Roughly chop the mushrooms and set aside. Reserve the soaking liquid.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D4550D8D66E41498AC3384077F7109F', X'FE8A0D1538189C429254BCA7B3B0A9C9', X'FE8A0D1538189C429254BCA7B3B0A9C9', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, bring the broth to a simmer. Peel and thinly slice the daikon and add to the broth. Trim and thinly slice the celery and add to broth. Cook until the vegetables are tender, about 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5EA71737720FF4BB12FD4D8C365BB2D', X'FE8A0D1538189C429254BCA7B3B0A9C9', X'FE8A0D1538189C429254BCA7B3B0A9C9', 2, NULL, NULL, 2, NULL, 0, 'Cut tofu into small cubes or thin slices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'794B8A06940ED040BCB46FB29EABB82E', X'FE8A0D1538189C429254BCA7B3B0A9C9', X'FE8A0D1538189C429254BCA7B3B0A9C9', 3, NULL, NULL, 2, NULL, 0, 'Add the tofu and the reserved mushrooms to the broth. Add reserved soaking liquid, being careful to leave any grit behind. Cook, simmering until everything is heated through, 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'451B33EFEC36904BA8ED64097541A7DA', X'FE8A0D1538189C429254BCA7B3B0A9C9', X'FE8A0D1538189C429254BCA7B3B0A9C9', 4, NULL, NULL, 2, NULL, 0, 'Divide between 4 bowls and garnish with cilantro, if you like. Serve immediately.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D2B3B03F98AA545A5E75DBAFB2D91FD', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A385B4FA7C054D459693510D6719F497', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 1, NULL, NULL, 2, NULL, 0, 'Coat a large frying pan or wok with oil and sauté pork over medium-high heat until almost cooked through. Drain oil if necessary.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F3E4D29B47AEE42B5CE93B8FA4481A9', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 2, NULL, NULL, 2, NULL, 0, 'Add garlic and sauté for a couple of more minutes, mixing to combine.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1222DF100A94144880A03810E4A5012D', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 3, NULL, NULL, 2, NULL, 0, 'Add kimchi and fry for about 5 to 7 minutes, until it becomes a darker reddish brown.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F1F458F8AF07D4FBB1902079A0469C5', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 4, NULL, NULL, 2, NULL, 0, 'Add sugar, soy sauce, sesame seeds, and sesame oil and fry for another minute.');
