INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'588F514DECB3B6479D94B2E067F8615C', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 6, NULL, NULL, 2, NULL, 0, 'In a pot of gently boiling water, boil entire block of tofu for 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D90A350B59F1E34D8569ACC6926D8FDC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 7, NULL, NULL, 2, NULL, 0, 'Drain tofu and slice into rectangles.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66860C15FB7EFE488FAEF668BCF08FC7', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', X'8F7A4CC7D73BBA4A865E66D2279FA4EC', 8, NULL, NULL, 2, NULL, 0, 'Serve topped with sautéed kimchi (kimchi bokum) or place kimchi in the center of the plate and arrange tofu around it.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5CD7D1A6EBA964A80B400D96BC00AC4', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
