INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F316E64DB90F54684C2242F8CA4E672', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', 2, NULL, NULL, 2, NULL, 0, 'Add kimchi to the pot and stir-fry for about 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'758D28AC4A61284BA1092159958EE42B', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', 3, NULL, NULL, 2, NULL, 0, 'Add remaining oil, onion, garlic, gochujang, gochugaru, and soy sauce, mixing to combine. Pour the water into the pot and bring to a boil. Reduce heat to a simmer.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FCD66AD6FC2B8A49A7FF6B168E9ECFA5', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', 4, NULL, NULL, 2, NULL, 0, 'Cook for 20 to 30 minutes to concentrate the flavors, adding the tofu after the first 10 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A361C9E16DCA94F94697DAE0F5BF9CD', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', 5, NULL, NULL, 2, NULL, 0, 'Add the scallions at the very end.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AC941680F6F904A8521C303E3188F6C', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', X'5892F5B3CCE4CA4ABEA9AE03CC5103EA', 6, NULL, NULL, 2, NULL, 0, 'Serve this stew immediately after cooking, accompanied by steamed white rice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'965D03CA2ED2C546A268F0EC5C1EA2C0', X'D261BE213116D248818BD8EA770ED3C7', X'D261BE213116D248818BD8EA770ED3C7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6609B2D4E4CF1F438D6108EBA8F74FF4', X'D261BE213116D248818BD8EA770ED3C7', X'D261BE213116D248818BD8EA770ED3C7', 1, NULL, NULL, 2, NULL, 0, 'First, prepare your tofu. Like most vegan tofu recipes, this one will taste best if you press the tofu first.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DBBF48778EE2245AD19236868DCC01B', X'D261BE213116D248818BD8EA770ED3C7', X'D261BE213116D248818BD8EA770ED3C7', 2, NULL, NULL, 2, NULL, 0, 'Slice the tofu into desired `finger` shapes, about 1/2-inch wide and 1/2-inch thick. Arrange tofu pieces in a wide, shallow container');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'557A48E01868094A9D8864D45823D83A', X'D261BE213116D248818BD8EA770ED3C7', X'D261BE213116D248818BD8EA770ED3C7', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, whisk together the soy sauce, teriyaki sauce, and liquid sweetener. Pour this mixture over the tofu slices, and allow to marinate for at least one hour, and up to overnight. The longer the better! Turn the tofu occasionally to ensure all sides are well coated.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F60617100AE7A40BC1B7E335FF74B22', X'D261BE213116D248818BD8EA770ED3C7', X'D261BE213116D248818BD8EA770ED3C7', 4, NULL, NULL, 2, NULL, 0, 'In a shallow dish, place the breadcrumbs. Remove tofu from marinade and bread the marinated tofu slices.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE4006293B57CF4CB14B02486C8E5959', X'D261BE213116D248818BD8EA770ED3C7', X'D261BE213116D248818BD8EA770ED3C7', 5, NULL, NULL, 2, NULL, 0, 'Fry the breaded tofu slices in oil over medium heat until golden brown on all sides. Gently remove from oil and allow excess oil to drain on paper towels.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F3D583004273541B1DE9A51CEB3B502', X'D261BE213116D248818BD8EA770ED3C7', X'D261BE213116D248818BD8EA770ED3C7', 6, NULL, NULL, 2, NULL, 0, 'Serve your fried tofu fingers with a dipping sauce, such as ketchup, barbecue sauce, hot mustard or an Asian plum sauce. Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'376CE1B61B273C4FA3F50E214B92231D', X'47994EC1BD91354D8B590265059632D7', X'47994EC1BD91354D8B590265059632D7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'03FB12808C623C4EB6C59B70BC497281', X'47994EC1BD91354D8B590265059632D7', X'47994EC1BD91354D8B590265059632D7', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, mix tofu and oil together until smooth. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A77A4A1D1DC3A4DA143ED6EBED6602C', X'47994EC1BD91354D8B590265059632D7', X'47994EC1BD91354D8B590265059632D7', 2, NULL, NULL, 2, NULL, 0, 'Combine remaining ingredients (except vegetable broth) in a mixing bowl. On low speed, blend in the tofu mixture for 1 minute. Scrape sides and mix another 3 minutes at medium speed. Let it sit for 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'255D39AD4BA00F45B6988C2144C2AA52', X'47994EC1BD91354D8B590265059632D7', X'47994EC1BD91354D8B590265059632D7', 3, NULL, NULL, 2, NULL, 0, 'In a saucepan over medium or medium-low heat, place matzah ball mixture in broth by the tablespoonfuls. Let the broth boil for 35 minutes, covered.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35FE8A036385A24DA82FD4288F618436', X'1B74D7EBBB79D14F8487244F6D94E776', X'1B74D7EBBB79D14F8487244F6D94E776', 0, NULL, NULL, 2, NULL, 0, 'Loosen the precooked noodles by soaking in a bowl of hot water for 2 to 3 minutes, then drain and set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'277336A0A4BF164D8588C69BCF08C10B', X'1B74D7EBBB79D14F8487244F6D94E776', X'1B74D7EBBB79D14F8487244F6D94E776', 1, NULL, NULL, 2, NULL, 0, 'Mix together the soy sauce, oyster sauce, sesame oil, and water in a bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'889ECBF01327604E87D4D71E8A601802', X'1B74D7EBBB79D14F8487244F6D94E776', X'1B74D7EBBB79D14F8487244F6D94E776', 2, NULL, NULL, 2, NULL, 0, 'Heat a wok or large frying pan over high heat, add the oil and stir-fry the carrot and broccoli for 2 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70C8F66F3391F34D94368A706B3D1849', X'1B74D7EBBB79D14F8487244F6D94E776', X'1B74D7EBBB79D14F8487244F6D94E776', 3, NULL, NULL, 2, NULL, 0, 'Add the tofu and stir-fry for 4 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43502D65E66474E86C31C394188DEB9', X'1B74D7EBBB79D14F8487244F6D94E776', X'1B74D7EBBB79D14F8487244F6D94E776', 4, NULL, NULL, 2, NULL, 0, 'Add the garlic and ginger and stir-fry for a minute.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CED4B4631C4EA42A8192BFF42CAE605', X'1B74D7EBBB79D14F8487244F6D94E776', X'1B74D7EBBB79D14F8487244F6D94E776', 5, NULL, NULL, 2, NULL, 0, 'Stir in the noodles and then add the sauce mixture and cashews and mix until thoroughly combined and heated through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99E9B5B95B86C846AA1260C6760B3009', X'21363776B9EB954FA2A15FA436119338', X'21363776B9EB954FA2A15FA436119338', 0, NULL, NULL, 2, NULL, 0, 'Combine the tofu, vanilla, and sugar in a blender or a food processor and process until smooth.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94742FD9376AA8469D20A9EC7169BA66', X'21363776B9EB954FA2A15FA436119338', X'21363776B9EB954FA2A15FA436119338', 1, NULL, NULL, 2, NULL, 0, 'With the machine running, gradually add the soy milk through the feeder cap.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD24CB199E0142459CB1562D5EC1B15A', X'21363776B9EB954FA2A15FA436119338', X'21363776B9EB954FA2A15FA436119338', 2, NULL, NULL, 2, NULL, 0, 'Refrigerate until serving time. It will keep for a week, but it can also be frozen.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E6D562021DF6B49B9DE6A1F418834C8', X'2CDF72BD814D8E48BEDF647589955AF0', X'2CDF72BD814D8E48BEDF647589955AF0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95ACD3E36A64BE429E47CE2A83753599', X'2CDF72BD814D8E48BEDF647589955AF0', X'2CDF72BD814D8E48BEDF647589955AF0', 1, NULL, NULL, 2, NULL, 0, 'Combine all ingredients using a blender or food processor. If mixing by hand, use a fork or whisk and beat until smooth.Instead of using a small bowl and a whisk, you can also place everything together in a mason jar, cover, and then shake it all up until fully emulsified.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F9259F5D7E084D4F8325512081AFAED5', X'2CDF72BD814D8E48BEDF647589955AF0', X'2CDF72BD814D8E48BEDF647589955AF0', 2, NULL, NULL, 2, NULL, 0, 'Add a bit of water, a teaspoon at a time, if desired, for a thinner consistency.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5B198F517FC8C4D814D91289256AD6B', X'2CDF72BD814D8E48BEDF647589955AF0', X'2CDF72BD814D8E48BEDF647589955AF0', 3, NULL, NULL, 2, NULL, 0, 'Serve as the dressing to a salad or use as a dipping sauce and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDF779F8A409B247A510B7C21FFC6E0F', X'D23F9C73E528A84D8C8EDF549F69F2A2', X'D23F9C73E528A84D8C8EDF549F69F2A2', 0, NULL, NULL, 2, NULL, 0, 'Make the miso sauce (marinade). In a small bowl, combine miso, sugar, honey, minced garlic and ginger juice (or finely minced ginger), and sesame oil. Mix well. Add salt and pepper to taste (optional). If you''d like to add green onion (or `nira` garlic chives), chop finely, then add to the marinade and mix well. If you like, reserve some of the chopped green onion for garnish. Set the miso sauce aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDF786987BFE54429C30DC447111B3BA', X'D23F9C73E528A84D8C8EDF549F69F2A2', X'D23F9C73E528A84D8C8EDF549F69F2A2', 1, NULL, NULL, 2, NULL, 0, 'If you are using thicker pork loin cuts instead of the medium thin (or sukiyaki cut) sliced pork, be sure to cut the thicker loins in half with a horizontal cut to make them slightly thinner.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F31CE2C1CA33747909C6A41DAF07E0B', X'D23F9C73E528A84D8C8EDF549F69F2A2', X'D23F9C73E528A84D8C8EDF549F69F2A2', 2, NULL, NULL, 2, NULL, 0, 'Generously brush each slice of pork with the miso sauce, and then stack the slices one on top of the other in a deep dish or bowl to marinate.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'320FDFFA6BD1F141A8C02B9E93A84C1A', X'D23F9C73E528A84D8C8EDF549F69F2A2', X'D23F9C73E528A84D8C8EDF549F69F2A2', 3, NULL, NULL, 2, NULL, 0, 'Marinate the pork for 20 to 30 minutes. If you don''t have time to marinate the pork, the pork may be cooked with the sauce and still have good flavor.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC31AB53E367944083254E2D4715BAA0', X'D23F9C73E528A84D8C8EDF549F69F2A2', X'D23F9C73E528A84D8C8EDF549F69F2A2', 4, NULL, NULL, 2, NULL, 0, 'In a non-stick pan, heat pork until done. Or, coat pan with olive oil if needed and then saute and until pork is cooked through and the sauce has browned slightly.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94CA132A1CDFFF4D9B74FA442B573FCD', X'D23F9C73E528A84D8C8EDF549F69F2A2', X'D23F9C73E528A84D8C8EDF549F69F2A2', 5, NULL, NULL, 2, NULL, 0, 'Serve the miso pork with a side of green leafy lettuce or Japanese shungiku and wrap the pork with the greens and then enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A56BAC07295F374E81EFF3CF7226E726', X'51932D038D54F34E8B41E91F3EAEFA42', X'51932D038D54F34E8B41E91F3EAEFA42', 0, NULL, NULL, 2, NULL, 0, 'Put dashi soup stock in a pan and bring to a boil.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'924A974BFB81B64AB344D339F97308D5', X'51932D038D54F34E8B41E91F3EAEFA42', X'51932D038D54F34E8B41E91F3EAEFA42', 1, NULL, NULL, 2, NULL, 0, 'Cut tofu into small cubes and add them to the soup.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0982D2742A819848BE040B3CA702EB2A', X'51932D038D54F34E8B41E91F3EAEFA42', X'51932D038D54F34E8B41E91F3EAEFA42', 2, NULL, NULL, 2, NULL, 0, 'Simmer the tofu for a few minutes on low heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6711ADC18385C44A47165BA052B3331', X'51932D038D54F34E8B41E91F3EAEFA42', X'51932D038D54F34E8B41E91F3EAEFA42', 3, NULL, NULL, 2, NULL, 0, 'Scoop out some soup stock from the pan and dissolve miso in it.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C61B3C5E91DA1489342B0D6FB0B2D96', X'51932D038D54F34E8B41E91F3EAEFA42', X'51932D038D54F34E8B41E91F3EAEFA42', 4, NULL, NULL, 2, NULL, 0, 'Gradually return the miso mixture in the soup. Stir the soup gently. Stop the heat and add chopped green onion.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00093A02E6C38B4997819D4266DE4588', X'51932D038D54F34E8B41E91F3EAEFA42', X'51932D038D54F34E8B41E91F3EAEFA42', 5, NULL, NULL, 2, NULL, 0, 'Try not to boil the soup after you put miso in.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01305AB344DE4F40A04C8AAAFF969579', X'AFF6F9E4166BE34C9E4995D764D37362', X'AFF6F9E4166BE34C9E4995D764D37362', 0, NULL, NULL, 2, NULL, 0, 'Soak the raisins in the water for up to an hour or until they are very mushy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAD1D6A6B9A7CB42BDD0A48786CB5263', X'AFF6F9E4166BE34C9E4995D764D37362', X'AFF6F9E4166BE34C9E4995D764D37362', 1, NULL, NULL, 2, NULL, 0, 'Blend the raisins and the raisin soak water together with the remaining ingredients until smooth and saucy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A970BAB99D7574F98D89B6834F4AA63', X'AFF6F9E4166BE34C9E4995D764D37362', X'AFF6F9E4166BE34C9E4995D764D37362', 2, NULL, NULL, 2, NULL, 0, 'Serve immediately or store in an airtight glass container in the refrigerator for up to a week.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E12CA96A9390840B2B128F48C33D8E7', X'658CC7C3955253489D40ED9C7CCF62E0', X'658CC7C3955253489D40ED9C7CCF62E0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96D10548BE82234C9C5374BE9F2F89BA', X'658CC7C3955253489D40ED9C7CCF62E0', X'658CC7C3955253489D40ED9C7CCF62E0', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan or stock pot, bring the dashi or stock to a simmer over medium-high heat but do not boil. Combine the white and red miso in a small dish, then add 1 1/2 cups of the hot dashi and whisk to combine. Set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7968BCF3F7101449AFEB0C1387091156', X'658CC7C3955253489D40ED9C7CCF62E0', X'658CC7C3955253489D40ED9C7CCF62E0', 2, NULL, NULL, 2, NULL, 0, 'Simmer the dashi for 5 minutes more. Gently stir in the tofu and simmer for another 5 minutes. Again - do not boil! Add the baby bok choy, scallions, and miso mixture, and cook for another 5 minutes, or until the bok choy is slightly wilted but still bright green.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DEAEB261B527D143835ECDAFB088E460', X'658CC7C3955253489D40ED9C7CCF62E0', X'658CC7C3955253489D40ED9C7CCF62E0', 3, NULL, NULL, 2, NULL, 0, 'Serve immediately while hot, taking care to stir before serving as the miso will settle.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12E8E0730B383D44AAFA13003944C54C', X'658CC7C3955253489D40ED9C7CCF62E0', X'658CC7C3955253489D40ED9C7CCF62E0', 4, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D76DDD52E7010540A989E62796EBDF94', X'53EDFF7218EE974DB09B06D41E6DDC99', X'53EDFF7218EE974DB09B06D41E6DDC99', 0, NULL, NULL, 2, NULL, 0, 'In a large pot, saute the tomatoes, leek, and mushroom for 1 to 3 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75D71B36FE2AE84EB0BB9934FC8472C6', X'53EDFF7218EE974DB09B06D41E6DDC99', X'53EDFF7218EE974DB09B06D41E6DDC99', 1, NULL, NULL, 2, NULL, 0, 'Add the vegetable broth and seaweed and bring to a slow simmer. Add the tofu, soy sauce (or the gluten-free tamari or Nama Shoyu if you''re doing the gluten-free version), and the miso and scallions and reduce the heat to low.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DCE5B23039AED344AECD0724D78DA0BE', X'53EDFF7218EE974DB09B06D41E6DDC99', X'53EDFF7218EE974DB09B06D41E6DDC99', 2, NULL, NULL, 2, NULL, 0, 'Stir well to dissolve and mix the miso.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B5F168068A41154FAE7C100F1A9B417A', X'53EDFF7218EE974DB09B06D41E6DDC99', X'53EDFF7218EE974DB09B06D41E6DDC99', 3, NULL, NULL, 2, NULL, 0, 'Allow to cook for at least 8 more minutes over low heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D4ED4EA584F47468AAEE4655E5282C7', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81FEE4A4D15D9A47BED63BD629B721A3', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 Fand grease a 9 x 9-inch pan with cooking spray or softened butter. Line the bottom with a piece of parchment paper.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2580EC0C6EE3894EB08EF5D48051A9CF', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 2, NULL, NULL, 2, NULL, 0, 'Place the butter and sugars in a large mixing bowl and whisk until fully incorporated.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF40568A7154264BB9AA104C50F2A61F', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 3, NULL, NULL, 2, NULL, 0, 'Add the eggs and yolk one at a time, whisking gently after each addition. Add the vanilla and gently whisk again.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8467FFD97E6C2D45BE45920F3DC79DCE', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 4, NULL, NULL, 2, NULL, 0, 'Add the miso and whisk until smooth.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E44810CE03DEB44BA6E7366E726BA0CF', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 5, NULL, NULL, 2, NULL, 0, 'Whisk the flour, baking powder and salt together in a medium sized bowl. Add the chunks to the bowl and whisk again.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'784A6A6B5CC3DF48B8F088F663D2161A', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 6, NULL, NULL, 2, NULL, 0, 'Add the dry ingredients to the wet and using a rubber spatula, very gently fold the two together. Do not over-mix.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39B46B7DFD3E484D91A39A54DDB91DB7', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 7, NULL, NULL, 2, NULL, 0, 'Transfer the batter to the prepared pan and use a flexible spatula to smooth the top.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FB86968F5D5164F9D31E4D5E44C3485', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 8, NULL, NULL, 2, NULL, 0, 'Sprinkle the Maldon sea salt (if using) over the top of the blondies.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'158CCDC1998B11499BDB07187C172323', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 9, NULL, NULL, 2, NULL, 0, 'Bake for about 35 to 38 minutes, until the top is browned and a toothpick comes out with a moist crumb or two.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C9CBDF9D17ACE4C80FEDEAC4EEB2560', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', X'E68F6ACAF3C2E54998E32F1B0F3ADB3E', 10, NULL, NULL, 2, NULL, 0, 'Let the blondies cool on a rack until room temp and slice and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D61DD63412E4D245ACDB58B5CEAC4E2B', X'5B4CBF23F9493645A598AC78E3EBF2AF', X'5B4CBF23F9493645A598AC78E3EBF2AF', 0, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium-low heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FE5231F44A954489C292447B2FEFFBF', X'5B4CBF23F9493645A598AC78E3EBF2AF', X'5B4CBF23F9493645A598AC78E3EBF2AF', 1, NULL, NULL, 2, NULL, 0, 'Combine marinade ingredients in a small bowl.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C44F47D0668C924693E159E5B37CF8F1', X'5B4CBF23F9493645A598AC78E3EBF2AF', X'5B4CBF23F9493645A598AC78E3EBF2AF', 2, NULL, NULL, 2, NULL, 0, 'Place mixture on the flesh of salmon fillet and let sit for 5 or so minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F57535D9921A284AA134E1F00DA1FE9C', X'5B4CBF23F9493645A598AC78E3EBF2AF', X'5B4CBF23F9493645A598AC78E3EBF2AF', 3, NULL, NULL, 2, NULL, 0, 'Place salmon on a well-oiled grill grate, skin-side down, and cook for about 12 to 14 minutes (or until internal temperature at thickest part of fillet reads 145 F).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58A5B97D669EA44480E2D54DDA585480', X'5B4CBF23F9493645A598AC78E3EBF2AF', X'5B4CBF23F9493645A598AC78E3EBF2AF', 4, NULL, NULL, 2, NULL, 0, 'Once cooked through, remove from heat and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E18B44071C13E84EAA148E8434D35800', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C2388F036368F499B2780D6F6827896', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 1, NULL, NULL, 2, NULL, 0, 'Heat canola oil in a large deep skillet or a wok over medium-high heat. Add minced ginger, garlic, and ground pork to the skillet and saute on medium heat until the pork is done.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4587388831AC5449BC391C5E78AC0F3A', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 2, NULL, NULL, 2, NULL, 0, 'To the skillet with the meat, add carrot, bean sprouts, and cabbage and saute together for a few minutes until vegetables are tender.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2085AA0354663D46B9E8C36DC68154DA', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 3, NULL, NULL, 2, NULL, 0, 'Pour warm water into the skillet. Season with chicken bouillon powder or chicken base, sugar, and soy sauce and bring the soup to a boil. Turn down the heat to low, and melt miso in the soup. Add sesame oil last, and then turn off the heat.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F679EDC95E117409476D1A06054D037', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 4, NULL, NULL, 2, NULL, 0, 'In the meantime, prepare the noodles. Boil water in a large pot. Put chukamen noodles into the boiling water and cook for a few minutes, until the noodles are al dente, or reach your desired firmness. Drain the noodles well and serve in two deep soup noodle bowls.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C905633CD7E01E408DEF1A97282F2716', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 5, NULL, NULL, 2, NULL, 0, 'Pour the hot miso soup mixture with pork and vegetables over the noodles.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'433C98F7AC05C4489E7A1E06375D6D0B', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 6, NULL, NULL, 2, NULL, 0, 'Add optional garnishes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94DD81E7835CAD48913F790E32162762', X'22BBEB72A1B72C4287BB5E27769715DD', X'22BBEB72A1B72C4287BB5E27769715DD', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A69158229B7C946ADB0F5CB00876A92', X'96B9A21BA06DC74EA5CAB87C7F18E517', X'96B9A21BA06DC74EA5CAB87C7F18E517', 0, NULL, NULL, 2, NULL, 0, 'First, in a skillet or saucepan, sautee the minced garlic, the minced onions and the sliced mushrooms in vegan margarine until soft, and then set aside.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4B43CDD7E844E4E859C99D2F3907A15', X'96B9A21BA06DC74EA5CAB87C7F18E517', X'96B9A21BA06DC74EA5CAB87C7F18E517', 1, NULL, NULL, 2, NULL, 0, 'In separate medium-sized saucepan, combine the miso, soy milk and vegetarian bouillon cube over medium to low heat. Stir these ingredients together until the bouillon cube has fully dissolved and ingredients are well combined.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'533882B41CBF544FA4DA42D852DCEEDE', X'96B9A21BA06DC74EA5CAB87C7F18E517', X'96B9A21BA06DC74EA5CAB87C7F18E517', 2, NULL, NULL, 2, NULL, 0, 'Next, add in the onion powder, flour, celery salt, soy sauce, and the nutritional yeast, and bring to a slow slimmer, stirring to combine well. If your mixture does not thicken, add more flour, mix well and turn up the heat a bit. Allow to cool.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FADC55E05156F4EB8CFAD1DEF7CB26A', X'96B9A21BA06DC74EA5CAB87C7F18E517', X'96B9A21BA06DC74EA5CAB87C7F18E517', 3, NULL, NULL, 2, NULL, 0, 'Next, add the sauteed mushrooms, garlic, and onion mix to the soy milk mix and process in a blender or food processor until smooth or until the desired texture is reached.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66F0D9568377A3478FA933DD25CF26B5', X'96B9A21BA06DC74EA5CAB87C7F18E517', X'96B9A21BA06DC74EA5CAB87C7F18E517', 4, NULL, NULL, 2, NULL, 0, 'Reheat the blended mix, stirring occasionally, and allow to cool slightly before transferring to a fondue pot for serving.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5E077A11620E35458200D82F21E597D2', X'8508398AAF51724EAB55CB408D14D2D9', X'8508398AAF51724EAB55CB408D14D2D9', 0, NULL, NULL, 2, NULL, 0, 'Make a thick (less watery) batch of cashew milk for this recipe, closer to a raw cashew cream than raw cashew milk.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'143EB1863782A54F8AE8C3568735B26D', X'8508398AAF51724EAB55CB408D14D2D9', X'8508398AAF51724EAB55CB408D14D2D9', 1, NULL, NULL, 2, NULL, 0, 'Process together the cashew cream or thick cashew milk in a food processor or blender along with the fresh lemon juice, miso, garlic, chopped onion, olive oil, and nutritional yeast until thick and creamy. This should take about one full minute on medium or high speed (be patient!).');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A36C827AF9A7148A4959DB150DC1678', X'8508398AAF51724EAB55CB408D14D2D9', X'8508398AAF51724EAB55CB408D14D2D9', 2, NULL, NULL, 2, NULL, 0, 'That''s it! Taste and adjust the flavors to your personal liking. You can add a bit of salt and fresh ground pepper if you''d like, or if it''s not salty enough for you, add a bit more miso or even a tiny bit of tamari or Nama Shoyu.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AC20B010080444792864038E040C0F9', X'8508398AAF51724EAB55CB408D14D2D9', X'8508398AAF51724EAB55CB408D14D2D9', 3, NULL, NULL, 2, NULL, 0, 'Serve and enjoy over salad, steamed veggies, zoodles, or your raw food meal of choice.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3A337266E519C428C7863558A344289', X'212DB6AD46630841A76234D775C02B18', X'212DB6AD46630841A76234D775C02B18', 0, NULL, NULL, 2, NULL, 0, 'Sauté the onion and garlic in olive oil until soft, about 4 to 5 minutes.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED35BC9CCD969249ACBF56DCCD90CB50', X'212DB6AD46630841A76234D775C02B18', X'212DB6AD46630841A76234D775C02B18', 1, NULL, NULL, 2, NULL, 0, 'Add spinach and salt, stirring to combine well and cook just until spinach is heated through.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A17650691E69543B66BB33A4F8519CA', X'212DB6AD46630841A76234D775C02B18', X'212DB6AD46630841A76234D775C02B18', 2, NULL, NULL, 2, NULL, 0, 'Add crumbled tofu and mix well. Allow to cool completely.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A70F0BDEF0077B4882322D7AC7CD6257', X'212DB6AD46630841A76234D775C02B18', X'212DB6AD46630841A76234D775C02B18', 3, NULL, NULL, 2, NULL, 0, 'In a small saucepan over low heat, combine the cashew or almond butter, soy milk, miso, soy sauce, lemon juice, nutritional yeast, and onion powder until smooth and creamy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2183B18D2DA734885BE6F5D4E3CAC75', X'212DB6AD46630841A76234D775C02B18', X'212DB6AD46630841A76234D775C02B18', 4, NULL, NULL, 2, NULL, 0, 'Prepare the lasagna noodles according to package instructions, if needed, and preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3E070761F245B47A5B10F7146F66DFA', X'212DB6AD46630841A76234D775C02B18', X'212DB6AD46630841A76234D775C02B18', 5, NULL, NULL, 2, NULL, 0, 'In a lightly greased lasagna pan, place a thin layer of the white cashew sauce and then a layer of noodles. Next, add spinach and more cashew white sauce, then a layer of noodles, continuing until all ingredients are used up. The top layer should be spinach and then sauce.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9327C0F43BE73941BAC0A807FE8FA5CD', X'212DB6AD46630841A76234D775C02B18', X'212DB6AD46630841A76234D775C02B18', 6, NULL, NULL, 2, NULL, 0, 'Bake for 40 minutes, or until done. Allow to cool for at least 10 minutes before serving, to allow lasagna to set.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DA1FBC17C233D4E9F706ACD109B156A', X'3E2F29185488444694BE6E1478FE60AE', X'3E2F29185488444694BE6E1478FE60AE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'128F1090805DA447A769AB18167691F5', X'3E2F29185488444694BE6E1478FE60AE', X'3E2F29185488444694BE6E1478FE60AE', 1, NULL, NULL, 2, NULL, 0, 'Heat water just to a low simmer, then reduce the heat so the water is hot but not actually simmering or boiling and then stir in the miso. You want to be careful that the water doesn''t boil when you add in the miso, but rather it should be just below the boiling point.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'468214CC255B17478D354A7C8C930C08', X'3E2F29185488444694BE6E1478FE60AE', X'3E2F29185488444694BE6E1478FE60AE', 2, NULL, NULL, 2, NULL, 0, 'Stir well to fully dissolve the miso and make sure there are no clumps, then, stir in the remaining ingredients and allow to heat for a couple minutes, just to gently soften the garlic and mushrooms.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95D411AAD96B4B428181E9F4BBB63038', X'3E2F29185488444694BE6E1478FE60AE', X'3E2F29185488444694BE6E1478FE60AE', 3, NULL, NULL, 2, NULL, 0, 'Stir well, and serve.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3E49A1F7D88334E94AB604267805BD9', X'33A352B4F4B04C439775EAA33141FE12', X'33A352B4F4B04C439775EAA33141FE12', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C7222341A26F8488553F58F330F3828', X'33A352B4F4B04C439775EAA33141FE12', X'33A352B4F4B04C439775EAA33141FE12', 1, NULL, NULL, 2, NULL, 0, 'Combine the miso and the water and whisk them both together until the miso is completely dissolved in the water. You can also heat the water up a bit first in order to speed up this process.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABE98BD11FB17146A0E93B5BE61D3151', X'33A352B4F4B04C439775EAA33141FE12', X'33A352B4F4B04C439775EAA33141FE12', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, melt the vegan margarine over medium-low heat and whisk in the flour until a paste forms.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F15EA09107C7FB46B8476BE3646B32EF', X'33A352B4F4B04C439775EAA33141FE12', X'33A352B4F4B04C439775EAA33141FE12', 3, NULL, NULL, 2, NULL, 0, 'Add the miso and water mixture, vegetable broth, soy sauce, nutritional yeast, and black pepper. Stir to combine very well.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3F0C7984FFF0542A8E7F33F1BC7F78D', X'33A352B4F4B04C439775EAA33141FE12', X'33A352B4F4B04C439775EAA33141FE12', 4, NULL, NULL, 2, NULL, 0, 'Allow to cook over low heat until thickened, stirring frequently. Just like when making a non-vegan gravy, you will need to do a lot of stirring to avoid any lumps forming as the liquid thickens up.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39553BAD15740244A66B413B6607F3E5', X'33A352B4F4B04C439775EAA33141FE12', X'33A352B4F4B04C439775EAA33141FE12', 5, NULL, NULL, 2, NULL, 0, 'Add the cornstarch, if needed, to make the gravy even thicker. But don''t forget that your gravy will thicken slightly as it cools, just like a meat-based gravy.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02D09E1AA38B744FBFDAEBD5CB0DD509', X'33A352B4F4B04C439775EAA33141FE12', X'33A352B4F4B04C439775EAA33141FE12', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD7B4183F4C4CE42890288379D6CA04C', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 0, NULL, NULL, 2, NULL, 0, 'Slice eggplant in half lengthwise, then into 1/4 inch-thick cubes and half-rounds.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C99F23EAAA56F46AA40BA12C427E466', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 1, NULL, NULL, 2, NULL, 0, 'Optional: Place cut eggplant pieces into a large colander and sprinkle with salt. Leave standing for 30 minutes for the bitter juices to drain away.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A86671CBB4DAF04E907F493EE3CF25AA', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 2, NULL, NULL, 2, NULL, 0, 'Mix miso, mirin, and sugar in a small bowl and set aside. (If the miso glaze has been prepared and frozen ahead of time, warm-up the miso glaze in the microwave for just 30 seconds so that its nice and warm.)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D4A69F4876F4147AA9FE9093B91F13E', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 3, NULL, NULL, 2, NULL, 0, 'Heat oil in a medium skillet and fry eggplants on medium heat until softened.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFB6778C118FED4EB4323BF3D11727DD', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 4, NULL, NULL, 2, NULL, 0, 'Add green bell pepper and stir-fry them until softened.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1A467F66BC1BB41BE231815113668C8', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 5, NULL, NULL, 2, NULL, 0, 'Turn heat to low.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A36E8395573AD44BB94E2BF84467A2DB', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 6, NULL, NULL, 2, NULL, 0, 'Add miso mixture and stir quickly with the ingredients. (If the sauce begins to burn, lower the heat some more and add 1-2 tbsp. of water to the pan)');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92927FFBDECC8B41B6C836F332E733CC', X'10245E9CEEC4E74AB06464B410BD269E', X'10245E9CEEC4E74AB06464B410BD269E', 7, NULL, NULL, 2, NULL, 0, 'Remove from heat, stir in toppings and serve hot.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8D2A2347C40D14BB602A2D02C26010F', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', 0, NULL, NULL, 2, NULL, 0, 'Pre-heat oven to broil or highest setting. Cover a cookie sheet with tin foil. Combine all ingredients except tofu and oil using blender or mixer. Slowly add oil and mix until smooth and creamy. Pour mixture into wide bowl or pan.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBCD4BE35549334E935C178EFDB4D304', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', 1, NULL, NULL, 2, NULL, 0, 'Cut tofu into 1/2 inch thick pieces and dip into miso mixture until coated with a thin layer, reserving some of the mixture.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6A4E96B8408A14AA7E63C6CF73E1DBF', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', 2, NULL, NULL, 2, NULL, 0, 'Gently lay tofu pieces on covered cookie sheet. Broil 8 to 10 minutes until golden brown and slightly crisp. Remove from oven.');
INSERT INTO `VWR INTERNAL Recipesbying recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1866A8775DBADF459F4944BA8FF419CC', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', X'8CB7E8FE81976E4DBCD8C1C2DE10DBCA', 3, NULL, NULL, 2, NULL, 0, 'Turn the pieces over and `baste` another layer of miso mixture over the pieces with a spoon. Broil 8 to 10 more minutes until browned and crisp. Sprinkle with sesame seeds if desired and enjoy!');
INSERT INTO `VWR INTERNAL Recipesbying recipes` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'D768A5401130D2459BCDC06A947CE17A', X'D1040D478E99AF46A3321296E828AEF8', X'D1040D478E99AF46A3321296E828AEF8', 0, NULL, NULL, 2, NULL, 0);
INSERT INTO `VWR INTERNAL Recipesbying recipes` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment) VALUES (X'ADBFAFE5C71C0A45A406C9D3CF8A0907', X'D1040D478E99AF46A3321296E828AEF8', X'D1040D478E99AF46A3321296E828AEF8', 1, NULL, NULL, 2, NULL, 0);
