INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22499BC429795241979D735E95337E51', X'C5C0460B903ED848B0F9D2E0AF4AEAF3', X'C5C0460B903ED848B0F9D2E0AF4AEAF3', 3, NULL, NULL, 2, NULL, 0, 'Place the salmon into a small bowl and toss with the lime juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B67CF1A4ADD02B43BF68FC7F04A99700', X'C5C0460B903ED848B0F9D2E0AF4AEAF3', X'C5C0460B903ED848B0F9D2E0AF4AEAF3', 4, NULL, NULL, 2, NULL, 0, 'In a larger bowl, whisk together the vegetable oil, sesame oil, and lime zest. Then add all of the remaining ingredients and toss to combine.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'235FF3DF27CB9345935952AFAC702EC2', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DB1B84A2A06A8499D60884A5CC924E3', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 1, NULL, NULL, 2, NULL, 0, 'Cut salmon fillets into 3 inch chunks and remove any skin or bones that remain. Place chunks into a food processor with white parts of the green onion. Add salt, white pepper, ginger, almond flour and rice wine vinegar.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C4C94EC063DD34C812EE1A53D74F27D', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 2, NULL, NULL, 2, NULL, 0, 'Process ingredients until well combined. Mixture should have a little texture, so do not over process. Remove lid, add green portion of green onion, replace lid and pulse 5 times. Set aside while you prepare the grill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ADF7365AC75744289AF60DBA16DA1E8', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 3, NULL, NULL, 2, NULL, 0, 'Preheat grill for medium high heat. Once grill comes up to full heat (about 10-15 minutes later), oil grill grates with an oil soaked paper towel. Secure into a large pair of cooking tons and wipe across grill grate 3-4 times to create a non-stick cooking surface.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8AA16F5D6B5C624F93FCA318B01837E2', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 4, NULL, NULL, 2, NULL, 0, 'While grill is heating up, form salmon mixture into 5 equal sized patties. Place onto grill and cook for 8-10 minutes, or until the internal temperature reaches 145 degrees F/65 degrees C. Once cooked through, remove patties from heat and place onto a large plate. Tent with aluminum foil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5DEFB4CC2724B44B3119EA6604CBADE', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 5, NULL, NULL, 2, NULL, 0, 'To prepare slaw: Add shredded radicchio and carrots to a marge bowl. Add soy sauce, rice wine vinegar, sesame oil, salt and black pepper. Toss to coat. Taste and add more soy sauce or salt to you liking. Add peanuts at the very end and toss to distribute through slaw. You can prepare the slaw before working on the salmon. Simply cover bowl and place into refrigerator until ready to use.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F8D6A08C9EA374396060296B00A037B', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 6, NULL, NULL, 2, NULL, 0, 'To prepare mayonnaise: Combine mayonnaise with sambal sauce, pepper, onion powder.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA22B0D15153F040A9F96AF31AD8FD91', X'464658166ECAC0479FC6164FE9CE6476', X'464658166ECAC0479FC6164FE9CE6476', 7, NULL, NULL, 2, NULL, 0, 'To assemble burgers: add a generous amount of sambal mayonnaise to bottom bun, place grilled salmon patty on top, then radicchio slaw and top bun. Serve with your favorite sides.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'92562579A792944794BF472935F8A301', X'843DBADB1ED20048AFD353A1F06519B2', X'843DBADB1ED20048AFD353A1F06519B2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4E78C304022564D86311BBA167B5A30', X'843DBADB1ED20048AFD353A1F06519B2', X'843DBADB1ED20048AFD353A1F06519B2', 1, NULL, NULL, 2, NULL, 0, 'Place your salmon fillets in a large bowl. In another one, whisk together the soy sauce, brown sugar, garlic powder, ginger powder, and cornstarch in a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'570389450E0C134E8885CA02E02DAF9F', X'843DBADB1ED20048AFD353A1F06519B2', X'843DBADB1ED20048AFD353A1F06519B2', 2, NULL, NULL, 2, NULL, 0, 'Add the marinade to the salmon and allow it to sit for at least 20 minutes in the refrigerator. You can marinate overnight if you want to.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0DEFBFC6F2B3E459070D01D6557204C', X'843DBADB1ED20048AFD353A1F06519B2', X'843DBADB1ED20048AFD353A1F06519B2', 3, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F. Place the salmon fillets on a parchment paper-lined baking sheet. Reserve the leftover marinade. Bake them in the oven for 15 to 20 minutes or until cooked through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F88D060374365846A8AFAF23B5F6A25D', X'843DBADB1ED20048AFD353A1F06519B2', X'843DBADB1ED20048AFD353A1F06519B2', 4, NULL, NULL, 2, NULL, 0, 'While the salmon is baking, place the rest of the marinade in a small saucepan and cook on medium heat until it has thickened. Add water if it gets too thick.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8C03E1FFE6FA443B830396037DE952A', X'843DBADB1ED20048AFD353A1F06519B2', X'843DBADB1ED20048AFD353A1F06519B2', 5, NULL, NULL, 2, NULL, 0, 'Remove the salmon fillets from the oven and spread the reduced marinade over the top of each piece.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1907DB660D5CB242A5D5B7557F049712', X'843DBADB1ED20048AFD353A1F06519B2', X'843DBADB1ED20048AFD353A1F06519B2', 6, NULL, NULL, 2, NULL, 0, 'Top the salmon with the sesame seeds and chopped scallions and serve with hot white rice or noodles.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'291FF04390F74C47BD926F771487B57E', X'1C0F369DEC35C24DA3061D18245BD3FD', X'1C0F369DEC35C24DA3061D18245BD3FD', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'139056DEF806E84AA250053D5907DAAF', X'1C0F369DEC35C24DA3061D18245BD3FD', X'1C0F369DEC35C24DA3061D18245BD3FD', 1, NULL, NULL, 2, NULL, 0, 'Start by making the sauce. Put the sugar and water into a non-reactive saucepan, gently bring to a gentle boil stirring all the time. Once boiling, add the ginger, garlic and chilli. Stir and boil to reduce to a thick, glossy sauce. Do not want to walk away while this is happening as the sauce can burn quickly and easily; any slight smell of burning, remove the pan from the heat immediately. Let the pan cool then return to a lower heat to finish the cooking. Once syrupy, add the fish sauce and lime juice. Put to one side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BF778E0905E204CA231B00E6B928ED5', X'1C0F369DEC35C24DA3061D18245BD3FD', X'1C0F369DEC35C24DA3061D18245BD3FD', 2, NULL, NULL, 2, NULL, 0, 'Lay the fresh salmon fillets on a sheet of kitchen paper. Check the salmon over for any small bones taking care to remove them. Cut the fillets into 1-inch cubes and place into a bowl. Spoon over the caramel sauce and stir gently. Put to one side while you prepare the salad.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'963525A8F4558147A07BCC1DE7F46AEA', X'1C0F369DEC35C24DA3061D18245BD3FD', X'1C0F369DEC35C24DA3061D18245BD3FD', 3, NULL, NULL, 2, NULL, 0, 'Place the bean sprouts in a large bowl and pour over a pint of boiling water. Drain and then put to one side. Pick over the herbs and salad leaves to make sure they are clean, place in a large salad bowl and mix thoroughly. Add the chopped onion and the bean sprouts.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04EBFD6948D951499513DEAE73B9DED2', X'1C0F369DEC35C24DA3061D18245BD3FD', X'1C0F369DEC35C24DA3061D18245BD3FD', 4, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a non-stick frying pan until hot but not smoking. Add the salmon pieces coated in the sauce and fry in the hot oil for five minutes whilst moving the fish pieces around the pan with a spatula to prevent them from sticking and burning. Once cooked remove from the pan and keep warm. Add any remaining marinade to the frying pan, stir well over medium heat to warm through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35BDB6EE3C3DDC45B52C29FDF3A4D37D', X'1C0F369DEC35C24DA3061D18245BD3FD', X'1C0F369DEC35C24DA3061D18245BD3FD', 5, NULL, NULL, 2, NULL, 0, 'Divide the salad into four bowls, lay the fish on the salad. Lightly crush the peanuts in a pestle and mortar and scatter over the dishes. Spoon the warm sauce over the fish and serve immediately with the lime quarters and rice if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65824CF5DB90C34694024E11F7F0AAF4', X'E47CDF3D21A71A44A7BC2919E33130D1', X'E47CDF3D21A71A44A7BC2919E33130D1', 0, NULL, NULL, 2, NULL, 0, 'Cut the salmon into 1-inch pieces. In a large skillet over medium heat, melt 1 tablespoon of the butter. Add half the salmon and saut just until the outside turns opaque, about 2 minutes. Remove from the skillet with a slotted spoon, melt the remaining tablespoon of butter, and repeat with the other half of the salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AFEAF14F71F71141AFC9A3222B6DD7DA', X'E47CDF3D21A71A44A7BC2919E33130D1', X'E47CDF3D21A71A44A7BC2919E33130D1', 1, NULL, NULL, 2, NULL, 0, 'Over medium-high heat, heat the olive oil in a stockpot or very large saucepan. Add the shallots and saut until softened, about 3 minutes. Add the broth, increase the heat to high and bring to a simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'014D978799F77445B68114E39B25F170', X'E47CDF3D21A71A44A7BC2919E33130D1', X'E47CDF3D21A71A44A7BC2919E33130D1', 2, NULL, NULL, 2, NULL, 0, 'Reduce the heat to medium-high and add the potatoes. Partially cover the pot, keep the broth at a simmer, and cook until the potatoes are tender, 12 to 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'572F4187DEC93545B63E471EA4BBE8FD', X'E47CDF3D21A71A44A7BC2919E33130D1', X'E47CDF3D21A71A44A7BC2919E33130D1', 3, NULL, NULL, 2, NULL, 0, 'In a blender or food processor, combine 1 cup of the corn kernels with the half-and-half, and use a slotted spoon to scoop out about 1/2 cup of cooked potatoes and add those to the blender; puree until smooth.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1BE4DF4FFBA3448BEC31A58F6747237', X'E47CDF3D21A71A44A7BC2919E33130D1', X'E47CDF3D21A71A44A7BC2919E33130D1', 4, NULL, NULL, 2, NULL, 0, 'Add the pureed mixture, along with the remaining corn kernels, back into the pot, and return to just barely a simmer. Simmer gently for 2 minutes, add the partially cooked salmon and the dill, season with salt and pepper and simmer for another 2 minutes until the salmon is just cooked through. Use the back of the spoon to break up the salmon into smaller chunks. Taste and adjust seasonings as needed and serve hot, with whatever garnishes you like.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'303A2EC05E581C418E46F9B1FC375F96', X'7523572D4ECED84B9C0A2F961C9F8AE8', X'7523572D4ECED84B9C0A2F961C9F8AE8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'338B8DEA4A105842AA3EF5CF28D3E723', X'7523572D4ECED84B9C0A2F961C9F8AE8', X'7523572D4ECED84B9C0A2F961C9F8AE8', 1, NULL, NULL, 2, NULL, 0, 'Turn the salmon and liquid into a medium-size bowl. Flake the salmon with a fork, removing any skin and bones. Smaller bones are edible and may be mashed. Add the grated onion, parsley, and pepper; blend thoroughly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5F8207EEBD8564A80F5A4EF4C97939A', X'7523572D4ECED84B9C0A2F961C9F8AE8', X'7523572D4ECED84B9C0A2F961C9F8AE8', 2, NULL, NULL, 2, NULL, 0, 'Add the beaten eggs and blend well. Add enough breadcrumbs, about 1/2 to 3/4 cup, to make the mixture thick enough to shape into about 12 small patties.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'534FF68B14662D46B669537A88D62889', X'7523572D4ECED84B9C0A2F961C9F8AE8', X'7523572D4ECED84B9C0A2F961C9F8AE8', 3, NULL, NULL, 2, NULL, 0, 'Roll the patties in the remaining breadcrumbs to coat thoroughly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34094CE6D3B8444CB72D53BDE78DBBCD', X'7523572D4ECED84B9C0A2F961C9F8AE8', X'7523572D4ECED84B9C0A2F961C9F8AE8', 4, NULL, NULL, 2, NULL, 0, 'In a large heavy skillet or saut pan over low heat, melt 2 tablespoons of butter. When the butter is hot, add the salmon patties. Fry patties slowly on one side until browned. Add the remaining butter, turn the patties, and fry them until browned on the other side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'033BAF71DC8A2148828A09BC5A2066B1', X'7523572D4ECED84B9C0A2F961C9F8AE8', X'7523572D4ECED84B9C0A2F961C9F8AE8', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90A8060D966F2A4CAC306D2871530E7C', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33398DD23AE9554A814B9B0A61FC71F5', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 1, NULL, NULL, 2, NULL, 0, 'Lay the salmon fillets on a piece of paper towel and dry carefully. Check the fish over for any bones (just in case). Remove with tweezers if you find any. Lay the fillets side by side in a glass or ceramic dish and put it to one side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69217EADFA64B043AA0B1760124F3773', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 2, NULL, NULL, 2, NULL, 0, 'Combine the maple syrup, lemon juice, soy sauce, garlic, and ginger and mix thoroughly in a jug. Add the chili flakes and stir again.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DC59C8524C296429A1246B6553D4750', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 3, NULL, NULL, 2, NULL, 0, 'Pour the marinade over the salmon. Cover and put to one side, but not in the refrigerator, for 20 minutes. Turn after 10 minutes, so all sides of the fillets come into contact with the marinade.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA343F7CE9396B4183366216C1335009', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 4, NULL, NULL, 2, NULL, 0, 'Heat a griddle or cast-iron frying pan with the oil until hot, but not smoking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5640F8447AF17D42BBD50A830D850C2B', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 5, NULL, NULL, 2, NULL, 0, 'Take the fillets one at a time from the marinade and pop into the pan, taking care as this may sizzle and splash. Keep the heat high for 2 minutes after all the fillets are in the pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0A507999DFABF4A840F7629EDAE9D1D', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 6, NULL, NULL, 2, NULL, 0, 'Reduce the heat to medium and cook the fillets for 4 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C18DEE3D49E2840BEB152853A72F10C', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 7, NULL, NULL, 2, NULL, 0, 'Then flip over and cook for a further 4. Remove from the pan and keep warm.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9E5F1EA75F8A44EB73D1832642F47C6', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 8, NULL, NULL, 2, NULL, 0, 'While the salmon is cooking, put the marinade into a small saucepan and bring to a gentle boil. Reduce to create a thick, syrupy sauce, then strain into a little jug.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'600E1D8F7D5D9841B1FC63284965C1FF', X'ECBB98C9E16FEE4F85594453C7A6E013', X'ECBB98C9E16FEE4F85594453C7A6E013', 9, NULL, NULL, 2, NULL, 0, 'Serve this hot, tasty salmon over brown or wild rice, or a bed of nutty grains with green vegetables, and the sauce on the side. The cold salmon is also super-delicious when cold and flaked onto a salad, or even stirred through pasta.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F6998071474B2A43A5900D44973772A5', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68982D5D45D60F438941492DABBA2C3B', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium heat, melt the butter. Add the salmon fillet and reduce the heat to low.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'675857031039224BAFDF0D1A85603CDF', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 2, NULL, NULL, 2, NULL, 0, 'Cook, turning once, until the salmon is just cooked through; about 5 minutes on each side. The salmon''s internal temperature should be at least 145 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14C329A84D3B0642946A5BF9746D132D', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 3, NULL, NULL, 2, NULL, 0, 'Remove the salmon from the saucepan and cool. Flake the salmon into small pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B802D2522F77E548A656D2E430E09953', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat the cream cheese using a hand mixer until soft and fluffy.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'008110C98E8C094E9C14FB63C639B733', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 5, NULL, NULL, 2, NULL, 0, 'Gradually add the mayonnaise and lemon juice and beat well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'373075BFE6729B4C8F877BCBA8B830FB', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 6, NULL, NULL, 2, NULL, 0, 'Stir in the mustard, salt, pepper, and thyme.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BD3906B19545C468391D8B4DFF01A7E', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 7, NULL, NULL, 2, NULL, 0, 'Fold in the flaked salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9EFB08F8005F4468375C7CB06A46790', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 8, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for 1 to 2 hours to blend the flavors.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0781A93D21D62543A9C2624EAC2BC7FB', X'0128A78E1F00554FB786DA32F2125D33', X'0128A78E1F00554FB786DA32F2125D33', 9, NULL, NULL, 2, NULL, 0, 'Serve with crackers, fresh vegetables, or toasted French bread.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'68A9AF2D5E90A04785FD2AA21FD8E4F7', X'9B925961A24D21448CB76071E74FE77E', X'9B925961A24D21448CB76071E74FE77E', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'763FB782D3F805429D8F5A15002D8F7D', X'9B925961A24D21448CB76071E74FE77E', X'9B925961A24D21448CB76071E74FE77E', 1, NULL, NULL, 2, NULL, 0, 'Lay the vegetables and fish on your greased sheet pan. Preheat the oven to 425 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FBA648AC00C22448E6CE9756E44F6CF', X'9B925961A24D21448CB76071E74FE77E', X'9B925961A24D21448CB76071E74FE77E', 2, NULL, NULL, 2, NULL, 0, 'Whisk together all of the other ingredients in a small bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C7C22458D1BFE4BA1728BF62FE01116', X'9B925961A24D21448CB76071E74FE77E', X'9B925961A24D21448CB76071E74FE77E', 3, NULL, NULL, 2, NULL, 0, 'Drizzle half of the sauce over the top of the salmon and veggies. Use a brush to brush the sauce over the salmon too.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47B0373D92B9A84699F198D84FFCA02C', X'9B925961A24D21448CB76071E74FE77E', X'9B925961A24D21448CB76071E74FE77E', 4, NULL, NULL, 2, NULL, 0, 'Bake the salmon in the preheated oven for 15 to 20 minutes. While it is baking add the rest of the sauce to a saucepan and cook it on medium heat, whisk frequently until it has thickened, about 3 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E27647717842EE468058EA1B7CAC8556', X'9B925961A24D21448CB76071E74FE77E', X'9B925961A24D21448CB76071E74FE77E', 5, NULL, NULL, 2, NULL, 0, 'Remove the sheet pan from the oven and pour the remainder of the sauce over the top of the salmon and veggies.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CDC70A795741C749BDB7F5E18520119C', X'9B925961A24D21448CB76071E74FE77E', X'9B925961A24D21448CB76071E74FE77E', 6, NULL, NULL, 2, NULL, 0, 'Garnish with the sesame seeds and dig in!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0EFAD4612062946ADEFBACAA68B9F67', X'F53C6DC1E385A54394BA6F6EC0D00DCB', X'F53C6DC1E385A54394BA6F6EC0D00DCB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA935B70AE2B8E41A4936F11072CFAD7', X'F53C6DC1E385A54394BA6F6EC0D00DCB', X'F53C6DC1E385A54394BA6F6EC0D00DCB', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 500F. Line a rimmed baking sheet with parchment paper or aluminum foil, and spray with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D20A8BB7C50FB48AF9892543B8F74CC', X'F53C6DC1E385A54394BA6F6EC0D00DCB', X'F53C6DC1E385A54394BA6F6EC0D00DCB', 2, NULL, NULL, 2, NULL, 0, 'In a container or dish large enough to hold the salmon combine the Ponzu sauce, sesame oil, and ginger syrup, and stir to combine. Place the salmon in the marinade, turn to coat well, and marinate at room temperature for 15 to 20 minutes, skin side up if your salmon has skin on it.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFC5C85E5E33274CA01EC188F7A91FB8', X'F53C6DC1E385A54394BA6F6EC0D00DCB', X'F53C6DC1E385A54394BA6F6EC0D00DCB', 3, NULL, NULL, 2, NULL, 0, 'Roast the salmon for about 10 minutes, until it is cooked through and the top has a nice brown sheen. Serve hot, over rice');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69BD538C27E29F40B25596AF2A3D9472', X'F9D69E49B3E7EB4A89A0A53951B01DC7', X'F9D69E49B3E7EB4A89A0A53951B01DC7', 0, NULL, NULL, 2, NULL, 0, 'Place a 2/3 cup of cooked rice in a small rice bowl. If using leftover rice, reheat the rice in the microwave first.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F956E16DC350D149A433D4F161F13E62', X'F9D69E49B3E7EB4A89A0A53951B01DC7', X'F9D69E49B3E7EB4A89A0A53951B01DC7', 1, NULL, NULL, 2, NULL, 0, 'Add a 1/4 cup of grilled salmon and desired garnishes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5CDCD90924B98A4DB520D447B9AF5F78', X'F9D69E49B3E7EB4A89A0A53951B01DC7', X'F9D69E49B3E7EB4A89A0A53951B01DC7', 2, NULL, NULL, 2, NULL, 0, 'Pour a 1/2 to 2/3 cup of hot brewed green tea over the rice, salmon, and garnishes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16312387582D5443BA3B4015C5200A19', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 0, NULL, NULL, 2, NULL, 0, 'Rinse the salmon head halves and pat dry. Pry or scape off as much of the scales as you can without damaging the skin.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AB486E76921A847A83D68A854B0B20C', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 1, NULL, NULL, 2, NULL, 0, 'Heat the cooking oil in a wide frying pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'73C8D335BB7A0042A661CE5CC08099D4', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 2, NULL, NULL, 2, NULL, 0, 'Saute the shallots, garlic, ginger, tomato and chilis with a little fish sauce, until fragrant and softened, about two minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3C963EBED5BE94DA422B6B55A52A354', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 3, NULL, NULL, 2, NULL, 0, 'Stir in the curry paste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'464173E4C631794AA4BC1C9CBE8D907E', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 4, NULL, NULL, 2, NULL, 0, 'Pour in the coconut milk. Season with more fish sauce. Bring to the boil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6AE0E72FC3CA94F827AD4408128ECB5', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 5, NULL, NULL, 2, NULL, 0, 'Lower the fish head halves into the sauce. Ladle some of the sauce over them');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B393830920732641B45EADD600DC0E68', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 6, NULL, NULL, 2, NULL, 0, 'Cover the pan, lower the heat and simmer for 12 to 15 minutes or just until done. Taste the sauce occasionally and add more fish sauce, if needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD872B19C9095E47B8616BA7951FB627', X'3417EFA2896A7046A392B142459559EB', X'3417EFA2896A7046A392B142459559EB', 7, NULL, NULL, 2, NULL, 0, 'Scoop out the fish head halves and transfer to a shallow bowl (or two shallow bowls). Pour the sauce over them. Garnish with fried onion slices, toasted garlic and snipped cilantro before serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A0EB1CAA518FC4F89C3695C8FA50CA8', X'B02C02271275BF4B9C876CEB754374F3', X'B02C02271275BF4B9C876CEB754374F3', 0, NULL, NULL, 2, NULL, 0, 'Cut the smoked salmon into small pieces and set aside. Use a sharp knife or kitchen shears.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF45CE44EBF5840AF8B2CB0179D4D2C', X'B02C02271275BF4B9C876CEB754374F3', X'B02C02271275BF4B9C876CEB754374F3', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, beat the eggs with cream, salt, and pepper until combined.?');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89D0F5D643BC4B49B56FFE93FE858CCD', X'B02C02271275BF4B9C876CEB754374F3', X'B02C02271275BF4B9C876CEB754374F3', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, melt butter over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18DA222138C15F4983B13AF8C9D500A7', X'B02C02271275BF4B9C876CEB754374F3', X'B02C02271275BF4B9C876CEB754374F3', 3, NULL, NULL, 2, NULL, 0, 'Add the egg mixture to the skillet. Cook, stirring occasionally with a heatproof spatula until the eggs are almost cooked but still shiny and moist. Scrape the bottom of the skillet with the spatula and turn the eggs over as they cook. This should take about 6 to 7 minutes for 12 eggs.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31DBC62A412F5046A41A5301F1904A8A', X'B02C02271275BF4B9C876CEB754374F3', X'B02C02271275BF4B9C876CEB754374F3', 4, NULL, NULL, 2, NULL, 0, 'Stir the salmon into the eggs; cook for another minute or two until the eggs are cooked to 160 F. Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB7BCCB3FB8112448F659DBE7AD115B4', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29A994B415F24449AD51B32B7EEB3659', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the garlic, 3 tablespoons of the extra-virgin olive oil, honey, turmeric, kosher salt and freshly ground black pepper to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E5EBBE910DC034EADEFC669A326091C', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 2, NULL, NULL, 2, NULL, 0, 'Season the salmon fillets with kosher salt and freshly ground pepper and place in a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A11CE96370CD84458AF8459F3AB225DA', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 3, NULL, NULL, 2, NULL, 0, 'Halve the marinade. Set one half aside. Use a pastry brush to coat the salmon fillets. Pour the excess marinade on top of the salmon fillets and gently toss to coat. Let the marinated salmon stand 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DA50F6EC8B97B469A703AD5B5F06FFC', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Line a baking sheet with parchment paper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4A363906F4E6D48B17E93F95FAC6FBC', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 5, NULL, NULL, 2, NULL, 0, 'Drain the can of beets. Use a heart cutter, if you like, to create heart shapes. Reserve the scraps for future use in salads, etc. or discard. You may want to use kitchen gloves when handling the beets to prevent staining your hands.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D30F2CBBFE9D8A45A127987A4F3A496B', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 6, NULL, NULL, 2, NULL, 0, 'Peel the red onions and cut into rings.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3569283875BD904EB3653119C81FA0F3', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 7, NULL, NULL, 2, NULL, 0, 'Place the beet slices or hearts and the raw red onion rings in a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86D6A6444CEDD5498478F4321DF345EF', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 8, NULL, NULL, 2, NULL, 0, 'Pour the other half of the marinade over the beets and onions and toss to coat. Let stand 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC38DC8CD05C60408131FFC6B34046BC', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 9, NULL, NULL, 2, NULL, 0, 'Transfer the beets and onions to the baking sheet. Spread them out on the parchment paper and bake for 25 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAEBC062316720449D1F51851873CA5B', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 10, NULL, NULL, 2, NULL, 0, 'In the meantime, cut the blood orange in half. Cut each half into thin round slices, then cut the round slices in half.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A185E7DE447D8840B07123669483B8F4', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 11, NULL, NULL, 2, NULL, 0, 'Heat the remaining extra-virgin olive oil in a medium sides skillet over high heat. Add the marinated salmon fillets and reserve the marinade in the bowl. Sear the salmon pieces on both sides.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2AF8967ACF7F2D448ADF96C22C7E5295', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 12, NULL, NULL, 2, NULL, 0, 'Remove the marinated roasted beets and onions from the oven. Lay the salmon on the baking sheet on top of the beets and onions. Pour the rest of the marinade over the top and bake for another 10 to 12 minutes, until the salmon has cooked through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'37F221846E0C564F8E0F6660830B03D5', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 13, NULL, NULL, 2, NULL, 0, 'Roughly chop the almonds and place them in a naked frying pan over medium heat. Stir them around until they are lightly toasted. This happens fast so do not turn your back on the pan or get distracted. Remove and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84834862331E1B45A82F38F80E818F8C', X'0D12ABF252E109438D3A10C9B98236CD', X'0D12ABF252E109438D3A10C9B98236CD', 14, NULL, NULL, 2, NULL, 0, 'Remove the salmon from oven, transfer everything to a serving platter, garnish with the toasted almonds and oranges. Tuck the cilantro leaves around the platter and serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'191F2534175F4F47B3155A7B8C7F3866', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8541E144749844286E229F254BCD2DC', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 1, NULL, NULL, 2, NULL, 0, 'Cut salmon into serving pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B3E7D6DAF3C9145B9CC88F68CD80AED', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 2, NULL, NULL, 2, NULL, 0, 'Salt the salmon lightly and set it out at room temperature for 15 to 20 minutes before cooking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B824AE3D0C56F149AD5473BCAD55B117', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 3, NULL, NULL, 2, NULL, 0, 'Fire up the grill. Make sure the surface is clean. Get some vegetable oil ready to wipe on the grill later.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA2BFEB72C97304BA38514EA5A0F7422', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 4, NULL, NULL, 2, NULL, 0, 'After 15 to 20 minutes of setting out, pat the salmon dry and then use a butter knife to scrape the skin side to get any moisture or scales off. This makes the skin side drier so it will not stick to the grill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'569A5B3637998D4EB2421BE156DC8E93', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 5, NULL, NULL, 2, NULL, 0, 'Coat the salmon with oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C8D018420D9EF4AB2CC483D52003A93', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 6, NULL, NULL, 2, NULL, 0, 'When the fire is good and hot, wipe down the grate with a paper towel that has been dipped in the oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BB417852455A44CA7AB30B81306C083', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 7, NULL, NULL, 2, NULL, 0, 'Put the salmon on the grill skin side up. It seems counterintuitive, but do it anyway because you want a good sear on the fish, and you cannot do this well if you start skin-side down; the fish will cook too much and will fall apart when you try to flip it. The uncooked skin helps hold things together.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B23CF088C573844BC73F761978BDAAB', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 8, NULL, NULL, 2, NULL, 0, 'Leave the salmon on the grill for a solid 4 to 6 minutes, depending on thickness. A thin sockeye fillet might need closer to 3 minutes on one side. You will know it is ready when you can slide a spatula underneath the salmon. It might stick in one or two spots, but it should be mostly crusted over.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D2F65AC80497488BC5D17A8FE0D98E', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 9, NULL, NULL, 2, NULL, 0, 'Gently flip the salmon onto the skin side and leave it again for at least 3 minutes, a bit longer if it is on the thick side. Exactly how long depends on thickness, but it''ll never be less than 2 minutes on high heat. Watch the fish, and if the flakes begin to separate, you''re done.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32C5CA877CD4E54C919DB3CF6F832EC6', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 10, NULL, NULL, 2, NULL, 0, 'Take the salmon off the heat, sprinkle lemon juice on it, then let it rest for 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'891439F2A295034AB5931DE6106B7525', X'C6A60813FB94E54F85BFD816D57F02BA', X'C6A60813FB94E54F85BFD816D57F02BA', 11, NULL, NULL, 2, NULL, 0, 'Serve with your favorite sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8190E32579864A4FAE918846F598A4B7', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5EDB10B1FCB1B44AE07164DD0391542', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 1, NULL, NULL, 2, NULL, 0, 'Cut the salmon fillet into two halves cutting across the piece.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1EEB62F628D554B8297539D57A5135B', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, mix the sugar, salt, peppercorns, chili (if using), juniper berries, and dill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E673B01851C644D93BF7D85934FF1FB', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 3, NULL, NULL, 2, NULL, 0, 'Place one half of the salmon, skin side down, onto a long sheet of plastic wrap. Cover the flesh of the salmon with the sugar, salt, and pepper mix. Place the second fillet flesh side down on top to create a `salmon sandwich`. Wrap the fish pieces tightly in the plastic wrap.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDB558CF7C5FEB4B9D4F9FE3F2E23A75', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 4, NULL, NULL, 2, NULL, 0, 'Put the `salmon sandwich` into a shallow baking dish, making sure the fish stands higher than the sides of the pan. Place a baking tray on top of the salmon and weigh it down with any heavy object, like canned beans, rice bags, or heavy books (covered in plastic wrap to avoid passing on fish smells).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D713220B49CC3F4AB728304F0122406C', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 5, NULL, NULL, 2, NULL, 0, 'Put the fish into the refrigerator and leave it to cure for 3 to 4 days, turning the salmon twice a day or at least once every day. If there is any accumulated liquid, pour it out and change the plastic wrap.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A9781C9B689FB449B06997A709440DA6', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 6, NULL, NULL, 2, NULL, 0, 'When ready to serve, remove the wrap, pour away any liquid and wipe away most of the sugar, salt, and peppercorns, leaving a little on the edges for decoration. Slice as desired: thin for bagels, thick if it''s meant to be a main course.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40DDF97F35406B46BD4BA5BE28D8543F', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 7, NULL, NULL, 2, NULL, 0, 'The lox will keep well in the refrigerator for five days. If you bought frozen salmon or froze it before curing, do not refreeze.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDCC34B05CA0EA4BA752713FC2D1DA45', X'9445F1037ABEE1489913721996D23484', X'9445F1037ABEE1489913721996D23484', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF6B216C0D5B204980DC1AE69C5DA75E', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 0, NULL, NULL, 2, NULL, 0, 'Steam rice according to your rice cooker''s instructions or on the stovetop.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48F5E02E29CF1A498742787877D3F934', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 1, NULL, NULL, 2, NULL, 0, 'While the rice is cooking, boil green beans and slice on the diagonal into bite-sized pieces. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95C4E0239B43A248815B8A5278FE656E', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl scramble eggs and add a pinch of salt. Heat medium size pan with olive oil and cook a thin layer of egg to make kinshi tamago (thin omelet). Remove from pan and let cool on cutting board. Cut into thin strips and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB9F119DA3CF70439CF12CBC7BFC750B', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 3, NULL, NULL, 2, NULL, 0, 'Boil a small pot of water to cook the baby broccolini.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF6238D3292D144DBDA1E4C529DD44E6', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 4, NULL, NULL, 2, NULL, 0, 'Thaw cooked frozen shrimp, remove tails if desired, and then slice in half vertically to create 6 halved pieces of shrimp. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'895D2A0640C6394796C1638D0AD7C607', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 5, NULL, NULL, 2, NULL, 0, 'Mix in sushi rice vinegar powder with cooked rice. You may or may not use all of the cooked rice, as it will depend on how thick you make your rice layers, as well as the diameter of the cake pan. Be sure to use diagonal cutting motions with the side edge of the rice paddle (pre-moistened with water) to mix with the warm rice. This helps to prevent the rice from getting mashed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F2A3FECBBA42A4F82ABEC8DFAC4046E', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 6, NULL, NULL, 2, NULL, 0, 'Line cake pan with a large sheet of plastic wrap. It will help you to remove the sushi with ease. If you are using a springform pan (with removable bottom), there''s no need to line the pan with plastic wrap, but instead, make sure the pan is wet with water, to prevent the rice from sticking too much from the side of the pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87D1F7C78542AE47A9EA141288EC0522', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 7, NULL, NULL, 2, NULL, 0, 'Add a layer of rice on the bottom of the pan. Top with pink sakura denbu shredded codfish. Layer more rice on top and then garnish with sliced green beans. Layer with the last layer of rice. Garnish top with thinly sliced egg, baby broccolini, shrimp, ikura (salmon roe caviar), and any optional garnishes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2477ACE87780AC45B6496D881C8AB11E', X'0E6029B1BA51854683529773F97F4BBA', X'0E6029B1BA51854683529773F97F4BBA', 8, NULL, NULL, 2, NULL, 0, 'Remove from pan onto a plate and serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0B598DEF160414785A1A98DF17FCB39', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16025591BA80344496E59F42C4624542', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 375 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91D88D7368921A458F4405F0928BADFB', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 2, NULL, NULL, 2, NULL, 0, 'Lightly grease a 9-by-13-by-2-inch baking pan or line the pan with foil and lightly oil the foil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CFBE216EEE0A749A91EA9726B59526A', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the minced garlic, olive oil, and parsley. Blend the ingredients well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'544622D54C5F3744AE80126B259CC807', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 4, NULL, NULL, 2, NULL, 0, 'Arrange the salmon fillets in the prepared baking pan and spread the garlic and parsley mixture evenly over them. Sprinkle the fillets lightly with salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6566834DDB4F6E4EAA1BED44DD9E72EF', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 5, NULL, NULL, 2, NULL, 0, 'Bake the salmon for about 20 minutes, or until the fillets register 145 F or flake easily with a fork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C960DA232B95DD478C0110666E49A867', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 6, NULL, NULL, 2, NULL, 0, 'Meanwhile, juice one of the lemons.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD6146A01D80A84E9E4DEAD79E0F47B6', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 7, NULL, NULL, 2, NULL, 0, 'Drizzle the baked salmon fillets with 2 tablespoons of lemon juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6259DB8597C3F343A89D0BE3A1AE682C', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 8, NULL, NULL, 2, NULL, 0, 'Slice the remaining lemon into wedges and serve them with the salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B81E7B864D68B340A53D5667771596AE', X'8D1925D8990FF14E8B4DC3E2DFEAC879', X'8D1925D8990FF14E8B4DC3E2DFEAC879', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D49667B64A490144BB080A914ABDE492', X'9F5420AD5891BC4D821B694AA9FFB6FE', X'9F5420AD5891BC4D821B694AA9FFB6FE', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil and cook the pasta as directed on the package. Meanwhile, cook the bacon in a large skillet until crisp. Drain the bacon on paper towels and crumble; set aside. Discard the bacon fat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D49E3A3385D754CA1F4C8AB1CE06921', X'9F5420AD5891BC4D821B694AA9FFB6FE', X'9F5420AD5891BC4D821B694AA9FFB6FE', 1, NULL, NULL, 2, NULL, 0, 'In a small bowl beat together the egg yolks with the cream; season with the salt and pepper. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'852715B04A0D2E41B9C901D1DCC0BF6E', X'9F5420AD5891BC4D821B694AA9FFB6FE', X'9F5420AD5891BC4D821B694AA9FFB6FE', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat the olive oil over low heat and add the salmon, peas, and cooked bacon; gently heat. Shake the pan from time to time, so the food doesn''t stick.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC064CD6768813469449E076D1DD7D09', X'9F5420AD5891BC4D821B694AA9FFB6FE', X'9F5420AD5891BC4D821B694AA9FFB6FE', 3, NULL, NULL, 2, NULL, 0, 'When the pasta is done, drain well and add immediately to the skillet with the salmon; do not stir.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C38BFD79A9499846BC766E38A90F97C0', X'9F5420AD5891BC4D821B694AA9FFB6FE', X'9F5420AD5891BC4D821B694AA9FFB6FE', 4, NULL, NULL, 2, NULL, 0, 'Immediately pour the egg mixture over the hot pasta. Turn the heat under the skillet to medium. Using tongs, gently lift and turn the pasta, egg mixture, salmon, peas, and bacon together until creamy sauce forms on the pasta. The egg will cook on contact with hot pasta. Sprinkle the food with the cheese and toss gently. Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44C4BF1AEB0DBF4AB771AEFDD0C6564D', X'F6FB75170243C24C80BB1413DBFD8EE4', X'F6FB75170243C24C80BB1413DBFD8EE4', 0, NULL, NULL, 2, NULL, 0, 'Place salt, brown sugar, soy sauce, and water into a pot. Next, bring that to a boil and stir until the sugar has dissolved. Remove the pot from heat and let cool to room temperature.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B85FA1F0FDF92042BDC8D70E2F2841A8', X'F6FB75170243C24C80BB1413DBFD8EE4', X'F6FB75170243C24C80BB1413DBFD8EE4', 1, NULL, NULL, 2, NULL, 0, 'Slice the salmon lengthwise (not across the width) into strips 1/4- to 1/2-inch thick. Place the salmon strips in the cooled brine and refrigerate for 8 to 10 hours to marinate.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF86A883F56B3F47A310C633E4EC3FBF', X'F6FB75170243C24C80BB1413DBFD8EE4', X'F6FB75170243C24C80BB1413DBFD8EE4', 2, NULL, NULL, 2, NULL, 0, 'Drain the brine. Rinse the salmon with clean water and let drain. Pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45040C22289AC44CB0F8BBC274B03F16', X'F6FB75170243C24C80BB1413DBFD8EE4', X'F6FB75170243C24C80BB1413DBFD8EE4', 3, NULL, NULL, 2, NULL, 0, 'Spray dehydrator racks with vegetable oil. Place salmon strips on racks, not touching each other so air can circulate. Sprinkle with cayenne pepper to taste. Dry to the desired doneness according to dehydrator manufacturer''s recommendations.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F105F6EC0AD2954FB60EF7AF0079278E', X'F6FB75170243C24C80BB1413DBFD8EE4', X'F6FB75170243C24C80BB1413DBFD8EE4', 4, NULL, NULL, 2, NULL, 0, 'The salmon jerky may also be dried in the oven on its lowest setting or in a smoker. The drying time could take 8 hours or more, depending on the method and how dry you like the jerky.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40D481DE9A72E746A278A28A961A0760', X'F6FB75170243C24C80BB1413DBFD8EE4', X'F6FB75170243C24C80BB1413DBFD8EE4', 5, NULL, NULL, 2, NULL, 0, 'Store salmon jerky in an airtight container or ziptop bag.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3A9646A85364746A6FF30783D51BA14', X'4048010A53394848BBCF355FBCE15B15', X'4048010A53394848BBCF355FBCE15B15', 0, NULL, NULL, 2, NULL, 0, 'Grease a 9-by-13-by-2-inch baking pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38755969E5CD7344BCC284C0A2A42C1C', X'4048010A53394848BBCF355FBCE15B15', X'4048010A53394848BBCF355FBCE15B15', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 400 F/200 C/Gas 6.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FE3B59A51636D4089877ED8AE64BA5B', X'4048010A53394848BBCF355FBCE15B15', X'4048010A53394848BBCF355FBCE15B15', 2, NULL, NULL, 2, NULL, 0, 'Place the salmon fillets, skin-side down, in the prepared baking pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77508FA2051FD34C95B9CF54E23133C6', X'4048010A53394848BBCF355FBCE15B15', X'4048010A53394848BBCF355FBCE15B15', 3, NULL, NULL, 2, NULL, 0, 'Combine the honey, lime juice, and orange juice; stir until well blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83ED94A4DD44CA44977263270CD044CE', X'4048010A53394848BBCF355FBCE15B15', X'4048010A53394848BBCF355FBCE15B15', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle the salmon fillets with salt and pepper then spoon the honey and juice mixture over the fillets.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2582C2093518B74AA453697188F77B29', X'4048010A53394848BBCF355FBCE15B15', X'4048010A53394848BBCF355FBCE15B15', 5, NULL, NULL, 2, NULL, 0, 'Bake for 10 to 12 minutes, or until the fish flakes easily with a fork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E71BFEC20825A48ABAC910A3BC3C691', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B66DC59E5B5B2946A558A7B237116D42', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 1, NULL, NULL, 2, NULL, 0, 'Cut each nori sheet (if using) into 8 to 9 strips and put about a 1/2 cup of steamed rice in a rice bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EF445FB64CD59641B4E87DCAE92CE41E', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 2, NULL, NULL, 2, NULL, 0, 'Wet your hands with water so that the rice won''t stick.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F553334070A6B24F84F4C5A9B6433CD4', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 3, NULL, NULL, 2, NULL, 0, 'Rub some salt on your wet hands.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DAD371C8DED2849B6FBA1E70F492A09', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 4, NULL, NULL, 2, NULL, 0, 'Place the steamed rice in your hand and form into a triangle, making sure it is dense and thick.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'498C3D9373F42F408ABC52046FCBCD89', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 5, NULL, NULL, 2, NULL, 0, 'Put your favorite filling, such as umeboshi or grilled salmon, on the rice and push the filling into the rice lightly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D57CB7273A06541ACDF403D2D12468B', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 6, NULL, NULL, 2, NULL, 0, 'Hold the rice between your palms.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C350569AD8338E43802F9D2BDCA86EE1', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 7, NULL, NULL, 2, NULL, 0, 'Form the rice into a round, a triangle, or a cylinder by pressing lightly with your both palms, securing the filling in the middle. Roll the rice ball in your hands a few times, pressing lightly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B431A873670964F97D77875F8E177FE', X'CC376EAD485555498A3E24860930511F', X'CC376EAD485555498A3E24860930511F', 8, NULL, NULL, 2, NULL, 0, 'Wrap the rice ball with a strip or two of nori (if using), or sprinkle some sesame seeds on them (if using).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02D8DC54824B7641937BC92ABC960603', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6840DD98826B9240BD3749FB97C67D44', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F/180 C/Gas 4. Butter the bottom of a shallow baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA476A5A887950428D06AA5D0886E5E4', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 2, NULL, NULL, 2, NULL, 0, 'Arrange the salmon steaks or fillets in the buttered baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85519E5A81465C4C86FEFE21404FC5BA', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 3, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the lemon juice and onion.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9046D2855AA6B54B81A990C5F79971C3', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 4, NULL, NULL, 2, NULL, 0, 'Sprinkle about 2 teaspoons of the lemon-onion mixture over each piece of salmon. Season with salt and freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ACFC34B1AABCB34D8F30E925063C0DEF', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 5, NULL, NULL, 2, NULL, 0, 'Bake, uncovered, for about 20 minutes (about 15 minutes for fillets), or until fish flakes easily with a fork or a minimum of 145 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B1D292F614DB549BFE3101B70862DB7', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 6, NULL, NULL, 2, NULL, 0, 'Remove from oven and spread sour cream over salmon pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FCD7F1D2A7AA547BC21F84C0C62BC0F', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle with lemon zest and dill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EB7F0CC0F9D864F9918B4475384A445', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 8, NULL, NULL, 2, NULL, 0, 'Return to the oven for 3 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3A2984B17609AF499DBA681FA9CDA20B', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 9, NULL, NULL, 2, NULL, 0, 'Serve the salmon hot with lemon wedges.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6934A37484D3B49989C17EAE82080FC', X'54AF4BD24458C0438A95E9C68FE9B405', X'54AF4BD24458C0438A95E9C68FE9B405', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD9BA11D83260B469C3CF671E132F3B7', X'8C4457C02EB33A4BA720F76B9A8D9BBF', X'8C4457C02EB33A4BA720F76B9A8D9BBF', 0, NULL, NULL, 2, NULL, 0, 'Separate the salmon into individual slices. Heat butter in a medium skillet and saut the salmon until it is done, turning once, for just a minute or so. You can skip this step if you prefer to use the salmon uncooked. Break the salmon into pieces. If you aren''t cooking the salmon, cut it into pieces with a sharp knife.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20957D7CDE3C8149B4CFD4AECEB6C3A8', X'8C4457C02EB33A4BA720F76B9A8D9BBF', X'8C4457C02EB33A4BA720F76B9A8D9BBF', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, beat cream cheese with an electric mixer until soft and fluffy. Beat in mayonnaise until smooth.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF9E7A25803AB74D92DA081992C58AC2', X'8C4457C02EB33A4BA720F76B9A8D9BBF', X'8C4457C02EB33A4BA720F76B9A8D9BBF', 2, NULL, NULL, 2, NULL, 0, 'Stir in the salmon and dried basil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A72C9C7B41DCB3478ED84988F67FE98A', X'8C4457C02EB33A4BA720F76B9A8D9BBF', X'8C4457C02EB33A4BA720F76B9A8D9BBF', 3, NULL, NULL, 2, NULL, 0, 'Make sandwiches with the salmon filling, the whole basil leaves, and croissants. Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A8403AD15A37345A6C70DE60397FC82', X'8C4457C02EB33A4BA720F76B9A8D9BBF', X'8C4457C02EB33A4BA720F76B9A8D9BBF', 4, NULL, NULL, 2, NULL, 0, 'You can make the filling ahead of time; just cover it well and store it in the refrigerator up to 3 days. Use it when you want as a sandwich filling or for a snack with crackers.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F43A5A7DA8F83341A4D00098CDCEE1E7', X'88DFC422D27A0A4C965BC880C0877A7D', X'88DFC422D27A0A4C965BC880C0877A7D', 0, NULL, NULL, 2, NULL, 0, 'In a small mixing bowl, combine 2 tablespoons of brown sugar, spices, 1/4 teaspoon of salt, 1/8 teaspoon of pepper, and 1/2 teaspoon of grated orange rind. Mix until well combined.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49EE21F886FD8B4F9F8ACD044A7CBB5B', X'88DFC422D27A0A4C965BC880C0877A7D', X'88DFC422D27A0A4C965BC880C0877A7D', 1, NULL, NULL, 2, NULL, 0, 'Rinse the salmon fillets and pat dry. Arrange the fillets skin-side-down on a baking sheet or a large dish. Divide the brown sugar-spice mixture amongst the four fillets, sprinkling the mixture evenly over top each.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A649106AE1A1E49B20A17C1324ED29A', X'88DFC422D27A0A4C965BC880C0877A7D', X'88DFC422D27A0A4C965BC880C0877A7D', 2, NULL, NULL, 2, NULL, 0, 'Squeeze juice from one orange over the salmon fillets. Set aside to marinate while the grill is pre-heating.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'857C70805DE221409710517545BE6252', X'88DFC422D27A0A4C965BC880C0877A7D', X'88DFC422D27A0A4C965BC880C0877A7D', 3, NULL, NULL, 2, NULL, 0, 'Heat the grill over medium-high heat. Brush the grill grates with vegetable or canola oil. When the grill is hot, place the fillets skin-side-down on the grill grates.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14009D0007C1174D94A0EA8B6FB1DBE8', X'88DFC422D27A0A4C965BC880C0877A7D', X'88DFC422D27A0A4C965BC880C0877A7D', 4, NULL, NULL, 2, NULL, 0, 'Cover and cook for 8 to 10 minutes for 1-inch fillets. If fillets are thicker than 1-inch, cook for additional 8 minutes, or until salmon flesh easily flakes apart.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7035D996F424849AF22058D0FBB0463', X'88DFC422D27A0A4C965BC880C0877A7D', X'88DFC422D27A0A4C965BC880C0877A7D', 5, NULL, NULL, 2, NULL, 0, 'With a spatula, remove just the cooked salmon portion from the grill, leaving the skin on the grill or removing the skin and setting it aside once salmon is placed on a clean dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C49AD38FE424D4FB97C0D4349DE43D8', X'88DFC422D27A0A4C965BC880C0877A7D', X'88DFC422D27A0A4C965BC880C0877A7D', 6, NULL, NULL, 2, NULL, 0, 'Serve salmon immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'57AFEDF32A2CA643ABA8EBDED5EC7358', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', 0, NULL, NULL, 2, NULL, 0, 'Place the salmon and cracker crumbs in a large bowl. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F51ACBD5AAD45E43B6A77AB84DF201E2', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', 1, NULL, NULL, 2, NULL, 0, 'In a heavy skillet over medium heat, cook onion and red bell pepper until tender in 1 tablespoon of the olive oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3196213285FB3C4DA0E3E275F703FD95', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', 2, NULL, NULL, 2, NULL, 0, 'Add the cooked vegetables to the salmon and cracker crumbs in a bowl and mix gently. Add dill weed, egg, and lemon juice and mix gently.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'154C79FDA0785042BEE881795AF851F0', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', 3, NULL, NULL, 2, NULL, 0, 'Form the salmon mixture into four patties using your hands. At this point, you can cover the patties and refrigerate for a couple of hours before you finish cooking them.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'52095598BD3A28458ABE36D309818983', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', 4, NULL, NULL, 2, NULL, 0, 'When you are ready to cook, add the remaining olive oil and tablespoon of butter to the same skillet over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B163E7D488229147AE67D9CA9C9C7C84', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', X'D5B4BE9BD5DE0B4DB9BC8E568EE733B0', 5, NULL, NULL, 2, NULL, 0, 'Once the butter has melted, add the cold salmon patties to? the skillet. Cook the patties for 3 to 4 minutes on each side until golden brown and hot. Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F676DADA20A8D8429DB2B37392908D3B', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'405CF017D64DBB45829C43BB84401A2A', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 190 C/375 F/Gas 5');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63ACB4CFA2DB08459230C57C0D729AE2', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 2, NULL, NULL, 2, NULL, 0, 'Place the flour and salt into the bowl of a food processor. Add the butter and 2 tbsp of cold water. Pulse mix until all the ingredients are incorporated into a dough, add more water a tsp at a time if the mixture doesnt stick together and pulse again. Wrap in plastic wrap and rest in the refrigerator for 2 hours.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'695CA8AA45C0FC4FB2E5C67B95E8D481', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 3, NULL, NULL, 2, NULL, 0, 'Butter a 25cm/10-inch loose-bottomed tart tin. Unwrap the pastry, roll and line the tart dish making sure it is even on the bottom and sides and there are no holes. Prick the base of the dish using a fork. Refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAA7B0F474BB8E43BD71E01ABE04AABA', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 4, NULL, NULL, 2, NULL, 0, 'Cover the pastry with baking parchment and fill with baking beans, or use long grain rice. Bake for 35 minutes in the preheated oven, remove the parchment and beans or rice and bake for a further 10 minutes until the crust is golden. Leave until completely cold.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'67EED5C081A3F5469A136B29EE2C03D1', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 5, NULL, NULL, 2, NULL, 0, 'Lower the oven to 175 C/350 F/gas mark 4.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'454DE6C82883664F8199CF5AFFCEF408', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 6, NULL, NULL, 2, NULL, 0, 'Cut the broccoli into pieces, so that each floret end is about 7 to 8 cm in length. Roughly chop the stalk pieces. Cook all the broccoli in boiling water for 4 minutes, refresh under cold water and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E7539A40F75203418E6841A938FAF84F', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 7, NULL, NULL, 2, NULL, 0, 'Mix the eggs, egg yolks, cream, spring onions, and parsley. Season with pepper and a little salt, if liked (the smoked salmon may add enough salt to your taste).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1AB8EF52AD6D849B3CAE25943329E15', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 8, NULL, NULL, 2, NULL, 0, 'Arrange the smoked salmon and the chopped broccoli stalks in the baked pastry case. Pour the egg and cream mixture over. Arrange the floret end pieces of Tenderstem broccoli in the tart like the spokes of a wheel, with the florets around the edge of the tart.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49FB0AAE21DD10438A22A8DD57D052EB', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 9, NULL, NULL, 2, NULL, 0, 'Bake the tart in the preheated oven for about 25 minutes or until golden brown on top and just set.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4C57C7D64C65540936D4FB89E60A043', X'8A3DE67C3BBB5E40B27A506730E4672F', X'8A3DE67C3BBB5E40B27A506730E4672F', 10, NULL, NULL, 2, NULL, 0, 'Serve the tart with a crisp green salad with a lemony dressing.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3843A1C4A5FB1147A43AA4D6A2A41380', X'41034AE51CE34E45AE3B7C3DF65F0516', X'41034AE51CE34E45AE3B7C3DF65F0516', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D766FF9D9FEB3647BE012654054DFB16', X'41034AE51CE34E45AE3B7C3DF65F0516', X'41034AE51CE34E45AE3B7C3DF65F0516', 1, NULL, NULL, 2, NULL, 0, 'Place all marinade ingredients in a food processor or mini chopper. Process well to create a fragrant Thai spice paste (a pestle & mortar can also be used to make this paste).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14828123CF7A8048899B442E3E7E9550', X'41034AE51CE34E45AE3B7C3DF65F0516', X'41034AE51CE34E45AE3B7C3DF65F0516', 2, NULL, NULL, 2, NULL, 0, 'Rinse salmon pieces and pat dry. Place a bowl and slather the marinade/paste all over the fish, ensuring all surfaces are covered. Marinate 10 minutes or up to 24 hours.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1039DF1C6722F84B87AD5A07D26E4119', X'41034AE51CE34E45AE3B7C3DF65F0516', X'41034AE51CE34E45AE3B7C3DF65F0516', 3, NULL, NULL, 2, NULL, 0, 'To make the side sauce, simply combine everything together in a cup, stirring well to dissolve the sugar. Then taste-test it, looking for a tangy flavor that is a balance between sweet, sour, spicy and salty. Add more lime juice if too salty or sweet for your taste. If too spicy, add more coconut milk. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF878AE896181548A67772FB27392A0C', X'41034AE51CE34E45AE3B7C3DF65F0516', X'41034AE51CE34E45AE3B7C3DF65F0516', 4, NULL, NULL, 2, NULL, 0, 'Heat up your grill and brush with a little vegetable oil. Grill the salmon, allowing each piece to cook several minutes before disturbing/turning (this will prevent the flesh from tearing). Baste the first time you turn it with some of the leftover marinades. Salmon is cooked when inner flesh is no longer translucent.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7A112226115BA489BAE1EFDC99578EE', X'41034AE51CE34E45AE3B7C3DF65F0516', X'41034AE51CE34E45AE3B7C3DF65F0516', 5, NULL, NULL, 2, NULL, 0, 'Serve the salmon with rice or salad, plus the sauce on the side (this sauce is usually served at room temperature). An easy salad that goes beautifully with this dish is Easy Thai Cucumber Salad.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB477713902184B83487E6032DE8936', X'41034AE51CE34E45AE3B7C3DF65F0516', X'41034AE51CE34E45AE3B7C3DF65F0516', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2047FA6A98338E448FF4F71023E87C6A', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EA32044E7AD7642959D997D95886D07', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 1, NULL, NULL, 2, NULL, 0, 'Place salmon on a broiler rack that has been sprayed with nonstick cooking spray.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'250D240B5B9C3644A803EAD8F18AE534', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine 2 tablespoons lemon juice, 2 tablespoons melted butter, salt, and pepper. Brush over salmon and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1D0F488CD61364D8AA710F60B2B22C7', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 3, NULL, NULL, 2, NULL, 0, 'In a large saucepan, melt 1 tablespoon butter over medium heat. Add onion; cook and stir until tender, about 6 to 7 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C09B0B84E651DC478FA16E1C2E65259A', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 4, NULL, NULL, 2, NULL, 0, 'Add cranberries, brown sugar, granulated sugar, currant jelly, orange juice, and 2 tablespoons lemon juice to the onion mixture in the saucepan. Bring to a boil over high heat, then reduce heat to low.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B952DD9CCA7EE4991335C12D2EFA492', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 5, NULL, NULL, 2, NULL, 0, 'Simmer the sauce for 15 to 20 minutes until cranberries pop and sauce thickens. Remove from heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'56925B6D29DC3D4F8BC712A921D6862E', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 6, NULL, NULL, 2, NULL, 0, 'Preheat broiler.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F89E7A7D6EB7F4EB11EFDCFCAFB4BAC', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 7, NULL, NULL, 2, NULL, 0, 'Broil salmon 4 to 5 inches from the heat for 8 to 10 minutes, or until fish flakes when a fork is inserted and twisted.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA30D5E518760543AE54C0C6DAB63A5D', X'E72FBC8740B4E14DB1AAA41586473C4A', X'E72FBC8740B4E14DB1AAA41586473C4A', 8, NULL, NULL, 2, NULL, 0, 'Serve fish with sauce. Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8FFEE25D216744695E9E04C31831D52', X'DC41A1B681C70A4081F4DE644C48BE3C', X'DC41A1B681C70A4081F4DE644C48BE3C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B09A778829911C4FA0D62C509B739B38', X'DC41A1B681C70A4081F4DE644C48BE3C', X'DC41A1B681C70A4081F4DE644C48BE3C', 1, NULL, NULL, 2, NULL, 0, 'Soak the cedar plank in water for at least 30 minutes and up to four hours before you start the grill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0025D1ED8068C149A6CCF31080CD07B4', X'DC41A1B681C70A4081F4DE644C48BE3C', X'DC41A1B681C70A4081F4DE644C48BE3C', 2, NULL, NULL, 2, NULL, 0, 'Preheat the grill ?for medium-high heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40022171ED505849A512A03522F05085', X'DC41A1B681C70A4081F4DE644C48BE3C', X'DC41A1B681C70A4081F4DE644C48BE3C', 3, NULL, NULL, 2, NULL, 0, 'Remove the cedar plank from the water and place the salmon fillet in the middle of it, skin-side down. Brush the top of the salmon fillet with olive oil; squeeze the lemon juice over the salmon. Scatter the lemon zest and the dill on top, then season the fillet to taste with the salt and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61ABE8787D991543B73A430C4DE9BC43', X'DC41A1B681C70A4081F4DE644C48BE3C', X'DC41A1B681C70A4081F4DE644C48BE3C', 4, NULL, NULL, 2, NULL, 0, 'Place the plank on the grill in the center. Cover the grill and allow the salmon to cook, undisturbed, for about 20 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5D6F6B8FB0DBC4E85DC1EA9083FE47E', X'DC41A1B681C70A4081F4DE644C48BE3C', X'DC41A1B681C70A4081F4DE644C48BE3C', 5, NULL, NULL, 2, NULL, 0, 'Test the internal temperature at the thickest part of the fillet with an instant-read or meat thermometer. Pull it off to rest at about 138 to 140 F; the USDA recommends cooking salmon to an internal temperature of 145 F, but the temperature will continue to rise another 5 to 10 degrees after you pull it off the grill. Let it rest for 5 to 10 minutes before you serve it.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C18C7555D5AF684798026C0F2E8C2EB5', X'DC41A1B681C70A4081F4DE644C48BE3C', X'DC41A1B681C70A4081F4DE644C48BE3C', 6, NULL, NULL, 2, NULL, 0, 'Serve with your favorite sides and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B721DB74145C44B23305DDC95D486C', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8BC1FCE336A9BE4DBC28C01A51A74C0D', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 1, NULL, NULL, 2, NULL, 0, 'Heat butter over medium-low heat. Add celery, carrot, and onion and saut, stirring, for 5 to 7 minutes, until celery is tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'627F91219D0BC54A890EFCA32ADDC373', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 2, NULL, NULL, 2, NULL, 0, 'Stir in the flour until well blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65375C01873B5D43A087733192FA5D6B', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 3, NULL, NULL, 2, NULL, 0, 'Stir in chicken broth and add potatoes. Bring to a simmer, stirring frequently.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F72718CD3114E34EB60F80F43CFE25DA', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 4, NULL, NULL, 2, NULL, 0, 'Cover and simmer, stirring frequently, for 15 to 18 minutes, or until potatoes and carrots are tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96A577EF50151A46BECABF1E2BB4DB3E', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 5, NULL, NULL, 2, NULL, 0, 'Add the salmon and peas; cook, stirring, for 2 to 4 minutes, or until the peas are tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5A5C7F3BE82D2429222EB44CA27A915', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 6, NULL, NULL, 2, NULL, 0, 'Add half-and-half or milk, along with the cheese and parsley. Taste test and season with salt and pepper, to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48002394775C5E46B36DD7E521A7E1B3', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 7, NULL, NULL, 2, NULL, 0, 'Cook while stirring until cheese is melted and the soup just begins to bubble.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'813EB22671067A40A426EA736FE8BA24', X'AA4BF86AA468A84DA46187CFE89AC12A', X'AA4BF86AA468A84DA46187CFE89AC12A', 8, NULL, NULL, 2, NULL, 0, 'Serve in a bowl with some crackers and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93E2DD626B417B4A85300CA415145EED', X'438481F01268264F8C76CAE40FA1D3F6', X'438481F01268264F8C76CAE40FA1D3F6', 0, NULL, NULL, 2, NULL, 0, 'Briefly rinse salmon fillets or steaks under cool water. Shake off excess or pat dry and place pieces in a flat-bottomed baking dish (you will need a lid or foil to cover it).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A8D36A5DED5EE642B5394CC17844A967', X'438481F01268264F8C76CAE40FA1D3F6', X'438481F01268264F8C76CAE40FA1D3F6', 1, NULL, NULL, 2, NULL, 0, 'In a separate bowl or container, combine the hoisin sauce, soy sauce, lime juice, honey, and chili flakes. Stir well to dissolve the honey. Add garlic and white parts of the green onion (save the green parts for serving).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B214BBD8CBFE0B4B8E02DC57B2B957B8', X'438481F01268264F8C76CAE40FA1D3F6', X'438481F01268264F8C76CAE40FA1D3F6', 2, NULL, NULL, 2, NULL, 0, 'Pour over salmon, turning pieces to saturate. Cover and allow to marinate in your refrigerator 30 minutes or up to 2 hours. When getting close to your desired cooking time, preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EE940EB85966441ADE617A786B2FFEC', X'438481F01268264F8C76CAE40FA1D3F6', X'438481F01268264F8C76CAE40FA1D3F6', 3, NULL, NULL, 2, NULL, 0, 'Spoon a little more sauce over each piece, then recover and bake at 350 F for 20 minutes, or until sauce is lightly bubbling and inner flesh of salmon has turned from translucent to opaque. If undercooked, cover and return salmon to the oven for another 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'01F84D14F88ECF4784409075DF8BE164', X'438481F01268264F8C76CAE40FA1D3F6', X'438481F01268264F8C76CAE40FA1D3F6', 4, NULL, NULL, 2, NULL, 0, 'Remove from oven and taste-test the sauce, looking for the following flavors: sweet followed by sour, then spicy. If too sweet or too salty for your taste, add a few more squeezes of lime juice. If not sweet enough, add a little more honey. More chili can also be added.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FDAC9C456EA254E84619C44E3BC8177', X'438481F01268264F8C76CAE40FA1D3F6', X'438481F01268264F8C76CAE40FA1D3F6', 5, NULL, NULL, 2, NULL, 0, 'Serve salmon with some of the sauce spooned over each piece, topping with a sprinkling of the reserved green onion.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2397A0006B8304B8E9D4A9A2BD46711', X'3ABFF64EDB733A4D9F43F773A9603B6B', X'3ABFF64EDB733A4D9F43F773A9603B6B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2B2106E8FD4BB948AAD546BD0EDFA6AC', X'3ABFF64EDB733A4D9F43F773A9603B6B', X'3ABFF64EDB733A4D9F43F773A9603B6B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 450 F. Line a baking sheet or baking pan with heavy-duty aluminum foil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E8FBD537C95C6438BEAB979B19480A5', X'3ABFF64EDB733A4D9F43F773A9603B6B', X'3ABFF64EDB733A4D9F43F773A9603B6B', 2, NULL, NULL, 2, NULL, 0, 'Place the fillets on the prepared pan. Brush the salmon with the olive oil and sprinkle with the seasonings.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C4392C8CD2EF174EBA3FA3C35D111048', X'3ABFF64EDB733A4D9F43F773A9603B6B', X'3ABFF64EDB733A4D9F43F773A9603B6B', 3, NULL, NULL, 2, NULL, 0, 'Roast the fish for 8 to 10 minutes, then turn off the heat and let the fish sit in the closed oven for 3 to 5 minutes longer. Aim for an internal temperature of 125 to 130 F (measured with a meat thermometer) in order to prevent overcooking and drying out the salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'538976399751ED47A7816DAE1425F951', X'3ABFF64EDB733A4D9F43F773A9603B6B', X'3ABFF64EDB733A4D9F43F773A9603B6B', 4, NULL, NULL, 2, NULL, 0, 'Allow the fillets to rest, covered, for 5 minutes, before serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E364120988635C40869CD35CDF5D7215', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 0, NULL, NULL, 2, NULL, 0, 'Coat the salmon in the vegetable oil and salt generously.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'613C299AAA701D429BDA45EDEC4C58E1', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 1, NULL, NULL, 2, NULL, 0, 'Get the grill good and hot, and make sure the grates are clean.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EACF7185DFA34444B0E57EB60119DDAB', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 2, NULL, NULL, 2, NULL, 0, 'Cut the stick of butter into pieces and heat over medium heat in a small pot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74ADCADA1431BF4CB7A33B025767A3C7', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 3, NULL, NULL, 2, NULL, 0, 'Lay the salmon down on the grill, skin facing up. Don''t move it for at least 3 minutes, even for a thin sockeye fillet. For a big slab of king salmon, you might need 6 minutes. Watch the sides of the fish -- you want to see it turn opaque almost halfway up before you flip the fish. Test before flipping: The salmon might stick in a few places, but most of it should come right up when you try to flip with a spatula. Dislodge the sticky spots before you flip.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87F15783D814E04798428DAF127E7CA3', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 4, NULL, NULL, 2, NULL, 0, 'While the salmon is grilling, fry the sage leaves whole in the butter. When they''re almost done sizzling, remove and let drain on paper towels. Add the thyme and parsley and turn off the heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24B0EF69B950864F9218FFE0C119B4BA', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 5, NULL, NULL, 2, NULL, 0, 'Flip the salmon onto the skin side and cook for 2-5 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1978979A30F8D44BAF862685CB2AC55B', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 6, NULL, NULL, 2, NULL, 0, 'Get the plates ready by placing a bed of salad greens onto them. I prefer a mix of baby greens, like mesclun mix.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E00BC2F483A2134DB9FD9900F4BDE60C', X'94498203A7454342B76D04ADC5A98510', X'94498203A7454342B76D04ADC5A98510', 7, NULL, NULL, 2, NULL, 0, 'To serve, lay a piece of salmon onto each plate -- whichever side looks better, skin or meat. Pour some of the herb butter over the salmon and top with a fried sage leaf or two. Serve with the lemon wedges.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A60D6D116FB07147B329836584F47D1B', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2512EAC1747B84B95925A7113BE6F05', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', 1, NULL, NULL, 2, NULL, 0, 'In a large pot filled with boiling, salted water, cook the pasta according to the package directions. Place the frozen peas in the colander, then drain and rinse the pasta (it will simultaneously thaw the peas). Shake well to remove excess water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77CF730949E3644FA1F7C5006D46D257', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', 2, NULL, NULL, 2, NULL, 0, 'In a medium mixing bowl, add the mayonnaise, yogurt, honey mustard, lemon juice, chives, paprika, salt, and pepper. Mix the dressing well and adjust to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96BE5914126C06489F4FC98F1A25FE98', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', 3, NULL, NULL, 2, NULL, 0, 'Assemble the salad in the serving bowl you intend to use. Add the pasta and peas, then the salmon, bell pepper, and Havarti cheese. Top with the dressing.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'91582FB96EAD2D4E83A1353971A008D9', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', 4, NULL, NULL, 2, NULL, 0, 'Gently toss to combine. Cover and chill for at least 1 or 2 hours in the refrigerator.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CEEA1E41D0384E498BB1925FE30068E3', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', X'9BCF498AC5A8C842BB3E0B1878CAEEB7', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'86B5246E7090AF4CBE7C6F0A304CA83D', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', 0, NULL, NULL, 2, NULL, 0, 'Evenly spread shio koji on both sides of the salmon filets.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B1039A3B717EC644ABFC7E0542BD6E24', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', 1, NULL, NULL, 2, NULL, 0, 'Store in a re-sealable plastic bag or (non-reactive) glass or plastic container.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F00B6BBBD518644B1EF9DE4AB44EEA5', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', 2, NULL, NULL, 2, NULL, 0, 'Marinate a minimum of 30 minutes or up to four hours. Salmon can also be marinated overnight.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE7A9C707310AF41974F29D560B60231', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', 3, NULL, NULL, 2, NULL, 0, 'Prepare a baking sheet by lining it with foil and then placing a metal broiler rack on top.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06E668ECD64D4B46AD9A06DACE159469', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', 4, NULL, NULL, 2, NULL, 0, 'Spray the broiler rack with cooking spray to prevent the fish from sticking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0259D0BCABBF84F8F26B566B7BE16E2', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', 5, NULL, NULL, 2, NULL, 0, 'Set the oven to broil on high and grill the fish for 6 to 8 minutes on one side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B02522EC6AB5B54BB3B69958ADF16DD1', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', X'86A6E74CC0A8B344BCDEDFFFD57BA75A', 6, NULL, NULL, 2, NULL, 0, 'Turn fish over and broil an additional 3 minutes until cooked through. Time may vary slightly depending on the thickness of the filets.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60C406DED3E7E8479077D3F0928A50BC', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', 0, NULL, NULL, 2, NULL, 0, 'Peel and finely chop the onion. Rinse the green peppers and remove the stems, seeds and veins. Finely chop parsley. Peel the garlic, but leave whole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E89039C5FFE46F4B9AAD85FA3FB4C5C8', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', 1, NULL, NULL, 2, NULL, 0, 'Rinse and pat dry the salmon steaks. Place them in a glass baking dish, squeeze lemon juice over them and sprinkle with salt. Set aside to marinate in the juice for about 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8043B72D531FAD4DA55E085D0C537EB2', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', 2, NULL, NULL, 2, NULL, 0, 'While the salmon is marinating, pour 2 to 3 tablespoons olive oil into a medium-size frying pan and heat on medium-low heat. Saut the peppers and onions, stirring often until soft. Pour in the white wine and fish stock. Reduce the heat to low and add the chopped parsley. Using a stick blender, blend the vegetables and liquid into a smooth sauce. Remove from heat. Alternatively, you can use a standing blender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9714C0329B17F46B0F7D1F73926C7A7', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', 3, NULL, NULL, 2, NULL, 0, 'In a medium frying pan, heat approximately 2 tablespoons olive oil on medium. Brown the peeled garlic cloves in the oil and remove. Cook the salmon on both sides in the same olive oil, adding more oil if necessary.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A4A35FDA16094081D30F174F57E1DA', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', X'94A2BE8C2AFE514BB0D7A840AC85A5AA', 4, NULL, NULL, 2, NULL, 0, 'Ladle green sauce onto plates and place salmon steak on top. Serve each steak with a clove of garlic and garnish with a lemon wedge and parsley sprig if desired. Serve with rice or fried potatoes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'14A7CB6ED984354E9010F005DFC3FBB2', X'5992E587FAFF4E4A8DE7B2946E1E1A27', X'5992E587FAFF4E4A8DE7B2946E1E1A27', 0, NULL, NULL, 2, NULL, 0, 'Combine all ingredients except the salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A85B2FEA303BE944BDEC0B5E164CF3E3', X'5992E587FAFF4E4A8DE7B2946E1E1A27', X'5992E587FAFF4E4A8DE7B2946E1E1A27', 1, NULL, NULL, 2, NULL, 0, 'Lay salmon fillets out on a tray or baking sheet. Top with the butter-rum mixture, spreading it evenly over salmon. Let sit for about 15 to 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'506F7F724D230A4FB5B07F620753EE8F', X'5992E587FAFF4E4A8DE7B2946E1E1A27', X'5992E587FAFF4E4A8DE7B2946E1E1A27', 2, NULL, NULL, 2, NULL, 0, 'Preheat grill and prepare for indirect grilling. This recipe works particularly well with a charcoal grill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'567ABA94B0932E4CBAF8E48F44B28B27', X'5992E587FAFF4E4A8DE7B2946E1E1A27', X'5992E587FAFF4E4A8DE7B2946E1E1A27', 3, NULL, NULL, 2, NULL, 0, 'When grill is ready, gently place salmon fillets over the unheated part (indirect) of the grill. Keep the grill on low heat. You can add soaked mesquite chips for an extra smoky flavor. Close ?the lid and let salmon grill for 30 to 45 minutes depending on thickness.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4918F4A4F9AF3D4EAE6811C9C9C37A54', X'5992E587FAFF4E4A8DE7B2946E1E1A27', X'5992E587FAFF4E4A8DE7B2946E1E1A27', 4, NULL, NULL, 2, NULL, 0, 'Check for doneness by flaking apart the fish at the thickest part. Cooked salmon flakes easily and evenly. The color should be light pink without any shine.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA822FC8FEDDA343A8757F9D9FB2EE5E', X'343A9F67B3ABB34DBCAB0D253B5FCC89', X'343A9F67B3ABB34DBCAB0D253B5FCC89', 0, NULL, NULL, 2, NULL, 0, 'Preheat the broiler and coat pan with cooking spray.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'841DCF1A1BAED740AE1A42474E784A36', X'343A9F67B3ABB34DBCAB0D253B5FCC89', X'343A9F67B3ABB34DBCAB0D253B5FCC89', 1, NULL, NULL, 2, NULL, 0, 'Brush soy honey glaze on the flesh of salmon and let stand 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A823994D05BB4B4AB29A8239C342835D', X'343A9F67B3ABB34DBCAB0D253B5FCC89', X'343A9F67B3ABB34DBCAB0D253B5FCC89', 2, NULL, NULL, 2, NULL, 0, 'Place salmon on a pan with skin side down, drizzling any remaining sauce over fish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6E332E6CF568B45A031362E0BBFE328', X'343A9F67B3ABB34DBCAB0D253B5FCC89', X'343A9F67B3ABB34DBCAB0D253B5FCC89', 3, NULL, NULL, 2, NULL, 0, 'Broil the fish on top rack for 6 to 8 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99B7A3A68763274C9A8BBCC0C6E7BA43', X'343A9F67B3ABB34DBCAB0D253B5FCC89', X'343A9F67B3ABB34DBCAB0D253B5FCC89', 4, NULL, NULL, 2, NULL, 0, 'For grilled salmon, cook 5 minutes per side on a heated grill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AAA39FC1211DA43B85762687901ECF6', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil; add salt.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B55EDEDA58EB6141939F31C15B47E751', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 1, NULL, NULL, 2, NULL, 0, 'Combine the sour cream, half and half, lemon peel and dill weed and mix well; set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78B79BCA10E6844E8DEB57E2D7EA0D5F', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 2, NULL, NULL, 2, NULL, 0, 'Cook the pasta according to package directions in the boiling water until al dente; drain, reserving 1/2 cup pasta cooking liquid.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11B2C56C775B93409C5425967B6AD662', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large skillet, heat the olive oil over medium heat and add salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E1969F4F19C3B46BCA952B724347E92', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 4, NULL, NULL, 2, NULL, 0, 'Cook and stir very gently over medium-high heat until salmon''s color lightens about 4 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12A343B9E86EA74EB0DDE19444C062BE', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 5, NULL, NULL, 2, NULL, 0, 'Remove the salmon from the skillet and set aside on a plate.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72F2A9FA9130104A9718BCC423B2680B', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 6, NULL, NULL, 2, NULL, 0, 'Add the leeks to the skillet and saut for 1 minute. Add the bell pepper and peas to skillet and cook for 3 minutes longer, stirring constantly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E90B1B04C29A914C9137ED906BBA4483', X'ACE144D3A9758140A36D8926600D69E7', X'ACE144D3A9758140A36D8926600D69E7', 7, NULL, NULL, 2, NULL, 0, 'Reduce the heat to medium-low and add cooked and drained pasta, salmon, sour cream/lemon mixture, and lemon juice to the skillet. Stir gently to coat and heat through, adding reserved pasta water if necessary to create a smooth sauce. Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E6738F03F271246A1DE3C1754AC3107', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE2F71025C983848BFFC00A5F5CE7438', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 1, NULL, NULL, 2, NULL, 0, 'Place salmon in a shallow, nonreactive dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F70C9BCDE3E4BF43929D270CF7F4CBC3', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, combine marinade ingredients of lemon juice, lemon zest, vegetable oil, dill, and garlic. Stir to combine. If you don''t have fresh dill, dried dill can be used.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A77CED3E3E03984E90A429081F0C4AA7', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 3, NULL, NULL, 2, NULL, 0, 'Pour marinade over salmon. Cover and refrigerate for 1 to 2 hours.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA236497F24F24EB0D4975FE5B8C470', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 4, NULL, NULL, 2, NULL, 0, 'Remove the salmon from the marinade and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19CFC78B2057E6469834B35819DA2D93', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 5, NULL, NULL, 2, NULL, 0, 'Pour the marinade into a saucepan and bring to a boil. Remove from the heat and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD3E6850CD328440BFB8FF0BADADB32C', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 6, NULL, NULL, 2, NULL, 0, 'Grill salmon directly over medium coals, basting with the boiled marinade, for about 4 minutes on each side or until fish flakes easily.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C88C60288A38104B9AD947639C9ECCAC', X'0449483560B4F34DA9BFB8B6F5A09374', X'0449483560B4F34DA9BFB8B6F5A09374', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy with white rice, a side green salad, or a minted pea puree.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D8734D31C434C43B8441A5DC5E04D46', X'05C010A885001E4490E08105FCB4759B', X'05C010A885001E4490E08105FCB4759B', 0, NULL, NULL, 2, NULL, 0, 'Peel the potatoes and cut into 1/2-inch dice. You should have about 2 cups of diced potatoes. Put them in a saucepan and cover them with water and 1 teaspoon of salt. Place the pan over medium-high heat and bring to a boil. Reduce the heat to medium-low, cover the pan, and cook for about 10 minutes, or until the potatoes are tender. Drain and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D70A6917F28204D91EF43C557B1CA04', X'05C010A885001E4490E08105FCB4759B', X'05C010A885001E4490E08105FCB4759B', 1, NULL, NULL, 2, NULL, 0, 'Steam the peas on the stovetop or in the microwave following the package directions. Drain and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2DF526AD0E120E468A334A5D4CFEEC17', X'05C010A885001E4490E08105FCB4759B', X'05C010A885001E4490E08105FCB4759B', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large saucepan over medium-low heat. Add the celery and saute until tender, stirring frequently.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BCB30406F04CF47B35917653FCAE2C0', X'05C010A885001E4490E08105FCB4759B', X'05C010A885001E4490E08105FCB4759B', 3, NULL, NULL, 2, NULL, 0, 'Add the flour to the celery and butter mixture; blend well. Continue to cook for 2 minutes, stirring constantly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'511E8C88D77C044D8867B9E3987A5798', X'05C010A885001E4490E08105FCB4759B', X'05C010A885001E4490E08105FCB4759B', 4, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and gradually add the milk. Place the pan back over medium heat. Bring to a gentle boil, stirring constantly. Cook, stirring, for 1 minute.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F6BEA523B4E3C4FA1C5AA43CC3634C5', X'05C010A885001E4490E08105FCB4759B', X'05C010A885001E4490E08105FCB4759B', 5, NULL, NULL, 2, NULL, 0, 'Add the cooked potatoes, peas, and salmon. Heat through and then stir in cheese. Continue to cook until the cheese has melted.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54257AE5461C864488E6D578AD294653', X'B54E7D33E4AA0849B9914976EA1C4F40', X'B54E7D33E4AA0849B9914976EA1C4F40', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F. Spray a 13` x 9` glass baking dish with nonstick cooking spray. Layer the potatoes, salmon, cheese, and peas in the prepared dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABA7D40B3FBF5F4FA98F79ACA079A04D', X'B54E7D33E4AA0849B9914976EA1C4F40', X'B54E7D33E4AA0849B9914976EA1C4F40', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the cream, milk, mustard, salt, and pepper, making three layers so the food is evenly distributed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F38A1D4593E0034E8D17B57070B1DD0D', X'B54E7D33E4AA0849B9914976EA1C4F40', X'B54E7D33E4AA0849B9914976EA1C4F40', 2, NULL, NULL, 2, NULL, 0, 'Smooth out the top and sprinkle with the Parmesan cheese. At this point, you can cover the dish and refrigerate for 1 to 2 days if you''d like.?');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E99EC1AE5A645449C04C21999E83D72', X'B54E7D33E4AA0849B9914976EA1C4F40', X'B54E7D33E4AA0849B9914976EA1C4F40', 3, NULL, NULL, 2, NULL, 0, 'You can also bake the casserole immediately, covered, for 40 to 50 minutes or until hot. Uncover the dish and bake for 10 to 15 minutes longer until the potatoes are tender, the casserole is bubbly, and the top is browned. If you are baking this from the refrigerator, add another 10 to 15 minutes to the baking time.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F2CD218E0542648843C6C758A0E21D6', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E146EE11250E0A49ADD3D5BF5E0E4722', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 425 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7384B0ACEDA297428AC1747BED0CFC56', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 2, NULL, NULL, 2, NULL, 0, 'Clean, rinse, and pat dry the fillet.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E550DE3A3B26244E887EE3C7E20A9E6F', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 3, NULL, NULL, 2, NULL, 0, 'Place salmon filletskin side downon a piece of aluminum foil that is large enough to wrap around the whole fillet. Then place on a baking sheet or in a shallow pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A09E7B4C2243B4599AD50AEAC8577BF', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the lemon juice, oil, garlic, salt, and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6C21C925D4C104383E56DCCC8CE33BD', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 5, NULL, NULL, 2, NULL, 0, 'Pour the seasonings over the fillet. Then wrap the foil around the fish. Seal edges well, but leave room for the fish to expand.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D9166E9EAC3DF45BC015F197A297F59', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 6, NULL, NULL, 2, NULL, 0, 'Bake the fish for 10 minutes in order for the heat to penetrate the foil. Then cook another 10 minutes for each inch of fish thickness. The salmon is done when it flakes easily when tested with a fork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19936CCA9D458D459C6AA7BF4FDC0B1D', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 7, NULL, NULL, 2, NULL, 0, 'When done, remove from oven, unwrap, and discard salmon skin.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D9533F691A69D488243FCEA2BA8473A', X'2C16A90A5DB86546A334DDA8ADD48CB7', X'2C16A90A5DB86546A334DDA8ADD48CB7', 8, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66812FDAD515844381409E0FFEC8C6DE', X'8AE004922B73524E87D518DFC924F5BE', X'8AE004922B73524E87D518DFC924F5BE', 0, NULL, NULL, 2, NULL, 0, 'In saucepan cook green pepper and onion in 2 tablespoons of butter until tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D3CF0AF0238EC443BF49F14293C25972', X'8AE004922B73524E87D518DFC924F5BE', X'8AE004922B73524E87D518DFC924F5BE', 1, NULL, NULL, 2, NULL, 0, 'Add soup and milk; heat until bubbly, stirring constantly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D18E093ACD684438E6763CBC588A2B4', X'8AE004922B73524E87D518DFC924F5BE', X'8AE004922B73524E87D518DFC924F5BE', 2, NULL, NULL, 2, NULL, 0, 'Add cheese; stir until melted. Stir in salmon, drained noodles and olives.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DA5A63AD5944A488672423836E28CD2', X'8AE004922B73524E87D518DFC924F5BE', X'8AE004922B73524E87D518DFC924F5BE', 3, NULL, NULL, 2, NULL, 0, 'Transfer mixture to a 1 1/2-quart casserole and top with buttered bread crumbs.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD768E51FA41224D80C8B2B5A1DEF6F3', X'8AE004922B73524E87D518DFC924F5BE', X'8AE004922B73524E87D518DFC924F5BE', 4, NULL, NULL, 2, NULL, 0, 'Bake at 350 F for 25 to 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BBBBBD9C94D1B4E8FF6A8053C65D123', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Heat the oven to 450 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E9D05929B541F4E8799CAF1B0AEFFA3', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 1, NULL, NULL, 2, NULL, 0, 'Bake the crust in the heated oven for 5 minutes. Remove the crust from the oven and reduce the temperature to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E944944E870DE40AC2798AF625AB07C', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 2, NULL, NULL, 2, NULL, 0, 'Drain salmon liquid into a cup and set aside. Put salmon in a bowl and remove bones and skin. Flake the salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C48529DD38D8AD44A4699164031D3C04', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 3, NULL, NULL, 2, NULL, 0, 'Add the salmon to the cooled pastry and then sprinkle with lemon juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A2AD87953DF434580FBC46720415A91', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 4, NULL, NULL, 2, NULL, 0, 'In a skillet over medium heat, melt the butter. Cook the onion until translucent. Spoon the onions over the salmon and sprinkle with parsley.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B090251E30576C4FB009B10A0FEDE8C5', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 5, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk the salmon liquid with eggs, milk, seasoned salt, and pepper. Pour over salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'943A0F323B3C2941B32964BDB8D315DB', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 6, NULL, NULL, 2, NULL, 0, 'Bake the quiche at 350 F for 45 to 50 minutes, or until firm. Remove from oven and let rest for 5 minutes before slicing and serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FAD394DFC4DAA4CB8D990801B0A5F37', X'EC00C0B779B7E948A62CF30E9A89F165', X'EC00C0B779B7E948A62CF30E9A89F165', 7, NULL, NULL, 2, NULL, 0, 'Serve this salmon quiche hot with a tossed salad and bread and butter if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A871D6A0F0E96843BDF23E792C839ECB', X'7C408353676BA44786F9CAE91D5CAB7A', X'7C408353676BA44786F9CAE91D5CAB7A', 0, NULL, NULL, 2, NULL, 0, 'Combine 2 cups water, wine, onion, lemon, salt, peppercorns, dill and parsley in ?a large skillet. Heat to boiling. Reduce heat, cover and simmer for 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'938DA237CB3CC84FA4AD6EEE981ED21D', X'7C408353676BA44786F9CAE91D5CAB7A', X'7C408353676BA44786F9CAE91D5CAB7A', 1, NULL, NULL, 2, NULL, 0, 'Add salmon steaks, cover and simmer gently for 5 minutes (a little longer for steaks thicker than 1/2-inch), or until fish flakes easily with a fork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19299B6AD0052A4789C55B4BE566C3DD', X'7C408353676BA44786F9CAE91D5CAB7A', X'7C408353676BA44786F9CAE91D5CAB7A', 2, NULL, NULL, 2, NULL, 0, 'Lift steaks from cooking liquid with a slotted spatula and place on serving platter. Serve with cucumber sauce, hollandaise sauce, or any of your favorite sauces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'449D7DCB7235F8489ABA7403D2F71816', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 0, NULL, NULL, 2, NULL, 0, 'The recipe assumes that steamed rice is available. If you have not done so already, make rice according to your rice cookers instructions. Allow the rice to cool slightly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC996ED3C97C9F4394FFB97300949B31', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle Sushinoko brand sushi rice mix over the rice, mixing it in with the edge of the rice paddle in slashing motions. Note, this helps to prevent the rice from becoming smashed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45C5B59EE5EFF142A634603EE242EC7A', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 2, NULL, NULL, 2, NULL, 0, 'If you are not using powdered sushi vinegar mix, set aside the rice to cool towards room temperature. Meanwhile, make homemade sweet vinegar for the sushi. Recipe available here.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F4BDC13294E724C9631FBD65F61C1D7', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 3, NULL, NULL, 2, NULL, 0, 'Slice avocado and cube into small pieces. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7AD271C6B6FA384C9685163B4DE9F73B', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 4, NULL, NULL, 2, NULL, 0, 'Take the salmon sashimi pieces and cube them into small bite sized pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30C0DD982348DD43990F42F43BE6EB14', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 5, NULL, NULL, 2, NULL, 0, 'The sushi rice shouldnt be hot when garnishing the chirashi toppings over the rice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7489F148B202364D92EFD16CA9FD7F95', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 6, NULL, NULL, 2, NULL, 0, 'Scatter salmon sashimi pieces, avocado cubes, and masago (capelin roe/caviar) over the sushi rice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D34B9FFC8D6FCB45B90167AC904DB6E8', X'676352990173AB40BEAA7850EA777189', X'676352990173AB40BEAA7850EA777189', 7, NULL, NULL, 2, NULL, 0, 'Garnish the chirashi with Kewpie brand Japanese mayonnaise and kizaminori (thinly sliced dried seaweed). Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3925615BC2D214580CC7A366ACD8848', X'BE3550A2D2D7F447A8ED6953CEB7132E', X'BE3550A2D2D7F447A8ED6953CEB7132E', 0, NULL, NULL, 2, NULL, 0, 'Place the olive oil and butter in a 4-quart crockpot. Add the onions, baby carrots, and garlic, stir, cover, and cook on low for 6 to 7 hours, stirring once during cooking time, until vegetables are beginning to caramelize. This mixture can cook up to 8 to 9 hours if you are away from home while the food is cooking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6033870A88BDB4CA6758FCCB8C3E799', X'BE3550A2D2D7F447A8ED6953CEB7132E', X'BE3550A2D2D7F447A8ED6953CEB7132E', 1, NULL, NULL, 2, NULL, 0, 'Place the salmon filet (or the smaller individual fillets) over the vegetables in the crockpot and sprinkle with salt, pepper, and dill weed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07456E167D6955409AA3440ED7EF0B69', X'BE3550A2D2D7F447A8ED6953CEB7132E', X'BE3550A2D2D7F447A8ED6953CEB7132E', 2, NULL, NULL, 2, NULL, 0, 'Cover and cook on low for 1 to 2 hours until salmon flakes when tested with a fork (test at one hour mark; if salmon is done, turn the crockpot off and keep covered until serving time). If you are cooking salmon fillets, cook them for 40 to 50 minutes at most.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C701EC082CDA9D40AA1DBFF70671646C', X'BE3550A2D2D7F447A8ED6953CEB7132E', X'BE3550A2D2D7F447A8ED6953CEB7132E', 3, NULL, NULL, 2, NULL, 0, 'When you''re ready to eat, place the salmon on a serving plate and top with the onion mixture.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2024C7A14E43EA4D869AE5138FDAC0CA', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 325 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4291C5BCDC65EF47A2D0DE0261E8463C', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 1, NULL, NULL, 2, NULL, 0, 'Lightly butter a 1 1/2-quart casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17CC7109E99840489DECEEDFDF1AC508', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 2, NULL, NULL, 2, NULL, 0, 'Drain the salmon and flake it lightly. Pick out any bits of skin and larger bones. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C78B5C12623DD64FA349CFEB8400F6F6', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 3, NULL, NULL, 2, NULL, 0, 'Separate the eggs; set the whites aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D65221F2D6A3B4E8E130277ABD2220C', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 4, NULL, NULL, 2, NULL, 0, 'Beat the egg yolks in a small bowl and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DB754668B6DA6F4288FFED2AF9734B4A', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 5, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large saucepan over low heat; stir in flour. Cook, stirring, for two minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D660EA2D46932747AC203AE09F1511F2', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 6, NULL, NULL, 2, NULL, 0, 'Gradually add the milk to the flour and butter mixture, stirring constantly. Continue to cook until the sauce has thickened, stirring frequently.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6F35DA9AF82004E86CF172958058BA6', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 7, NULL, NULL, 2, NULL, 0, 'Add about 1/4 cup of the hot mixture to the beaten egg yolks, and then return the egg yolk mixture to the sauce, stirring quickly to blend.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19C6D5DCF0135845AFE5AE2E68A8DA23', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 8, NULL, NULL, 2, NULL, 0, 'Add the parsley, chives, nutmeg, salt, and pepper. Stir in the flaked salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C688B65FC29BD34A95F7809CD27BB9E9', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 9, NULL, NULL, 2, NULL, 0, 'Beat the egg whites to stiff peaks; don''t overbeat. They should be glossy and firm and should not look dry.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DF6DF8976BDD543B93F62D601FF7EC5', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 10, NULL, NULL, 2, NULL, 0, 'Gently fold the egg whites into the salmon mixture and then transfer the mixture to the prepared casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49E71C1D32CC4848AA9959E476E44081', X'C26079BBBEB55A44A1D6C66D365E4F19', X'C26079BBBEB55A44A1D6C66D365E4F19', 11, NULL, NULL, 2, NULL, 0, 'Bake for 50 to 60 minutes or until golden brown and set. Do not open the oven door until it looks done.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5584EB3891A604C96F37E352A72CFDB', X'E3FA589980F0B940A5687482ED359E1F', X'E3FA589980F0B940A5687482ED359E1F', 0, NULL, NULL, 2, NULL, 0, 'Heat the butter in a medium saucepan until melted; add onion and saute for 4 to 5 minutes, until tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCD72318FD3AFF489A75984A7AF1B5FD', X'E3FA589980F0B940A5687482ED359E1F', X'E3FA589980F0B940A5687482ED359E1F', 1, NULL, NULL, 2, NULL, 0, 'Blend the flour into the butter and onion mixture. Cook, constantly stirring, for 2 minutes. Add the bay leaf and gradually stir in broth. Cook, stirring until thickened and smooth.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16B150F86B3F0345939413B2C6B3A6CD', X'E3FA589980F0B940A5687482ED359E1F', X'E3FA589980F0B940A5687482ED359E1F', 2, NULL, NULL, 2, NULL, 0, 'Stir in wine and cook over low heat for 10 minutes. Discard the bay leaf.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3B11A1E2843EB8479169DE7BCE1CD155', X'E3FA589980F0B940A5687482ED359E1F', X'E3FA589980F0B940A5687482ED359E1F', 3, NULL, NULL, 2, NULL, 0, 'Stir in tomato paste and salmon with juices. Process in the blender in batches until smooth, then return to the saucepan. Stir in the half-and-half and season with salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'303705B15A4C9945A612321BCD81289E', X'E3FA589980F0B940A5687482ED359E1F', X'E3FA589980F0B940A5687482ED359E1F', 4, NULL, NULL, 2, NULL, 0, 'Garnish with roasted red pepper strips, croutons, parsley, or sour cream.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F87C644C1DD1F84FA4991D07AE946CD5', X'3FC6458C216C0544960352AFCFB49A4B', X'3FC6458C216C0544960352AFCFB49A4B', 0, NULL, NULL, 2, NULL, 0, 'Combine the cream cheese, lemon juice, onion, horseradish, dill, salt, pepper, and garlic powder in a large bowl. Use a stand mixer or handheld electric mixer until the mixture is well-blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB6000339ADEE146B14A5C98057261A0', X'3FC6458C216C0544960352AFCFB49A4B', X'3FC6458C216C0544960352AFCFB49A4B', 1, NULL, NULL, 2, NULL, 0, 'Drain and flake salmon, removing skin and bone. Add it to the cream cheese mixture and beat at low speed until it''s incorporated. Be careful not to overmix and shred the fish into unrecognizable pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'974A6E58F1C75C40817F95D80D9230CB', X'3FC6458C216C0544960352AFCFB49A4B', X'3FC6458C216C0544960352AFCFB49A4B', 2, NULL, NULL, 2, NULL, 0, 'Put the bowl in the refrigerator and chill until the mixture is firm enough to handle. If you need it to be done faster, put the mixture into the freezer for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'13D3B4A32922604AA57F3416361CA6FD', X'3FC6458C216C0544960352AFCFB49A4B', X'3FC6458C216C0544960352AFCFB49A4B', 3, NULL, NULL, 2, NULL, 0, 'Wash and dry your hands, leaving them a little damp. When the mixture is firm, use your hands to shape it into a ball.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E48ABD4247199140825E386BA1E7A1C8', X'3FC6458C216C0544960352AFCFB49A4B', X'3FC6458C216C0544960352AFCFB49A4B', 4, NULL, NULL, 2, NULL, 0, 'Combine the fresh chopped parsley and chopped walnuts or pecans on a large plate. Roll the ball in the mixture to coat the sides of the ball thoroughly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CC7C25CCA3100C47834F6AD414153AEE', X'3FC6458C216C0544960352AFCFB49A4B', X'3FC6458C216C0544960352AFCFB49A4B', 5, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate for at least an hour before serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C9DE528E5130048AEA31D87B0CDAAED', X'2DC432E56CCFB141A9D445F8830B3DFA', X'2DC432E56CCFB141A9D445F8830B3DFA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC8B58453803364A929038DB808B37CB', X'2DC432E56CCFB141A9D445F8830B3DFA', X'2DC432E56CCFB141A9D445F8830B3DFA', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49EB129223BD1446AC6A4DCE088B072D', X'2DC432E56CCFB141A9D445F8830B3DFA', X'2DC432E56CCFB141A9D445F8830B3DFA', 2, NULL, NULL, 2, NULL, 0, 'Cut the baguette into slices approximately 1/4-inch thick. Place slices on a baking sheet and lightly toast both sides, 5 to 7 minutes total. Allow? them to cool 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E91ADD5987F834BB90BCDDB8A31D7A3', X'2DC432E56CCFB141A9D445F8830B3DFA', X'2DC432E56CCFB141A9D445F8830B3DFA', 3, NULL, NULL, 2, NULL, 0, 'Spread cheese over each piece of toast.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2378D6DCF107F148883C8587D9BDAB1E', X'2DC432E56CCFB141A9D445F8830B3DFA', X'2DC432E56CCFB141A9D445F8830B3DFA', 4, NULL, NULL, 2, NULL, 0, 'Cut salmon into toast-size pieces and place over cheese.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FDE1537D4DD874799D98F09D11D5E57', X'2DC432E56CCFB141A9D445F8830B3DFA', X'2DC432E56CCFB141A9D445F8830B3DFA', 5, NULL, NULL, 2, NULL, 0, 'Serve with lemon slices, as your guests may wish to squeeze lemon over top.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00E0C0BAA515934AAC03ED50A1A4E832', X'2DC432E56CCFB141A9D445F8830B3DFA', X'2DC432E56CCFB141A9D445F8830B3DFA', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFB5592906EAD94EA1527557DF35249B', X'CA8B4E31616B7347B04A2710B1111EED', X'CA8B4E31616B7347B04A2710B1111EED', 0, NULL, NULL, 2, NULL, 0, 'Use a rolling pin to roll the bread out flat and thin. The crust can be left on or cut off.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97744BD3F87AAD4996A332F0017A2475', X'CA8B4E31616B7347B04A2710B1111EED', X'CA8B4E31616B7347B04A2710B1111EED', 1, NULL, NULL, 2, NULL, 0, 'Spread a layer of cream cheese on the bread. Lay a thin layer of smoked salmon on top of the cream cheese.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02C523DC14ED744BB376ECA9A2A8C955', X'CA8B4E31616B7347B04A2710B1111EED', X'CA8B4E31616B7347B04A2710B1111EED', 2, NULL, NULL, 2, NULL, 0, 'Tightly roll up the slice of bread, rolling from the longest side. Squeeze the roll together gently to secure. Use a serrated knife then cut the roll into 4 or 5 pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66467674ED7E0D4785B5C0A0D13ABD3B', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 0, NULL, NULL, 2, NULL, 0, 'The day before serving (if possible, since this intensifies the flavors): To Make the basic smrgstrta spread: Whip together the softened cream cheese, mayonnaise, and sour cream until light and creamy. Place in the refrigerator until ready to use.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3D45089EDA19A47A759C295DE0A238B', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 1, NULL, NULL, 2, NULL, 0, 'To make the baby shrimp spread: Mix together 1 cup of the cream-cheese mixture, 2 cups cooked baby shrimp, 1 tablespoon chopped dill, and chopped sweet pepper. Place in the refrigerator until ready to use.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C71D27957CBA5341BDE7546F0202BC00', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 2, NULL, NULL, 2, NULL, 0, 'To make the avocado spread: Peel and lightly mash two avocadoes into a chunky spread. Combine with 1/2 cup cream-cheese mixture, 1 tablespoon lemon juice, 1/4 cup red onion, and 1 tablespoon chopped dill. Place in the refrigerator until ready to use.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3EB831893ACD3B47A5D28223775A6A88', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 3, NULL, NULL, 2, NULL, 0, 'To make the smoked salmon spread: Remove smoked salmon from skin (if present) and dice into small pieces. Fold into 1 cup cream-cheese mixture; mix in 1 tablespoon roughly chopped capers. Place in the refrigerator until ready to use.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E8E1616F3348D438E6E65D34BDDFAC0', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 4, NULL, NULL, 2, NULL, 0, 'To construct the smrgstrta: Slice the crusts off of the bread. Arrange four slices in a square pattern on a serving plate and thinly butter this bottom layer. Cover completely with the shrimp spread. Thinly butter the bottoms of four more bread slices, then place on top of the shrimp layer. Thinly butter the tops of the slices. Cover completely with the chunky avocado spread. Thinly butter the bottoms of four more bread slices, then place on top of the avocado spread. Thinly butter the tops of the slices. Cover completely with the smoked salmon spread. Thinly butter the bottoms of the final four slices and place on top of the smoked salmon layer. Refrigerate any remaining cream-cheese spread.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D4C628AEB82ED64F82EB7C88EFCB7E6F', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 5, NULL, NULL, 2, NULL, 0, 'If possible, cover the smrgstrta with plastic wrap and refrigerate overnight.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'294E65D5A6CD304483F1157E902EEBBD', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 6, NULL, NULL, 2, NULL, 0, 'If not possible, refrigerate for at least three hours to allow the flavors to blend (it will still taste fabulous!).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9C6C484F9774664EBD8D03340C48A312', X'7D67229F0391D6448EBEFC9AE0212354', X'7D67229F0391D6448EBEFC9AE0212354', 7, NULL, NULL, 2, NULL, 0, 'One hour before serving, frost the top and sides of the smrgstrta with the remaining cream cheese spread. Then, use your creativity to decorate with 1 cup cooked baby shrimp (pile it high for effect!), cucumber slices, lemon slices, halved cherry tomatoes, endive, chopped dill, capers, and caviar paste or anchovy paste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5266C55737A9AC4583412F1DEBC93526', X'08BCBF86D1FD2446B79220200CD070BB', X'08BCBF86D1FD2446B79220200CD070BB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C68E3B32D62A44496F240B61AF69C4E', X'08BCBF86D1FD2446B79220200CD070BB', X'08BCBF86D1FD2446B79220200CD070BB', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 400 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE150904187C704494339443E220AA0C', X'08BCBF86D1FD2446B79220200CD070BB', X'08BCBF86D1FD2446B79220200CD070BB', 2, NULL, NULL, 2, NULL, 0, 'Boil peppers in boiling salted water for 5 minutes; drain well. Combine onion, 1/2 cup bread crumbs, egg, milk, salt and pepper, lemon juice, and salmon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97495CFB137AD3488A5A1B28BE802E4B', X'08BCBF86D1FD2446B79220200CD070BB', X'08BCBF86D1FD2446B79220200CD070BB', 3, NULL, NULL, 2, NULL, 0, 'Fill pepper shells.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9454C2EAA885E458DA816DA6826FD8B', X'08BCBF86D1FD2446B79220200CD070BB', X'08BCBF86D1FD2446B79220200CD070BB', 4, NULL, NULL, 2, NULL, 0, 'Mix melted butter with remaining 1/2 cup breadcrumbs and Parmesan cheese; sprinkle over stuffed peppers.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'648F971B4C2E5E478F275510E8A600C8', X'08BCBF86D1FD2446B79220200CD070BB', X'08BCBF86D1FD2446B79220200CD070BB', 5, NULL, NULL, 2, NULL, 0, 'Place in a shallow baking dish in preheated oven for 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C194D639EDE9648BAB5AC0BA7E0299B', X'08BCBF86D1FD2446B79220200CD070BB', X'08BCBF86D1FD2446B79220200CD070BB', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04F8FC9B33668D4F9375BAAAD22A165A', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C2090801D5DF346967A2727CE8256D1', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 1, NULL, NULL, 2, NULL, 0, 'With the help of a sharp knife, cut the fish into large chunks about 1-inch thick. Generously sprinkle the pieces of fish with salt on both sides.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4552A0898ADF1947B40356A811B9B9A7', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 2, NULL, NULL, 2, NULL, 0, 'In a deep saucepan, mix the infused oil ingredients. Slowly heat the oil mixture over medium heat until you begin to see a gentle simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'29853AF396FD3B418218096D82437C9E', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 3, NULL, NULL, 2, NULL, 0, 'With the help of a food thermometer check that the oil is close to 160 F. Hold this temperature for about 15 minutes and then turn off the heat. Cover the pot and leave the oil to cool off for approximately 45 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C81081C42FC8D4CAF9778CCB4A3D9C9', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 4, NULL, NULL, 2, NULL, 0, 'Once the oil has cooled, turn the heat back on to medium and heat it up again to 150 F. Once you''ve reached this temperature, slip in the fish and arrange the pieces in one layer, so the fish is completely covered in oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63E354901952BB43BA86A2BC4CB635BD', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 5, NULL, NULL, 2, NULL, 0, 'Turn off the heat and cover the pot again. Let the fish steep in the oil for another 30 to 45 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C94D03588D5A5C43A576122E2A0850FE', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 6, NULL, NULL, 2, NULL, 0, 'After the fish has steeped in the hot oil, use a slotted spoon to remove it and transfer it to clean plastic or glass container.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D43BDFC4371B854A8227EEC085D97E42', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 7, NULL, NULL, 2, NULL, 0, 'Let the oil cool to room temperature, then strain it through a piece of cheesecloth or fine mesh and pour it over the fish. Make sure you do not include any liquid that the fish gave off during cooking. You want oil only to cover the fish. If your infused oil seems too contaminated with fish bits, use clean olive oil if needed. With just oil covering it, the fish will last in the refrigerator for two weeks.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'908DD360088B2749B5C5981CFD0554A9', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', X'9D8B6A4081DA044CBBD1E9EA0808D7FC', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA385233741F014AB04F75F669EC6B44', X'06404C9F2BC38047A9AF250D540249A9', X'06404C9F2BC38047A9AF250D540249A9', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 400 F (200 C/Gas 6). Butter a 2-quart baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'755D002394C5BE42A369DCE3AC0DC908', X'06404C9F2BC38047A9AF250D540249A9', X'06404C9F2BC38047A9AF250D540249A9', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium-low heat, cook onion and celery in 4 tablespoons of butter until tender, about 5 minutes. Stir in flour, pepper, and dry mustard. Continue cooking, stirring, until smooth and just bubbly. Add Worcestershire sauce and gradually stir in milk. Cook, stirring, until thickened. Add about half of the cheese to the sauce mixture; stir until melted. Add flaked tuna and peas. If desired, stir in the pimientos. Taste and add salt, as needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD978258CFF70E41AB4BFEE0F1DE4E11', X'06404C9F2BC38047A9AF250D540249A9', X'06404C9F2BC38047A9AF250D540249A9', 2, NULL, NULL, 2, NULL, 0, 'Spoon half of the hot rice into the prepared baking dish and spread about half of the remaining cheese over the rice; top with half of the hot tuna mixture, then repeat layers. Melt remaining 2 tablespoons of butter and toss with the bread crumbs. Sprinkle crumbs over the top.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65F5CCF4B6B22647AE1CE38F33B2C398', X'06404C9F2BC38047A9AF250D540249A9', X'06404C9F2BC38047A9AF250D540249A9', 3, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 20 minutes or until the bread crumb topping is lightly browned and the casserole is bubbly around the edges.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4050514E32201D4FAC227456193A31FC', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9936CE21E3E68D49B47A4D3964287527', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 1, NULL, NULL, 2, NULL, 0, 'Hard boil the eggs. Allow them to cool in cold water. Peel and cut the eggs into quarters or slice into thick slices and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC0407F4803D714288CECEADCB89E183', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 2, NULL, NULL, 2, NULL, 0, 'Cut the lettuce head in half. Rinse the lettuce under cold water and allow to drain. While it is draining, prep the vegetables.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'238C3B335242364FAB73C2D2CEBFFEA7', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 3, NULL, NULL, 2, NULL, 0, 'Quarter the tomatoes (so you have 8 pieces). Peel and slice the cucumber. Remove the stem and seeds from the peppers and slice them into long, thin strips. Thinly slice the onion and grate the carrot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F629574C4557AD4D9D23FC4666969A25', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 4, NULL, NULL, 2, NULL, 0, 'Open the cans of tuna, white asparagus, and artichoke hearts and drain each one well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EE02ADFAF729C46B10CDBCAE5B9D554', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 5, NULL, NULL, 2, NULL, 0, 'Break up the lettuce into small pieces for a salad. Make a bed of lettuce on a large platter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C8E49BE4F0C9D448CC529FD3F25919C', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 6, NULL, NULL, 2, NULL, 0, 'On top of the bed, place the tomatoes, cucumbers, onions, peppers, and carrots.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'40A7F47D8C1956408D3EFB341271508C', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 7, NULL, NULL, 2, NULL, 0, 'Spread the tuna around the bed of lettuce, breaking it up into small chunks with a fork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'738D7AF10D6573418441B61FB1FA6968', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 8, NULL, NULL, 2, NULL, 0, 'Place the reserved egg slices, asparagus, artichoke hearts, olives, and baby corn ears (if using) on top.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B87A356F49B5154F87E110FD378F8586', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 9, NULL, NULL, 2, NULL, 0, 'Dress with oil and vinegar and sprinkle with salt to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'287A6306EF9E564188BC3CF6C7DEBE97', X'68C7E69F4E0CE14BBCA8EC5EA2732447', X'68C7E69F4E0CE14BBCA8EC5EA2732447', 10, NULL, NULL, 2, NULL, 0, 'Serve cold with crispy artisan bread if you like.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12CF7D0BB7F6CF4AB0470D42F2FEAB8A', X'C1335A0C10777D49BB1D1C393F64C3B0', X'C1335A0C10777D49BB1D1C393F64C3B0', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FEB6DC56CDF67E4AB8BB41C38E7D38B7', X'C1335A0C10777D49BB1D1C393F64C3B0', X'C1335A0C10777D49BB1D1C393F64C3B0', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B22501CE6E2274EBC6F3550384912E2', X'C1335A0C10777D49BB1D1C393F64C3B0', X'C1335A0C10777D49BB1D1C393F64C3B0', 2, NULL, NULL, 2, NULL, 0, 'Spray a 2 1/2- to 3-quart casserole dish with cooking spray.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60F6E0C2232B0F4C82FDDC517EB16865', X'C1335A0C10777D49BB1D1C393F64C3B0', X'C1335A0C10777D49BB1D1C393F64C3B0', 3, NULL, NULL, 2, NULL, 0, 'Combine the noodles, soup, peas, cheese, tuna, and milk. Add salt and freshly ground black pepper. Spoon into the prepared baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CAA2D0B142FC3C4AA808C0E727893C65', X'C1335A0C10777D49BB1D1C393F64C3B0', X'C1335A0C10777D49BB1D1C393F64C3B0', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, toss the breadcrumbs with the melted butter and sprinkle over the top of the casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F0B0D2A62ED4748973F55D574F8B3C0', X'C1335A0C10777D49BB1D1C393F64C3B0', X'C1335A0C10777D49BB1D1C393F64C3B0', 5, NULL, NULL, 2, NULL, 0, 'Bake for 15 to 20 minutes until hot and bubbly and the breadcrumbs are browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F74B12CD26FBAB4BAD4FBF0B6D326F0A', X'C1335A0C10777D49BB1D1C393F64C3B0', X'C1335A0C10777D49BB1D1C393F64C3B0', 6, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77DF1FEE926F524E9ACBF11C8EF74A82', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'487B4C0CD8EF5445BAAA80124EE59A3B', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 375 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CFAE9C8828C674190B2E019FA99B592', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 2, NULL, NULL, 2, NULL, 0, 'Lightly butter a 2-quart casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C3F9F03EF534A949A6FF11BD22505DA9', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 3, NULL, NULL, 2, NULL, 0, 'Melt the butter in a saucepan or saute pan over low heat; cook the mushrooms, celery, and chopped onion until tender and onion is translucent.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D90CF45D4A51643A946EE87B2546C4F', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 4, NULL, NULL, 2, NULL, 0, 'Blend in the flour and cook, stirring, for 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'996BA44CB63735499077DFEAAEE8194D', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 5, NULL, NULL, 2, NULL, 0, 'Remove from the heat and gradually stir the milk into the flour mixture.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0333521432E7164EB77A5B7687814C43', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 6, NULL, NULL, 2, NULL, 0, 'Add salt and cayenne pepper; return to heat and cook, stirring constantly, until thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87960A1592BACF499E527FCD53553827', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 7, NULL, NULL, 2, NULL, 0, 'In a bowl, combine tuna with the cooked rice and all but 2 tablespoons of the shredded cheese. Add the sauce mixture and gently blend.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F14D225D57DFA24CB1D618282217DA0F', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 8, NULL, NULL, 2, NULL, 0, 'Turn the rice and tuna mixture into the prepared casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E293B943913F9F409BC8C260387DBD32', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 9, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter; toss the melted butter with the cracker crumbs or bread crumbs.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F589CAF46A08CA4FA272018851ED39C4', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle crumbs and remaining cheese over casserole. Sprinkle with paprika.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31554F616886614287B20E14E70D2E2F', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 11, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for about 30 minutes, until hot and bubbly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CCEFB08D8DBEE47BF318D517A92BA8F', X'6026BD77A9E7A5469922D0B6DF309734', X'6026BD77A9E7A5469922D0B6DF309734', 12, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F45B45F5F713543AEF91D8581661061', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAAB6EFBB3532342A93C420F7F2F7471', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 1, NULL, NULL, 2, NULL, 0, 'Place the cided cucumber into a bowl and sprinkle with 1/2 teaspoon salt and stir then leave for 5 minutes. Rinse quickly under running water and dry on kitchen paper. Keep to one side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'602CB5515D459845AF61C2B27874D675', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 2, NULL, NULL, 2, NULL, 0, 'Boil potatoes in lightly salted water for 3 minutes maximum. Drain and dry with kitchen paper, then season with a tiny sprinkle of salt and pepper. The potatoes must be very dry before they are used.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6EFED883D3D07469E365A8B20CCFECD', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 3, NULL, NULL, 2, NULL, 0, 'Use a sharp knife to cut through the grain lines of the tuna to create even slices, then cut into cubes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'578193A02A59D04A9D5406D67C5BFDB7', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 4, NULL, NULL, 2, NULL, 0, 'In a bowl, mix the tuna and all the diced vegetables, chopped parsley, and mayonnaise. Do this carefully so as to not break up the vegetables.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D0836FDF55185449AD29821BFEFF2C9', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 5, NULL, NULL, 2, NULL, 0, 'Place a 3-inch pastry ring in the center of a serving plate and spoon vegetable and fish mixture into it. Press down firmly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54DE3024E2E896499E3D1F270CBE7FF8', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 6, NULL, NULL, 2, NULL, 0, 'Press a small indentation in the center of the tartare and place a large tablespoon of sour cream into it. Smooth the sour cream with a spatula or knife.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D18BCDE4D6B694EB557B1383226149B', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 7, NULL, NULL, 2, NULL, 0, 'Loosen the ring and remove it very carefully. Lightly whisk together the vinaigrette ingredients and drizzle lightly on and around the tartare. Decorate with the tomatoes and fresh herbs (if using) and a little extra pepper. The tartare also works really well with slices of avocado for a more substantial dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A74D50949C90A439A08106687F12579', X'A8D920A4768F48439C56B4AF334289D5', X'A8D920A4768F48439C56B4AF334289D5', 8, NULL, NULL, 2, NULL, 0, 'Serve the tartare immediately, or if serving later, cover very lightly with plastic wrap. Eat on the same day, this will not keep longer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54F738D07715864B98F321EFAE54D9A1', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F75896F6F0D9E4395BD0C780725EAB6', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 1, NULL, NULL, 2, NULL, 0, 'Place the veal in a large, nonreactive bowl with the bay leaves, cloves (if using), sage, and celery; pour the wine over. Cover and marinate in the refrigerator for 24 hours, turning the meat occasionally.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74E98E3B68ECE040B2DAA01147FCD3F8', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 2, NULL, NULL, 2, NULL, 0, 'The next day, transfer the meat to a Dutch oven. Strain the spices and celery out of the wine, then add the wine to the Dutch oven, plus enough water to cover the meat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8AEE7A63A33194FAA5D5A7F0F6AF412', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 3, NULL, NULL, 2, NULL, 0, 'Add a little salt and simmer the meat for 1 hour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D744E3EBF8964845BF4610D85ED179B0', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 4, NULL, NULL, 2, NULL, 0, 'In the meantime, wash, scale, and bone the anchovies.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'738458DFE407654EA715C8BDACE4EE7D', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 5, NULL, NULL, 2, NULL, 0, 'When the hour is up, add the anchovies to the pot and continue simmering for another 30 minutes; the liquid should be reduced by half.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80F97EC8B36BD7448F486278D433CCD8', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 6, NULL, NULL, 2, NULL, 0, 'Place the eggs in a small pot with 1 inch of cold water to cover. Bring the water just to a rolling boil, then cover the pot, remove it from the heat, and let sit for 10 minutes to hard-boil the eggs. Drain the eggs, rinse them under cold water, then let them sit in cold water for a few minutes until cool enough to peel.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9F692587ACBF54BBE1FCAB1F37118AF', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 7, NULL, NULL, 2, NULL, 0, 'Separate the yolks from the whites. (The whites will not be used in this recipe; you may discard them or set them aside for another purpose.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E29C7BADCB7ACF408EC61B331C957501', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 8, NULL, NULL, 2, NULL, 0, 'Rinse, dry, and finely mince the capers.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6F0B8944967B8B4094D2BE0D8043980D', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 9, NULL, NULL, 2, NULL, 0, 'When the meat is fork-tender, remove it from the heat. Strain the broth into a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60C9CE911CB0574EA071E8B8DA493688', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 10, NULL, NULL, 2, NULL, 0, 'Transfer the anchovy fillets to a food processor; add the tuna, hard-boiled yolks, olive oil, vinegar, and lemon juice and process until creamy. Dilute the sauce to taste with some of the reserved broth and process again. Stir in the minced capers.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24A8A7355320CF45AF85BE4520CEBD5C', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 11, NULL, NULL, 2, NULL, 0, 'When the veal has cooled, slice it thinly and lay the slices out on one or more platters. Spread the sauce over the meat and garnish the platters with the lemon slices, capers, and parsley. Cover with plastic wrap and chill in the refrigerator before serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F524C3C45277E34FAAECD8FC604C414A', X'0D2A9D86E2956C499A47E8B2D34DEA2F', X'0D2A9D86E2956C499A47E8B2D34DEA2F', 12, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E845534D4115844881CEBE8C97622F3', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E896AD210BB0D478D342A137B6F8C4F', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 1, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F/180 C. Lightly butter a 2-quart baking dish or spray with cooking spray.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8830C55EFBAEFF4E908782A9B3865D40', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 2, NULL, NULL, 2, NULL, 0, 'Cook the noodles in boiling salted water following package directions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2C58C487A89C0428B0BBA990E2B861F', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 3, NULL, NULL, 2, NULL, 0, 'Drain in a colander and rinse under hot water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38AC59FF51511B46BA2E35C00E98DCD8', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 4, NULL, NULL, 2, NULL, 0, 'Meanwhile, melt the butter in a medium saucepan over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EEF2B2EAA9A8243A79CDFD306AAEE98', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 5, NULL, NULL, 2, NULL, 0, 'Add the sliced mushrooms and saute until golden brown and tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA138BFE69B4D54AAB2758CE243F7BE6', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 6, NULL, NULL, 2, NULL, 0, 'Add the flour. Cook, stirring, for 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F27795380639C14BB6A991493BDCF430', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 7, NULL, NULL, 2, NULL, 0, 'Gradually add the milk and continue to cook, stirring, until thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6723321B5B04404A86B90DF78C9946AE', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 8, NULL, NULL, 2, NULL, 0, 'Stir in the mayonnaise, frozen peas, tuna, and drained pimiento. Bring to a simmer and continue cooking, stirring, for 1 minute longer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83E95191B8F8D54B803085696DCCC683', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 9, NULL, NULL, 2, NULL, 0, 'Combine the sauce mixture with the noodles and stir to blend ingredients thoroughly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CD4ADC8FF613949B6D3EAA94A9DDCF6', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 10, NULL, NULL, 2, NULL, 0, 'Turn the mixture into the prepared baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7326ABED9EF0A8478A59536886FA2A0A', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 11, NULL, NULL, 2, NULL, 0, 'In a small bowl, make the topping by combining the breadcrumbs with the melted butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC7D2F1BC35DAC42996AE62B0CFD61C3', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 12, NULL, NULL, 2, NULL, 0, 'Sprinkle the buttered breadcrumbs over the casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAE3D62BE7C6194FA6A85831E488709F', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 13, NULL, NULL, 2, NULL, 0, 'Bake for 25 minutes, or until the casserole is bubbly and topping is browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'188C9E97109AA7439A7D32A67E117B90', X'0BFFDA7D257B3A42B7ED642031BAF994', X'0BFFDA7D257B3A42B7ED642031BAF994', 14, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CED8BD5E99AA564DB4672BE26A7A86FD', X'7D857523C022004DA275A688CBE9F22C', X'7D857523C022004DA275A688CBE9F22C', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BB1907834E9B54E880D57B0EFA89DB7', X'7D857523C022004DA275A688CBE9F22C', X'7D857523C022004DA275A688CBE9F22C', 1, NULL, NULL, 2, NULL, 0, 'Drain the peppers and pat dry with a paper towel. Finely chop the olives. Dice the green onions and include a part of the green stock. Finely chop 1 sprig of the parsley.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'90D457F96A849842AF4BF3EACD88EE7C', X'7D857523C022004DA275A688CBE9F22C', X'7D857523C022004DA275A688CBE9F22C', 2, NULL, NULL, 2, NULL, 0, 'Drain the tuna. In a small bowl, break the tuna into flakes using a fork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1AE7BEE2AFCD2B488FB0C66EE0ABDF74', X'7D857523C022004DA275A688CBE9F22C', X'7D857523C022004DA275A688CBE9F22C', 3, NULL, NULL, 2, NULL, 0, 'Add the chopped green onions and parsley to the tuna. Sprinkle with vinegar and sweet paprika. Drizzle oil on top and mix thoroughly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B6D6BD4B9E535449B56DA1F303663E95', X'7D857523C022004DA275A688CBE9F22C', X'7D857523C022004DA275A688CBE9F22C', 4, NULL, NULL, 2, NULL, 0, 'Carefully open each pepper and spoon in the tuna mixture. Arrange on a serving plate. Garnish with the parsley sprig, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4E5F7BC0C2FF648A0A2C834CC87684C', X'66114ECF05BE63459335357811DD0309', X'66114ECF05BE63459335357811DD0309', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CA0791C92886E48A6614F29369CED5A', X'66114ECF05BE63459335357811DD0309', X'66114ECF05BE63459335357811DD0309', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the mayonnaise, sour cream, cream, and lemon or lime juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3589A52BBC18214C90B61E9A77818C48', X'66114ECF05BE63459335357811DD0309', X'66114ECF05BE63459335357811DD0309', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, stir together the onions and cilantro. Set this garnish and the cream sauce aside for the moment.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C38133540975542BD9F528F62EC42D9', X'66114ECF05BE63459335357811DD0309', X'66114ECF05BE63459335357811DD0309', 3, NULL, NULL, 2, NULL, 0, 'Heat oil in a small pan and saut the tuna, stirring often, until tuna is heated through and very slightly browned. Take the pan off the heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B0396303616B704CA9C20F689B000838', X'66114ECF05BE63459335357811DD0309', X'66114ECF05BE63459335357811DD0309', 4, NULL, NULL, 2, NULL, 0, 'If using soft tortillas, heat a griddle over high heat and place them on it. Heat the tortillas, turning once or twice in the process, until they are very hot. For hard taco shells, place them on a baking sheet and heat until crispy.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6505859CF811CD459C2BFB847B6E75FA', X'66114ECF05BE63459335357811DD0309', X'66114ECF05BE63459335357811DD0309', 5, NULL, NULL, 2, NULL, 0, 'Place 1/4 of the tuna mixture onto each tortilla, then top each with some of the onion-cilantro mix, and a drizzle of cream sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7BB726E85E32544A71F20E0984199C6', X'66114ECF05BE63459335357811DD0309', X'66114ECF05BE63459335357811DD0309', 6, NULL, NULL, 2, NULL, 0, 'Serve tacos immediately. Offer Mexican table sauce, bottled hot sauce, or other condiments of your choice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A3B5674A23387F4082AC480B73CC5F81', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C7ED50446ABFE542956EE082D7D7A60B', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 1, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F. Butter a 2-quart casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C94D11B5A26174994F99FC6356EAD9B', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 2, NULL, NULL, 2, NULL, 0, 'Cook macaroni in boiling salted water according to package directions until tender, drain and rinse.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAD08A394DA76A44824814B5ED192873', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 3, NULL, NULL, 2, NULL, 0, 'Melt 4 tablespoons of the butter in a large saucepan or Dutch oven; add chopped onions and bell pepper and saut over low heat for about 3 to 4 minutes, until tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD6D92767F379048A7E08A78A0DEBF1F', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 4, NULL, NULL, 2, NULL, 0, 'Add flour, salt, and pepper; cook, stirring, for about 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32B63521FC1C16409E9DEE584C5AECF9', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 5, NULL, NULL, 2, NULL, 0, 'Add milk and condensed soup and stir over low heat until the sauce is smooth and thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74FAAE46CC8CA34A8C99394CF3765AD9', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 6, NULL, NULL, 2, NULL, 0, 'Add cooked drained macaroni, peas, flaked tuna, and about half of the cheese to sauce mixture, stirring constantly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CF9139323E7014C96BD89315026ECF7', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 7, NULL, NULL, 2, NULL, 0, 'Pour the mixture into the prepared casserole; top with remaining shredded cheese.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D3DEB1CA7B3404D9578538710737437', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 8, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter and toss with the breadcrumbs until thoroughly coated.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBE7C141DCAAA14391EA213229983A27', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 9, NULL, NULL, 2, NULL, 0, 'Scatter the crumbs over the cheese layer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10C549612FA6B04F8445D099A71B51C7', X'9236FEF8F171A440ABFE9A15B404EE67', X'9236FEF8F171A440ABFE9A15B404EE67', 10, NULL, NULL, 2, NULL, 0, 'Bake the casserole for 30 to 40 minutes, or until the crumb topping is nicely browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5BADA4E397E984CA010FBD2BD72806B', X'0A74FBF37A279C49891D21C34BC9B60E', X'0A74FBF37A279C49891D21C34BC9B60E', 0, NULL, NULL, 2, NULL, 0, 'Buy a piece of fish you can later cut, across the grain of the meat, into squares or rounds of at least two inches across. You may need to buy more fish than you need to get the presentation right. Use the excess in either a tartare or a stir-fry.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B444D720B0512B45AB2C6AD286784E61', X'0A74FBF37A279C49891D21C34BC9B60E', X'0A74FBF37A279C49891D21C34BC9B60E', 1, NULL, NULL, 2, NULL, 0, 'Wrap the fish tightly in plastic and put it in the freezer for an hour or more. After an hour, check to see if it is hardbut not solid. When it reaches that state, take it out and get your sharpest knife.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B289881487FBE149ADAAE8B777540B3A', X'0A74FBF37A279C49891D21C34BC9B60E', X'0A74FBF37A279C49891D21C34BC9B60E', 2, NULL, NULL, 2, NULL, 0, 'Slice the fish across the grain as thin as you can. I shoot for a cut of about 1/8-inch. Do not pound it to make it flatter. (That''s carpaccio, not crudo).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'034BD0B2D853F245B0E59125E9CCDABF', X'0A74FBF37A279C49891D21C34BC9B60E', X'0A74FBF37A279C49891D21C34BC9B60E', 3, NULL, NULL, 2, NULL, 0, 'Coat the slices in the olive oil and set in a sealed container in the refrigerator over a bed of ice for at least 20 minutes, or up to an hour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'796994BD03A2FE4584BD416580247E7D', X'0A74FBF37A279C49891D21C34BC9B60E', X'0A74FBF37A279C49891D21C34BC9B60E', 4, NULL, NULL, 2, NULL, 0, 'To serve, take the fish out of the fridge. On the plate, pour some olive oil (which you did not use in the marinade) on the plate and arrange the slices over it. Each guest should get a quarter of the lemon and a tiny bowl of the sea salt, or you can salt the fish right before serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2BF22C92CCBAF04BAF8900D7D966C423', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 0, NULL, NULL, 2, NULL, 0, 'Cook the noodles in boiling salted water following the package directions. Drain well and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D34AB3E7A88054093F44755CC3630DD', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 1, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6AE39EC34FF8F428739B8B2AE926C3D', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 2, NULL, NULL, 2, NULL, 0, 'Lightly butter a 2 to 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E222FE043EEDDF41BC0F7CBA4350DCE5', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 3, NULL, NULL, 2, NULL, 0, 'In a large saucepan, melt 3 tablespoons of butter over medium-low heat. Add the green onions and saute for 1 minute. Stir in flour and continue cooking, stirring, for 2 minutes. Gradually add the milk; cook, stirring, until thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA06463917108F4AB36BFA20E9D30F7A', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 4, NULL, NULL, 2, NULL, 0, 'Add the seasonings to the milk along with the artichoke hearts, olives, pimientos, mushrooms, and 1/2 cup Parmesan cheese. Cook the sauce, stirring, until the Parmesan cheese has melted. Add the flaked tuna; taste and add salt, as needed. Stir in noodles.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C29CD27ED4024944B3C22C6AC0DE21BA', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 5, NULL, NULL, 2, NULL, 0, 'Spoon the mixture into the prepared baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E70A7AE7B2D0554CB1D48ED573F5A102', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 6, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter; toss with the bread crumbs until well coated. Add 1 or 2 tablespoons of grated Parmesan cheese to the breadcrumbs. Sprinkle the crumbs over the casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6B6EA84610B634184E2BD7EFED8B768', X'68184C69EAB2CD468856E622542F333E', X'68184C69EAB2CD468856E622542F333E', 7, NULL, NULL, 2, NULL, 0, 'Bake for 25 to 30 minutes, until hot and bubbly and the topping is browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'808A794406FE6D4D853065C0C2C40697', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F94B8051596A1F4EAE7A0086CC88E3F2', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', 1, NULL, NULL, 2, NULL, 0, 'Arrange the split muffins on a cookie sheet which has been lined with foil or a silicone baking mat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'64212CDE76224A45834ED68CB2194182', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine tuna with celery, 2 to 4 tablespoons green onion, and mayonnaise; blend all ingredients well. Stir in salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63580E112615994593694D93BDA0CEE2', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', X'F2BFFCE5D6A3B54DBBA8ABB1E94F264D', 3, NULL, NULL, 2, NULL, 0, 'Spoon tuna mixture evenly over the muffins. Top each sandwich with a thin slice of tomato. Bake for 8 minutes; top with cheese slices then bake for about 2 minutes longer, or until cheese is melted. Sprinkle each sandwich with green onion and a freshly ground black or seasoned pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'894A4DC9E3B88146AC9496B1833F3D07', X'042A108433C9014681EAFB69CDA1E206', X'042A108433C9014681EAFB69CDA1E206', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 375 F. Grease an 8-inch square baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F07D3018C026CE488F5B8030870A0865', X'042A108433C9014681EAFB69CDA1E206', X'042A108433C9014681EAFB69CDA1E206', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the soup and water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88E674B14001094ABF626B8912F93E06', X'042A108433C9014681EAFB69CDA1E206', X'042A108433C9014681EAFB69CDA1E206', 2, NULL, NULL, 2, NULL, 0, 'Add 1 cup of chow mein noodles, finely chopped onion, pepper, tuna, and celery. Toss lightly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D38CC47468A6024BAC356C08D7B40884', X'042A108433C9014681EAFB69CDA1E206', X'042A108433C9014681EAFB69CDA1E206', 3, NULL, NULL, 2, NULL, 0, 'Turn the mixture into the prepared baking dish. Sprinkle remaining 1 cup chow mein noodles over the top.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22505B7637BE0243B42560D70DF3619D', X'042A108433C9014681EAFB69CDA1E206', X'042A108433C9014681EAFB69CDA1E206', 4, NULL, NULL, 2, NULL, 0, 'Bake for 30 minutes and serve hot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F34183F3CA02840B0B1D01599F5C31D', X'A4F84FAAB287F34886623EEC6554CD7B', X'A4F84FAAB287F34886623EEC6554CD7B', 0, NULL, NULL, 2, NULL, 0, 'Cook the pasta in boiling salted water following package directions; drain, rinse, and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'49F89EA75C92B04A842600B8D8073476', X'A4F84FAAB287F34886623EEC6554CD7B', X'A4F84FAAB287F34886623EEC6554CD7B', 1, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the tuna, finely chopped onion, minced garlic, bell pepper, dill, and lemon juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E75121D1CA5D5D4C8A494DF705C9740A', X'A4F84FAAB287F34886623EEC6554CD7B', X'A4F84FAAB287F34886623EEC6554CD7B', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl or cup, combine 3/4 cup of the mayonnaise and the sour cream.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE56C97B85F8E94686869700EC3864C4', X'A4F84FAAB287F34886623EEC6554CD7B', X'A4F84FAAB287F34886623EEC6554CD7B', 3, NULL, NULL, 2, NULL, 0, 'Put the pasta in the bowl with the vegetables, add the mayonnaise mixture, and gently toss to combine. Add more mayonnaise if necessary to reach desired consistency.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C3B6829A694DF44BB0417FC6C22CD89', X'A4F84FAAB287F34886623EEC6554CD7B', X'A4F84FAAB287F34886623EEC6554CD7B', 4, NULL, NULL, 2, NULL, 0, 'Add the sliced tomatoes and stir gently. Taste and add salt and pepper to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7D1628263D648F4ABA7C90186CAB8177', X'752D8EA7E8A76D458C3101DBA48C49C8', X'752D8EA7E8A76D458C3101DBA48C49C8', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A03A1182ECA964EBB02FAF5BF83ADBB', X'752D8EA7E8A76D458C3101DBA48C49C8', X'752D8EA7E8A76D458C3101DBA48C49C8', 1, NULL, NULL, 2, NULL, 0, 'Grill tuna on the grill, stovetop grill pan, or broil for just 3 to 4 minutes on each side. The tuna should be rare to medium rare for best flavor.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10A3A0EB298DB54680C3A04F96FBA824', X'752D8EA7E8A76D458C3101DBA48C49C8', X'752D8EA7E8A76D458C3101DBA48C49C8', 2, NULL, NULL, 2, NULL, 0, 'Cook the new potatoes in boiling salted water until just tender, about 10 to 15 minutes. Drain, rinse with cold water and then slice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'21BA2F5B36400949A55BBEB131558471', X'752D8EA7E8A76D458C3101DBA48C49C8', X'752D8EA7E8A76D458C3101DBA48C49C8', 3, NULL, NULL, 2, NULL, 0, 'Cook green beans in boiling salted water until just tender but still vibrant green, about 2 minutes. Drain and rinse with cold water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C299F307E552A149B693DC72BDC09B32', X'752D8EA7E8A76D458C3101DBA48C49C8', X'752D8EA7E8A76D458C3101DBA48C49C8', 4, NULL, NULL, 2, NULL, 0, 'Arrange spinach leaves, hard cooked eggs, and potato slices on the salad plates. Layer the sliced grilled tuna, green beans, red onion, and chopped olives to make a mound, repeating layers until ingredients are used. Garnish the plate with tomato wedges.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'657DEFDD2FBE434BA202F323DEB0ABBE', X'752D8EA7E8A76D458C3101DBA48C49C8', X'752D8EA7E8A76D458C3101DBA48C49C8', 5, NULL, NULL, 2, NULL, 0, 'Combine dressing ingredients in a blender or screw-top jar and use processor or shake until well blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E1735921B67F4A8612846466E81681', X'752D8EA7E8A76D458C3101DBA48C49C8', X'752D8EA7E8A76D458C3101DBA48C49C8', 6, NULL, NULL, 2, NULL, 0, 'Serve the salad drizzled with the dressing or serve it on the side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB49AF301FE6D846B21BDC17D46DF613', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 0, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFE74C743CDDA44EBD85D41735880079', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 1, NULL, NULL, 2, NULL, 0, 'Grease a 1 and 1/2-quart casserole or baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D355420962E14244A163F7F50CA61A13', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 2, NULL, NULL, 2, NULL, 0, 'Put 2 tablespoons of butter in a large skillet over medium heat; add onions and peppers.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'185345690F9F834AA238384DB610144E', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 3, NULL, NULL, 2, NULL, 0, 'Cook until onions are softened. Add flour, milk, and soup; stir until thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4417E130A54FF042A53A45CE81B072BE', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 4, NULL, NULL, 2, NULL, 0, 'Add pimiento, vegetables, tuna, mixed vegetables, chopped eggs, and cooked macaroni.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DF2CEBF4ED9B4459D6041F03615EA87', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 5, NULL, NULL, 2, NULL, 0, 'Taste and add salt and pepper. Place in the prepared casserole or baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83D2D970C133B444A449ADDF32C86475', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 6, NULL, NULL, 2, NULL, 0, 'Toss the breadcrumbs with the 2 tablespoons of melted butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51CFD9DA3767B7468C181C90CEB9757D', X'FB35CFC784368340A5FAD44C3BD3A1DC', X'FB35CFC784368340A5FAD44C3BD3A1DC', 7, NULL, NULL, 2, NULL, 0, 'Sprinkle the crumbs evenly over the top and bake for about 30 minutes, or until nicely browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D7D556C34D6FC4485CDCD60C7C11985', X'1AE8D7CF8AC0F9468F8D51810037F5CD', X'1AE8D7CF8AC0F9468F8D51810037F5CD', 0, NULL, NULL, 2, NULL, 0, 'Heat oil in a large skillet and add rice and garlic. Cook and stir for 2 to 3 minutes until rice is coated with oil and starts to turn a bit golden in spots.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17176C71E4B7FB43A384621E3734F520', X'1AE8D7CF8AC0F9468F8D51810037F5CD', X'1AE8D7CF8AC0F9468F8D51810037F5CD', 1, NULL, NULL, 2, NULL, 0, 'Add wine, pepper, and 1 cup chicken broth to the skillet. Bring to a boil, then reduce heat to medium and cook mixture for 8 to 10 minutes, stirring frequently, until liquid is absorbed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E61B67BDD23E7F4788827E89F42417E1', X'1AE8D7CF8AC0F9468F8D51810037F5CD', X'1AE8D7CF8AC0F9468F8D51810037F5CD', 2, NULL, NULL, 2, NULL, 0, 'Add about 1/2 cup of the remaining broth, stirring the rice until liquid is absorbed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'729612959D6B3D408B97F4366FBF1C61', X'1AE8D7CF8AC0F9468F8D51810037F5CD', X'1AE8D7CF8AC0F9468F8D51810037F5CD', 3, NULL, NULL, 2, NULL, 0, 'Continue until the rice is tender, and the risotto is the consistency you want.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3145E7D9FA2F04B93C828E759544B47', X'1AE8D7CF8AC0F9468F8D51810037F5CD', X'1AE8D7CF8AC0F9468F8D51810037F5CD', 4, NULL, NULL, 2, NULL, 0, 'Add lemon juice, spinach, lemon peel, and tuna. Cook and stir gently until spinach is wilted.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C2DCAA75CDD4142B34472167F04D3D4', X'1AE8D7CF8AC0F9468F8D51810037F5CD', X'1AE8D7CF8AC0F9468F8D51810037F5CD', 5, NULL, NULL, 2, NULL, 0, 'Stir in butter until melted along with 1/2 cup cheese. Sprinkle with remaining cheese and serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31670905F739E84982F96A2A48850467', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3FC3C531603E6448ABC00070FCCF5234', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 1, NULL, NULL, 2, NULL, 0, 'This recipe assumes that steamed rice is cooked and available for use. If not, please make rice according to your rice cooker''s instructions, or make steamed rice stovetop with this recipe available here.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9E55E2EC86A9574AB16D9760AF10A668', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 2, NULL, NULL, 2, NULL, 0, 'Take two servings of rice and place in a medium-size bowl. Fan the rice to cool slightly and sprinkle Sushinoko brand powdered sushi vinegar over the rice to season. Using the edge of the rice paddle, `cut` the rice diagonally and toss to mix. Note, this motion prevents the rice from becoming smashed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72F9074A1E785749A7F7295EB39717B4', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 3, NULL, NULL, 2, NULL, 0, 'On individual servings plates, spread sushi rice out.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19246265DC48984493A0EA01614AFE27', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 4, NULL, NULL, 2, NULL, 0, 'Garnish sushi rice with roasted white sesame seeds.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'15CF7D0EBD91634AB7733F622C8814F0', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl add drained canned tuna. Squeeze a small amount of fresh lemon juice over the tuna and toss. Break up any large chunks of the tuna.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E872E91FF560A48A50038F6FA1FED20', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 6, NULL, NULL, 2, NULL, 0, 'Garnish the rice with sesame seeds with tuna flakes, and cucumber matchsticks.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'552AD91A2A0BA545BF83A2E3616838BF', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 7, NULL, NULL, 2, NULL, 0, 'Chiffonade shiso leaves. Stack the leaves together, roll up, then slice into thin pieces. You should end with thin pieces of shiso that resemble ribbon. Garnish the sushi with shiso.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4CBC7829B074C444AF12AA2D309C1FFC', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', X'4A4BA36D1BEBEF4B8445B71F6BEA53B2', 8, NULL, NULL, 2, NULL, 0, 'Finally, add a pinch of the sakura denbu to the center of each chirashi sushi plate. Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09C49CE6A6A90744828063390C172A7E', X'1ABE87B14244F6478E9D8FEB7E114196', X'1ABE87B14244F6478E9D8FEB7E114196', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, combine the tuna, celery, mayonnaise, lemon juice, 1/8 teaspoon salt, and a dash of pepper. Mix with a fork until well blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B53E4938177EC44E999C8325501E25A3', X'1ABE87B14244F6478E9D8FEB7E114196', X'1ABE87B14244F6478E9D8FEB7E114196', 1, NULL, NULL, 2, NULL, 0, 'Spread the tuna salad mixture on 4 slices of the bread. Cover with remaining 4 slices of bread then cut each sandwich in half diagonally.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9DD25A6F84AB24D89CA6350FF7A760C', X'1ABE87B14244F6478E9D8FEB7E114196', X'1ABE87B14244F6478E9D8FEB7E114196', 2, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk eggs with 1/2 teaspoon salt and 1/2 cup milk.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D882D70C01891E40AEDEFB81527C6708', X'1ABE87B14244F6478E9D8FEB7E114196', X'1ABE87B14244F6478E9D8FEB7E114196', 3, NULL, NULL, 2, NULL, 0, 'Carefully dip the sandwich halves in the egg mixture.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D40083BA0FA354FAB3282690C2C67F5', X'1ABE87B14244F6478E9D8FEB7E114196', X'1ABE87B14244F6478E9D8FEB7E114196', 4, NULL, NULL, 2, NULL, 0, 'Heat the butter in a skillet over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F10BDAF46D2C7847A604DE5046685922', X'1ABE87B14244F6478E9D8FEB7E114196', X'1ABE87B14244F6478E9D8FEB7E114196', 5, NULL, NULL, 2, NULL, 0, 'Fry the sandwich halves in hot butter until nicely browned on both sides.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E0DD4B2E4BB2A5419F5CAF3334716918', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', 0, NULL, NULL, 2, NULL, 0, 'Spoon rice into small Japanese donburi style bowls which are slightly larger than traditional rice bowls. Make sure the rice has cooled to almost room temperature.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C1EE5A1AA84D8C4FB9704BDCB56DD9E0', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', 1, NULL, NULL, 2, NULL, 0, 'Chop the tuna so that it is almost minced. In a medium bowl, gently combine minced tuna, 2 tablespoons Kewpie mayonnaise, 1 tablespoon Sriracha chili sauce, 1/4 teaspoon sesame oil, 1/8 teaspoon shichimi togarashi, and 5 to 6 drops Rayu chili oil, until all of the ingredients are evenly incorporated.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1EDA56737189944D91A3FD1987132466', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', 2, NULL, NULL, 2, NULL, 0, 'Split the mixture into two portions, generously spreading it over the rice in donburi bowls.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A19AB2DD1096F34EBC333B54D8A45956', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', X'37F516E8C2FE3B4B8AB5B0ACB8DC6DF2', 3, NULL, NULL, 2, NULL, 0, 'Top each spicy tuna donburi with sliced green onions, roasted sesame seeds, if using, and a drizzle of soy sauce.?');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D9C92A251F1C6A4D86EF6E94D64F1180', X'8EA9FCFCDAA83C429938EFC705881485', X'8EA9FCFCDAA83C429938EFC705881485', 0, NULL, NULL, 2, NULL, 0, 'Place chickpeas in a blender or food processor along with 1/8 cup of olive oil. Blend to roughly puree the chickpeas. Transfer to a mixing bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B03B90019BE2CE47B11D2D4F62012A40', X'8EA9FCFCDAA83C429938EFC705881485', X'8EA9FCFCDAA83C429938EFC705881485', 1, NULL, NULL, 2, NULL, 0, 'Add tuna, grated carrots, onion, garlic, cilantro, parsley, sea salt, and chile flakes. Stir well to combine.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8654DEE45620274D99051FBE18C7693B', X'8EA9FCFCDAA83C429938EFC705881485', X'8EA9FCFCDAA83C429938EFC705881485', 2, NULL, NULL, 2, NULL, 0, 'Shape mixture into small patties and then cover and refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4EFF769D12B584EAF6B360FA67427BE', X'8EA9FCFCDAA83C429938EFC705881485', X'8EA9FCFCDAA83C429938EFC705881485', 3, NULL, NULL, 2, NULL, 0, 'Remove patties from ?the fridge and then dredge in a little flour, followed by the breadcrumbs.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B00D76FD6CF5FA4197A010186138BB71', X'8EA9FCFCDAA83C429938EFC705881485', X'8EA9FCFCDAA83C429938EFC705881485', 4, NULL, NULL, 2, NULL, 0, 'Heat about 1 tablespoon of vegetable oil in a nonstick skillet and fry patties until golden brown on each side. Place patties on a paper towel to drain. If they seem a little moist, then fry them for a second time and then drain. This helps the patties keep their shape.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EEC7649F0603F4BAA72A57B522EB23D', X'8EA9FCFCDAA83C429938EFC705881485', X'8EA9FCFCDAA83C429938EFC705881485', 5, NULL, NULL, 2, NULL, 0, 'To make the dipping sauce, combine yogurt, cucumber, mint and grated garlic in a small bowl. Stir well and then serve with the tuna chickpea patties along with some lemon wedges for squeezing.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55829C8749D18347BC0DFF8ADF5B817D', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 0, NULL, NULL, 2, NULL, 0, 'Scrub the potatoes to clean off any loose dirt and sand.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A4AE861D49ECC40B83C705C1CB79110', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 1, NULL, NULL, 2, NULL, 0, 'Pour water into a large pot, cover and bring to a boil on high heat. Place potatoes in the pot and boil them with skins on until they are cooked, but not too soft. Test the doneness by pricking with a fork to make sure they are cooked, but still firm. Dont overcook the potatoes or youll end up with mashed potatoes when mixing your salad.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00F1B2BAB35E2342871E5ACEB68DB4D8', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 2, NULL, NULL, 2, NULL, 0, 'Boil the eggs until hard. Cool the eggs.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22F4831F453EF843B664E64C6260BAFC', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 3, NULL, NULL, 2, NULL, 0, 'Drain the water from the pot and add cold water to the pot, covering the potatoes. Change water every few minutes until the potatoes are cool enough to handle with your bare hands. Refrigerate for a few minutes to cool further.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99D1C72B0E39DE4B9B76FB58B78C9307', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 4, NULL, NULL, 2, NULL, 0, 'Remove from refrigerator and peel potatoes. Cut into small (approximately 1/2 inch) cubes. Return to refrigerator while you prepare the other ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0AFB3B016CFA149907CD607341255D4', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 5, NULL, NULL, 2, NULL, 0, 'Put approximately 1 1/2 cups of mayonnaise into a bowl. Slice 1 of the roasted pepper into strips about 1/3 inch wide, then cut into smaller pieces and add to bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27BF418BE2C56342868230A703E62CD9', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 6, NULL, NULL, 2, NULL, 0, 'Drain tuna thoroughly, then flake with a fork and add to bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'199AD4F663E751428F89960CEC362E1D', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 7, NULL, NULL, 2, NULL, 0, 'Drain carrots and peas and add to bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBDEF555352B41458A48D02D209D6026', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 8, NULL, NULL, 2, NULL, 0, 'Peel 1 egg, chop and add to bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD14CC402B01B04C8D86C170BF46A671', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 9, NULL, NULL, 2, NULL, 0, 'Mix all ingredients together.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EA994486235B1B4F90F44ECA4E7172D0', X'4D91CD662C4D0142892C5666C16CFAAF', X'4D91CD662C4D0142892C5666C16CFAAF', 10, NULL, NULL, 2, NULL, 0, 'Add the mayonnaise mixture to the potatoes and mix thoroughly. If necessary, add more mayonnaise. Smooth top of potato salad, preparing for decoration.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D3EA43233E0042B7845B32D32571BB', X'4B57BDD48BDC6E45A8B3E865D166B812', X'4B57BDD48BDC6E45A8B3E865D166B812', 0, NULL, NULL, 2, NULL, 0, 'Heat the oven to 350 F (180 C/Gas 4). Butter a 2-quart baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F1E523378701549AB376C7C5088493B', X'4B57BDD48BDC6E45A8B3E865D166B812', X'4B57BDD48BDC6E45A8B3E865D166B812', 1, NULL, NULL, 2, NULL, 0, 'In a large skillet or saute pan, melt 2 tablespoons of butter; saute the onion and celery until the onion is translucent. Stir in tuna, soup, mayonnaise, milk, mushrooms, parsley, and pimiento.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A67B246FACD0434EBE7E97430024112F', X'4B57BDD48BDC6E45A8B3E865D166B812', X'4B57BDD48BDC6E45A8B3E865D166B812', 2, NULL, NULL, 2, NULL, 0, 'In the prepared casserole, combine the hot cooked noodles and the tuna mixture.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22C240E7DBDCF846A871D4994F589867', X'4B57BDD48BDC6E45A8B3E865D166B812', X'4B57BDD48BDC6E45A8B3E865D166B812', 3, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter and toss with the bread crumbs.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C7A20C1702BC845B728971C98373ADF', X'4B57BDD48BDC6E45A8B3E865D166B812', X'4B57BDD48BDC6E45A8B3E865D166B812', 4, NULL, NULL, 2, NULL, 0, 'Top the casserole with the buttered bread crumbs.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3C2059A1A65E0C4B8922D73FEBFF1A1B', X'4B57BDD48BDC6E45A8B3E865D166B812', X'4B57BDD48BDC6E45A8B3E865D166B812', 5, NULL, NULL, 2, NULL, 0, 'Bake the casserole for 25 to 30 minutes, or until the topping is lightly browned and the filling is hot and bubbling around the edges.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2CC9EB258ACE8342B2BEDEC90A91ACB0', X'7C88EFEE3A48E54DB341F70092D59ADB', X'7C88EFEE3A48E54DB341F70092D59ADB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F.');
