INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'966009BFCC21E4458D4D7E70AD821831', X'2BEF69CCB9F1FC46BC4E81B322580053', X'2BEF69CCB9F1FC46BC4E81B322580053', 3, NULL, NULL, 2, NULL, 0, 'Season with the pepper, and mix again.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A978E800DD19F645A8112871D3E18604', X'2BEF69CCB9F1FC46BC4E81B322580053', X'2BEF69CCB9F1FC46BC4E81B322580053', 4, NULL, NULL, 2, NULL, 0, 'Cover and refrigerate until ready to serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB7E7BE4E09A4D4FAAAC273F2CE5B3DF', X'285EB4D9C2A019459D60BAFE597FEE13', X'285EB4D9C2A019459D60BAFE597FEE13', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D13952B705DCB6439714206DD9885A7F', X'285EB4D9C2A019459D60BAFE597FEE13', X'285EB4D9C2A019459D60BAFE597FEE13', 1, NULL, NULL, 2, NULL, 0, 'Grease a 2 1/2 to 3-quart baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3776A60A93ADC743A6EDE607010EF5D3', X'285EB4D9C2A019459D60BAFE597FEE13', X'285EB4D9C2A019459D60BAFE597FEE13', 2, NULL, NULL, 2, NULL, 0, 'Cook the spaghetti following the package directions in salted water. Drain and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DF80D7EF9006F4B953B852A67F0A924', X'285EB4D9C2A019459D60BAFE597FEE13', X'285EB4D9C2A019459D60BAFE597FEE13', 3, NULL, NULL, 2, NULL, 0, 'In a large saucepan over medium-low heat, melt 4 tablespoons of the butter. Add the mushrooms and saute, stirring, until mushrooms are browned and tender. Add the green onions and continue cooking for 1 minute. Stir in the flour until well blended. Gradually stir in the milk. Continue cooking, stirring constantly, until thickened. Add salt to taste and pepper. Stir in the flaked tuna and peas.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EECADDD55053F64587A1250EF88FE4C9', X'285EB4D9C2A019459D60BAFE597FEE13', X'285EB4D9C2A019459D60BAFE597FEE13', 4, NULL, NULL, 2, NULL, 0, 'Combine the tuna sauce mixture with the drained spaghetti until well blended. Turn the mixture into the prepared casserole. Sprinkle Parmesan cheese over the casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EB2EDDDA72D724FBCB7B68E03B3CB76', X'285EB4D9C2A019459D60BAFE597FEE13', X'285EB4D9C2A019459D60BAFE597FEE13', 5, NULL, NULL, 2, NULL, 0, 'Melt the remaining 2 tablespoons of butter and toss with the bread crumbs. Sprinkle the buttered crumbs over the Parmesan layer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'860A7E2B9D217E419C9A419E42060BD6', X'285EB4D9C2A019459D60BAFE597FEE13', X'285EB4D9C2A019459D60BAFE597FEE13', 6, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 25 to 30 minutes, until lightly browned and bubbly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D44D554EC99A1D408069357DE7BCED68', X'251553787149C34283B2908D7C74197F', X'251553787149C34283B2908D7C74197F', 0, NULL, NULL, 2, NULL, 0, 'To make tuna salad mixture, mix chopped onion, tuna, mayonnaise, and salt in a bowl. Alternatively, use your favorite tuna salad recipe. If you prefer spicy tuna salad, add some karashi mustard to the tuna salad mix.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'958593BCBF50CF43B7D3254872E7C7C6', X'251553787149C34283B2908D7C74197F', X'251553787149C34283B2908D7C74197F', 1, NULL, NULL, 2, NULL, 0, 'Cover the bamboo mat with a piece of plastic wrap to prevent the seaweed or any stray rice from sticking to the mat. This also makes for easy cleanup. Place one nori sheet on top of the bamboo mat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'655B321E00FB17419C9757566EC89888', X'251553787149C34283B2908D7C74197F', X'251553787149C34283B2908D7C74197F', 2, NULL, NULL, 2, NULL, 0, 'Spread 1/4 portion of sushi rice on top of the nori sheet. In the center, spread a line of the tuna salad horizontally across the rice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'332620AF37EA0D448C55B7B2DAD4405E', X'251553787149C34283B2908D7C74197F', X'251553787149C34283B2908D7C74197F', 3, NULL, NULL, 2, NULL, 0, 'Roll up the bamboo mat, pressing forward to shape the sushi into a cylinder. Press the bamboo mat firmly and remove it from the sushi. Repeat the process and make more tuna salad rolls.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF89F2755BC1D04A80B44ACB85269AFF', X'251553787149C34283B2908D7C74197F', X'251553787149C34283B2908D7C74197F', 4, NULL, NULL, 2, NULL, 0, 'Wipe a knife with a wet cloth before slicing sushi. Cut the sushi roll into bite-size pieces. Garnish with gari shoga and serve with soy sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C6F7A0ACCE15C4E8F45E4A44C9FC6AE', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BA2B92D797618418C3EDC1A2971AA57', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 1, NULL, NULL, 2, NULL, 0, 'About 1 to 2 hours before grilling, place the tuna in a glass bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5219FFEEC326C043941926A21FED4FE1', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 2, NULL, NULL, 2, NULL, 0, 'Combine the Dijon honey mustard, olive oil, soy sauce, and rice wine or white vinegar in a small bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F121ABF46467CA4F8B572D704338F08D', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 3, NULL, NULL, 2, NULL, 0, 'Pour over tuna and turn to make sure all pieces are well coated.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07F7385C9D7678459C299FB1F002A999', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 4, NULL, NULL, 2, NULL, 0, 'Cover the bowl with plastic wrap and refrigerate until it''s time to grill.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E1CF89984A4A14B8B47DCFC772ABA1F', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 5, NULL, NULL, 2, NULL, 0, 'Spray a clean grill or grilling pan with non-stick cooking spray or brush it with vegetable oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDF127742DE99541951EA8D90DC983DC', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 6, NULL, NULL, 2, NULL, 0, 'Grill the tuna over hot coals for about 5 to 7 minutes, or until nicely charred on the outside and still a bit pink in the middle.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C42489EEC7EAF246AAB7AAAAD6F43D0A', X'1ACF67265253744F82AAFD1CFB9C105B', X'1ACF67265253744F82AAFD1CFB9C105B', 7, NULL, NULL, 2, NULL, 0, 'Slice, serve, and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4964998FE3D13749BEDA139554CB80C4', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35735F476C2F8F40B4164BADDC767085', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 1, NULL, NULL, 2, NULL, 0, 'Drain the tuna.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'508CF9D551C9864E9AE46B9F82E74093', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 2, NULL, NULL, 2, NULL, 0, 'Transfer the tuna to a medium bowl and flake it with a fork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70F15FC31FAE254493B57FDA8F78FAD2', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 3, NULL, NULL, 2, NULL, 0, 'Finely chop the celery and hard-boiled egg and add them to the tuna along with the mayonnaise, and chopped dill pickle. If desired, add 1 or 2 tablespoons of finely chopped onion, chopped parsley, or Dijon mustard.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD1F2FCD37202E429FC786DA3D7E3F1A', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 4, NULL, NULL, 2, NULL, 0, 'Taste and add salt and pepper, as needed, and more mayonnaise, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6E50BA43E86445499A7A40F8CEF48885', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 5, NULL, NULL, 2, NULL, 0, 'Toast or grill the bread or rolls, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50915B75F23F19459128D4B0B5DA46F7', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 6, NULL, NULL, 2, NULL, 0, 'Line the bread or rolls with lettuce leaves and spread the tuna salad on the lettuce. Add a slice or two of thinly sliced tomato, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04EBBF51E0104547ADB3EFAD1631F93C', X'391E7497083DFC45BBB7980D986BEA5B', X'391E7497083DFC45BBB7980D986BEA5B', 7, NULL, NULL, 2, NULL, 0, 'Serve the tuna sandwiches with sliced dill pickles and potato chips or French fries.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CEBFC1A42448D42BE1F9F133C11818C', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 0, NULL, NULL, 2, NULL, 0, 'Bring a pot of salted water to boil. You''re going to boil the potatoes for about 4 minutes, then remove them with a slotted spoon, drain, rinse and dry them and then slice them in half. (Leave the water in the pot, though; you''ll use it for blanching the green beans in a few moments.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25593B49B60F4146BE9293F8F7ADC426', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 1, NULL, NULL, 2, NULL, 0, 'Heat a skillet with a little bit of oil until it''s quite hot, then place the sliced potatoes in the pan, cut side down.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'323E8B0F307E1646AD1B0AC7538F23B8', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 2, NULL, NULL, 2, NULL, 0, 'Let them cook for a minute or two until the potatoes are lightly browned, then remove them from the pan and transfer to a bowl. Toss with Kosher salt and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E40FF619452FC246A04455F0DDD78229', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 3, NULL, NULL, 2, NULL, 0, 'Bring the water back to a boil, and add the green beans. Blanch for two minutes, then drain and either shock them a bowl of ice water or just rinse with very cold water to stop the cooking. Then drain and dry them with paper towels and set aside. (Don''t leave them in the ice water bath or they''ll become waterlogged.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A35001A40205242B7E9EA071A05BEAB', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 4, NULL, NULL, 2, NULL, 0, 'Combine the oil, vinegar, mustard, garlic, anchovies and chopped chives in a blender. Process until smooth. Season to taste with Kosher salt and freshly ground black pepper. (The anchovies are salty, so you might not need much additional salt.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1A970671B5C104DBA1687AFE3F57389', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 5, NULL, NULL, 2, NULL, 0, 'Place the salad greens in a bowl. Drizzle two tablespoons of the dressing and toss to coat. Shake off any excess dressing. Now arrange the greens on four serving plates or one large platter. Distribute the potato halves equally, with the browned sides facing up.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FC73DD82A9E446469C2F7FE74C118635', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 6, NULL, NULL, 2, NULL, 0, 'Toss the green beans and tomatoes with enough dressing to lightly coat them, and arrange them on the serving plates or platter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66E614CF7C244B44BAB15D832C7D79FA', X'93E0833C9072F345A8373A9C3FF960FD', X'93E0833C9072F345A8373A9C3FF960FD', 7, NULL, NULL, 2, NULL, 0, 'Now divide the tuna and place it in the center of plates. Arrange the hard-boiled egg quarters, then garnish with the fresh parsley and serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CE28B0864B2AF548977A7F132679A889', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl, combine the cream cheese and whipped salad dressing and beat until smooth. Stir in the drained tuna, onions, carrot, and lemon juice and blend well to combine. At this point, you can refrigerate this mixture up to 3 days before you make the sandwiches.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'452557D101DC7447B9746BA9A6119DCB', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', 1, NULL, NULL, 2, NULL, 0, 'Spread the softened butter thinly on each slice of the bread and spread the tuna filling on half of the slices. Put the bread slices together to make sandwiches.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A50A0526BFD0F46AE06E3DB21CA0C9C', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', 2, NULL, NULL, 2, NULL, 0, 'Wrap the sandwiches in freezer wrap and place in a gallon freezer bag. Label the sandwiches with the recipe name and the date you made them and freeze.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6A83DBCF6DD554885DF88DA61B47A90', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', X'BEF2B4A0CCCF9C41AFAD69A5A9131B52', 3, NULL, NULL, 2, NULL, 0, 'To thaw, let the sandwiches thaw overnight in the refrigerator, or add to brown bag lunches (use an insulated lunch box only please!) in the morning and let the sandwiches thaw until lunchtime. Make sure that the sandwiches are eaten within 2 hours of being completely thawed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'41E6A26100D66548A3A0FB6E3B130F28', X'2B39CB57C758A643A3246004A4A5D28A', X'2B39CB57C758A643A3246004A4A5D28A', 0, NULL, NULL, 2, NULL, 0, 'Melt butter in a saucepan; blend in flour and seasonings. Gradually add milk. Cook over low heat, stirring until thick.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'527E801CD007444982615FC46EBBEBC1', X'2B39CB57C758A643A3246004A4A5D28A', X'2B39CB57C758A643A3246004A4A5D28A', 1, NULL, NULL, 2, NULL, 0, 'Arrange potatoes, tuna, and chopped onion in a greased 1 1/2-quart casserole; add sauce. Bake at 350 F for 50 to 60 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA36D3C074BBE149B0DA06A65410B641', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D51E0363963F8145B3E1FA689EB9E7C4', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 1, NULL, NULL, 2, NULL, 0, 'In a large zip-top bag, combine the soy sauce, chopped scallions, lemon juice, sesame oil, and fresh ginger. Swish everything around in the bag until it''s well combined.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'940D04165757ED409A496A0A71BD8824', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 2, NULL, NULL, 2, NULL, 0, 'Add the tuna steaks, turning within the bag to coat them completely with the marinade. Press the excess air out of the bag, seal, and marinate in the refrigerator for about 20 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A14DB4B3E2FB3A4994342400C8D9B9D2', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 3, NULL, NULL, 2, NULL, 0, 'Place the sesame seeds on a plate or a shallow dish. Remove the tuna steaks from the marinade bag, brushing the scallions off the steaks and reserving the marinade. One at a time, coat the steaks in sesame seeds on all sides, pressing the seeds into the steak so they''ll stick.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'99F1BE359ED81C4BA87F4F02ADD848F5', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 4, NULL, NULL, 2, NULL, 0, 'Spray the grill plates lightly with nonstick spray and place the steaks on the grill, closing the cover so the top grill rests evenly on the steaks. Do not press down. Grill for about 3 minutes for a rare, pink interior. Grill longer if you prefer your tuna cooked through. Remove from the grill and keep warm.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AB556C1A00F2F438016A0C9535794D3', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 5, NULL, NULL, 2, NULL, 0, 'If you are using an open grilling surface, carefully flip the tuna steaks with a pair of tongs after 3 minutes and cook for 3 minutes on the other side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82ECD8CB3E30D543BB80C494F82411FC', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 6, NULL, NULL, 2, NULL, 0, 'While the tuna cooks, pour the marinade into a small saucepan and bring to a boil. Add the cornstarch, stirring with a whisk. Simmer for about 3 to 4 minutes, until the sauce thickens.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8605CAE4E44FD84280FEA80D1A86DD53', X'76F563279D93D3469AE5BE52FAF3E4F7', X'76F563279D93D3469AE5BE52FAF3E4F7', 7, NULL, NULL, 2, NULL, 0, 'To serve, slice the tuna steaks on the diagonal into thin slices. Fan the slices on a plate and drizzle with the sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38E6608B2D35CB4B8FA38874486BE3DF', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 0, NULL, NULL, 2, NULL, 0, 'Cook pasta according to package directions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5F75CA6C08A81641807A2EA7E5ED5172', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 1, NULL, NULL, 2, NULL, 0, 'For the last minute of cooking, add the broccoli florets.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FD2A4B8C5D9CAF45858EDBE24D44A82B', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 2, NULL, NULL, 2, NULL, 0, 'Drain noodles and broccoli.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0437AEE44B9AF498AA79969BA6E510D', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 3, NULL, NULL, 2, NULL, 0, 'Rinse immediately under very cold running water and drain well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60FAB72E3FB4814ABA808C845CD10546', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 4, NULL, NULL, 2, NULL, 0, 'In a large bowl, combine the pasta and broccoli with the tuna, tomatoes, olives, and scallions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2900EA2520D9C34894C9AC1CE37B15C6', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix the mayonnaise, olive oil, vinegar, mustard (if using), salt and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A84E2A9B0B14E141B7E109FB727F63B6', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 6, NULL, NULL, 2, NULL, 0, 'Beat with a whisk for a creamy-textured dressing.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'00142C1CF5931548BB61908EF7B80C23', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 7, NULL, NULL, 2, NULL, 0, 'Just before serving, pour dressing over tuna-pasta salad and mix well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5814E9CF454A94B855F64717A81C1AC', X'C56F680CD0914B4F97884A133E84362A', X'C56F680CD0914B4F97884A133E84362A', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27F60C6E9AB37847B1F6BAF1709635DB', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'06F5E9177FE09E4F91C09C4C5CC59163', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 1, NULL, NULL, 2, NULL, 0, 'Chop sushi or sashimi-grade tuna and mix with mayonnaise and ichimi togarashi in a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'213A6A802FF7B641952E30FB7204F92B', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 2, NULL, NULL, 2, NULL, 0, 'Put a nori sheet on top of a bamboo mat and spread 1/4 portion of sushi rice on top of the nori sheet. Sprinkle sesame seeds on top of the sushi rice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1DFFA885ED63684799FC60581776BE20', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 3, NULL, NULL, 2, NULL, 0, 'Place 1/4 portion of tuna mixture lengthwise on the rice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E3D77616B0DB8443940945DCD718DBB4', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 4, NULL, NULL, 2, NULL, 0, 'Roll up the bamboo mat, pressing forward to shape the sushi into a cylinder.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F0FC60788A5C6441995A034C5FD6537E', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 5, NULL, NULL, 2, NULL, 0, 'Press the bamboo mat firmly and remove it from the sushi.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36836D29A9F7FA4694818FDB7E9D65DB', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 6, NULL, NULL, 2, NULL, 0, 'Repeat this process to make more rolls.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC37943EF072174AA6555C5DA4866754', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 7, NULL, NULL, 2, NULL, 0, 'Wipe a knife with a wet cloth before slicing sushi. Cut the rolled sushi into bite-sized pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75C27593ADBBB6488A5FCB3269C5166E', X'E3A180AF50C9D34990BF24F273CDBB96', X'E3A180AF50C9D34990BF24F273CDBB96', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2F17D580B92214A8C31247E57553AE5', X'86A27E70885B69459F31702B4489FF59', X'86A27E70885B69459F31702B4489FF59', 0, NULL, NULL, 2, NULL, 0, 'Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB25AC205AE8FE4EA92DDCAAD58E711B', X'86A27E70885B69459F31702B4489FF59', X'86A27E70885B69459F31702B4489FF59', 1, NULL, NULL, 2, NULL, 0, 'Grease a 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB0E845B1757B94282E46E8E2780BBC9', X'86A27E70885B69459F31702B4489FF59', X'86A27E70885B69459F31702B4489FF59', 2, NULL, NULL, 2, NULL, 0, 'Cook pasta in boiling salted water following package directions; transfer to a colander to drain. Rinse with hot water and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E82ED398845B44AB757204F0D21E1FB', X'86A27E70885B69459F31702B4489FF59', X'86A27E70885B69459F31702B4489FF59', 3, NULL, NULL, 2, NULL, 0, 'In a medium saucepan, melt butter over medium-low heat. Cook green onions and garlic for about 1 minute. Add flour and stir until blended and bubbly. Add the milk and chicken broth and cook, stirring, until thickened. Add 1 cup of the Parmesan cheese, sherry, pepper, and nutmeg. Stir in the tuna and peas, stir in the drained pasta, then taste and add salt, as needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF87D6086A503241935849EDDB9CE481', X'86A27E70885B69459F31702B4489FF59', X'86A27E70885B69459F31702B4489FF59', 4, NULL, NULL, 2, NULL, 0, 'Transfer the mixture to the prepared baking dish and sprinkle the remaining 1/4 cup of Parmesan cheese over the top.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94A1E607F1B158419AD58CCCC0D26209', X'86A27E70885B69459F31702B4489FF59', X'86A27E70885B69459F31702B4489FF59', 5, NULL, NULL, 2, NULL, 0, 'Toss bread crumbs with the melted butter until well blended and sprinkle the crumbs over the Parmesan layer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A0BEB1F90009349828B7F98A29CE4EC', X'86A27E70885B69459F31702B4489FF59', X'86A27E70885B69459F31702B4489FF59', 6, NULL, NULL, 2, NULL, 0, 'Bake the casserole for 25 minutes, or until browned and bubbly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6FA5CAEFEAB3840A9FA1448D98B7942', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 0, NULL, NULL, 2, NULL, 0, 'Grease a 3-quart baking dish. Heat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3D5A60C43A919448B3C34E47E262CA47', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 1, NULL, NULL, 2, NULL, 0, 'Cook pasta in boiling salted water following package directions; drain, rinse, and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBC7350065CA7B46B601036780898B87', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet or saut pan over medium-low heat, melt butter. Add celery and onion; cook, stirring frequently until vegetables are tender. Add garlic and cook for 1 minute longer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C164BF997C88654DA07A0A39DF9EB664', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 3, NULL, NULL, 2, NULL, 0, 'Stir the flour into the sauteed vegetable mixture along with the Creole seasoning and pepper. Cook, stirring, for 2 minutes. Gradually stir in the chicken broth and sherry. Bring to a simmer and continue cooking, stirring occasionally, for 2 minutes. Stir in milk and about 1 1/2 cups of the cheese. Continue cooking until the mixture begins to simmer again and cheese has melted.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CD04731104B4E149A12BAA5DD80C5BD4', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 4, NULL, NULL, 2, NULL, 0, 'Break tuna up and stir into the sauce along with the peas.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A936DB19DE2819448FC9C71FBC942F04', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 5, NULL, NULL, 2, NULL, 0, 'Combine the sauce mixture with the drained pasta. Spoon into the prepared baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E50AD3F3B1DE94F82104AE0F07A3355', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 6, NULL, NULL, 2, NULL, 0, 'Crush the crackers and combine with the remaining 1/2 cup of shredded cheese. Sprinkle over the casserole.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51AB99ECC6018340A7A7722FFD85ACD4', X'63A028E0C14C5E4589AED052554A9B13', X'63A028E0C14C5E4589AED052554A9B13', 7, NULL, NULL, 2, NULL, 0, 'Bake for 20 to 25 minutes, until bubbly and lightly browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BFAB360CCD748A4FB53F6E88948004B1', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', 0, NULL, NULL, 2, NULL, 0, 'Cut peppers in half lengthwise; remove tops and seeds. Cook peppers in a small amount of boiling salted water for about 5 minutes; drain. Sprinkle peppers inside with a little salt; place in a shallow baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8017FA70233AE747B9F5859DE2A7ED5B', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', 1, NULL, NULL, 2, NULL, 0, 'In a bowl, combine rice with tuna, celery, onion, pimiento, 1/2 teaspoon salt and dash pepper. Gently stir in mayonnaise until well blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E67728AF82061347ABEC01CEFC5C2368', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', 2, NULL, NULL, 2, NULL, 0, 'Spoon tuna rice mixture into pepper shells. Pour about 1/2 cup of water into the baking dish around tuna and rice stuffed peppers. Sprinkle potato chip crumbs over top of each stuffed pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C15D01C35DE8148B7DD3ACB90F979DA', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', X'54C0D94AF0A8A44ABAF5A905CFDE1E79', 3, NULL, NULL, 2, NULL, 0, 'Bake tuna and rice stuffed peppers at 350 F for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D417B02999E78C439F3F79FBB933886B', X'A8348780710C3548A6CF4FCFD3AC85C9', X'A8348780710C3548A6CF4FCFD3AC85C9', 0, NULL, NULL, 2, NULL, 0, 'Combine the first 5 ingredients in a jar or covered container; shake to mix well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D51098939F8C4A42A48D5D51A9D27A57', X'A8348780710C3548A6CF4FCFD3AC85C9', X'A8348780710C3548A6CF4FCFD3AC85C9', 1, NULL, NULL, 2, NULL, 0, 'Put tuna in a food storage bag; add about 4 tablespoons of the mustard mixture.');
