INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1B1F7BB6FC4DB5418DC5EE71E05580AD', X'9496BC7F03942748BE8AB49C4A4C5323', X'9496BC7F03942748BE8AB49C4A4C5323', 4, NULL, NULL, 2, NULL, 0, 'Bake for 10 minutes; serve hot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24268C614BAC494BA85B25F63156F046', X'9496BC7F03942748BE8AB49C4A4C5323', X'9496BC7F03942748BE8AB49C4A4C5323', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A92FAEA77F009D4CB2026BBBE1ED6769', X'BF384F542184A34FB59D64D3696DCEEB', X'BF384F542184A34FB59D64D3696DCEEB', 0, NULL, NULL, 2, NULL, 0, 'Cook the rice according to the directions. (As a general rule, one cup of rice can be gently simmered, covered, in 2 1/4 cups water for about 25 minutes, until the water is absorbed.) Allow the rice to cool thoroughly before mixing the salad.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6DAF25CE9F4FF24B8F9419B3B8FEC5E4', X'BF384F542184A34FB59D64D3696DCEEB', X'BF384F542184A34FB59D64D3696DCEEB', 1, NULL, NULL, 2, NULL, 0, 'Place the cooled rice in a large bowl with the tuna and vegetables.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E2EF869BFE9684AAE1B479B577780F8', X'BF384F542184A34FB59D64D3696DCEEB', X'BF384F542184A34FB59D64D3696DCEEB', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix the mayonnaise with the oil, lemon juice, salt and pepper and add it to the rice. Gently stir to mix everything well. Refrigerate the salad for about two hours before serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'819FE8A69ADC1F46922BC076A0655A79', X'AC10B8C94466AB45B64B6F9D83E360B1', X'AC10B8C94466AB45B64B6F9D83E360B1', 0, NULL, NULL, 2, NULL, 0, 'Combine olive oil, lemon juice, 3 tablespoons of the cilantro, garlic, shallots, cayenne pepper, cumin, black pepper, and salt. Mix well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F16077BB82E45469A7073909FC1F157', X'AC10B8C94466AB45B64B6F9D83E360B1', X'AC10B8C94466AB45B64B6F9D83E360B1', 1, NULL, NULL, 2, NULL, 0, 'Place tuna steaks in a large resealable bag or a glass baking dish. Pour marinade over top. Turn to coat and then refrigerate for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0739C82C565C6B4AB7592C9FEFE36225', X'AC10B8C94466AB45B64B6F9D83E360B1', X'AC10B8C94466AB45B64B6F9D83E360B1', 2, NULL, NULL, 2, NULL, 0, 'Brush the cooking grate of your grill with oil and then preheat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DE5E71300F753459E93A88EC657CA21', X'AC10B8C94466AB45B64B6F9D83E360B1', X'AC10B8C94466AB45B64B6F9D83E360B1', 3, NULL, NULL, 2, NULL, 0, 'Place tuna steaks on grill over high heat. Grill for 4 to 5 minutes per side or until they reach the desired doneness.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'82CDF77CEB575D4898FA3A5ADD632946', X'AC10B8C94466AB45B64B6F9D83E360B1', X'AC10B8C94466AB45B64B6F9D83E360B1', 4, NULL, NULL, 2, NULL, 0, 'Remove from grill and serve with remaining cilantro');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B39A43EBA34D034D91554214CF2785B9', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9FF6CF7476279D4594233C82953F863F', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AFFD56472849B4DA58FEC34E6EADDCF', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl combine the salt, oregano, paprika, cumin, garlic powder, chili powder, and black pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A03CFE089B1D64B8112C38180DB4F5B', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 3, NULL, NULL, 2, NULL, 0, 'To a medium bowl, add the melted butter and the shrimp. Toss to coat. Add the spice mixture and toss again until the shrimp are coated in the seasoning.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A85DAA0BE7242489245C1F9484DC422', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 4, NULL, NULL, 2, NULL, 0, 'Set a large skillet, preferably cast-iron, over high heat. Lightly coat the pan with a little butter, oil, or pan spray. When hot, add the shrimp in a single layer and sear for about two minutes on each side. The seasoning will blacken and the shrimp will turn pink and opaque. Remove the shrimp from the pan and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2F2FC8A0FFF4446B22E19B67F2F8CC4', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 5, NULL, NULL, 2, NULL, 0, 'Add a single layer of tortilla chips to the cast iron pan and sprinkle some of the cheese on top. Add the rest of the chips and top with a little more cheese, peppers, cooked shrimp, and finally the remaining cheese.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'394E22DC88E9B444BA8CC576088C74B1', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 6, NULL, NULL, 2, NULL, 0, 'Bake the nachos until the cheese is melted and hot, 8 to 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FA85BD787368A4098E4058C6ABD9749', X'C29CB8059D99AA4D8790C563A44E7F3B', X'C29CB8059D99AA4D8790C563A44E7F3B', 7, NULL, NULL, 2, NULL, 0, 'Remove from oven and top with chopped cilantro, if desired. Serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB24146074CBEE4898FFD24CC73239DF', X'00E245804A3C7F42BB86618DD0895ED5', X'00E245804A3C7F42BB86618DD0895ED5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27BE558BE6C28B48ABFB9DECB171A764', X'00E245804A3C7F42BB86618DD0895ED5', X'00E245804A3C7F42BB86618DD0895ED5', 1, NULL, NULL, 2, NULL, 0, 'After deveining and peeling the shrimp, pat them completely dry with paper towels. Season with half of the salt and pepper the shrimp and set aside. You can remove the tails if you wish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77FFED3FB2913841B5AF31B0B4508826', X'00E245804A3C7F42BB86618DD0895ED5', X'00E245804A3C7F42BB86618DD0895ED5', 2, NULL, NULL, 2, NULL, 0, 'Heat the butter and olive oil in a large skillet over medium heat. Once the butter is melted, add the shrimp to one side of the skillet and the asparagus to another. Sprinkle with the remaining salt and pepper and the dried basil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'459DDDCF336E8B46BCAF6230301D00DF', X'00E245804A3C7F42BB86618DD0895ED5', X'00E245804A3C7F42BB86618DD0895ED5', 3, NULL, NULL, 2, NULL, 0, 'Cover, and cook for 5 to 7 minutes or until the asparagus is bright green and just tender and the shrimp is opaque. You can gently stir or toss the shrimp and asparagus as it cooks so they cook evenly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE57543D0606942AD4FF222305B4F55', X'00E245804A3C7F42BB86618DD0895ED5', X'00E245804A3C7F42BB86618DD0895ED5', 4, NULL, NULL, 2, NULL, 0, 'Add the garlic, lemon juice, lemon zest, and parsley. Toss and continue cooking for another minute.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAAD39FAC41FC94B90D9D8A6EBBF7A53', X'00E245804A3C7F42BB86618DD0895ED5', X'00E245804A3C7F42BB86618DD0895ED5', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle with the Parmesan cheese and serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1592F08A754FFA42A87677BC87971DC0', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Fill a large bowl with ice and place in the refrigerator while you prepare the broth.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF0A18A193EC5B4E84FCD90063B6F53A', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 1, NULL, NULL, 2, NULL, 0, 'Add the water to a large pot, followed by the carrot, celery, onion, garlic, lemon, parsley, thyme, and bay leaves. Boil over high heat, then lower the heat to a simmer and cook for 10 to 30 minutes or until the vegetables are slightly softened and the broth is fragrant.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDE94D9951027D42AA4C9E4D022E55F7', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 2, NULL, NULL, 2, NULL, 0, 'Add the shrimp to the cooking liquid and turn off the heat. Leave the shrimp in the hot water, stirring occasionally, until they are pink and the tails have curled. This will take about 2 to 2 1/2 minutes. The larger the shrimp, the longer it will take.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C4A3C015DD66247A8E07B53420DA6B7', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 3, NULL, NULL, 2, NULL, 0, 'Remove the shrimp from the cooking liquid and promptly add them to the bowl of ice to cool quickly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDE53556496EB0479C7349170520E822', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 4, NULL, NULL, 2, NULL, 0, 'Once they feel cool to the touch, remove the vein of the shrimp if you wish, then remove the shell down to the tail. Leave the tail on the shrimp. Place the shrimp on the serving platter, cover, and place in the refrigerator.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7359FE009914744BBC3262FC73FE54B8', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 5, NULL, NULL, 2, NULL, 0, 'While the shrimp continue to chill in the refrigerator, prepare the cocktail sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'55E3C29685117D48BBDD3110D2B4E72E', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 6, NULL, NULL, 2, NULL, 0, 'Add the ketchup, chili sauce, horseradish, lemon juice, lemon zest, garlic powder, hot sauce, and Worcestershire sauce to a bowl. Whisk together until completely combined.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8F868569A60C34798EB84CCEF264ED5', X'CAC8C3BECB65AC428C521B2859DE7B27', X'CAC8C3BECB65AC428C521B2859DE7B27', 7, NULL, NULL, 2, NULL, 0, 'Serve the shrimp alongside the cocktail sauce and additional lemon slices.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC8724ACCAFA845B1DD9A6DED1557C5', X'CD495860AB663A4A9A99E0C1CC464216', X'CD495860AB663A4A9A99E0C1CC464216', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A79AE58D2B0D74AAF4990FD7C61EF39', X'CD495860AB663A4A9A99E0C1CC464216', X'CD495860AB663A4A9A99E0C1CC464216', 1, NULL, NULL, 2, NULL, 0, 'Whisk together the garlic powder, paprika, Sriracha, salt, pepper, lime juice, and olive oil in a bowl. Throw in the shrimp and toss until they are completely coated.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EE46D578DC53742B0A6DCDF50FBF054', X'CD495860AB663A4A9A99E0C1CC464216', X'CD495860AB663A4A9A99E0C1CC464216', 2, NULL, NULL, 2, NULL, 0, 'Grill the shrimp on high heat until cooked through, about 2 minutes per side. You can grill on an indoor griddle if you don''t have an outdoor grill, or simply cook the shrimp in a large saute pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6F0B189215AD24E9F357C2FA483A46B', X'CD495860AB663A4A9A99E0C1CC464216', X'CD495860AB663A4A9A99E0C1CC464216', 3, NULL, NULL, 2, NULL, 0, 'Gather the coleslaw ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'11C8430A39E55E469C6A409E63465181', X'CD495860AB663A4A9A99E0C1CC464216', X'CD495860AB663A4A9A99E0C1CC464216', 4, NULL, NULL, 2, NULL, 0, 'Add the mayonnaise, sour cream, salt, pepper, garlic clove, Sriracha, and lime juice to your blender or food processor. Blend until completely smooth. Taste and adjust the salt and pepper as needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D8F81BF6022394AB408AEC65D86587C', X'CD495860AB663A4A9A99E0C1CC464216', X'CD495860AB663A4A9A99E0C1CC464216', 5, NULL, NULL, 2, NULL, 0, 'Toss the dressing with the coleslaw in a large bowl until completely coated. Cover and refrigerate if you aren''t using right away.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FDF4273A457E2A4D8F828953E41C3466', X'CD495860AB663A4A9A99E0C1CC464216', X'CD495860AB663A4A9A99E0C1CC464216', 6, NULL, NULL, 2, NULL, 0, 'Place the shrimp on top of the tortillas then add the coleslaw, cotija cheese, and cilantro. Then squeeze a little lime juice over the top. Dig in and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80606425FC1BA64A9CBDA89FF9B9386F', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A89E253FF45E148B42F8409DB2EE1E4', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 1, NULL, NULL, 2, NULL, 0, 'Pat the shrimp dry with paper towels until they are as dry as possible. Salt and pepper generously.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4E1CED9DB968B4F979DA0F09DC56F7A', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 2, NULL, NULL, 2, NULL, 0, 'In a shallow dish, stir together the flour, garlic powder, and 1/2 teaspoon of the salt and 1/2 teaspoon of the pepper. Dredge the shrimp in the flour, coating both sides. Shake off the excess.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A136F22796BB941A4797EE114CC1F9A', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 3, NULL, NULL, 2, NULL, 0, 'In another dish whisk together the eggs and the soy sauce. Dip the flour-coated shrimp in the beaten egg mixture, coating both sides completely so you cant see any flour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'093375EA7A5A7C42B8D8DD68F31A2BAC', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 4, NULL, NULL, 2, NULL, 0, 'In a third shallow dish, mix the shredded coconut and the panko breadcrumbs together until they are evenly dispersed. Add the rest of the salt and pepper to this mixture.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CB2AFB451CB48D4CBCB2F0F202AC16EF', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 5, NULL, NULL, 2, NULL, 0, 'Transfer the shrimp to a baking sheet while you continue to bread the rest of the shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'781F10CCC41F2E46811C05466A4AD507', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 6, NULL, NULL, 2, NULL, 0, 'Add the shrimp in an even layer to the bottom of your air fryer. Coat with olive oil spray and cook on 400 F for 8 minutes, flipping them halfway through. You will need to cook them in two batches.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6C38DA19D8B7640BC4207F351514D42', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 7, NULL, NULL, 2, NULL, 0, 'While the shrimp are air frying, prepare the sauce. Whisk together the sweet chili sauce, apricot preserves, and mayonnaise in a small bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CA4F4A6DE1532459C706810F7A36BEA', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 8, NULL, NULL, 2, NULL, 0, 'Remove the shrimp from the air fryer once they are golden brown.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C752BF43AB85342AAD709E8C1BBB286', X'14221C87B3285A43BFB3BDBA75159C20', X'14221C87B3285A43BFB3BDBA75159C20', 9, NULL, NULL, 2, NULL, 0, 'Serve the shrimp with the sauce immediately. Top with chopped scallions and a squeeze of lime juice if you wish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CBCB817C2B98BC419F120FE5189140A0', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'61246D0E911294488EE36746BE7AD3E8', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 1, NULL, NULL, 2, NULL, 0, 'Cut off the root end of the green onions and slice them thinly, keeping the white and light green parts separate from the dark green parts. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A30872FED03CA4F8F6F309A93EC3BB2', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 2, NULL, NULL, 2, NULL, 0, 'Add 1 tablespoon of vegetable oil to the Instant Pot inner pot and select the saut button. When the oil is hot and shimmering, add the sliced andouille sausage. Cook the sausage slices for about 3 minutes, or until lightly browned, stirring frequently. With a slotted spoon, remove the sausage to paper towels to drain and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D25B701D78C1854FBD158D788667B281', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 3, NULL, NULL, 2, NULL, 0, 'Add the diced chicken to the pot along with the remaining 1/2 tablespoon of vegetable oil; cook for 2 to 3 minutes, or until lightly browned, stirring frequently. With a slotted spoon, remove the chicken to a plate and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A22EBEBE6054745B796627A4D20190E', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 4, NULL, NULL, 2, NULL, 0, 'Add the chopped yellow onion to the pot along with the bell pepper and celery. If necessary, add more vegetable oil. Cook for about 3 minutes, or until the onion is translucent, stirring frequently.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B7899749FFD047A968C00B76794F57', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 5, NULL, NULL, 2, NULL, 0, 'Add the minced garlic, Creole seasonings, and thyme to the vegetables and cook for 1 minute longer. Cancel the saut function.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10E1F647567090488FCA5A2820C0CA73', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 6, NULL, NULL, 2, NULL, 0, 'Add the bay leaf and chicken stock. Stir, scraping the bottom to ensure there are no browned bits stuck to the pan. Taste and add salt if needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44125423FAFB4347A8DFA2C72D41B3FF', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 7, NULL, NULL, 2, NULL, 0, 'Add the tomatoes (with their juices) along with the rice and the white and light green parts of the sliced green onions. Return the sausage and chicken to the pot. Stir to combine the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'507B7C04704FDE42903913A24B22CA60', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 8, NULL, NULL, 2, NULL, 0, 'Lock the lid in place and turn the steam release knob to the sealing position. Select the manual or pressure cook button (high pressure) and set the timer to 7 minutes. When the time is up, do a quick release following the manufacturer''s instructions. When the float valve drops, remove the coverthe pot will automatically switch to the keep warm setting.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C18355526C5AE444A90763AC221C21CA', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 9, NULL, NULL, 2, NULL, 0, 'Add the shrimp to the pot and stir to combine the ingredients. Cover the pot and let it stand on the warm setting for 10 minutes. The shrimp should be cooked through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BDFA7E3E372085458AD9F4A4CCFD997B', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 10, NULL, NULL, 2, NULL, 0, 'Remove the cover and stir in about half of the reserved dark green parts of the sliced green onions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'930540A020C07646B684DE470F898419', X'7DD68DBF07BB7B45B84519F2FBC7A043', X'7DD68DBF07BB7B45B84519F2FBC7A043', 11, NULL, NULL, 2, NULL, 0, 'Garnish servings with the remaining dark green parts of the green onions and chopped parsley, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9319747E6749DC4ABEA7CF06A5B9EBD5', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ECB8560F521C8B4D8503FF39CA2F21EA', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 1, NULL, NULL, 2, NULL, 0, 'Cut the romaine hearts in half lengthwise. Brush them with olive oil and sprinkle with salt and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B7987B9E07DC6D498947C432769282CE', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 2, NULL, NULL, 2, NULL, 0, 'Place the romaine hearts directly on the grill and quickly char them. You don''t want the lettuce to be completely wilted, just a nice, quick char. Place them on a plate to cool.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2BDB1C67B945345BB38B50990DE1F61', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 3, NULL, NULL, 2, NULL, 0, 'Gather your charred corn ingredients. If you are using corn on the cob, follow the directions in the variations section. Place the olive oil, salt, pepper, and corn in a sauce pan. Heat on high heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3DC14C3E5778BB44B38D7F4E152622F1', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 4, NULL, NULL, 2, NULL, 0, 'Cook the corn until charred. Shake and toss the pan every few minutes so that both sides of each kernel are cooked.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E33007FC99FA94885469C81DB69A61C', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 5, NULL, NULL, 2, NULL, 0, 'While the corn is cooking, make the dressing.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8695F8CFDA7F944A44167EE451DEE69', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 6, NULL, NULL, 2, NULL, 0, 'Combine all of the dressing ingredients (except the olive oil) in a blender or food processor and blend until completely smooth. Drizzle in the olive oil as you are blending. Add more water if the dressing is too thick.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'891C4AF10595874D9338DCFA814EF38D', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 7, NULL, NULL, 2, NULL, 0, 'Place the peeled shrimp onto skewers. Use a few tablespoons of the dressing to brush over both sides of the shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE47546278DF2542A800359994881780', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 8, NULL, NULL, 2, NULL, 0, 'Add them to the grill and grill for a few minutes on both sides, or until cooked through. Brush with more dressing as you are grilling. Just make sure to keep that portion of dressing separate so you don''t cross-contaminate the salad.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A552624817DCB4792709A95145590CA', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 9, NULL, NULL, 2, NULL, 0, 'Roughly chop the romaine.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA81F9D33254C543A9BE184FA873D22F', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 10, NULL, NULL, 2, NULL, 0, 'Add the romaine and the other chopped vegetables, including the corn, to a bowl. Add 1/4 cup of the dressing and toss together until the salad is lightly dressed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E166EA694050F4FA07C367497284D58', X'C73370356F366E448BBB5A022AE960FE', X'C73370356F366E448BBB5A022AE960FE', 11, NULL, NULL, 2, NULL, 0, 'Divide the salad among four bowls, and top each salad with the shrimp, cotija cheese, avocado, and an extra drizzle of dressing. Serve immediately, and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'204DDBB44CF1A7418BAC9F8D4DE29DFD', X'2FBE2EDA92786441B91A7A0924BF1E78', X'2FBE2EDA92786441B91A7A0924BF1E78', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7CFC1AC32946EF46860EA7F1722EC806', X'2FBE2EDA92786441B91A7A0924BF1E78', X'2FBE2EDA92786441B91A7A0924BF1E78', 1, NULL, NULL, 2, NULL, 0, 'In a medium bowl whisk together honey, soy sauce, lemon juice, garlic, ginger, onion salt, and red pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'601BEE8392349D4FAAAF7D3DBEA035EB', X'2FBE2EDA92786441B91A7A0924BF1E78', X'2FBE2EDA92786441B91A7A0924BF1E78', 2, NULL, NULL, 2, NULL, 0, 'Place shrimp in a large bowl and pour half the marinade mixture on top. Toss well to coat the shrimp fully. Cover with plastic wrap and allow the shrimp to marinate in the refrigerator for at least an hour or up to 12 hours. Cover and refrigerate remaining marinade.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85BEFEBCB1C0AA4EADD418FF57A169FF', X'2FBE2EDA92786441B91A7A0924BF1E78', X'2FBE2EDA92786441B91A7A0924BF1E78', 3, NULL, NULL, 2, NULL, 0, 'Heat olive oil in a large heavy-bottomed skillet or cast iron over medium-high heat until oil is shimmering.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1140C144EB4FD248BAAECC44E91C8FA4', X'2FBE2EDA92786441B91A7A0924BF1E78', X'2FBE2EDA92786441B91A7A0924BF1E78', 4, NULL, NULL, 2, NULL, 0, 'Place shrimp in the skillet and cook one side until pink, about 1 minute. Then flip shrimp and pour the remaining marinade over the shrimp. Cook about 1 minute more then remove to a serving plate along with the sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D185B84589424248A9A772A9FE7AF774', X'2FBE2EDA92786441B91A7A0924BF1E78', X'2FBE2EDA92786441B91A7A0924BF1E78', 5, NULL, NULL, 2, NULL, 0, 'Serve over rice, garnished with chopped green onions. Serve with green vegetables on the side, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'507EFA60FCA6114DBA5CAB17AD116E2B', X'C8E98C7CC2287842A474815A2038F5F6', X'C8E98C7CC2287842A474815A2038F5F6', 0, NULL, NULL, 2, NULL, 0, 'Cook the pasta in a large pot of boiling salted water according to package instructions. For the final 5 minutes of cooking, place a steamer over the boiling water and gently steam the broccoli, haricot verts, and peas until they are bright green and just-tender about 4 minutes. (If you do not have a steamer, bring a large saucepan of water to a boil. Add the broccoli, beans and peas and cook for 4 minutes.) Drain and set aside in the pan covered with a kitchen towel.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3F4A395DED3B44BAB6924996BFF1ADB', X'C8E98C7CC2287842A474815A2038F5F6', X'C8E98C7CC2287842A474815A2038F5F6', 1, NULL, NULL, 2, NULL, 0, 'Heat the butter in a large frying pan, add the garlic and gently fry for one minute taking care not to burn. Add the shrimp and cook for 4 minutes turning frequently. Once pink and cooked through, spoon the shrimp into a bowl to reserve and cover with a kitchen towel.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65034A5AA04D164DB07B8A800E6BD4F3', X'C8E98C7CC2287842A474815A2038F5F6', X'C8E98C7CC2287842A474815A2038F5F6', 2, NULL, NULL, 2, NULL, 0, 'Once cooked, drain the pasta, reserving  cup of the pasta water and set aside. Transfer the pasta to the frying pan, add the broccoi, haricot verts, peas, and reserved pasta water. Return the pan to medium heat and gently stir to reheat the pasta and vegetables. Add the cherry tomatoes, shrimp and parmesan and stir through the pasta. Finally, tear the basil into small pieces and stir through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E1C294D5EBAB047A9A21651A9339C47', X'C8E98C7CC2287842A474815A2038F5F6', X'C8E98C7CC2287842A474815A2038F5F6', 3, NULL, NULL, 2, NULL, 0, 'Serve the pasta immediately in four warmed pasta bowls and sprinkle with the lemon zest and red pepper flakes. Serve at once.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D28A8184EEE7B447AD232E767FB11AB2', X'FE2CF640250657439F111818D24C310D', X'FE2CF640250657439F111818D24C310D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72E184AD8F2CBE44B85F781AB0A1D409', X'FE2CF640250657439F111818D24C310D', X'FE2CF640250657439F111818D24C310D', 1, NULL, NULL, 2, NULL, 0, 'Combine the chili powder, paprika, Italian seasoning, cayenne, cumin, garlic powder, brown sugar, salt, and pepper in a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7BBA6A9C2493264A9E2B63DE68E8D494', X'FE2CF640250657439F111818D24C310D', X'FE2CF640250657439F111818D24C310D', 2, NULL, NULL, 2, NULL, 0, 'In a medium bowl mix the seasoning mixture with the shrimp and mix well. Set aside to marinate for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2D5ECD0DB12DA94A9C2F401F708C69D3', X'FE2CF640250657439F111818D24C310D', X'FE2CF640250657439F111818D24C310D', 3, NULL, NULL, 2, NULL, 0, 'Heat the olive oil in a skillet over medium-high. Arrange the shrimp in a single layer and cook for 2 minutes. Flip and cook for another 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F03A07B65DC0141BA85415B7645B5EA', X'FE2CF640250657439F111818D24C310D', X'FE2CF640250657439F111818D24C310D', 4, NULL, NULL, 2, NULL, 0, 'Add the garlic, stir, and cook for 30 seconds.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB652BD852BADB4D8512CCF063495B25', X'FE2CF640250657439F111818D24C310D', X'FE2CF640250657439F111818D24C310D', 5, NULL, NULL, 2, NULL, 0, 'Garnish with lime wedges to serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8659E4F54B44BE43918A3A1CB7E071E7', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 0, NULL, NULL, 2, NULL, 0, 'Gather your ingredients and start by making the pesto.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39B2926352AE7B44BBF0888F528C8A76', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 1, NULL, NULL, 2, NULL, 0, 'Place the basil and garlic into a food processor and pulse several times.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79D951CC3678F54EB164488F967A862F', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 2, NULL, NULL, 2, NULL, 0, 'With the motor running continuously, slowly add the olive oil through the top funnel to form a thick (but not oily) paste. You may need less olive oil than the recipe states.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18266E3F5E7A454DB8CCB25F09464219', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 3, NULL, NULL, 2, NULL, 0, 'Add the Pecorino and Parmesan and pulse two or three times.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DA65B36B682F942B48CD58C8CF98733', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 4, NULL, NULL, 2, NULL, 0, 'Finally, add the pine nuts (pignoli) and stir. Taste and add a tiny pinch of salt if needed. Scoop the pesto into a bowl, cover with a tea cloth, and put to one side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D70951DDD538E942840EFE9DD1FFDB72', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 5, NULL, NULL, 2, NULL, 0, 'Cook the tagliatelle according to the packet instructions in a large volume of boiling lightly salted water (approximately 8 to 10 minutes). The pasta should be slightly firm (al dente).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF544310BD508642B95D04B349949FC0', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 6, NULL, NULL, 2, NULL, 0, 'While the pasta is cooking, check over the shrimp to make sure they are clean and there is no black vein remaining down the back. If there is, wipe with a paper towel.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'169CC3000D8CBA48985009B6C6EB4EE9', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 7, NULL, NULL, 2, NULL, 0, 'Heat the oil in a non-stick skillet to hot, but not smoking. Add the shrimp and fry until the shrimp are pink through to the middle. Remove from the pan and keep warm.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'927DA9238A689546927104986233B98A', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 8, NULL, NULL, 2, NULL, 0, 'Add the halved cherry tomatoes to the pan you cooked the shrimp in, sprinkle with a tiny pinch of salt, and cook over a medium for 3 to 4 minutes to soften the tomatoes. Keep warm.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98362F545FF93046A4EE900DAE154CB1', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 9, NULL, NULL, 2, NULL, 0, 'Drain the pasta reserving a cup of the cooking water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D5C8410C39B7C1409948755C9D2003E7', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 10, NULL, NULL, 2, NULL, 0, 'Toss the pasta in a large warmed bowl. Stir the pesto through one tablespoon at a time until you have the strength of taste you want; pesto is a very strong flavoured, so be careful not to drown the pasta. If the pasta feels a little dry, add a bit of the cooking water and stir through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F540C7408F7BE4CB628A24425923D73', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 11, NULL, NULL, 2, NULL, 0, 'Add the shrimp and cherry tomatoes, stir again.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A097683A7D71AA4885CBD157C8F028F5', X'3EC210551785DE4398476ACE8059A737', X'3EC210551785DE4398476ACE8059A737', 12, NULL, NULL, 2, NULL, 0, 'Serve immediately in warmed pasta bowls. The shrimp pesto is also good cold and served as a summer salad.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AB44D9DDF100324BBE094DC83C1F04D2', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C09960DBEF466942A1FC7A9085ED741E', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F or the grill to medium-high (about 400 F). If using frozen shrimp place the package under cold water for 10 minutes to thaw.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4ACBE1756CC55D40853374E7EEDC6043', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 2, NULL, NULL, 2, NULL, 0, 'Bring 2 quarts of water to a roaring boil and add the potatoes and the corn. Reduce the heat and bring to simmer. Cook for 10 minutes on simmer and drain into a colander. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1C5C9C5C109DFD42A8D97AAA8A7212FF', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 3, NULL, NULL, 2, NULL, 0, 'While the potatoes and corn are simmering prepare 4 sets of 12 x 24 inch long aluminum foil sheets. Fold each in half, forming a square. If using industrial aluminum foil simply prepare sheets measuring 12 x 12 inches. Slightly fold up the edges of the sheets.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3997FFB3463FC340A10CD7E9C38400F5', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 4, NULL, NULL, 2, NULL, 0, 'In a large bowl combine the drained potatoes and corn, the sausage, the thawed shrimp, the onion, and the garlic. Drizzle with the 4 tablespoons of melted butter, season with salt, pepper, and Old Bay seasoning. Mix very well to coat all ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B2783D64D6687A4999F88B025ECCFCCB', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 5, NULL, NULL, 2, NULL, 0, 'Divide the shrimp boil ingredients between the four aluminum foil sheets.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8E2B1F0651488648BF7DC07F8B94543A', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 6, NULL, NULL, 2, NULL, 0, 'Neatly fold into packets.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BF2D5C1EBB2904994CBF3577F8230BC', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 7, NULL, NULL, 2, NULL, 0, 'Place them onto a baking sheet and bake for 15 minutes. If grilling, place the packets directly onto the grill surface and cook for 15 minutes, flipping once after about 8 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9DC0A4875BBA5D46BC16A2CE5570B283', X'99FC00B411C67E45AD4A28FDE9F78619', X'99FC00B411C67E45AD4A28FDE9F78619', 8, NULL, NULL, 2, NULL, 0, 'Serve the packets slightly opened up, garnished with chopped parsley, a pinch of Old Bay seasoning and a few lemon wedges. Serve the 1 stick melted butter as a side for optional drizzling over the shrimp boil or dipping.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DD4B861C094624E8DA74C9D1F94E2B3', X'A8A656113A179C49825962C11F1BE9CA', X'A8A656113A179C49825962C11F1BE9CA', 0, NULL, NULL, 2, NULL, 0, 'Remove the shells from the shrimp. With a sharp knife, make a shallow cut down the back of a shrimp and pull the dark vein out. If the vein doesn''t come out easily, scrape it out with the tip of the knife. The vein is the digestive system of the shrimp, so it should be removed. Repeat with the remaining shrimp. Rinse well, chop large shrimp coarsely. Leave small to medium shrimp whole, if desired. Transfer the shrimp to a large bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CCBCB2156C91CD498F8B27DDC8D33F05', X'A8A656113A179C49825962C11F1BE9CA', X'A8A656113A179C49825962C11F1BE9CA', 1, NULL, NULL, 2, NULL, 0, 'To the bowl with the shrimp, add the lobster, scallops, or crabmeat along with the sherry, lemon juice, and nutmeg. Cover the bowl and refrigerate for about 45 minutes to 1 hour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'373B7E4D779A444AA6BC68EFE3CAEFCD', X'A8A656113A179C49825962C11F1BE9CA', X'A8A656113A179C49825962C11F1BE9CA', 2, NULL, NULL, 2, NULL, 0, 'Melt the butter in a medium saucepan over medium-low heat. Add the flour and continue cooking for about 3 minutes while stirring constantly. Gradually whisk in about 1/4 cup of the half-and-half. Gradually add the remaining half-and-half, whisking constantly. Add the salt and continue cooking until bubbling and thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B98D43081CBE6442B8A70E04B2A45567', X'A8A656113A179C49825962C11F1BE9CA', X'A8A656113A179C49825962C11F1BE9CA', 3, NULL, NULL, 2, NULL, 0, 'In a bowl, whisk the heavy cream with the egg yolk. Add to the thickened sauce while whisking constantly. Stir in the cheese and continue cooking until the cheese has melted.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E1CEF91B1B1D34296A9EA07E00A561B', X'A8A656113A179C49825962C11F1BE9CA', X'A8A656113A179C49825962C11F1BE9CA', 4, NULL, NULL, 2, NULL, 0, 'Add the seafood to the sauce and cook for about 10 minutes, or until the seafood is thoroughly cooked.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'44D7CF136D529643ABA09EA1CA69F2BB', X'A8A656113A179C49825962C11F1BE9CA', X'A8A656113A179C49825962C11F1BE9CA', 5, NULL, NULL, 2, NULL, 0, 'Spoon the seafood Newburg over toast points, puff pastry shells, or hot cooked rice or noodles. Or serve the seafood over freshly baked biscuits.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4D858C760720645B1F56E65787BD919', X'A8A656113A179C49825962C11F1BE9CA', X'A8A656113A179C49825962C11F1BE9CA', 6, NULL, NULL, 2, NULL, 0, 'Sprinkle with paprika or garnish with fresh chopped parsley, as desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C56275FC4889C947BCD193D07CBEF923', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DD747626812DF64C854083140FB3C7B1', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 1, NULL, NULL, 2, NULL, 0, 'Prepare the cabbage by first removing the hard core. The core of the cabbage is shaped like a cone. If the cabbage is standing upright, the `mouth` of the `cone` is at the bottom. The opposite end of the cone ends about an inch or two below the top side of the cabbage. First, cut off the cone-shaped core. Position the cabbage with the base on top. Using a sharp, pointed knife held at a slight angle, cut into the core of the cabbage stopping about an inch or two from the opposite end. Rotating the cabbage little by little, repeat until you have cut around the base of the cire. If you did the procedure correctly, you should be able to pull out the entire core without much resistance.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17F14B865BAB6440B0A8B105928C8A7A', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 2, NULL, NULL, 2, NULL, 0, 'Next, boil the cabbage until softened. Drop the cabbage into a pot with the hole pointing upward. Pour boiling water into the pot (I pour the boiling water directly into the hole) until the cabbage is completely submerged.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24BBB332B6D4594FAC2A0ADD0242695A', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 3, NULL, NULL, 2, NULL, 0, 'Place a heavy heatproof plate to keep the cabbage underwater.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C702B2D0C8B5494B8682816EDF864917', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 4, NULL, NULL, 2, NULL, 0, 'Boil until softened. Depending on the size of the cabbage, this can take anywhere from 7 to 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5B7D701BA7AE448326E731FBCD09B4', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 5, NULL, NULL, 2, NULL, 0, 'Scoop out the cabbage, plunge in iced water and drain.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94186D25CCC0CB448364105F15B6C3D3', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 6, NULL, NULL, 2, NULL, 0, 'Separate the cabbage leaves taking care not to tear them. You need 12 to 16 whole cabbage leaves (you won''t be able to use the small ones; keep them for another use). Using a small, sharp knife, cut off the thick white portion in the middle of each leaf. Start at the middle of each leaf and cut all the way down using as though paring a potato. The cabbage leaves are now ready to be used as wrappers.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C45407978A5BA45A566A7003C1DACB0', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 7, NULL, NULL, 2, NULL, 0, 'Blanch the onion leaves in boiling water for a few seconds to wilt. Rinse well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE30CFE1775A2E4698BE7E931DCB02A9', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 8, NULL, NULL, 2, NULL, 0, 'Peel and finely chop the shallots (or onions) and carrots.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5DA39E4E5F409549AC5B6403CC73EF83', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 9, NULL, NULL, 2, NULL, 0, 'Crush, peel, and finely mince the garlic.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A4E579B274E954992754D79F989E996', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 10, NULL, NULL, 2, NULL, 0, 'Place the ground pork and minced shrimp in a bowl and add the chopped shallots, carrots, garlic, and cilantro. Season with fish sauce and ground pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D22FCA11C9EB341907C700D0DA63898', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 11, NULL, NULL, 2, NULL, 0, 'Lay a cabbage roll flat, the core end nearest you. Place 1 to 2 tablespoonfuls of filling at the center of the cabbage leaf.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'002796AE47E62849BB3957B5244D07C2', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 12, NULL, NULL, 2, NULL, 0, 'Take the edge nearest you, fold over the filling.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65E9714F34C858499EC36D01492BC825', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 13, NULL, NULL, 2, NULL, 0, 'Take the side edges and fold in.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'36E6FA06DFB8BF41AFADC398AA773A00', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 14, NULL, NULL, 2, NULL, 0, 'Roll the whole thing outward to close and seal.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3532E97409E2D741B32A7A8C9CC24445', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 15, NULL, NULL, 2, NULL, 0, 'Take an onion leaf and use it to tie the cabbage roll. Repeat until all the cabbage leaves are stuffed and tied.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F4DEF11F1B6C514591341CFC6873195A', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 16, NULL, NULL, 2, NULL, 0, 'In a large cooking pot, bring the broth to a boil. Drop the cabbage rolls one by one. Wait until the broth is boiling once more then lower the heat, cover, and simmer for 10 to 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFD6244EAFBE614FB5C4AC629E5C423C', X'99D2DD8A1460394E9BB4FD1FDF20863E', X'99D2DD8A1460394E9BB4FD1FDF20863E', 17, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50FC82C2B57B4244BE3DCD52B1CAFDFA', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0BF1E40490F6A241821908FF4085BF02', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', 1, NULL, NULL, 2, NULL, 0, 'Prepare the white or brown rice ahead of time. If you have a rice cooker, use the keep warm setting when it is done cooking. If you are using a stove top, start making the rice five or ten minutes before you start the rest of the cooking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6AABDC3FB889A84AB20261DFE0772756', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', 2, NULL, NULL, 2, NULL, 0, 'Whisk together the vinegar, sugar, and water in a saucepan on medium heat until the sugar dissolves. Remove the vinegar mixture from the heat and pour it over the sliced radishes and place in a covered container in the refrigerator while you finish cooking the shrimp. You can also make this up to a week in advance. The flavor will intensify the longer it has to soak.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69B54D881AFD784CA0CC58FAE2F833D5', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', 3, NULL, NULL, 2, NULL, 0, 'Toss the shrimp (tails off) in the corn starch until completely coated. Then shake the excess corn starch off of the shrimp and set them on a plate while you prepare the sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6623801AE6CE06428C2FE06E724D090F', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', 4, NULL, NULL, 2, NULL, 0, 'Whisk together the greek yogurt, mayonnaise, chili sauce and Sriracha in a medium sized mixing bowl and set aside while you cook the shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34B5620EBF11474C9546C8B1F4B80522', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', 5, NULL, NULL, 2, NULL, 0, 'Heat a non-stick skillet (a regular skillet will work as well) on medium heat and coat generously with cooking spray (a tablespoon of vegetable oil will also work). Place the shrimp in the hot pan and cook for 2 minutes per side or until cooked through and crisp. If you aren''t concerned with calories you can also deep fry them in a deep fryer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC161C2BABE7DF4387349A3F609AFA35', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', X'D3D81F8C38CC3F4FA746ACF8227A6F2D', 6, NULL, NULL, 2, NULL, 0, 'Toss the shrimp in the chili mayonnaise sauce and serve it immediately on top of the hot cooked rice. Top with the drained pickled radishes, scallions, and sesame seeds.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1E0DE0F4EB6A648AD74807198B1DF99', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F9D2B6D6CB4A34BB5F0789CE6288826', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 1, NULL, NULL, 2, NULL, 0, 'If using uncooked baby shrimp, boil 3 to 5 minutes then drain. Be sure to pat dry.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'80B361B7AE6D67439EB96E7FA49A1513', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 2, NULL, NULL, 2, NULL, 0, 'Place dried coconut into a frying pan to toast it. Turn on the heat to medium-high and stir the coconut continuously, `dry frying` until it turns golden (1 to 2 minutes). Place toasted coconut in a mixing bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9C83D7EA46F584D9088210CEBBD1E9E', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 3, NULL, NULL, 2, NULL, 0, 'If using dried shrimp, place it in a food processor and process into powder-like bits. A pestle & mortar works well for this too. Add the powdered shrimp to the coconut in the mixing bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'481C668F83B6434FA3A2894EBD467999', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 4, NULL, NULL, 2, NULL, 0, 'Grind up the peanuts, reducing them to very small pieces (you can also chop/crush them finely with a knife), and add them to the bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D614168E57FC2940A5E5F1585E901E27', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 5, NULL, NULL, 2, NULL, 0, 'Put garlic and galangal/ginger into the food processor and process, or finely mince/grate. (Remove any fibrous or `stringy` bits from the galangal/ginger.) Add to the bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0980A2DB2D441F41AEE8B48B8016BDBC', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 6, NULL, NULL, 2, NULL, 0, 'Now add the chili powder, chili, and fish sauce. Stir well. Note: At this point you can do a taste-test, adding more chili if you''d prefer it spicier, or more fish sauce if you''d prefer it saltier. If you''d like some sweet notes, add 1/4 tsp. white sugar. You should be able to taste a little of each flavor.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'66981A9D8140B74E9DDECEDE0C1DFF6E', X'B4405A74CB99D645987B39921E0C258F', X'B4405A74CB99D645987B39921E0C258F', 7, NULL, NULL, 2, NULL, 0, 'To plate appetizer, arrange 4 or more leaves (spinach, lettuce, banana leaf segments - diamond shape look nice) on a platter. Spoon some of the filling onto each leaf. Top with more toasted coconut and serve with wedges of lime.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CD1F5AD0F882B4D951304CF6EB0DE5D', X'65D69C7D8702BC46A7D3F01290E11596', X'65D69C7D8702BC46A7D3F01290E11596', 0, NULL, NULL, 2, NULL, 0, 'Heat a skillet over medium high heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2C814280A1AB04D87009C2A962C52C0', X'65D69C7D8702BC46A7D3F01290E11596', X'65D69C7D8702BC46A7D3F01290E11596', 1, NULL, NULL, 2, NULL, 0, 'Once hot, add in the cooking oil and red curry paste. Whisk for about 30 seconds.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0904B864582BD041BB7F7FD5C6F84ACC', X'65D69C7D8702BC46A7D3F01290E11596', X'65D69C7D8702BC46A7D3F01290E11596', 2, NULL, NULL, 2, NULL, 0, 'Pour in the coconut milk and continue whisking. Add in the mushrooms and the red peppers and continue cooking for 3 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94DBD103F394774087E3BE403E6FA1B6', X'65D69C7D8702BC46A7D3F01290E11596', X'65D69C7D8702BC46A7D3F01290E11596', 3, NULL, NULL, 2, NULL, 0, 'Add the shrimp to the curry and cook for another 2 minutes. Stir in the basil and serve over cooked rice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4EB55646E62FD84DABA53D3324C030D5', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C64F75AC4F763947A27E5527FED60166', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 1, NULL, NULL, 2, NULL, 0, 'In a skillet over medium heat, brown the sliced sausage in 2 to 3 teaspoons of vegetable oil; remove the sausage to a plate and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2374EE208EEA8145BD7A51DAA1FAA91C', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 2, NULL, NULL, 2, NULL, 0, 'In a large stockpot or Dutch oven, combine the chicken broth and tomatoes. Place the pot over medium heat to bring to a simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'35E08CD0BAAD0248A7802EC439B70EB1', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, in a large skillet over medium-high heat, combine the 1/2 cup of oil and the flour. Cook, whisking constantly until the roux is medium to deep brown. Don''t let the mixture burn, or you will have to start over with fresh oil and flour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD3DFBF503809344AC32EB9C25920DAA', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 4, NULL, NULL, 2, NULL, 0, 'To the skillet with the roux, add the chopped onion, celery, and bell pepper; cook, stirring constantly with a spoon until vegetables are softened. Stir the vegetable and roux mixture into the simmering tomato and broth mixture.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83615EA4C566E741A03B6173ED63948D', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 5, NULL, NULL, 2, NULL, 0, 'Put the skillet back on the heat and add 2 more teaspoons of oil; reduce to medium heat. Add the okra and cook, stirring, until lightly browned and not stringy or sticky.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF7C31F0EE54D54DA63D1261A9CD70F4', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 6, NULL, NULL, 2, NULL, 0, 'Transfer the cooked okra to the simmering pot along with the Creole or Cajun seasoning, the bay leaf, and cooked sausage. Cover and cook over medium-low heat, stirring occasionally, for 1 hour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'245E55ECE8B80C47ACCDB7DFFE950A7A', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 7, NULL, NULL, 2, NULL, 0, 'Add the prepared shrimp and cook for about 10 minutes longer, until the shrimp is cooked through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'45B6A4C615E9C942B02DECB13A3B9F67', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 8, NULL, NULL, 2, NULL, 0, 'Taste and add salt and pepper, as needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'414B137C8107134482801DF648B3C3C6', X'C4B398333FC08746914106D05EBB8D85', X'C4B398333FC08746914106D05EBB8D85', 9, NULL, NULL, 2, NULL, 0, 'Serve the gumbo over a mound of hot boiled long-grain white rice. Sprinkle with chopped fresh parsley or green onions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5B2231FEB7A4F5418A9EAF9504A5876E', X'C64C0E9450FA7143845876B86ACE0FD3', X'C64C0E9450FA7143845876B86ACE0FD3', 0, NULL, NULL, 2, NULL, 0, 'Cook the pasta according to package directions, making sure to salt the cooking water generously. Drain and place in a shallow serving bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D25D488B6CD27E419FEADB62EA109CE8', X'C64C0E9450FA7143845876B86ACE0FD3', X'C64C0E9450FA7143845876B86ACE0FD3', 1, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the olive oil in large skillet over medium-low heat. Saut the garlic for 2 minutes, until softened (do not allow it to brown). Turn the heat to medium-high, add the shrimp, and scallions and pepper flakes, if using, and cook, stirring frequently, until the shrimp just turn pink and cook through, about 4 minutes. Remove to a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5EAC8F3C0B2414F8D7254BB884059E5', X'C64C0E9450FA7143845876B86ACE0FD3', X'C64C0E9450FA7143845876B86ACE0FD3', 2, NULL, NULL, 2, NULL, 0, 'Dont wipe out the pan! Return it to medium-high heat, add the broth, bring to a simmer then add the lemon juice, and season with salt and pepper. Return the shrimp mixture to the pan, then pour the whole thing over the cooked pasta, toss well, and serve it up (adding in any fresh minced herbs you like).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8A4192071B19147B9551FC4E96BE438', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 0, NULL, NULL, 2, NULL, 0, 'Gather ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1069FDA7714074D8E1EC3C5EF32D206', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 1, NULL, NULL, 2, NULL, 0, 'First, clarify the butter. Melt the butter in a heavy saucepan over low heat. Let it simmer until the foam rises to the top. Once the butter stops making crackling noises and there is no longer any foam rising to the top, remove from the heat and let it cool slightly. Most of the solids will be on the bottom. Skim off any foam with a spoon. Strain through a cheesecloth-lined strainer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'765D786FE2675D45AA115C6037BDFF81', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 2, NULL, NULL, 2, NULL, 0, 'After clarifying the butter, you will have about 4 to 5 tablespoons for the roux.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F88079D202880E409B17D6AD8C4E0725', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 3, NULL, NULL, 2, NULL, 0, 'Chop the onions, celery, bell pepper, and garlic. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D5FF87849E19E429D0933EB201A940A', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 4, NULL, NULL, 2, NULL, 0, 'Put the clarified butter in a Dutch oven or large heavy saucepan. Heat over medium-low heat and add the flour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38E31267DEAB8145B9E20345D5B709F7', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 5, NULL, NULL, 2, NULL, 0, 'Cook, whisking constantly, until the roux is golden brown, about the color of peanut butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'090DB40B49A11545A1E61865DB8531AA', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 6, NULL, NULL, 2, NULL, 0, 'Add the chopped vegetables to the roux and continue cooking, stirring frequently, until the onions are translucent, about 10 to 12 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A50149A550C7A41A71987D39606804D', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 7, NULL, NULL, 2, NULL, 0, 'Add the clam juice or shrimp stock and tomatoes, along with 1 tablespoon of the Creole seasoning, bay leaf, and ground black pepper. Bring to a boil. Reduce heat to low and simmer for 30 to 45 minutes or until the vegetables are softened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'09EED91DE01CEC439DAFBC319382D48C', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 8, NULL, NULL, 2, NULL, 0, 'If the sauce mixture seems too thick, thin it with a little more clam juice, shrimp stock, or some chicken broth.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CFDB8531DCAA914C9FF89FC6F89A4919', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 9, NULL, NULL, 2, NULL, 0, 'Taste and add more Creole seasoning and salt, as needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8487A2F05CC2854F81F8670F603A437F', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 10, NULL, NULL, 2, NULL, 0, 'Add the shrimp and continue cooking for about 3 to 4 minutes or until the shrimp are cooked through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'526CCAC79D116C42BCD7D1DB98BABD1B', X'D5369B2BFE7EE74BA8BDC663C69C3056', X'D5369B2BFE7EE74BA8BDC663C69C3056', 11, NULL, NULL, 2, NULL, 0, 'For each serving, put a big scoop of rice in the center of a deep plate or shallow bowl. Spoon the shrimp touffe around the rice. Sprinkle with a little fresh chopped parsley or green onion tops. Serve with a simple tossed salad and crusty French bread or rolls.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B8BA6CCD5928ED4ABCA4843368842774', X'5034BBAA5DFE624384F82C96B9C59D47', X'5034BBAA5DFE624384F82C96B9C59D47', 0, NULL, NULL, 2, NULL, 0, 'Pour stock plus lemongrass and lime leaves in a deep soup pot over medium-high heat. If using fresh lemongrass, also add the upper stalk pieces for extra flavor. Bring to a boil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D8156AF98F1B145A73DD57D51D395D5', X'5034BBAA5DFE624384F82C96B9C59D47', X'5034BBAA5DFE624384F82C96B9C59D47', 1, NULL, NULL, 2, NULL, 0, 'Add garlic, galangal or ginger, chili, and mushrooms (if using). Reduce heat to medium and simmer 2 to 3 minutes, or until mushrooms are soft.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B23BC49C576F2948B6B8DABC963D9256', X'5034BBAA5DFE624384F82C96B9C59D47', X'5034BBAA5DFE624384F82C96B9C59D47', 2, NULL, NULL, 2, NULL, 0, 'Add the shrimp, any other seafood you''re using, tomato, and baby bok choy (if using). Simmer over medium heat 3 to 4 minutes, or until shrimp turns pink and plump and mussels have opened. Scallops, crab, and fish should all be firm to the touch and no longer translucent.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DDB2F2455D459E40A329D49C927153B2', X'5034BBAA5DFE624384F82C96B9C59D47', X'5034BBAA5DFE624384F82C96B9C59D47', 3, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium-low and add the coconut milk, fish sauce, soy sauce, lime juice, and sugar. Stir well to combine and gently simmer until hot (do not boil at this point). Taste-test the soup for salt and spice, adding more fish sauce instead of salt (about 1 more tablespoon), or more chili as desired. If too sour, add more sugar. If too spicy for your taste or if you''d like it creamier, add more ?coconut milk. If too salty, add another squeeze of lime juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FBED1B6F9F4ABA4181DFD9B44C9883FF', X'5034BBAA5DFE624384F82C96B9C59D47', X'5034BBAA5DFE624384F82C96B9C59D47', 4, NULL, NULL, 2, NULL, 0, 'Serve in bowls with fresh coriander sprinkled over. For an extra hit of flavor, you can also add some Thai chili sauce, Nam Prik Pao, either store-bought or homemade.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D369FADB770AE947A22FCCB8D17A12A5', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'38ACA2C6F63E9D4D9CF9A7F6EA8FE1EA', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter in a Dutch oven or large saucepan over medium-low heat; add the chopped green onion and celery. Saut, stirring, until tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E051D08533646F4182DA883FF206AE78', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 2, NULL, NULL, 2, NULL, 0, 'Blend the flour into the butter and vegetables until well incorporated. Continue cooking, stirring, for about 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FE894AAC0E4304D91C4801F43FBF9EC', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 3, NULL, NULL, 2, NULL, 0, 'Warm the milk in another saucepan over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9835264FE409074C96ED7AA34CEEFCE9', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 4, NULL, NULL, 2, NULL, 0, 'Slowly stir the warmed milk into the butter and vegetables. Continue cooking, stirring until thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'430E4B12FF6ADA47AD07498EEE7C986A', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 5, NULL, NULL, 2, NULL, 0, 'Add the freshly ground black pepper, tomato paste, and heavy cream.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F38EB0711F5D94BAD13996BD5A46CFD', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 6, NULL, NULL, 2, NULL, 0, 'If desired, puree the soup in a blender or food processor (see the tips) at this point and then return it to the saucepan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D2171FCFB68A294691984D08C63D9837', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 7, NULL, NULL, 2, NULL, 0, 'Stir in the shrimp, crab, and the sherry. Bring it to a simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5159FBF1B0642241A0A28E22E36E5F1A', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 8, NULL, NULL, 2, NULL, 0, 'Serve hot, garnished with parsley, cilantro, and/or chopped green onion tops.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6082A7BCF226104C8D45F8EFE40E3D15', X'1FD2B2B4209D3A4285AEBD193C79F1F4', X'1FD2B2B4209D3A4285AEBD193C79F1F4', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D51F0846F0D4419AE1AF985EA2D0AF', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D6CFCA950324B42AF7F51D75CD183A1', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 1, NULL, NULL, 2, NULL, 0, 'If using old rice, pour 1 to 2 tsp. coconut oil or other vegetable oil onto your fingers and then run fingers through the rice, separating any chunks back into grains. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A99CE7178F5254CA72786E2F700FB45', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 2, NULL, NULL, 2, NULL, 0, 'In a cup, stir the soy sauce/fish sauce together with the curry powder. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7388FBFF85DCD74AB30F392B742C4588', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 3, NULL, NULL, 2, NULL, 0, 'Drizzle 1 to 2 tbsp. oil in a wok/large frying pan over medium-high heat. Add shallots, garlic, and chili, stir-frying until fragrant (1 minute). Add shrimp at this point (if using). Whenever the wok/pan becomes dry, add a little stock (1/2 to 1 tbsp. at a time to keep ingredients sizzling).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F74D4EF68B849141AC4CD6FE7D3F72A7', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 4, NULL, NULL, 2, NULL, 0, 'Push ingredients aside and crack an egg into the wok, stirring quickly to cook (like making scrambled eggs).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDBD694592980E4BA9018FF08E7CD0D1', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 5, NULL, NULL, 2, NULL, 0, 'Add the carrot (if using) and peas. Stir-fry 1 minute in the same way, adding more stock if needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'522A04360BE405479D5A39BD3D13B557', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 6, NULL, NULL, 2, NULL, 0, 'Add the rice, pineapple chunks, cashews, and raisins/currents.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F5072887F7836145A60B19760C139F96', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 7, NULL, NULL, 2, NULL, 0, 'Drizzle the fish/soy sauce mixed with curry powder over and gently stir-fry to combine over medium-high to high heat until the rice `dances` (begins to make popping sounds)about 3 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04A92826E4B3C7439C9517663FE027ED', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 8, NULL, NULL, 2, NULL, 0, 'Remove from heat. Do a taste-test for saltiness, adding more fish sauce or soy sauce until desired taste is achieved. If you happen to over-salt the dish, add a squeeze or two of lime juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'79C7A50C51C3024FA7F454A16998AD2C', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 9, NULL, NULL, 2, NULL, 0, 'To serve, scoop rice onto a serving platter or individual plates and top with spring onions and coriander.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABC823628F9084428FB0968B8BCE54A3', X'57492A7169F496489D6CD18B728905AA', X'57492A7169F496489D6CD18B728905AA', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CEFB74C95F0FF4CB6F4387067EF02EB', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A69B7FCA32A824CB9920C70160761C3', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 1, NULL, NULL, 2, NULL, 0, 'Chop the shrimp into 3 to 4 pieces each and transfer to a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E1EA93BE632A3C4FB11EEF97A6B01570', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 2, NULL, NULL, 2, NULL, 0, 'Add the marinade ingredients to the bowl and marinate for 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'655F145F6EFA3F4E9803A48E401D557B', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 3, NULL, NULL, 2, NULL, 0, 'Heat the wok over medium heat and add 1 tablespoon oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C565DC1130E3D341A25F67B42BAB76F1', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 4, NULL, NULL, 2, NULL, 0, 'When oil is hot, pour 1/2 the beaten egg into the wok and scramble until just cooked through. Transfer egg to a plate and wipe out wok.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7EC41F7D547E7947AF78498ABD6A63C2', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 5, NULL, NULL, 2, NULL, 0, 'Heat the wok over medium-high heat and add 2 tablespoons of oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AE6D54C512026A419AF5F52ACD9FBB78', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 6, NULL, NULL, 2, NULL, 0, 'When oil is hot, stir fry the onion, peas, and shrimp on high heat for 2 to 3 minutes, remove and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'98FB9D1B7348D248A0B5C4780DDD13FF', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 7, NULL, NULL, 2, NULL, 0, 'Add another tablespoon of oil to the pan, reduce heat to medium, and stir fry the rice with soy sauce and oyster sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8DEA4FEDF8189D478384D78957ADEE55', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 8, NULL, NULL, 2, NULL, 0, 'Add the other ingredients and combine thoroughly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4A57DE419DAC14C8E9F94DC645AAA6B', X'CE240F9B2302EC4289537AD4B834848A', X'CE240F9B2302EC4289537AD4B834848A', 9, NULL, NULL, 2, NULL, 0, 'Serve garnished with more sliced green onions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'409732D5EBC8E04895B072F65FC0CBC3', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F929CE7C7690C641A3D30540CC59B7ED', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 1, NULL, NULL, 2, NULL, 0, 'Place the pineapple on a cutting board. Holding it securely with one hand, cut it lengthwise into halves. Scoop out the flesh from both halves, leaving about an inch of flesh attached to the skin.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C61143A6749DF4FB71BDD5F09BA351F', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 2, NULL, NULL, 2, NULL, 0, 'Take the pineapple flesh and dice. Measure a cup; reserve the rest for another dish or for munching.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A546A15A5294248BCDE3A17B64388D4', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 3, NULL, NULL, 2, NULL, 0, 'Save any juices that drip off.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7776D41AEDE5F4590D58117BCABC27F', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 4, NULL, NULL, 2, NULL, 0, 'Heat the cooking oil in a wok or frying pan. Add the shrimps. Sprinkle with salt and pepper. Stir fry just until the shrimps change color. Scoop out and transfer to a bowl. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'51998AF221B373418805EA840C95D724', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 5, NULL, NULL, 2, NULL, 0, 'Add the garlic, ginger, chili, shallots, tomatoes and bell peppers to the hot oil. Drizzle in about a tablespoonful of fish sauce and stir fry for half a minute. Add the ham and continue stir-frying for another half a minute.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6BE60DF36F4CD4FAFE43C0F6B18063F', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 6, NULL, NULL, 2, NULL, 0, 'Add the rice to the vegetables and ham. Drizzle in more fish sauce and the pineapple juice. Stir fry for a minute.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A353451856D6F46B19F42D8188BB5BE', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 7, NULL, NULL, 2, NULL, 0, 'Finally, add the pineapple chunks, shrimps and cashew nuts. Continue stir-frying for another minute. Taste the rice, adjust the seasonings if needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CAE99F91B68D2428CC3203AB5770876', X'13C202144B34A54DA87D1A21FC135490', X'13C202144B34A54DA87D1A21FC135490', 8, NULL, NULL, 2, NULL, 0, 'Ladle the rice into the hollowed pineapple halves. Sprinkle with scallions. Serve at once.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'818535B3305B944ABC773561F34D3A34', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 0, NULL, NULL, 2, NULL, 0, 'In a large Dutch oven bring to a boil the water with bay leaves, lemon juice, onion wedges, salt, pepper, and parsley.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9720B374B5C5544B163280AD0D156F8', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 1, NULL, NULL, 2, NULL, 0, 'Wash shrimp and add it (unpeeled) to stock; boil for 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E02CFFAFBCBE9E488570B2709B50539F', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 2, NULL, NULL, 2, NULL, 0, 'Peel shrimp. Return shells to the stock in the Dutch oven and set aside for later use.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18BA461A038BA74E8D503E91D5A67A99', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 3, NULL, NULL, 2, NULL, 0, 'Put shrimp and crab meat in the refrigerator for the time being.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69EE5455007A8347BD9517D050E1CC84', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 4, NULL, NULL, 2, NULL, 0, 'Saute the sliced okra in 2 tablespoons bacon grease or butter in a large heavy skillet or saut pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDBC4207403217459830FF5A9FF28A23', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 5, NULL, NULL, 2, NULL, 0, 'When okra is soft, transfer to a stew pot and add the tomatoes. Stir well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4D5A26C8C839A4F90B113710F963930', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 6, NULL, NULL, 2, NULL, 0, 'Clean skillet and heat remaining 2 tablespoons bacon grease, butter, or duck fat. Saut the chopped onion, green pepper, and red pepper until softened; add to the stew pot with the tomatoes and okra.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA5A863161AEAE41B04F7D6811C3CC3B', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 7, NULL, NULL, 2, NULL, 0, 'In a saucepan, warm the roux; strain the reserved shrimp stock and stir about 2 quarts of the stock into the roux.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9D794678E0A0DD478CEC2DA4E7E4C492', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 8, NULL, NULL, 2, NULL, 0, 'When well-blended, add to the large stew pot with the other ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75E042ACEF544248844D27EA50956764', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 9, NULL, NULL, 2, NULL, 0, 'Bring to a boil and simmer for 1 1/2 hours, adding more strained stock if needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A5D9439DFF3174C9AEB491074D066CC', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 10, NULL, NULL, 2, NULL, 0, 'Taste and add salt, pepper, thyme, and parsley to taste. Simmer for 1 to 2 hours longer; add the reserved shrimp and crab meat and cook for 15 minutes longer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A5E6196FBCC9D649AFF6CA68805E15F5', X'5A858AADF11F8042A9F9F358288409F3', X'5A858AADF11F8042A9F9F358288409F3', 11, NULL, NULL, 2, NULL, 0, 'Serve with hot boiled rice, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F372BCCB243B4A4DABEC3FF4F03D7B9D', X'D35570A5C910B94EA5808D519DC7E5C5', X'D35570A5C910B94EA5808D519DC7E5C5', 0, NULL, NULL, 2, NULL, 0, 'Cook the pasta in boiling salted water following the package directions. Drain the pasta in a colander and rinse with cold water. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'148D37E02A81BA459178680744E67BA5', X'D35570A5C910B94EA5808D519DC7E5C5', X'D35570A5C910B94EA5808D519DC7E5C5', 1, NULL, NULL, 2, NULL, 0, 'In a large serving bowl, combine the mayonnaise, seasonings, and mustard; whisk to blend thoroughly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12F19343883E094D8F94D79A67B38153', X'D35570A5C910B94EA5808D519DC7E5C5', X'D35570A5C910B94EA5808D519DC7E5C5', 2, NULL, NULL, 2, NULL, 0, 'Add the chopped celery, diced tomatoes, and green onions. Mix well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26DC5A0AE9CA5A45A0FB83D54623C78A', X'D35570A5C910B94EA5808D519DC7E5C5', X'D35570A5C910B94EA5808D519DC7E5C5', 3, NULL, NULL, 2, NULL, 0, 'Add the cooked shrimp and pasta to the bowl. Gently combine until well blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4149C0310B765443AB02E95AC043FF3E', X'D35570A5C910B94EA5808D519DC7E5C5', X'D35570A5C910B94EA5808D519DC7E5C5', 4, NULL, NULL, 2, NULL, 0, 'Refrigerate the salad for at least an hour or two before serving.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2F7586C2A7C4A468BFA98918F953512', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5035115417E8B04F92DBA54788759E0F', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 1, NULL, NULL, 2, NULL, 0, 'Soften the mushrooms by soaking in hot water for 20 to 30 minutes. Squeeze out any excess water. Cut off the stems and slice finely.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D61A0B96B6C508498B2B60EE07252F1D', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 2, NULL, NULL, 2, NULL, 0, 'Cut the pork into matchsticks. Add the soy sauce, sesame oil, and cornstarch. Marinate for 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C9BADD12F6973444A8E23D001EAF94E9', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 3, NULL, NULL, 2, NULL, 0, 'Rinse the shrimp under warm running water. Pat dry with paper towels. Chop finely. Toss with the rice wine and cornstarch. Marinate for 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18F6593372E9A543B69F7D65C842AD34', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 4, NULL, NULL, 2, NULL, 0, 'Rinse the mung bean sprouts and drain thoroughly. Wash the red bell pepper and dice. Wash and dice the garlic chives. Wash and shred the carrots and cabbage.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50FFA6DB8BE50349A9AF01C2CB1858A6', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 5, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix together the sauce ingredients. Set aside. Heat the wok over medium-high to high heat. When the oil is hot, add 2 tablespoons oil. When the oil is hot, add the garlic and stir-fry until aromatic. Add the pork.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A124B01425781641BE0CBD0D147E242A', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 6, NULL, NULL, 2, NULL, 0, 'Stir-fry until it turns white and is about 80 percent cooked through. Remove from the wok and drain on paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5D94422E59072C4393D6B2C44186AC30', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 7, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons oil. When the oil is hot, add the ginger. Stir-fry until aromatic. Add the shrimp. Stir-fry until they turn pink. Remove from the wok and drain.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'243812011462704C805BF5BB840F7308', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 8, NULL, NULL, 2, NULL, 0, 'Add 2 tablespoons oil. When the oil is hot, add the vegetables, one at a time, beginning with the mushrooms, then the red bell pepper, cabbage, garlic chives, shredded carrot, and mung bean sprouts.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A5BA8685302FF46A46AB4163741E11B', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 9, NULL, NULL, 2, NULL, 0, 'Add the sauce to the wok. Add the pork and shrimp back into the pan. Heat through. Remove and cool.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ADE3251B4D573D4186738C7396FFCB70', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 10, NULL, NULL, 2, NULL, 0, 'Clean out the wok. Preheat the oil for deep-frying to 360 degrees Fahrenheit while preparing the spring rolls.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A45550A2EB73045B8218ED90713D5BF', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 11, NULL, NULL, 2, NULL, 0, 'Lay a spring roll wrapper in front of you so that it forms a diamond shape.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'516917DE159F274F95AF2D1946C37619', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 12, NULL, NULL, 2, NULL, 0, 'Use your index finger to wet all the edges with water or a cornstarch/water paste. Place approximately 2 tablespoons of filling near the bottom. Rollover once, tuck in the sides and then continue rolling. Seal the top.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'69C873FBF8462749AD133CA14A468058', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 13, NULL, NULL, 2, NULL, 0, 'Deep-fry the spring rolls in 3 to 4 batches, cooking until they are golden brown and crispy (about 3 minutes). Remove with a slotted spoon and drain on paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'24C27D5350959B4EAF4C70DFAD7A2C79', X'E158B49CEC9C304BBAFD3BE4825E05CA', X'E158B49CEC9C304BBAFD3BE4825E05CA', 14, NULL, NULL, 2, NULL, 0, 'Serve the spring rolls with the plum sauce and hot mustard for dipping.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33C03343B64FC642BAA248254CBBF7BA', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B915D0CB4EADC142AE1A5486B1796499', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 1, NULL, NULL, 2, NULL, 0, 'Peel and devein the shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C29802FB6B48446A9AC14C93EE20034', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl, toss shrimp with garlic and basil. Refrigerate while linguine is cooking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C8368CBBF27911449AC1EFF85BD10E76', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 3, NULL, NULL, 2, NULL, 0, 'Cook linguine in boiling salted water following the package directions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB82D5DDF6CCBD49B9412E57770BAD4D', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 4, NULL, NULL, 2, NULL, 0, 'Spray a 1 1/2-quart baking dish with nonstick cooking spray. Spoon half the tomatoes into the baking dish; top with linguine.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B0A211C6CCD3948B8B5B072B10C2AA0', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 5, NULL, NULL, 2, NULL, 0, 'Preheat oven to 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D8526A20547B314E909D10B29D0F5D5D', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 6, NULL, NULL, 2, NULL, 0, 'In the same pot that you cooked the linguine in or a skillet, melt butter with olive oil over medium heat. Add sliced green onions; cook for 1 minute. Add shrimp mixture, peas, wine, salt and pepper, and the remaining tomatoes; cook for 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1B88D9F7DB8E64F9DD21CF5B81919D6', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 7, NULL, NULL, 2, NULL, 0, 'Pour the shrimp mixture over linguine in the baking dish then top with the mozzarella cheese.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BCFF62D0C95A4F44B0D3024ED466C420', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 8, NULL, NULL, 2, NULL, 0, 'Bake for 20 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34D6DC22F04C9541AB9C1E8AA9FA8770', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 9, NULL, NULL, 2, NULL, 0, 'Serve with freshly grated Parmesan cheese, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D74D0B1EB912E4FA73E5569F7D525BD', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', X'4A50FDC509DF5949A0CA04DC8F0CE2E7', 10, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'65DC1957E6ABBE4AB9AE09171F048F3D', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F770548E8A462C42B5944BAC677D377C', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 1, NULL, NULL, 2, NULL, 0, 'Soak the shrimp in warm, lightly salted water for 5 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAE9BCE6521C90478FE84453F39192C7', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 2, NULL, NULL, 2, NULL, 0, 'Rinse in cold water, drain and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C58283EE03C0946A020692838450788', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 3, NULL, NULL, 2, NULL, 0, 'Lightly coat the shrimp with the tapioca starch or cornstarch.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7307AA772A034C4D8BD44CEC185CED63', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 4, NULL, NULL, 2, NULL, 0, 'In a small bowl, mix the salt with the freshly ground peppercorns and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2923A432E4B41F498CAF9653C4B03CF1', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 5, NULL, NULL, 2, NULL, 0, 'Heat the oil to 360 F in a deep fat fryer, heavy saucepan with deep sides, or a second wok (it is easiest not to deep-fry and stir-fry the shrimp in the same ?wok). Carefully add the shrimp to the hot oil, 4 to 5 at a time, and deep-fry until they turn bright orange (about 40 seconds). Make sure the temperature does not fall below 350 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C76B67A526F396489F61FA7D68EC7475', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 6, NULL, NULL, 2, NULL, 0, 'Carefully remove the shrimp with a slotted spoon and drain on paper towels. Continue deep-frying the remainder of the shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F33155B23ACEAF459E75ED613578F193', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 7, NULL, NULL, 2, NULL, 0, 'Heat a wok over medium heat. Add the salt and pepper mixture and the deep-fried shrimp. Stir-fry briefly to coat the shrimp in the mixture (20 to 30 seconds).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'992164FA720B6F4784F8FD363FFF8BAC', X'4103C9CD13BB284DB9D2A524D0C7A881', X'4103C9CD13BB284DB9D2A524D0C7A881', 8, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1DEC354A38C6149A3CABBA890D2AB63', X'A1017E486B162C4EAE689BDC1E609D8A', X'A1017E486B162C4EAE689BDC1E609D8A', 0, NULL, NULL, 2, NULL, 0, 'In a bowl, stir together the vinegar and sugar until the sugar is dissolved. Fold in the strawberries, cover, and chill for at least one hour. Remove from the fridge when you''re ready to start cooking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'47012FB9642DAD4CA7C4D0A0E945F7C0', X'A1017E486B162C4EAE689BDC1E609D8A', X'A1017E486B162C4EAE689BDC1E609D8A', 1, NULL, NULL, 2, NULL, 0, 'Heat the extra virgin olive oil in a large skillet over medium heat. Saute the shallots until softened, then add the leaves and turn until well mixed. Cook just a minute or two until all the leaves have wilted but are still fully green. Remove greens to a strainer and keep warm. (See cook''s notes below)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74DD4A57860DB14A8821121B4F273C9B', X'A1017E486B162C4EAE689BDC1E609D8A', X'A1017E486B162C4EAE689BDC1E609D8A', 2, NULL, NULL, 2, NULL, 0, 'Dry the scallops and shrimp well and sprinkle with the coarse salt. Heat a skillet or saute pan over medium-high heat until quite hot. Add the grapeseed or light olive oil, let the oil heat a moment, then carefully add the scallops and shrimp (there will be some spattering.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'134C90DEE804AF42967CBC974511EA6F', X'A1017E486B162C4EAE689BDC1E609D8A', X'A1017E486B162C4EAE689BDC1E609D8A', 3, NULL, NULL, 2, NULL, 0, 'Let the shrimp sear for about one minute per side until just opaque. Remove and keep warm. Don''t move the scallops until you can see a brown edge forming around the bottom (about two to two and a half minutes.) Carefully lift one scallopif the surface has a nice caramelized color, turn it over.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7ABA5E4D2F87E44D8023E96D1AE4B26E', X'A1017E486B162C4EAE689BDC1E609D8A', X'A1017E486B162C4EAE689BDC1E609D8A', 4, NULL, NULL, 2, NULL, 0, 'Cook for just another minute until the scallops are springy but not quite firm. (See cook''s notes below) Remove the scallops and keep warm.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'28B6C00E8A30D143BE9ACE9B995695D9', X'A1017E486B162C4EAE689BDC1E609D8A', X'A1017E486B162C4EAE689BDC1E609D8A', 5, NULL, NULL, 2, NULL, 0, 'Wipe out the pan, and then add the strawberries and vinegar. Remove the strawberries after they are heated through (about 30 seconds) and let the balsamic cook down briefly until thickened to a syrupy consistency.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0033C6267A8DC48B269DE448CD912B8', X'A1017E486B162C4EAE689BDC1E609D8A', X'A1017E486B162C4EAE689BDC1E609D8A', 6, NULL, NULL, 2, NULL, 0, 'To serve, place the green on a plate, top with the scallops, shrimp, and strawberries, and then drizzle the balsamic reduction over the scallops and shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EF7F3CFE366204D85D8E9A9B5A21087', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A06C298D8AFDF47BFF1ECC47CC124D4', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 1, NULL, NULL, 2, NULL, 0, 'Add the beer, lemon juice, and salt to a large saucepan or stewpot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2937121EC9D6BE48BCEA708E28040165', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 2, NULL, NULL, 2, NULL, 0, 'Add your steamer basket to the pot, making sure that it sits well above the beer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9AFA2DEAA47F3249BA2B3FE7382CAD75', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 3, NULL, NULL, 2, NULL, 0, 'Add the celery and shrimp to the basket and cover.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94476E64249C494895B705FCFC03B9D6', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 4, NULL, NULL, 2, NULL, 0, 'Bring the beer to a boil, then reduce to a simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F010FDD041BDC840BF863CD517A76329', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 5, NULL, NULL, 2, NULL, 0, 'Cook until all of the shrimp turn pink. The top ones tend to change color first, so make sure to move them around and check that all have turned pink.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C5B90DBEF19AA4B85B4FBB1192D3842', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 6, NULL, NULL, 2, NULL, 0, 'Serve warm or cold as they are or with cocktail sauce or melted butter. As these are served with the shell on, remember to provide a bowl for the shells.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7A08214CD8E207438BCC788922E53C31', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', X'D7C6C001518AFD4EBC13CCFF5F1DF8FE', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5FEC13A464BCC4FA207C96892BD2AD0', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 0, NULL, NULL, 2, NULL, 0, 'Rinse the mussels and clams. Clean the shells using a stiff brush. Place in separate large bowls, cover with water and put in the fridge. Leave to soak for a few hours. You may need to change the clam''s water a few times if the clams are rather sandy.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'26C4C01D4B4EC245A1275F989BE3F344', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 1, NULL, NULL, 2, NULL, 0, 'Snip off the antennae of the shrimps.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6538503F161F8E45B58E9C4A57A3DCF9', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 2, NULL, NULL, 2, NULL, 0, 'Drain and rinse the clams and mussels several times. Pull out the mussel beards.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF385A424B6A90489EBF86E1030849DD', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 3, NULL, NULL, 2, NULL, 0, 'Chop the onion.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DE66D1E3CD17EE4D9A4405D5CD1591D4', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 4, NULL, NULL, 2, NULL, 0, 'Halve the tomatoes and squeeze out the seeds. Grate using the largest holes of a box grater.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'513F22F6F5652041BB81208F1A518150', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 5, NULL, NULL, 2, NULL, 0, 'Halve the bell peppers and remove the seeds. Dice the bell peppers.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EEB2BD412298DE4D92D36D7602BE8F76', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 6, NULL, NULL, 2, NULL, 0, 'Heat half a cup of the seafood broth. Sprinkle the saffron over the hot broth.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C6A8709A579DE2429F4FD720CA2437E9', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 7, NULL, NULL, 2, NULL, 0, 'Set the paella pan over medium-high heat. Pour in the olive oil. Use a wooden spatula to spread the oil over the entire bottom of the pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CF4212BE93F124CA2A6C4AD6C33421E', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 8, NULL, NULL, 2, NULL, 0, 'Saute the chopped onion and grated tomatoes until the onion pieces appear translucent. Add the rice. Stir to coat every grain with oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8830936DF9C9C446A42E6AA2CE9E3735', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 9, NULL, NULL, 2, NULL, 0, 'Pour in the seafood broth and the saffron with its soaking liquid. Season with salt and pepper. Stir then leave to boil for ten minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A272F2757F5DF44AC2666C919837152', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 10, NULL, NULL, 2, NULL, 0, 'Grate the zest of one lemon. Cut the two other lemons into wedges or slices.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BEADFF56A1E83148B927DB69D7D446DD', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 11, NULL, NULL, 2, NULL, 0, 'Scatter the lemon zest, diced bell peppers, sweet peas, shrimps, clams and mussels over the rice. Cook for another ten minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E5353D628A3AD74AAAB22355D5A41614', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 12, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat. Cover loosely with a large sheet of foil and leave to rest for ten minutes. Squeeze one lemon (the one without the zest) over the cooked paella.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'31DEA3E6C18323489C8FE0106ED41C0E', X'8F04306B5F245246AB42EB03851C5E0E', X'8F04306B5F245246AB42EB03851C5E0E', 13, NULL, NULL, 2, NULL, 0, 'Serve with lemon wedges or slices on the side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'144CF7EBD078074A87A869F4E8741E89', X'3246057D98330041AA52E3C1FE69974F', X'3246057D98330041AA52E3C1FE69974F', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D46ABB4D629F2C488708615C3A3F0E3A', X'3246057D98330041AA52E3C1FE69974F', X'3246057D98330041AA52E3C1FE69974F', 1, NULL, NULL, 2, NULL, 0, 'Lightly pound chicken between sheets of plastic wrap to an even thickness. Cut the chicken into strips about 1-inch wide. If you are using chicken tenders, omit this step.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10CE9141D2AB75418051FF7B792FB483', X'3246057D98330041AA52E3C1FE69974F', X'3246057D98330041AA52E3C1FE69974F', 2, NULL, NULL, 2, NULL, 0, 'To devein the shrimp, draw the tip of a small, sharp knife down the back of a shrimp. Remove the dark vein or coax it out with the knife. Repeat with the remaining shrimp. Rinse the shrimp under cold running water and then pat them dry with paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'20D2261F8C83994A82EBE894BA97ABF4', X'3246057D98330041AA52E3C1FE69974F', X'3246057D98330041AA52E3C1FE69974F', 3, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, melt the butter with the olive oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84A8321EE613A94CA98E9BD5D80C5D16', X'3246057D98330041AA52E3C1FE69974F', X'3246057D98330041AA52E3C1FE69974F', 4, NULL, NULL, 2, NULL, 0, 'When the butter stops foaming, add the garlic, basil, parsley, salt, and seasoning. Reduce the heat to medium-low and add the chicken and shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'098842E06EAFEA4D961B36DD0DF6227B', X'3246057D98330041AA52E3C1FE69974F', X'3246057D98330041AA52E3C1FE69974F', 5, NULL, NULL, 2, NULL, 0, 'Cook the shrimp and chicken mixture, stirring, for about 10 to 15 minutes, until cooked through. If the shrimp are small, add them a few minutes after adding the chicken. The shrimp will be opaque and pink when done, and the chicken will be white and opaque throughout.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54FEBC90730A8C4F923E2FAC7432080A', X'3246057D98330041AA52E3C1FE69974F', X'3246057D98330041AA52E3C1FE69974F', 6, NULL, NULL, 2, NULL, 0, 'Spoon the chicken and shrimp mixture over hot cooked pasta or rice for serving. Sprinkle with fresh chopped parsley, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EA7BC4D9560C040A887D8ADD101F4BE', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 0, NULL, NULL, 2, NULL, 0, 'Put the diced potato in a saucepan and cover with water. Add about 1/4 teaspoon of salt. Bring to a boil. Cover and reduce the heat to medium-low. Cook just until the potato is tender, about 5 minutes. Drain and set aside to cool.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'58E6D1FA0497794A93480CB551C37C4C', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 1, NULL, NULL, 2, NULL, 0, 'Melt 3 tablespoons of the butter in a skillet or saute pan over medium-low heat. When the butter is hot, saute the mushrooms and onion until the mushrooms are tender and the onion is translucent, or about 5 to 8 minutes. Stir in shrimp and remove from the heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DFE4F8A94167E249B95980B3AE61D910', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 2, NULL, NULL, 2, NULL, 0, 'Meanwhile, make a white sauce. In a saucepan over medium-low heat, melt remaining 3 tablespoons butter; stir in flour, salt, and pepper. Cook for 1 minute, while whisking constantly. Gradually add milk, whisking constantly. Add the sherry. Continue to cook, stirring, until thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9CDD1897E142EE4D855913D3ED38014A', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 3, NULL, NULL, 2, NULL, 0, 'With a slotted spoon, add the shrimp and mushroom mixture to the sauce along with the cubed cooked potatoes, the parsley, and the cooked peas and carrots. Add more liquid from cooking the mushrooms, as needed for thickness and flavor. Taste and adjust seasonings.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C889BCF64B2DB49A5884A88390BB940', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 4, NULL, NULL, 2, NULL, 0, 'Spoon the shrimp mixture into a 1-quart baking dish (an 8-inch round layer cake pan or 9-inch pie plate). Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A0B496435FBFDF41B26BE837D14CA31E', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 5, NULL, NULL, 2, NULL, 0, 'Heat the oven to 425 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'940DC2387B2CD740B97D04C765CB97DA', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 6, NULL, NULL, 2, NULL, 0, 'Roll out the pastry to about 11 inches in diameter. Fit the pastry over the shrimp filling. Trim and tuck the overhang under; flute as desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'71F7FADC3FAC3A4C966DAB2ED13885AE', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 7, NULL, NULL, 2, NULL, 0, 'Combine the egg white with 2 teaspoons of water and whisk to blend.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABB1EC248F7695429A659DF326861DC5', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 8, NULL, NULL, 2, NULL, 0, 'Brush the pastry lightly with the egg wash mixture and then sprinkle with sesame seeds, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB6E87EDC8185E4384B8CFC93743A670', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 9, NULL, NULL, 2, NULL, 0, 'With a sharp knife, cut several vents in the top of the crust to let steam escape.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A4610E5C4124494F80B760F3E533D63B', X'ED2DCCF1FFB279479FD263F17BB4771A', X'ED2DCCF1FFB279479FD263F17BB4771A', 10, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 20 to 25 minutes or until the crust is nicely browned and the filling is bubbly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8EC2B807BF7471429EE903528BD059F3', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7FC9752257898045A11E612DDE0F3641', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over medium heat, melt the butter. If using mushrooms, saute, stirring, until golden.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0EADBD5BFC40AA4AA2E18FF7402BA9E1', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 2, NULL, NULL, 2, NULL, 0, 'Blend the flour and seasonings into the butter. Cook, stirring, for about 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'398C5D738585A54A8C9D165FB80C6777', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 3, NULL, NULL, 2, NULL, 0, 'Gradually add half-and-half and sherry to the roux; cook until the sauce is smooth and has thickened, stirring constantly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DAF32D974B2CF04BAAAA814440DF61A9', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 4, NULL, NULL, 2, NULL, 0, 'Beat the egg yolks in a small bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BD7840074A51F140B353D1F39FFE14AF', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 5, NULL, NULL, 2, NULL, 0, 'Whisk about one-third of the hot cream sauce mixture into the egg yolks. Add the egg yolk and sauce mixture back to the saucepan and whisk to blend.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5D411946EF6AE49BD59249896109A7D', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 6, NULL, NULL, 2, NULL, 0, 'Heat to 175 F or until the cream sauce coats the back of a spoon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'085D9AB7C784644BB19677A3BEB64700', X'BDAD32CD188BBB4382A4540B896D27BB', X'BDAD32CD188BBB4382A4540B896D27BB', 7, NULL, NULL, 2, NULL, 0, 'Add shrimp and heat through, stirring constantly. Serve the shrimp and sauce over puff pastry shells, rice, angel hair pasta, or toast points.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6845F33465FC7B4B905A8F9EF70EAEBB', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75EBEC8E1931B9459F39F3A1D881EECE', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 1, NULL, NULL, 2, NULL, 0, 'Remove the shells from the shrimp. Run the tip of a small, sharp knife down the length of the back of each shrimp and remove the dark vein (this is the shrimp''s digestive tract, so it should be removed). Pick it out or scrape it out with the tip of the knife. Rinse the shrimp under cold running water and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F1F6BBEC92FCBA4E9468199A44219F98', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 2, NULL, NULL, 2, NULL, 0, 'Cook the angel hair pasta in boiling salted water following the package directions. If you''re using fresh pasta, you might want to wait to cook it at the last minute. Drain in a colander.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08396438C7EA564B962A70083C4DC4F2', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 3, NULL, NULL, 2, NULL, 0, 'Meanwhile, heat the butter and olive oil in a medium saucepan or saut pan over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D66FA0892A57C046AE4075F298199007', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 4, NULL, NULL, 2, NULL, 0, 'When the butter and oil are hot, add the minced garlic and cook until lightly golden and aromatic. Scoop the garlic out with a slotted spoon and discard.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23092D744E9C5B479D4137FD1D9EEC17', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 5, NULL, NULL, 2, NULL, 0, 'Add the shrimp to the garlic oil; sprinkle with the salt, pepper, and 1 tablespoon of the parsley.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D9E69BA044C4B4EBF4AD1B0143CC370', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 6, NULL, NULL, 2, NULL, 0, 'Cook over medium heat until shrimp is opaque and turns pink.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5014A1933697964DBB04179D49B003CB', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 7, NULL, NULL, 2, NULL, 0, 'Remove the pan from the heat and add the drained angel hair pasta, Parmesan cheese, and the remaining 1/2 tablespoon of parsley. Toss to blend.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E56C6218D73C694DAD4253580F5B44D3', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 8, NULL, NULL, 2, NULL, 0, 'Serve immediately with a Caesar salad or simple green salad, warm rolls or garlic bread, and more Parmesan cheese, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4AC65F62CB72B44594995541E1FD4B5E', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', X'2C0B3EAEDC0E1C4F85DD2BEE76085BC5', 9, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'985604487B9F074DA31B730E326823CA', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 0, NULL, NULL, 2, NULL, 0, 'Steam rice according to your rice cooker''s instructions or on the stovetop.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FB0E48524B867246AD748876EC3EB429', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 1, NULL, NULL, 2, NULL, 0, 'While the rice is cooking, boil green beans and slice on the diagonal into bite-sized pieces. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F50B6DF0CD773A43BC0E82746A6C8BAB', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 2, NULL, NULL, 2, NULL, 0, 'In a small bowl scramble eggs and add a pinch of salt. Heat medium size pan with olive oil and cook a thin layer of egg to make kinshi tamago (thin omelet). Remove from pan and let cool on cutting board. Cut into thin strips and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'832E053010A0A547942CF14BF7EB6FFC', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 3, NULL, NULL, 2, NULL, 0, 'Boil a small pot of water to cook the baby broccolini.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9398452002C069409CC32E9826F53E92', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 4, NULL, NULL, 2, NULL, 0, 'Thaw cooked frozen shrimp, remove tails if desired, and then slice in half vertically to create 6 halved pieces of shrimp. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'39DFD319C9A06A40932124962B82ACE7', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 5, NULL, NULL, 2, NULL, 0, 'Mix in sushi rice vinegar powder with cooked rice. You may or may not use all of the cooked rice, as it will depend on how thick you make your rice layers, as well as the diameter of the cake pan. Be sure to use diagonal cutting motions with the side edge of the rice paddle (pre-moistened with water) to mix with the warm rice. This helps to prevent the rice from getting mashed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D892B6489CB4148B50D9377D4139C99', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 6, NULL, NULL, 2, NULL, 0, 'Line cake pan with a large sheet of plastic wrap. It will help you to remove the sushi with ease. If you are using a springform pan (with removable bottom), there''s no need to line the pan with plastic wrap, but instead, make sure the pan is wet with water, to prevent the rice from sticking too much from the side of the pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BF0DA88DCC6C546A08BC49A28F9FBA3', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 7, NULL, NULL, 2, NULL, 0, 'Add a layer of rice on the bottom of the pan. Top with pink sakura denbu shredded codfish. Layer more rice on top and then garnish with sliced green beans. Layer with the last layer of rice. Garnish top with thinly sliced egg, baby broccolini, shrimp, ikura (salmon roe caviar), and any optional garnishes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A6CA91594DB1B489878DD36646D81E2', X'AC92E0F5A96E4047BED7EFE885363CBC', X'AC92E0F5A96E4047BED7EFE885363CBC', 8, NULL, NULL, 2, NULL, 0, 'Remove from pan onto a plate and serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10372327FD455A4CBE86E048DFDB65E9', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DA1FCAEF664E1A4CAFA0E6B4A4F2D047', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 1, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 325 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'53A46F70E3FA964286F287EDBC99AD9D', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 2, NULL, NULL, 2, NULL, 0, 'Butter a shallow 2 1/2-quart baking dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02B636F1EB816C43AA2E29C3190D8FE8', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 3, NULL, NULL, 2, NULL, 0, 'Place shrimp in an even layer in the dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F86088BE23CB44B82E828EF7C5B517B', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 4, NULL, NULL, 2, NULL, 0, 'Combine the butter, bread crumbs, cheese, salt, lemon juice, and sherry.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D35C43948F0A7649A6D93FDCB28690D8', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 5, NULL, NULL, 2, NULL, 0, 'Sprinkle crumb mixture evenly over shrimps.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'33C81A1241EB434BA9127214AD5A83B6', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 6, NULL, NULL, 2, NULL, 0, 'Bake in the preheated oven for 20 to 25 minutes, or until shrimps are cooked.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F91DA341F9F66F409B7DB3CC905EF45D', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 7, NULL, NULL, 2, NULL, 0, 'Increase the oven setting to broil and broil the shrimp for a minute or two, or just long enough to brown the topping.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2159C39BDD776C4BACAE328264A0FE5B', X'036149151BABA74D9407243CA6FC5386', X'036149151BABA74D9407243CA6FC5386', 8, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B768ED56C3692A4893061635DFD386BE', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA2D27F1D958C447A370F9B2A7234407', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 1, NULL, NULL, 2, NULL, 0, 'If using frozen shrimp, defrost in the refrigerator. Rinse the shrimp under cold running water and pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F7A96C795F8454AA8E0862AED7095EB', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 2, NULL, NULL, 2, NULL, 0, 'Place the shrimp in a large bowl and add the rice wine or sherry, 1/2 teaspoon salt, and 1 tablespoon cornstarch, stirring in one direction (this is to make sure the marinade spreads evenly).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9EBA4FAA458BA2418CDB32C67F39619E', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 3, NULL, NULL, 2, NULL, 0, 'Chop the bok choy stalks diagonally and the leaves across into 1-inch pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA0C5B664109DD4B89B6F89D8C5A1F5B', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 4, NULL, NULL, 2, NULL, 0, 'Wipe the mushrooms with a cloth or soft brush and cut into thin slices. If using Chinese dried mushrooms, soak in hot water for 20 minutes to soften. Drain the softened mushrooms, remove the stems, and cut into quarters.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19E98730D948914BAA7CC51BD4AE9688', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 5, NULL, NULL, 2, NULL, 0, 'Heat the wok and add 2 tablespoons oil. When the oil is hot, add the ginger. Stir-fry for about 30 seconds, until aromatic.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'980F8A83D151074A9BA5B403245E1407', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 6, NULL, NULL, 2, NULL, 0, 'Next, add the shrimp and stir-fry until they turn pink. Remove the cooked shrimp from the pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62E397A02EC8044FB465EA194560AAAA', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 7, NULL, NULL, 2, NULL, 0, 'Add a bit more oil if needed so there are about 1 1/2 tablespoons in the wok. Add the bok choy, mushrooms, and 1/4 teaspoon salt. Stir-fry for 1 minute. If the vegetables seem a bit dry at this point, add a small amount of water or rice wine. Add the chicken broth, cover and cook for 2 more minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F4113FC2B0DA94982998D6C5297DB28', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 8, NULL, NULL, 2, NULL, 0, 'Return the shrimp to the pan. Add the sugar, soy sauce, and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D51E2160233F547BEC4C5C050840A41', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 9, NULL, NULL, 2, NULL, 0, 'Give the cornstarch/water mixture a quick stir and add to the middle of the ingredients, stirring to thicken. Cook, stirring for another minute.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5669C4F773E6B840859FEDB1CD9B7D08', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 10, NULL, NULL, 2, NULL, 0, 'Serve hot with rice, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1FC0927F0DD34468E47CF77F1CD90BC', X'DE428AD1C56F9648BF0F5424D5248359', X'DE428AD1C56F9648BF0F5424D5248359', 11, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0B0FAB33F93D2246B8ED459D60836310', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 0, NULL, NULL, 2, NULL, 0, 'Whisk together the salt, cayenne pepper, paprika, garlic powder, and onion powder.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F3B757C6639B74488E584F4EB0252B2E', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle the chicken cubes with about a third of the seasoning mix and toss to coat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AF1B23ACF482844C8FF6A4A362D1576A', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 2, NULL, NULL, 2, NULL, 0, 'In a separate bowl, do the same with the shrimp, using another third of the seasoning mix.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE8037F8948C10449DFF294D7AE51387', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 3, NULL, NULL, 2, NULL, 0, 'Begin cooking the pasta.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3899D5F4FA9FC648AD838B325F211935', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 4, NULL, NULL, 2, NULL, 0, 'Place a large, deep, heavy skillet over medium-high heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6EA83EC26B3F545BA474F0A47BA1A08', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 5, NULL, NULL, 2, NULL, 0, 'When the skillet is hot, add 1 tablespoon of the olive oil and swirl to coat the pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D045F6B3B40BDA42B5B1BBD43C479344', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 6, NULL, NULL, 2, NULL, 0, 'Add the chicken to the skillet and quickly sear it on two sides, about 3 minutes, then add the shrimp and brown each side 30 seconds.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1769D48A234FB9469F81289E9BF3FA45', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 7, NULL, NULL, 2, NULL, 0, 'Remove the chicken and shrimp to a bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'81320DF1E24068408630106915F6C2FA', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 8, NULL, NULL, 2, NULL, 0, 'Add the remaining 1 tablespoon of olive oil to the pan and toss in the kielbasa sausage, tomatoes, bell pepper, and onion.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7352782BD5A7D84FBF4DD0AF176EDB2A', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle the sausage and vegetables with the remaining third of the spice blend and cook over high heat until the vegetables are browned and softened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4F7A2AEE2564A47BDBFAB3FA7B1FC25', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 10, NULL, NULL, 2, NULL, 0, 'Add half of the chicken stock and continue cooking over high heat until most of the stock has evaporated.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB56656106225F49AB7249F1DE4C57F1', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 11, NULL, NULL, 2, NULL, 0, 'Add the garlic and wine, stirring for 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B3CDCDB67D821B4F8E0A9E9FB31B0CE4', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 12, NULL, NULL, 2, NULL, 0, 'Add the remaining chicken stock, along with the reserved chicken and shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5BB275990DA31341AF122CAB3F42C2D9', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 13, NULL, NULL, 2, NULL, 0, 'Stir, scraping up the browned bits from the bottom of the skillet, and simmer another 3 minutes or until the liquid is slightly reduced.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'18ED30630A3508409BAC4BB04CC40005', X'4378323F178AA54CA8AB2BA1725E0A4C', X'4378323F178AA54CA8AB2BA1725E0A4C', 14, NULL, NULL, 2, NULL, 0, 'Serve the jambalaya over the cooked fettuccine.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDE9AC52B407D541B3B9932AC29E9392', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'785F7F7B1EEE154EB56247A872A2F767', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 1, NULL, NULL, 2, NULL, 0, 'Peel the shrimp, leaving the tails on, and put them in a colander. Rinse the shrimp under cold running water, then pat dry with paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17BCC73FC8324743886575C4B651F9D0', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 2, NULL, NULL, 2, NULL, 0, 'Put about 3 inches of vegetable oil in a deep, heavy saucepan or deep fryer. Heat the oil to 360 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C54ECBD16D9EB4A875B7DA03475954C', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 3, NULL, NULL, 2, NULL, 0, 'To keep the fried shrimp warm as you fry subsequent batches, heat the oven to 200 F. Place a rack in a baking sheet and set aside. Have paper towels ready for draining the shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0DE6CEA1B1A33F4BB04C16C3B13C7BC3', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 4, NULL, NULL, 2, NULL, 0, 'In a medium bowl, whisk the milk with the egg until well blended.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'25B3CE8A831768449AD6E288B25A6D29', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 5, NULL, NULL, 2, NULL, 0, 'Put the shrimp in the milk and egg mixture and let stand for 3 to 4 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'77E2FC90E79B47479484417AEF7270A9', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 6, NULL, NULL, 2, NULL, 0, 'Combine the flour, cornmeal, and baking powder in another bowl; blend well. Stir in salt and a dash of freshly ground black pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'32A34ACB5309554ABCE74423BF5EA4F9', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 7, NULL, NULL, 2, NULL, 0, 'Dip the shrimp into the flour and cornmeal mixture to coat thoroughly. Shake off excess.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8FC660D86DCCD46A948C5E772C6BC38', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 8, NULL, NULL, 2, NULL, 0, 'Place coated shrimp in the hot oil and cook in small batches until golden brown, about 2 to 3 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D7D811295BFEAC4FAE1D3643709EAAD4', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 9, NULL, NULL, 2, NULL, 0, 'Drain on paper towels and then move to the rack on the baking sheet. Place the pan in the oven to keep them warm while you fry the remaining shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3ACC3204DDCB20409BDE6B63E2779D45', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 10, NULL, NULL, 2, NULL, 0, 'Sprinkle the fried shrimp with salt and freshly ground black pepper, to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6112FB3888CAF44EB635E6CA33174BDE', X'C7199D31FECAED4A85219A642239EB66', X'C7199D31FECAED4A85219A642239EB66', 11, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'83A979C963882B4194CC5AE099F29290', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 0, NULL, NULL, 2, NULL, 0, 'Gather ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C25E6BBDECE264EA9BFCE1EAE53A26F', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 1, NULL, NULL, 2, NULL, 0, 'In a large saucepan add the shrimp to the hot roux and cook for a few minutes. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C88926B651731419CB56D88615EFC69', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet, heat up the oil and cook the okra and onions until the onions are transparent and fragrant and the okra is just becoming tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'742AE704620AAD4998EAB368696C6E74', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 3, NULL, NULL, 2, NULL, 0, 'Add tomatoes and garlic to the okra and onion mixture. Cook a few more minutes, adding the water, salt, and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5611A97FBEBA6C49B8FCA7BFBB58B843', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 4, NULL, NULL, 2, NULL, 0, 'Combine the shrimp and roux mixture with the okra mixture in the bigger pan. Simmer the stew for about 10 minutes. Add oysters, crab meat, and crab claws and simmer for an additional 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F973A29872B4BD4FB6425216C36E1313', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 5, NULL, NULL, 2, NULL, 0, 'Add the parsley and green onions and simmer for another 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BF84D1FBE76FBE4C8DD135490017FF42', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 6, NULL, NULL, 2, NULL, 0, 'Once the stew is ready and thick, serve with a side of cooked rice and add garnish with additional parsley and green onions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1311D4A70549DB4E86410549062A1C40', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', X'A4048C5DB3EDF14BA1FE90FE1A9D4CA2', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED4F854B1CA9214684516FF4D370EC80', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 0, NULL, NULL, 2, NULL, 0, 'Heat a large cast-iron pot or Dutch oven over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EFE8B9FE4C4EF040A0212312CD22106E', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 1, NULL, NULL, 2, NULL, 0, 'When the pot has heated, add the oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4374E78A4A836048BD71395E1A1AAD6E', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 2, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add the flour and cook, whisking almost constantly (that is, about every 15 seconds), for 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0C7A679E6A03147AF5F91BECF23C4D5', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 3, NULL, NULL, 2, NULL, 0, 'Reduce the heat to low and continue to cook, stirring almost constantly, for 5 to 10 minutes longer until roux is medium color  the color of peanut butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9BACD30F02330D40BD4068257E4B2DA8', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 4, NULL, NULL, 2, NULL, 0, 'Add the bell pepper, onion, and celery and cook, stirring often, for another 10 minutes. Add the salt, the three types of pepper and bay leaves and stir to combine seasonings with the roux.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F63E3FD1B7715D42BEE510665F136996', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 5, NULL, NULL, 2, NULL, 0, 'Add the stock and water. Raise heat and bring to a boil, whisking constantly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5AF914862E0C4B46BE1B6D0D7C799DD3', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 6, NULL, NULL, 2, NULL, 0, 'Reduce heat to medium, add the Andouille slices, and simmer for 30 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D8A8DAD33B00A44A61B703AA3DC7D13', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 7, NULL, NULL, 2, NULL, 0, 'Add the shrimp and simmer 5 minutes longer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EDB0AB83E304944F9CFF1AD93B3F623F', X'A8A626EABF1A654AB9FA62F1D727A14E', X'A8A626EABF1A654AB9FA62F1D727A14E', 8, NULL, NULL, 2, NULL, 0, 'Serve a scoop of rice on top of each bowl of gumbo, then sprinkle with the green onions and parsley. (Cook the rice while the gumbo is simmering for the final 30 minutes.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D869A72CDA4FA04A90C8ABACB292068A', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'43B07BD8F58ECA4DAD776FDB258AE738', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 1, NULL, NULL, 2, NULL, 0, 'With the tip of a sharp knife, make a shallow cut down the back of each shrimp. Peel the shrimp, remove the dark veins.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4711A54AFEBDDA488A7A506CE1051FC7', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 2, NULL, NULL, 2, NULL, 0, 'Rinse the shrimp under cold running water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'348DC65011552B4A83EB147604C11B95', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 3, NULL, NULL, 2, NULL, 0, 'Bring a small saucepan of lightly salted water to a boil. Add the shrimp and cook for about 2 minutes, or until they are pink and opaque.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F8B6CFFEB930F54F9E3D67D437AD5594', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 4, NULL, NULL, 2, NULL, 0, 'Drain and transfer the shrimp to a bowl. Cover and refrigerate.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48D82190D8068C4E85D95D872BCAEAC7', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 5, NULL, NULL, 2, NULL, 0, 'To make the roux, wipe the saucepan out with a paper towel and add the flour and oil, mixing well. Place the saucepan over medium-high heat and cook for 5 minutes, stirring constantly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C570C02149B5C44B0DABB093A5321CD', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 6, NULL, NULL, 2, NULL, 0, 'Reduce the heat to medium-low and continue cooking for 8 to 12 minutes, or until the mixture turns a light reddish-brown. Stir constantly to prevent scorching.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'54853498D6AC414B87D37716DD50AED1', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 7, NULL, NULL, 2, NULL, 0, 'Transfer the roux to the crock pot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8087BB7737A1C54E9F175EF5C9A87BB8', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 8, NULL, NULL, 2, NULL, 0, 'Add all but the shrimp to the crockpot. Cover and cook on low for 7 to 9 hours.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7DEECE962429B24483897E4989BA3F5B', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 9, NULL, NULL, 2, NULL, 0, 'Add the cooked shrimp to the gumbo and mix well. Cover and continue to cook on low for 15 to 20 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'440D04D77A41AD459F45E35A960FCA21', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 10, NULL, NULL, 2, NULL, 0, 'Meanwhile, cook the rice in the chicken stock or water, following the package directions.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'76A63619667CDD4EBC8424FCBE39EE22', X'ACAB7F14D16E824B99AD74E47BC36583', X'ACAB7F14D16E824B99AD74E47BC36583', 11, NULL, NULL, 2, NULL, 0, 'Serve the gumbo over the hot cooked rice along with crusty French baguettes, cornbread, or biscuits.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'675855E2AAFAAD4280BAD5061F3CEFFE', X'51B35DAD574ED049A4A6B2B7E7B8EE85', X'51B35DAD574ED049A4A6B2B7E7B8EE85', 0, NULL, NULL, 2, NULL, 0, 'Make the lobster sauce first, and keep it warm.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4827EABD24F7C44C97268EC563C6E373', X'51B35DAD574ED049A4A6B2B7E7B8EE85', X'51B35DAD574ED049A4A6B2B7E7B8EE85', 1, NULL, NULL, 2, NULL, 0, 'Run the shrimp under warm running water until thawed. Pat dry with paper towels. Shell and devein if necessary.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4676AAE76967BC47912D2C77B2ACA02F', X'51B35DAD574ED049A4A6B2B7E7B8EE85', X'51B35DAD574ED049A4A6B2B7E7B8EE85', 2, NULL, NULL, 2, NULL, 0, 'Marinate the shrimp in the rice wine or sherry, salt and cornstarch for 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0074B12AB9B96147B76A73B8AB0D55BE', X'51B35DAD574ED049A4A6B2B7E7B8EE85', X'51B35DAD574ED049A4A6B2B7E7B8EE85', 3, NULL, NULL, 2, NULL, 0, 'Heat the wok over medium-high to high heat. Add the oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A2A2F42E0B0E1041AF2B2B76D4CA82D2', X'51B35DAD574ED049A4A6B2B7E7B8EE85', X'51B35DAD574ED049A4A6B2B7E7B8EE85', 4, NULL, NULL, 2, NULL, 0, 'When the oil is hot, add the ginger and green onion. Stir-fry until aromatic (about 30 seconds).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D6AF60253255D74C823F7308FCC84DA8', X'51B35DAD574ED049A4A6B2B7E7B8EE85', X'51B35DAD574ED049A4A6B2B7E7B8EE85', 5, NULL, NULL, 2, NULL, 0, 'Add the shrimp and stir-fry until they turn pink.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'355A5557810C4947ADF78C02B7060F24', X'51B35DAD574ED049A4A6B2B7E7B8EE85', X'51B35DAD574ED049A4A6B2B7E7B8EE85', 6, NULL, NULL, 2, NULL, 0, 'Push the shrimp up to the sides of the pan and add the sauce in the middle. Bring to a boil and then mix in with the shrimp. Serve hot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA61E05EE45C8B4A93F273323827174D', X'809EEA828F54624EA8C30610D34E1938', X'809EEA828F54624EA8C30610D34E1938', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'12B2E4EBB42AA342B70F1C9E5067F07D', X'809EEA828F54624EA8C30610D34E1938', X'809EEA828F54624EA8C30610D34E1938', 1, NULL, NULL, 2, NULL, 0, 'Cook the pasta according to the directions on the package.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0C0B3C6DDB1C8540991EB69176835E5C', X'809EEA828F54624EA8C30610D34E1938', X'809EEA828F54624EA8C30610D34E1938', 2, NULL, NULL, 2, NULL, 0, 'While the pasta cooks, in a skillet, heat the butter and olive oil over medium heat. Add the garlic and cook 3 minutes, watching to make sure the garlic doesn''t brown.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3AD53D4D6B7644429E40137481F2042E', X'809EEA828F54624EA8C30610D34E1938', X'809EEA828F54624EA8C30610D34E1938', 3, NULL, NULL, 2, NULL, 0, 'Add the shrimp, parsley, lemon juice, and salt, and cook until the shrimp are pink and curled, 2 to 3 minutes a side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A7004434104644E95B9FCB2DC2B0E68', X'809EEA828F54624EA8C30610D34E1938', X'809EEA828F54624EA8C30610D34E1938', 4, NULL, NULL, 2, NULL, 0, 'Drain the pasta and toss together with the shrimp mixture and the Parmesan cheese.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'388A4F899CCA784789AB3DB611A3D7CC', X'809EEA828F54624EA8C30610D34E1938', X'809EEA828F54624EA8C30610D34E1938', 5, NULL, NULL, 2, NULL, 0, 'Serve and enjoy.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1089555D6CE7D649BD29B17B59788C2C', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F66F67C2F2AD946848E941474FCADEC', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 1, NULL, NULL, 2, NULL, 0, 'Soak about 45 toothpicks in water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'032E71C7013DC642995041027A1A4319', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 2, NULL, NULL, 2, NULL, 0, 'Fill a large bowl with ice and cold water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'049ED80A6AC4D442A7908DF1E90CE0C6', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 3, NULL, NULL, 2, NULL, 0, 'Peel the shrimp, leaving just the tails on if preferred.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59E2A7757BB27D45BDA424861AF61C72', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 4, NULL, NULL, 2, NULL, 0, 'With a sharp knife, make a shallow cut down the back of the shrimp. Pull or scrape out the dark vein.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9981DAD02C2734B915D3CA80D4D3E9D', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 5, NULL, NULL, 2, NULL, 0, 'Rinse each shrimp with cold water and place it in the ice water as you work with the remaining shrimp.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'999A365E5EC8C140A19F4219E8133961', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 6, NULL, NULL, 2, NULL, 0, 'With the help of scissors or a very sharp knife, cut the bacon crosswise into thirds.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ABD64827D3E6D4479520E16266EBC0F2', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 7, NULL, NULL, 2, NULL, 0, 'Lay the shrimp out on a work surface and pat dry with paper towels. Sprinkle them with garlic powder and Kosher salt.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'60A88BD930B5724BB04A81E7D6BD1E08', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 8, NULL, NULL, 2, NULL, 0, 'Wrap each shrimp in a piece of bacon and secure it with a toothpick. Arrange the wrapped shrimp on a broiler rack. Repeat with the remaining shrimp and bacon pieces.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1E1B2FB62CDA2A45A8D66619B719EE2A', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 9, NULL, NULL, 2, NULL, 0, 'Broil the wrapped shrimp, turning occasionally, 3 to 4 inches from heat for about 6 to 10 minutes total, or until bacon is crisp around the edges and well browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FF14BFD53C8DB44A1298F4B7E888CAB', X'D78BA9CCA6CC874A89A7D04CC18250BF', X'D78BA9CCA6CC874A89A7D04CC18250BF', 10, NULL, NULL, 2, NULL, 0, 'Serve immediately or transfer the shrimp to a warming tray or slow cooker for self-serving. Have a plate available for guests to discard the used toothpicks.');
