INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6293DDD6C4B394BB41F95B719EEC46B', X'F9F8495363D1FE4A8B1B88A7C4C91646', X'F9F8495363D1FE4A8B1B88A7C4C91646', 9, NULL, NULL, 2, NULL, 0, 'Steam over boiling water until the filling is cooked through, 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7B50E0545F2DF940B3D55CF16AC32E0E', X'F9F8495363D1FE4A8B1B88A7C4C91646', X'F9F8495363D1FE4A8B1B88A7C4C91646', 10, NULL, NULL, 2, NULL, 0, 'Serve with soy sauce and enjoy!');
