INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C40BEC8483424C468182E69DDBD29C18', X'4B6B44633E222B4594CF5171B73D33CA', X'4B6B44633E222B4594CF5171B73D33CA', 2, NULL, NULL, 2, NULL, 0, 'Bring 3 quarts of water with 1 tablespoon of salt to a boil over high heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'95754AB8ADD6DB48ACA2BB6F81230223', X'4B6B44633E222B4594CF5171B73D33CA', X'4B6B44633E222B4594CF5171B73D33CA', 3, NULL, NULL, 2, NULL, 0, 'Add the shrimp and reduce the heat to medium-low. Cover the pan and simmer for about 5 minutes, until the shrimp are opaque and pink. Make sure not to overcook.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0331FA9D1975F34EB8C012884192FEA2', X'4B6B44633E222B4594CF5171B73D33CA', X'4B6B44633E222B4594CF5171B73D33CA', 4, NULL, NULL, 2, NULL, 0, 'Drain immediately and transfer to a bowl. Cool completely, dry with a paper towel, and chop coarsely.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A16875E4EA899541B212C221B3D2A7AB', X'4B6B44633E222B4594CF5171B73D33CA', X'4B6B44633E222B4594CF5171B73D33CA', 5, NULL, NULL, 2, NULL, 0, 'Mix the chopped shrimp with the celery, bell pepper, lemon juice, and mayonnaise.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'081FB081F1088149AE03ABC57BC81419', X'4B6B44633E222B4594CF5171B73D33CA', X'4B6B44633E222B4594CF5171B73D33CA', 6, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to taste. Taste and adjust the mayonnaise and seasonings, as desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'07DB1FB2BC4E1B418D2A29F6E878356D', X'4B6B44633E222B4594CF5171B73D33CA', X'4B6B44633E222B4594CF5171B73D33CA', 7, NULL, NULL, 2, NULL, 0, 'Serve on a bed of lettuce or mixed greens and a slice of avocado or tomato wedges, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'74156B41FEF3E34CBE2EF009561634C0', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A393CDE817A17409D0A94A32C34B0A7', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', 1, NULL, NULL, 2, NULL, 0, 'Mix together the shrimp, celery, onion, pickle, black pepper, and cayenne.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1D2FAB1D69C9D34EBBB0179313A379BD', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', 2, NULL, NULL, 2, NULL, 0, 'Fold in the mayonnaise and lemon juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B21D919EDFCF264580BB5D761D953766', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', 3, NULL, NULL, 2, NULL, 0, 'Stir in the fresh bread crumbs, starting with half a cup and adding more as needed until most of the excess liquid is absorbed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B4E8CC8407CA17439EB070797CCCAE93', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', 4, NULL, NULL, 2, NULL, 0, 'Serve over greens, with crackers, or in sandwiches.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D412740032E1BE4BB576B9C8EA30430B', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', X'4B11CE15AF4C7E4CBDE2E5E5F6DDE902', 5, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F84B5727F7F7941BCAB18764D5244E6', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'34A5E2B3B856434E90F151CF9248DE78', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 1, NULL, NULL, 2, NULL, 0, 'To split the lobster tails in half, place them on a cutting board, backside down. Use a large, sharp knife to cut down through the center to the shell the whole length.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A358B1BD144484EBE9007885C0C83E0', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 2, NULL, NULL, 2, NULL, 0, 'Fold the shell back (like you are opening a book), breaking through the shell. If the shell doesn''t break, you can use kitchen shears to cut through it. The flesh should now be exposed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'84491E2B1645A94CBA707793EC92985B', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 3, NULL, NULL, 2, NULL, 0, 'Pat the lobster tails dry with paper towels.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3729834DF02B764296DA52EA6D737DB5', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 4, NULL, NULL, 2, NULL, 0, 'Preheat the grill for medium-high heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'10D3411AEF520844BF31706620342D06', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 5, NULL, NULL, 2, NULL, 0, 'Combine the olive oil (or butter), lemon juice, dill, and salt in small mixing bowl until the salt has dissolved.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'298FD7A5E8784B469EF1BC6A8B311F33', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 6, NULL, NULL, 2, NULL, 0, 'Brush the mixture onto the flesh side of lobster tails.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'23EB09202CEF01478B0FCF155AFD08B4', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 7, NULL, NULL, 2, NULL, 0, 'Place the lobster tails onto the grill and cook for 5 to 7 minutes, turning them once during the cooking process. Depending on the size of the lobster tail, it may need to cook longer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'02F1CCBB23F9ED438D5F625E01EE2F4E', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 8, NULL, NULL, 2, NULL, 0, 'Lobster tails will be done once meat reaches an internal temperature 140 F. The shells should turn a bright pink and the flesh should be opaque all the way through the center.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1F4B47D04794EE4A847C8C020E0B6957', X'A60FE8AE9BB29640BEF339527821C72A', X'A60FE8AE9BB29640BEF339527821C72A', 9, NULL, NULL, 2, NULL, 0, 'Remove the lobster tails from the heat. Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4205FC60E6DAD74A838786F18A30002E', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0035159EDE4B434BA21438A1CEAB8477', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 1, NULL, NULL, 2, NULL, 0, 'Cut each lobster tail in half. Wash thoroughly. Drain. Cut tails into pieces 1 1/2 inches in length, leaving shells on.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6BF042514823F54D9972869813D025E7', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 2, NULL, NULL, 2, NULL, 0, 'Sprinkle 1/2 teaspoon of salt and the flour on lobster.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'421B343D9D0CF041A0A9465FCD68E0FE', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 3, NULL, NULL, 2, NULL, 0, 'Mix garlic with black bean mixture in a bowl. Stir well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9A85A1CD60105345A9A9DC786C794D53', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 4, NULL, NULL, 2, NULL, 0, 'Heat oil for deep-frying to smoking hot. Put in lobster pieces. Deep-fry until shells are red. Drain. Remove.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F225709865755D43B3AF80C78E99A6E8', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 5, NULL, NULL, 2, NULL, 0, 'Reheat 1 tablespoon of oil in the wok. Stir-fry black bean mixture 30 seconds. Remove.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B9EE73414D6CF6499494954969FFB297', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 6, NULL, NULL, 2, NULL, 0, 'Reheat 2 tablespoons of oil in wok. Stir-fry ginger and scallion until there is an aroma, about 30 seconds. Add pork. Stir-fry on high heat until color changes. Add 1 tablespoon of sherry, soy sauce, black bean mixture, and stock. Bring to boil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8A940B082D1F9644980352F15B5FCB9C', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 7, NULL, NULL, 2, NULL, 0, 'Add lobster to meat mixture. Add 1 tablespoon sherry. Cover. Cook 3 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BADAECDB21D36140AF0ED940CE0D6B31', X'C9798E1317BE9F46B5909BE84884F115', X'C9798E1317BE9F46B5909BE84884F115', 8, NULL, NULL, 2, NULL, 0, 'Remove lid. Bring to boil. Thicken gravy with dissolved cornstarch. Season with 1/2 teaspoon salt. Add scallions. Slowly pour in beaten egg. Stir-fry about 10 seconds. Remove.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F7ECE23B10BAA2489486F4112F350F32', X'FD15D1EC23780A478045B9F14480D6C3', X'FD15D1EC23780A478045B9F14480D6C3', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'78B1C12B420F304F8B7C19A5C72425EA', X'FD15D1EC23780A478045B9F14480D6C3', X'FD15D1EC23780A478045B9F14480D6C3', 1, NULL, NULL, 2, NULL, 0, 'Melt the butter in a large pan, once melted add the flour, a pinch of salt and pepper and the paprika. Stir well.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A6F888B40ACE4C40BAA1F338B1B6A815', X'FD15D1EC23780A478045B9F14480D6C3', X'FD15D1EC23780A478045B9F14480D6C3', 2, NULL, NULL, 2, NULL, 0, 'Slowly whisk in the milk, followed by the chicken stock, the white wine, and continue stirring until a smooth and slightly thickened to a cream-like consistency. (This stirring can take several minutes but spending the time at this stage of the bisque is critical to the success of the dish. Be careful not to let the mixture boil as this may cause the bisque to curdle.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C34BCC78D562424C96FCB93CD5D95BCE', X'FD15D1EC23780A478045B9F14480D6C3', X'FD15D1EC23780A478045B9F14480D6C3', 3, NULL, NULL, 2, NULL, 0, 'Add the onion, the carrots and simmer (again, do not boil) for 10 minutes. Add the cooked lobster meat and stir. Taste the bisque and adjust the seasoning to your liking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0CFDB830AE98A7418030370DABA6315B', X'FD15D1EC23780A478045B9F14480D6C3', X'FD15D1EC23780A478045B9F14480D6C3', 4, NULL, NULL, 2, NULL, 0, 'Stir in the Cognac and finally, the cream. Heat through until piping hot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1FA01B2360038540A94A058B45239C4D', X'FD15D1EC23780A478045B9F14480D6C3', X'FD15D1EC23780A478045B9F14480D6C3', 5, NULL, NULL, 2, NULL, 0, 'Serve in hot bowls with a crusty baguette on the side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C83D31BC25E1204F84D59F40374DB344', X'FD15D1EC23780A478045B9F14480D6C3', X'FD15D1EC23780A478045B9F14480D6C3', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CF127ADBCE64DF4DBB036AAAF9612158', X'7AACD18D94A9F44BA692C2711D28F50D', X'7AACD18D94A9F44BA692C2711D28F50D', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'200E687F0B55AD4C8D22B573E8600D8A', X'7AACD18D94A9F44BA692C2711D28F50D', X'7AACD18D94A9F44BA692C2711D28F50D', 1, NULL, NULL, 2, NULL, 0, 'In a blender or food processor combine butter and basil. Blend until smooth. Season with salt and pepper to taste and blend to mix. (If you are using salted butter you might not need to add salt.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E55610F7A0BD8943BCA4D4CB9DF16285', X'7AACD18D94A9F44BA692C2711D28F50D', X'7AACD18D94A9F44BA692C2711D28F50D', 2, NULL, NULL, 2, NULL, 0, 'Transfer the mixture into a small bowl, cover and refrigerate.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE03F368E723FF4AA7011C136CD5CD75', X'7AACD18D94A9F44BA692C2711D28F50D', X'7AACD18D94A9F44BA692C2711D28F50D', 3, NULL, NULL, 2, NULL, 0, 'Prepare lobster tails by splitting in half. To do this use a large, sharp knife. Place the lobster tails on a cutting board backside down. Cut down through the center to the shell the whole length. Fold back, breaking through the shell. (You can use kitchen shears to cut through the shell if you need to.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1CFDB082B18AD840AEE159B201C8FE26', X'7AACD18D94A9F44BA692C2711D28F50D', X'7AACD18D94A9F44BA692C2711D28F50D', 4, NULL, NULL, 2, NULL, 0, 'Brush flesh lightly with oil and place cut side down on a preheated grill over medium heat. Grill for 5 to 7 minutes depending on the size of the lobster tails.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6751A8D4DDA89408492F35243C6A1C3', X'7AACD18D94A9F44BA692C2711D28F50D', X'7AACD18D94A9F44BA692C2711D28F50D', 5, NULL, NULL, 2, NULL, 0, 'Remove from grill, brush heavily with basil butter, and serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7874242324D02D4EA61ACA9938BB2DBA', X'7AACD18D94A9F44BA692C2711D28F50D', X'7AACD18D94A9F44BA692C2711D28F50D', 6, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'107E47B7675C5546BAD1EDDF2E9067B8', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 0, NULL, NULL, 2, NULL, 0, 'Split the live lobster in half.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA8A353E78FA124B9CA208282149A02E', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 1, NULL, NULL, 2, NULL, 0, 'Sprinkle with salt and pepper, and brush with olive oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7072C62640287B47B0894F826D8DAEA4', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 2, NULL, NULL, 2, NULL, 0, 'Bake it in a 425 F oven for about 18 minutes. Baste during the cooking with additional olive oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F92824CD4BC0A4EA23827D9E29EC402', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 3, NULL, NULL, 2, NULL, 0, 'When the lobster is cool enough to handle, remove the meat from the body and claws, and dice it.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BF570DAFE738E4DA8C365F41D1C6BCF', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 4, NULL, NULL, 2, NULL, 0, 'Combine the wine, broth, shallots, and tarragon.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'30EF606EDA5BB14898B48D799D508A8F', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 5, NULL, NULL, 2, NULL, 0, 'Cook until the mixture is reduced to practically a glaze.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A39132E7E568945BBDAF4FE5338741E', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 6, NULL, NULL, 2, NULL, 0, 'Add this to the bechamel sauce, and stir in the cream mixed with the egg yolks.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AAD433B6565FCE42862333FDF3E49034', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 7, NULL, NULL, 2, NULL, 0, 'Allow this to heat without boiling.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6263A92F3934214E975FCC781D19EA32', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 8, NULL, NULL, 2, NULL, 0, 'Add the mustard and taste for seasoning. When the sauce is well thickened, add the lobster meat, and heat through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1A876C0CEB8D64448D53621F10285EF9', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 9, NULL, NULL, 2, NULL, 0, 'Fill the lobster shells with this mixture, and sprinkle with the grated Parmesan cheese.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D84F3E4C5EDD4F4AA285ED04EEFA44E8', X'EFEAACE2DF340E4E8E344340A805A437', X'EFEAACE2DF340E4E8E344340A805A437', 10, NULL, NULL, 2, NULL, 0, 'Brush with melted butter, and brown in the oven at 375 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A188AB1ECF5DA7478E3C3549E26B9026', X'BE9777FB0CA4B045BD4CE29A4838832D', X'BE9777FB0CA4B045BD4CE29A4838832D', 0, NULL, NULL, 2, NULL, 0, 'Heat butter over medium heat; add sliced green onion, bell pepper, and celery. Saut, stirring until celery is tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FA73F632FA45B14EACA7373279544BCD', X'BE9777FB0CA4B045BD4CE29A4838832D', X'BE9777FB0CA4B045BD4CE29A4838832D', 1, NULL, NULL, 2, NULL, 0, 'Stir in flour until blended; add Creole seasoning.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6D2B322F9378CC4491E80828FC167839', X'BE9777FB0CA4B045BD4CE29A4838832D', X'BE9777FB0CA4B045BD4CE29A4838832D', 2, NULL, NULL, 2, NULL, 0, 'Stir in milk and continue cooking, stirring constantly, until thickened and bubbly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BB0E3D2448F6654D899E03B76B8DEB5F', X'BE9777FB0CA4B045BD4CE29A4838832D', X'BE9777FB0CA4B045BD4CE29A4838832D', 3, NULL, NULL, 2, NULL, 0, 'Stir in the Parmesan cheese until melted.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1B1E0E760611846913A539AA33D4951', X'BE9777FB0CA4B045BD4CE29A4838832D', X'BE9777FB0CA4B045BD4CE29A4838832D', 4, NULL, NULL, 2, NULL, 0, 'Add lobster and salt and pepper, to taste. Heat through; stir in parsley, if using.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A99BB471436314B97A946FB69395841', X'BE9777FB0CA4B045BD4CE29A4838832D', X'BE9777FB0CA4B045BD4CE29A4838832D', 5, NULL, NULL, 2, NULL, 0, 'Spoon the creamed lobster mixture into hot baked puff pastry shells or over toasted English muffins and garnish with a little more Parmesan cheese and parsley, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'62367228EFB4A74ABED89D7E90FF6F6D', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C94AE99922F99241AE11B1AA998DA9D8', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 1, NULL, NULL, 2, NULL, 0, 'Melt butter in a large skillet over medium heat, and blend in flour.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97DC7982AF9EB342BA5E777808A2D6C3', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 2, NULL, NULL, 2, NULL, 0, 'Stir the mixture for about 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'17F48A52C7D52549A241DEF4EE78708E', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 3, NULL, NULL, 2, NULL, 0, 'Gradually stir in the half-and-half, and stir until the sauce thickens over medium heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'735BD16192F0924B9FDA93CAB85A2556', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 4, NULL, NULL, 2, NULL, 0, 'Stir in a small amount of the hot cream sauce into the beaten egg yolks. This tempers the eggs so that they will not scramble.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EECC10B29209BC4FA7C753FF38CBFA2A', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 5, NULL, NULL, 2, NULL, 0, 'Whisk the now tempered egg yolk mixture into the hot pan sauce. Continue cooking, over medium heat, stirring constantly for about 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E13AB9096A1B754FA8F36D8DB557B057', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 6, NULL, NULL, 2, NULL, 0, 'Add the lobster, sherry, lemon juice, and salt. Continue to cook over medium heat until lobster is heated through, but do not boil or sauce will scald.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96AB7EE62BB4504C922D92C177A7688B', X'13BD5ECAC03CCD41BBCFADC582F52DEF', X'13BD5ECAC03CCD41BBCFADC582F52DEF', 7, NULL, NULL, 2, NULL, 0, 'Serve in hot puff pastry shells or over toast points.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4DF82FA286522343B177DE9E84CD7B2F', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 0, NULL, NULL, 2, NULL, 0, 'Put the clams, mussels, and cornmeal in a large bowl, and add enough cold salted water to cover. Let this stand for about 30 minutes to purge the clams and mussels of any sand.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4A96728F30E83D4E8C6ED9D0022C22A8', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 1, NULL, NULL, 2, NULL, 0, 'Lay a 12-inch piece of cheesecloth on a flat surface. Arrange the celery, parsley, thyme, and peppercorns in the center. Gather the ends of the cheesecloth together and tie it tightly with string (this is called a bouquet garni). Place the bouquet garni in a very large pot (about 16 to 20 gallons).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A60F10515A802E42991F91AF9EB75013', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 2, NULL, NULL, 2, NULL, 0, 'Fill the pot about halfway with water and bring it to a boil over medium-high heat. Add the lobsters head first, and cover the pot. Boil the lobsters until the shell is bright red, about 8 minutes. Using tongs, remove the lobsters to a large bowl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BA90B1F282D2A54BB3D0816D9AAAAE1C', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 3, NULL, NULL, 2, NULL, 0, 'Add the potatoes and onion to the pot. Ladle off excess water, leaving enough to cover the vegetables. Cover and cook for 10 minutes. Layer the sausages and corn over the vegetables. Cover and cook until the potatoes are just tender, about 10 minutes more.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A1D738AEE041D54486745C4EA1AD2DCD', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 4, NULL, NULL, 2, NULL, 0, 'Rinse the clams thoroughly. Arrange the clams over the corn and sausages. Place the lobsters on top of the clams. Cover the pot and boil until the clams have opened (about 10 minutes). Remove the pot from the heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE215A378091F540857AE096740ADFE3', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 5, NULL, NULL, 2, NULL, 0, 'Using tongs, remove the seafood, corn, potatoes, and chorizo to large platters or rimmed baking sheets. Mound the seafood in the center, discarding any unopened clams and mussels (these are not safe to eat). Surround the seafood with the potatoes, corn, and sausages. Sprinkle the vegetables with salt and pepper. Cover with foil to keep warm.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'470DB5C768B62340BC4DDBC100C46C49', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 6, NULL, NULL, 2, NULL, 0, 'Discard the bouquet garni from the pot and strain the cooking liquid through a fine sieve into a bowl. Add the butter, swirling to melt. Divide the broth among six small cups to serve with the seafood.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'262A2EDD095AAB4FB324DAE17FD46F4C', X'F732D42686CC664FA95E0203811481F1', X'F732D42686CC664FA95E0203811481F1', 7, NULL, NULL, 2, NULL, 0, 'Squeeze the lemons over the clambake and serve right away.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'08A120D6D8BD9B47AF68E6B4265E425D', X'4ADCDC287F4536469C6D45114343296C', X'4ADCDC287F4536469C6D45114343296C', 0, NULL, NULL, 2, NULL, 0, 'Heat butter in a medium saucepan over medium-low heat; add green onion, celery, and bell pepper. Saut until celery is tender, stirring frequently. Add flour and stir until blended. Cook, stirring, for 1 to 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A7C60FA9E1574747A0A95D12A50F4912', X'4ADCDC287F4536469C6D45114343296C', X'4ADCDC287F4536469C6D45114343296C', 1, NULL, NULL, 2, NULL, 0, 'Add the Creole seasoning and chicken broth; stir to blend. Stir in corn kernels. Cook, stirring, until thickened and bubbly. Stir in heavy cream, lobster, and tomato. Taste and add salt and pepper, to taste. Stir in sherry and heat through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0D27D4EAFEB2BF4A9BBF2EA7D9B1B384', X'4ADCDC287F4536469C6D45114343296C', X'4ADCDC287F4536469C6D45114343296C', 2, NULL, NULL, 2, NULL, 0, 'If desired, top each serving with a little crumbled cooked bacon or extra chunks of lobster.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46590608F29E814EA2660676479D5A6D', X'4ADCDC287F4536469C6D45114343296C', X'4ADCDC287F4536469C6D45114343296C', 3, NULL, NULL, 2, NULL, 0, 'Serve with crackers or hot buttered biscuits.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'496D493B5B173E499230595FA87B9F28', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'46172FF39D1C2D4293F893E4654BA91F', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 1, NULL, NULL, 2, NULL, 0, 'Heat the oil on medium heat in a large saucepan, and saute the onions until softened (about 5 minutes).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9F647CC82917D344AD3674007BA5C13E', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 2, NULL, NULL, 2, NULL, 0, 'Add the tomatoes, garlic, pepper, and ginger to the pot and stir. Allow to cook until the tomatoes reduce (about 15 minutes).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DC0B9D927CABCD40AE37CFD507C9A9D2', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 3, NULL, NULL, 2, NULL, 0, 'Add the okra to the pot, followed by the prawns, squid, and fish. Add the stock and stir. Reduce the heat and allow to simmer for 5 to 10 minutes or until the okra is just tender, then remove from heat to prevent overcooking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7993E1BF8A0A434E83887510BD8C795F', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 4, NULL, NULL, 2, NULL, 0, 'Preheat the oven to 400 F. Place the lobster tails flesh side-up on a baking sheet and brush generously with butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'42BA34915016B0429B5A2FF471F69247', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 5, NULL, NULL, 2, NULL, 0, 'Bake for 12-15 minutes (depending on size), then remove from the oven, and brush with a little extra butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'88824F7E22F2664EAA61C5D6C13F8081', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 6, NULL, NULL, 2, NULL, 0, 'Transfer the okra stew into a serving dish, garnish with the spring onions, then top with the lobster tails.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1BDB59F2DC5C1F45B2649C1A8C1BC926', X'DE90C0220B86F841857F4475ABAFE607', X'DE90C0220B86F841857F4475ABAFE607', 7, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A884BFC54F3E8546B52AAA3AAD807BF0', X'5666B0A374B2FF4BA84F7FBD0A224218', X'5666B0A374B2FF4BA84F7FBD0A224218', 0, NULL, NULL, 2, NULL, 0, 'In a small bowl, combine the lobster and sherry and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C0EEE1BBA45664BBAC57272868A2371', X'5666B0A374B2FF4BA84F7FBD0A224218', X'5666B0A374B2FF4BA84F7FBD0A224218', 1, NULL, NULL, 2, NULL, 0, 'In a medium saucepan over low heat, melt the butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AEEB6ED72CEB194385292DC6F0B4C290', X'5666B0A374B2FF4BA84F7FBD0A224218', X'5666B0A374B2FF4BA84F7FBD0A224218', 2, NULL, NULL, 2, NULL, 0, 'Blend in the flour and cook, stirring constantly, until it is smooth and bubbly.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5A4702AD5B42C44BB1E691D5FD008CB3', X'5666B0A374B2FF4BA84F7FBD0A224218', X'5666B0A374B2FF4BA84F7FBD0A224218', 3, NULL, NULL, 2, NULL, 0, 'Gradually add the milk, stirring constantly. Continue cooking and stirring until mixture is thickened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8C534D413369DD4596D822D88EBB9C12', X'5666B0A374B2FF4BA84F7FBD0A224218', X'5666B0A374B2FF4BA84F7FBD0A224218', 4, NULL, NULL, 2, NULL, 0, 'Stir in the steak sauce, seasoned salt, and pepper to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'009D677A07EB764EBA1B03A49912DB76', X'5666B0A374B2FF4BA84F7FBD0A224218', X'5666B0A374B2FF4BA84F7FBD0A224218', 5, NULL, NULL, 2, NULL, 0, 'Add the lobster and sherry to the cream sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E267345260758243AFC6069A5F4E740D', X'5666B0A374B2FF4BA84F7FBD0A224218', X'5666B0A374B2FF4BA84F7FBD0A224218', 6, NULL, NULL, 2, NULL, 0, 'Cover and simmer the soup for 5 to 10 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50468A48F88D9941A125C11781201EE4', X'C18566BEB4ADAC419BE7059CC494ACAF', X'C18566BEB4ADAC419BE7059CC494ACAF', 0, NULL, NULL, 2, NULL, 0, 'Split each tail lengthwise and rinse with cold water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'50C6F5BADDAD374A9533AE399F39D2D4', X'C18566BEB4ADAC419BE7059CC494ACAF', X'C18566BEB4ADAC419BE7059CC494ACAF', 1, NULL, NULL, 2, NULL, 0, 'Place, meat side up, on a foil-lined baking pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C197C94A6843F246A13571678973243F', X'C18566BEB4ADAC419BE7059CC494ACAF', X'C18566BEB4ADAC419BE7059CC494ACAF', 2, NULL, NULL, 2, NULL, 0, 'Lightly brush each tail with melted butter and sprinkle lightly with salt and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D077500CD19CEC4BB698B09459BFCA07', X'C18566BEB4ADAC419BE7059CC494ACAF', X'C18566BEB4ADAC419BE7059CC494ACAF', 3, NULL, NULL, 2, NULL, 0, 'Broil about 4 inches from heat for about 4 to 6 minutes, or until tails curl.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'75BC9CA66BE8CF40B38BF566462BABFA', X'C18566BEB4ADAC419BE7059CC494ACAF', X'C18566BEB4ADAC419BE7059CC494ACAF', 4, NULL, NULL, 2, NULL, 0, 'Remove from the oven and let cool. Cover and chill for an hour or two.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'171F26F1C904274FAF20E886D7B174E9', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AA9518F9EC492B4A8228B1C3CB9BD193', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 1, NULL, NULL, 2, NULL, 0, 'Remove the meat from steamed lobsters and cut it into cubes, or cube the already deshelled lobster meat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6A486094C558B549AED7DBFB940F7298', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 2, NULL, NULL, 2, NULL, 0, 'Pan-fry the meat in a generous amount of butter (4 to 6 tablespoons) until it''s lightly browned. Be careful not to burn the butter, or use ghee.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E615D23620B974687152A12C7D7A881', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 3, NULL, NULL, 2, NULL, 0, 'In a separate pan, heat up the milk, being careful not to boil it.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C9C5D0470206D42A54E9254D6EA19F4', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 4, NULL, NULL, 2, NULL, 0, 'Add the lobster meat to the hot milk and cook slowly for 5 to 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1649DBC9CECD8D4DA2D0A395E6CA8C02', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 5, NULL, NULL, 2, NULL, 0, 'Stir in the heavy cream and bring it nearly to a boil but not over. Lower the heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'DF3EB183EF3B804E81616C024A27E3A6', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 6, NULL, NULL, 2, NULL, 0, 'Add salt and pepper to taste and a little paprika for color. If desired, stir in a dash of sherry for extra flavor.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D21ADDAABF5B5B4297010A00D008D175', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 7, NULL, NULL, 2, NULL, 0, 'Garnish with sliced green onions or chopped parsley.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B190D1CD0A2E9D4F98B18E0AEA15AB71', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', X'C6F9C4AD418E8E45AE9D39FC0ADABE45', 8, NULL, NULL, 2, NULL, 0, 'Serve hot and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CA41C39E99ECD24DA908231CB85FA6A2', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 0, NULL, NULL, 2, NULL, 0, 'Remove the hard top shell from the lobster bodies and discard. Remove all the inner meat from the body (there is a fair amount, actually!) as well as the green `tomalley` (liver) as well as any of the bright red `coral` (roe) if there is any.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5893EE8774880A4696BCD82491BB9892', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 1, NULL, NULL, 2, NULL, 0, 'Put all the meat, tomalley, and roe in a bowl, and set aside in the fridge.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A70247DA07514B4DBFAAE205FED9BAE0', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 2, NULL, NULL, 2, NULL, 0, 'Returning to the remains of the lobsters, locate and remove any feathery lungs you find; they are shaped like spear points and are light and grayish. Also, discard the nasty-looking fluid-filled sac at the front of the lobster''s head.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96189DBF802D7A4CBB4A17AAAAC4F0A3', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 3, NULL, NULL, 2, NULL, 0, 'Now that the lobster body is cleaned, break up the shellsincluding broken pieces of legs, knuckles, and claws as well if you have theminto little pieces and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2842E99953D5C44B2D995DC17E17D85', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 4, NULL, NULL, 2, NULL, 0, 'Put the olive oil in a stockpot and heat over medium-high heat for a minute or two. Add the dried chilies and the garlic; turn the heat down to medium. Cook until the garlic browns. Be careful not to burn it.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'27E234DD6E82894BBB21DB67A4B8B6DE', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 5, NULL, NULL, 2, NULL, 0, 'Add the shells, peppercorns, bay leaves, and whole cloves, and mix well to combine. Turn the heat back up to medium-high and cook for 10 minutes, stirring frequently.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0E1E31D9C42CDE4D96D85E20F0CADC9D', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 6, NULL, NULL, 2, NULL, 0, 'Set a large pot of salty water on a burner to a boil. This will be your pasta water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9193B4D60434614A94083F01AC5A12EF', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 7, NULL, NULL, 2, NULL, 0, 'Now add both the cognac and the ouzo to the lobster sauce and mix well for 5 to 10 seconds. Add the tomato puree, tarragon, and parsley, then cover and bring to a simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'22AC7478D1B5E24B903EAB0D5E0E46D7', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 8, NULL, NULL, 2, NULL, 0, 'Let this simmer for 20 minutes, then crush everything in the pot using a potato masher. Do this again at 45 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C5197C52BEC5124AA31FB603FC0C063F', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 9, NULL, NULL, 2, NULL, 0, 'If the sauce gets too thick, add a little water. You want it to be thicker than water, but thinner than what you think of as a sauce; we will thicken it later. Taste for salt and add a little if it needs it.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8F3433EEE139B148ACBCF7DFCD1E3963', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 10, NULL, NULL, 2, NULL, 0, 'At the 55-minute mark, turn off the heat. Pour the sauce through a colander into another large pot. Mash the contents one more time. If you happen to have a food mill or duck press, use it. Discard the shells and other bits in the colander.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC17F36C3A3CE140AE36F7361E9EE242', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 11, NULL, NULL, 2, NULL, 0, 'Now would be a good time to start cooking your pasta.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'ED5C066640FA7B4ABE504A0052A8FC4A', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 12, NULL, NULL, 2, NULL, 0, 'Pour half the strained sauce into a food processor. Add the reserved lobster meat, tomalley, and coral, then buzz the mixture until smooth. If you have a lot of leftover meat, reserve some for garnishing the pasta.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0F034DCB5D796742888B18B76105953A', X'9B588938D3288F4B9B830E0CE0A13C66', X'9B588938D3288F4B9B830E0CE0A13C66', 13, NULL, NULL, 2, NULL, 0, 'Pour the contents of the food processor back into the pot with the rest of the strained sauce. If it is still too thin for your liking, add a little tomato paste. Heat gently for 5 minutesit is very important to not let the sauce boil!and serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FAD9233BA365B54F8AC7FFBC164EC707', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients. Preheat the oven to 400 F.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'290A6620DD5248428F76E8FD9DE0B91C', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 1, NULL, NULL, 2, NULL, 0, 'To instantly kill the lobster, place the lobster flat on a cutting board and plunge the tip of a sharp knife straight down just behind the lobster''s eyes to cut the spinal cord. (Alternatively, you can freeze the lobster for 30 to 60 minutes.)');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7409703236EE54458F0923C8590B8B65', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 2, NULL, NULL, 2, NULL, 0, 'Flip the lobster over on its back. Using a sharp knife or kitchen shears, cut the lobster''s body cavity open all the way down to the end of the tail but not all the way through.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4F6A4BD8C0D9D7409C9480C04E73E2F3', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 3, NULL, NULL, 2, NULL, 0, 'Remove and reserve the tomalley (yellowish-green colored liver).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'767152AB2F67674EBF50672DABEAE8E8', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 4, NULL, NULL, 2, NULL, 0, 'Remove the stomach and intestines, being careful not to rupture them, and discard.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'CABF62623005D84691231298E1E6F523', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 5, NULL, NULL, 2, NULL, 0, 'Rinse the lobster cavity and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'72770DC858776E48B6B454C5CF7D6714', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 6, NULL, NULL, 2, NULL, 0, 'In a mixing bowl, combine the cracker crumbs, melted butter, celery, onion, 1 tablespoon of the Parmesan cheese, garlic powder, lemon pepper, and salt to taste.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'63C1204E92E5A64FA2BA4BE4E5F66655', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 7, NULL, NULL, 2, NULL, 0, 'Add the tomalley from the lobster and mix gently.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8FB63BC1678C2042808CEF22591BD944', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 8, NULL, NULL, 2, NULL, 0, 'Pack the cracker mixture into the lobster''s body cavity.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7C3F0D451260D84F8E250C97B348FFDC', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 9, NULL, NULL, 2, NULL, 0, 'Sprinkle with the remaining tablespoon of Parmesan cheese and dust lightly with paprika.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BC2D3502763C9E4F9E57141C4BA1B03F', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 10, NULL, NULL, 2, NULL, 0, 'Bake the stuffed lobster in an uncovered 2-inch deep baking pan for 30 minutes. If needed, place under the broiler and broil for another 2 minutes to brown the stuffing.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'265C3D0C00FFF740AB579EACF7146159', X'F79F547E7BCA4C45ABE8F682FC758798', X'F79F547E7BCA4C45ABE8F682FC758798', 11, NULL, NULL, 2, NULL, 0, 'Serve the stuffed lobster immediately with melted butter on the side, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'85E8EF2EA5D4474EBAA4AD4A48925B83', X'C587E7510415FC4E9FD88533E78D2728', X'C587E7510415FC4E9FD88533E78D2728', 0, NULL, NULL, 2, NULL, 0, 'Fill a large stockpot or kettle about 3/4 full with water and add 2 teaspoons of salt for each quart of water. Bring the water to a boil over high heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C0C0C0422C93B440AC4EB851939835D5', X'C587E7510415FC4E9FD88533E78D2728', X'C587E7510415FC4E9FD88533E78D2728', 1, NULL, NULL, 2, NULL, 0, 'Lower the lobsters, head first, into the boiling water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'97FB6E999731474596FB7F237487494B', X'C587E7510415FC4E9FD88533E78D2728', X'C587E7510415FC4E9FD88533E78D2728', 2, NULL, NULL, 2, NULL, 0, 'Cover the pot and bring the water back to a boil. Continue boiling the lobsters for 15 to 20 minutes. They will be bright red when they''re done.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2FC776E9767B8440B79BE88541301483', X'C587E7510415FC4E9FD88533E78D2728', X'C587E7510415FC4E9FD88533E78D2728', 3, NULL, NULL, 2, NULL, 0, 'Rinse the lobsters under cold running water to cool enough to handle. Crack open the shells and remove the meat; chop coarsely.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'FE6563A421D6274D8D437BD236648CB4', X'E380C93C3BFAA64AA4E74A6D5ED891D2', X'E380C93C3BFAA64AA4E74A6D5ED891D2', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8D3ADE145F6FF148976B1450C9AAB778', X'E380C93C3BFAA64AA4E74A6D5ED891D2', X'E380C93C3BFAA64AA4E74A6D5ED891D2', 1, NULL, NULL, 2, NULL, 0, 'In a large pot, add all soup base ingredients except zucchini and lemon juice and bring to a boil. Boil over medium-high heat for 15 minutes, then add zucchini.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'BE43915DBDA5794CB1B0491F6554060F', X'E380C93C3BFAA64AA4E74A6D5ED891D2', X'E380C93C3BFAA64AA4E74A6D5ED891D2', 2, NULL, NULL, 2, NULL, 0, 'Cook for an additional 5 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7E5995D8BA61564EB4DE1F4DDC490402', X'E380C93C3BFAA64AA4E74A6D5ED891D2', X'E380C93C3BFAA64AA4E74A6D5ED891D2', 3, NULL, NULL, 2, NULL, 0, 'In order to prevent pieces of spine and bone from getting into the soup, wrap whole fish loosely in a piece of tulle and secure.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EBE1F10D2939CF42879412E3C01CCB95', X'E380C93C3BFAA64AA4E74A6D5ED891D2', X'E380C93C3BFAA64AA4E74A6D5ED891D2', 4, NULL, NULL, 2, NULL, 0, 'Add fish, lobster (if using), and cuttlefish (if using), to the soup base. When boil resumes, cook for 20-25 minutes, uncovered.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05452CAF4420154A906818E9F9F1E0BF', X'E380C93C3BFAA64AA4E74A6D5ED891D2', X'E380C93C3BFAA64AA4E74A6D5ED891D2', 5, NULL, NULL, 2, NULL, 0, '5 minutes before cooking time is up, add lemon juice.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8CA42DCB6183D34899196FCA61E7FFA9', X'E380C93C3BFAA64AA4E74A6D5ED891D2', X'E380C93C3BFAA64AA4E74A6D5ED891D2', 6, NULL, NULL, 2, NULL, 0, 'Traditionally, the whole fish, other seafood, and vegetables are placed on a large platter, and the soup is served separately in a tureen or individual soup bowls.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'1985D91071D7F54A952780D739464DC4', X'640FC77C4C52FA43BDB58C7208C72844', X'640FC77C4C52FA43BDB58C7208C72844', 0, NULL, NULL, 2, NULL, 0, 'Boil some water in a large pot for the pasta. Cook the pasta according to packet instructions and have it ready around the same time as the clams or just a bit before.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D0182BA390970244A5934FA1401C488F', X'640FC77C4C52FA43BDB58C7208C72844', X'640FC77C4C52FA43BDB58C7208C72844', 1, NULL, NULL, 2, NULL, 0, 'Soak the clams in a bowl of cold water for about 10 minutes. Drain and rinse under cold running water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2C2020E7A729304AACEF56B522FCDE44', X'640FC77C4C52FA43BDB58C7208C72844', X'640FC77C4C52FA43BDB58C7208C72844', 2, NULL, NULL, 2, NULL, 0, 'Heat the oil and butter over medium heat in a large, heavy-based pot. Add the onion and fry for 2 to 3 minutes, stirring occasionally, until translucent. Add the garlic, chili flakes and salt and stir for about 20 seconds.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D100D4C27D427E4794B7212B6D01FCF8', X'640FC77C4C52FA43BDB58C7208C72844', X'640FC77C4C52FA43BDB58C7208C72844', 3, NULL, NULL, 2, NULL, 0, 'Add the chopped tomatoes and fry for about 7 minutes until their juice starts to evaporate.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE73BB1922ADAB47B0BA6596D9A59BDB', X'640FC77C4C52FA43BDB58C7208C72844', X'640FC77C4C52FA43BDB58C7208C72844', 4, NULL, NULL, 2, NULL, 0, 'Turn the heat up to medium-high and add the clams to the pot. Cover with a lid. After about 2 to 3 minutes add the wine to the pot. Cover again and shake the pot from side to side.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E4CF15E04A492845A39388BE141153DC', X'640FC77C4C52FA43BDB58C7208C72844', X'640FC77C4C52FA43BDB58C7208C72844', 5, NULL, NULL, 2, NULL, 0, 'Cook the clams for a further 5 minutes or until they open. Add the parsley and stir with a wooden spoon. Cover and shake pot again.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2718D6B1A7824B40836948611DBDDA5C', X'640FC77C4C52FA43BDB58C7208C72844', X'640FC77C4C52FA43BDB58C7208C72844', 6, NULL, NULL, 2, NULL, 0, 'Drain the pasta and then pour the clam mixture on top. Stir well and serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'9083C6247DD36A41BC5238DE433D7EBB', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', 0, NULL, NULL, 2, NULL, 0, 'Remove stems from mushrooms; mince stems and set aside. Place mushroom caps on a lightly oiled rack in broiling pan rounded side down. Set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F2057D03E143184A9DDD729A3BA31B2D', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', 1, NULL, NULL, 2, NULL, 0, 'Drain clams, reserving liquid.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'172266BC30AA2F488C8E43376921DFA8', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', 2, NULL, NULL, 2, NULL, 0, 'In a large skillet over medium heat, melt butter.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4D108BC8AF111D459F7C2BF2A5403B05', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', 3, NULL, NULL, 2, NULL, 0, 'Brush some butter on mushroom caps.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'111BF87FC7E3654EB6E4B58F9E1051BE', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', 4, NULL, NULL, 2, NULL, 0, 'In remaining butter in skillet, simmer the minced stems, garlic, and reserved liquid from clams until mushroom stems are tender, about 4 to 6 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB0F5C5D7CE71543900B6B0208A510B6', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', 5, NULL, NULL, 2, NULL, 0, 'Stir in clams and remaining ingredients. Spoon clam mixture into mushroom caps.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A36E331FEAA6840BCB43976010D9B0A', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', X'C7A9FAE5AB590C42BFA97E1D524CB4CA', 6, NULL, NULL, 2, NULL, 0, 'Move the pan to the oven and broil mushrooms for 7 to 9 minutes, or until mushrooms are tender and the stuffing is browned.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2795EAB1BD4FE749A42DADCD23F71BAD', X'A38006849434E3489A3716086217DB78', X'A38006849434E3489A3716086217DB78', 0, NULL, NULL, 2, NULL, 0, 'Bring a large pot of water to a boil. Meanwhile, scrub the clams clean, if they need it, and set them aside in a colander.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'93230CE9A7181F459906E530428A029D', X'A38006849434E3489A3716086217DB78', X'A38006849434E3489A3716086217DB78', 1, NULL, NULL, 2, NULL, 0, 'Peel and mince the garlic. In a large pan or pot with a tight-fitting lid over medium-high heat, add the olive oil. Once the olive oil is hot, add the garlic. It should sizzle immediately. Cook, stirring, until fragrant, about 30 seconds. Add the pepper flakes, if using. Add the white wine, bring to a boil, reduce heat to maintain a simmer, and cook, stirring when you think of it, until the liquid is reduced by half, about 10 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6C4C88BD64C3C240AF45D1CDF80626EA', X'A38006849434E3489A3716086217DB78', X'A38006849434E3489A3716086217DB78', 2, NULL, NULL, 2, NULL, 0, 'Turn the heat back up to medium-high, add the clams, cover, and cook until the clams are open and cooked through, 5 to 10 minutes, depending on the size of the clams. When they''re done, add the parsley and stir to combine.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EC510B5A3BB8CE43B313CFFE998502F3', X'A38006849434E3489A3716086217DB78', X'A38006849434E3489A3716086217DB78', 3, NULL, NULL, 2, NULL, 0, 'While the clams cook, add salt to the boiling water (enough so the water tastes a bit salty) and the pasta. Cook until tender to the bite (timing will vary depending on brand. Drain.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0A3F3841A3E0D344925E1A6F9EF2CDFE', X'A38006849434E3489A3716086217DB78', X'A38006849434E3489A3716086217DB78', 4, NULL, NULL, 2, NULL, 0, 'There are 2 ways to serve the pasta. First, the easy one: Divide the pasta between 4 to 6 serving bowls and top with even amounts of clams and sauce. Second, which requires a bit more effort but I prefer it: Use tongs or a slotted spoon to remove the clams from the pot (just put them in a bowl), add the pasta to the sauce in the pot and stir to toss to coat the pasta thoroughly, divide the pasta between serving bowls, top with the clams and garnish with Parmesan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4E83CAAB8481B14987C670E4972B4C69', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 0, NULL, NULL, 2, NULL, 0, 'To prepare this recipe you will need a 17-inch (44cm) paella pan. Due to the size of the pan, we recommend that you cook the fideua on a kettle BBQ or a gas paella burner for even heat distribution. Assemble all the ingredients and kitchen equipment on a table near the BBQ, so that you can stay in the area and monitor the cooking.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2A686617E895FF41BA0228E3EE619874', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 1, NULL, NULL, 2, NULL, 0, 'Be sure to light the coals about 20 minutes before you will begin cooking. This allows time for the coals to heat and be covered in white ash.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C06BE6CD91B9E343A950871E69269618', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 2, NULL, NULL, 2, NULL, 0, 'Rinse shrimp, mussels, and clams under cold running water and set aside.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0755D50A876EF346AADFF62C57F729C5', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 3, NULL, NULL, 2, NULL, 0, 'Pour fish or shrimp stock into a medium saucepan and warm for use later.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'125C00DF94929C4CB7328D65786A8A4B', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 4, NULL, NULL, 2, NULL, 0, 'Rinse fish under cold water and cut into 2-inch cubes. Clean the squid and trim the tentacles. Slice squid into rings.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4C90D32B3B1AB14FBE76E45C7AC33CA0', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 5, NULL, NULL, 2, NULL, 0, 'De-seed and slice red pepper into thin strips. Cut each tomato into 8 pieces. Peel garlic cloves and place in a mortar and mash with the pestle.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'399C7A6844FA364484FBB59A1F2B7238', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 6, NULL, NULL, 2, NULL, 0, 'Place the paella pan on the BBQ grate or paella burner and add enough olive oil to coat the bottom and allow the pan to heat up. When hot enough, saut the shrimp and squid in the olive oil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3E088C94998BE44EAC401DF17C6372E2', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 7, NULL, NULL, 2, NULL, 0, 'Add olive oil as needed to prevent sticking. When the shrimp is pink, it is cooked. Remove shrimp and squid from the pan and set aside. Leave the oil in the pan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2916F663C65D9A4982479E2E2110A4B1', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 8, NULL, NULL, 2, NULL, 0, 'Put the diced tomatoes, red pepper, and mashed garlic in the olive oil with the two red pepper pods and saut for two minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'96BA14773BA70841904547D154FBD9DB', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 9, NULL, NULL, 2, NULL, 0, 'Crush saffron threads with finders. Add the fish stock and saffron threads to the pan and stir. Bring broth to a boil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6B480741CA9ACE4DBA8F439BA6208332', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 10, NULL, NULL, 2, NULL, 0, 'When liquid starts to boil, add the vermicelli and fish and stir. Spread the clams, mussels, and squid around the pan and arrange the shrimp on top. Cook for 15 minutes, or until the pasta is al dente.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6766E89123D31448A907F6C4F14167B1', X'0B84B477CB73B24CBC6EA88A319F106B', X'0B84B477CB73B24CBC6EA88A319F106B', 11, NULL, NULL, 2, NULL, 0, 'Remove the fideua from the barbecue or burner. Cover with aluminum foil and let it `rest` for 5 minutes. Uncover and serve with lemons.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'F131E9E8B2255E40AE28D2D9BF83F8F6', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'70D5511B67580F43833CF7EA7A17FE20', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 1, NULL, NULL, 2, NULL, 0, 'In a soup pot, stir-fry the beef, garlic, and gochugaru in the sesame oil for about five minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A957E232A3AA074D8068D50106A585BE', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 2, NULL, NULL, 2, NULL, 0, 'Add anchovy or beef stock or water and soy sauce to the pot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'461B165079E5E241ABA7CC455BA5C648', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 3, NULL, NULL, 2, NULL, 0, 'Bring to a simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'3F5046B749729C4B9B723FF5BDD74996', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 4, NULL, NULL, 2, NULL, 0, 'Add soft tofu and return to simmer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'B80F19521165804C87B9151B6781BF6E', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 5, NULL, NULL, 2, NULL, 0, 'Add clams and simmer until the clams are cooked, about 10 minutes, until they shrink, or until the shells open (if using unshucked).');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'562688FD3765C64CA5E954B2AF1961D5', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 6, NULL, NULL, 2, NULL, 0, 'Add scallions and take off heat.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'7F0575878748B749BF051697F64D99D3', X'7FE342D941F59545A9B04F4545B13A10', X'7FE342D941F59545A9B04F4545B13A10', 7, NULL, NULL, 2, NULL, 0, 'Serve and enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EE060DCC5FD3174BA539D0207282341B', X'F48298832E404E4F88A2C32F70495C97', X'F48298832E404E4F88A2C32F70495C97', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'762881CBE1B08A418236F32FE19D5CC9', X'F48298832E404E4F88A2C32F70495C97', X'F48298832E404E4F88A2C32F70495C97', 1, NULL, NULL, 2, NULL, 0, 'Add the clams to a bowl of salted, cold water (add about 1/4 cup of salt to a 1/2 gallon of water). Allow to sit either on the counter or the refrigerator for 30 minutes. The clams will filter out any sand from their shells while sitting in the water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'16A26932DD689F459B008959C457E026', X'F48298832E404E4F88A2C32F70495C97', X'F48298832E404E4F88A2C32F70495C97', 2, NULL, NULL, 2, NULL, 0, 'Add the butter to a large pot, one thats large enough to fit the clams as well. Heat the butter on medium heat until the butter melts. Add the diced onion, salt, and half of the garlic. Saute until the onions are soft and translucent.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EAFB32DE4ED4674BA2EA0C41461A9E0A', X'F48298832E404E4F88A2C32F70495C97', X'F48298832E404E4F88A2C32F70495C97', 3, NULL, NULL, 2, NULL, 0, 'Add the chicken bouillon to the onions and stir to combine. Add in 1/2 cup of water, the white wine, and lemon juice. Add the clams and remaining garlic to the broth.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E6A030D8A7C04C48800E51ED84B77480', X'F48298832E404E4F88A2C32F70495C97', X'F48298832E404E4F88A2C32F70495C97', 4, NULL, NULL, 2, NULL, 0, 'Cover the pot with a lid. Cook on medium high heat for about 5 minutes, or until the clams have just opened. Discard any clams that have not opened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'AD9527B46C0F0B4E9316DBF85EDC23A8', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'48F8D4252641EC49A5CBA91CE83AF3CE', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 1, NULL, NULL, 2, NULL, 0, 'Place shrimp, squid, mussels, and small clams in 4 different bowls of cold water with a little coarse salt added to each and soak for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'C471D16E2118034EAC829A6A66281ADC', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 2, NULL, NULL, 2, NULL, 0, 'Drain the shrimp and rinse under cold running water, then shell them. Place the shrimp in a small bowl and put the shells in a small saucepan.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D67CCA6B90B356458E4D89700EEB7F06', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 3, NULL, NULL, 2, NULL, 0, 'Pour the 2 cups cold water over shells, place the saucepan over medium heat, and simmer for 30 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'030420A94A75694BB4F03A151B5A2F6D', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 4, NULL, NULL, 2, NULL, 0, 'Strain the broth (it should yield about 1 cup), discard the shells, and set aside until needed.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'760A50A202E3CE45800ACF6E5394894C', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 5, NULL, NULL, 2, NULL, 0, 'Meanwhile, place a small saucepan with 1/4 cup of the olive oil over medium heat. When the oil is warm, add the garlic and saute for 2 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'386577155C81C44F8A3DDFD813DD4AB4', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 6, NULL, NULL, 2, NULL, 0, 'Add the tomatoes and white wine. Simmer for 30 minutes, seasoning to taste with salt and pepper.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'4B5BE7889DCE9F43BBE14B94E6FD9CB3', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 7, NULL, NULL, 2, NULL, 0, 'Pass the contents of the saucepan through a food mill, using the disc with the smallest holes, and into a flameproof enamel casserole dish.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D1322A5F24331142961B87DFF179856E', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 8, NULL, NULL, 2, NULL, 0, 'Set the casserole dish over low heat to reduce the liquid for 15 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5FB47BB8D2378B4BB26EA0A473987724', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 9, NULL, NULL, 2, NULL, 0, 'Meanwhile, drain the squid and rinse under cold running water.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'6CAF4B976BF6FF42A22ADC062C6F9234', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 10, NULL, NULL, 2, NULL, 0, 'Place another medium-sized saucepan with 1/4 cup of oil over low heat. When the oil is warm, add the squid and saute for 5 minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'89C0759B4EA09643916AAF42E4CC3C75', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 11, NULL, NULL, 2, NULL, 0, 'Season with salt and pepper and add shrimp broth a little at a time and simmer until the squid is tender, for 10 to 20 minutes, depending on the size of the squid.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'EB64C6A352068D4C99C39F4BA75C6290', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 12, NULL, NULL, 2, NULL, 0, 'Bring a large pot of cold water to a boil and add salt to taste. Once the squid is cooked, add the pasta to the boiling water and cook for 8 to 11 minutes depending on the brand: that is, 1 minute less than for normal al dente.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'94680934EE6F5A4F95257753176D40AD', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 13, NULL, NULL, 2, NULL, 0, 'As the pasta cooks, drain the mussels and clams, scrub them under cold water, then place in a large skillet with the remaining 1/4 cup of olive oil. Place over high heat, cover, and cook for 10 minutes, by which time they should be opened. Discard any that remain unopened.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'0ED2BCABE4575F48BBFD0509A5A3B40D', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 14, NULL, NULL, 2, NULL, 0, 'Add the shrimp to the pan with the squid and cook for 3 minutes. When the pasta is ready, drain it and transfer it to the skillet with the mussels and clams. Add the tomato sauce with the shrimp and squid, then mix very well and let cook for 1 minute more to let the pasta to absorb some of the sauce.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'625E2861D6BDAD47BDBFB80B97919E59', X'1683A4C31498B4418B47AB7F6DBD99E6', X'1683A4C31498B4418B47AB7F6DBD99E6', 15, NULL, NULL, 2, NULL, 0, 'Transfer to a warmed serving platter, sprinkle with the whole parsley leaves, and serve.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5C246BE9EC696E42B9203AE187FA45F4', X'33F8343546378E40B90AF65F55097AFC', X'33F8343546378E40B90AF65F55097AFC', 0, NULL, NULL, 2, NULL, 0, 'Fry 4 slices chopped bacon in a medium pot until soft and golden, about 4 minutes. Add 1 small chopped onion and 2 cloves chopped garlic and continue cooking until the bacon is nicely browned and the onion is tender, about 4 minutes longer.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'59E3804A54110243B2133CF99264D455', X'33F8343546378E40B90AF65F55097AFC', X'33F8343546378E40B90AF65F55097AFC', 1, NULL, NULL, 2, NULL, 0, 'Stir in 3 pounds well-scrubbed Manila clams and add 1 cup beer or water or chicken stock. Cover the pot and steam the clams for 3 to 5 minutes, until they open. Discard any clams that do not open.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'04FDF9D71E397C4EACFFE7295D09CADE', X'33F8343546378E40B90AF65F55097AFC', X'33F8343546378E40B90AF65F55097AFC', 2, NULL, NULL, 2, NULL, 0, 'Stir gently to mix with the bacon, garlic, and onion, and serve immediately.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8002EC6AEE0F924F9B150A06BAAF08F1', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 0, NULL, NULL, 2, NULL, 0, 'Gather the ingredients.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2F2B69D090EEE541BAD9DCFAB437E18C', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 1, NULL, NULL, 2, NULL, 0, 'Turn the Instant Pot to the saut function. Add 1/2 tablespoon olive oil to the pot. Transfer the diced bacon to the pot and cook for 3 to 4 minutes, stirring occasionally.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'2E0752E33E5F8A4DADE07087E74EC0F1', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 2, NULL, NULL, 2, NULL, 0, 'Carefully transfer the cooked and crisped bacon to a paper towel lined plate, leaving the rendered bacon fat in the pot.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'05DB99BA844B0744ADBE0B65C4DEF795', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 3, NULL, NULL, 2, NULL, 0, 'Add the remaining 1/2 tablespoon of olive oil to the pot and add the diced onions, celery, and garlic. Saut for about 4 minutes, or until softened, stirring occasionally.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87CED18EF54E1E45A1DED1F09B5ED520', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 4, NULL, NULL, 2, NULL, 0, 'Add the cubed potatoes, clams, clam juice, water, salt, thyme, and pepper to the pot. Secure the lid on the pot and set to manual. Cook on high pressure for 5 minutes. When the cooking is complete, manually release the pressure by moving the valve to venting.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A47A45186668D84ABECB19AEF7218B4A', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 5, NULL, NULL, 2, NULL, 0, 'Remove the lid and once again set to the saut function. (Use a low setting, if possible on your appliance). Add the milk and cream to the pot. Heat, stirring occasionally, to a very gentle boil.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E9C83AC2D238674CB8F1A0343A201C5F', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 6, NULL, NULL, 2, NULL, 0, 'In a small bowl, add the flour. Ladle about 3/4 cup of the liquid from the soup and transfer it to the bowl. Using a fork, stir the flour and liquid together until you get a thick paste. Scrape the thick paste into the pot and stir to incorporate with the rest of the soup. Let the soup simmer for about 4 minutes more to thicken.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'5151713FD5AE3446B7AFCA380DF4EDD5', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 7, NULL, NULL, 2, NULL, 0, 'Ladle the soup into serving bowls. Top with the crumbled bacon and a few twists of fresh black pepper. Serve with oyster crackers, if desired.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'87243ED358A19E4A97533F5620493C39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', X'9EDB4B67ED5CA84C8F5F198B324FFE39', 8, NULL, NULL, 2, NULL, 0, 'Enjoy!');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'D72321C9B5DDC3489B3D60BC47EC69EF', X'90FA15826216B746BE22DB004E6BE753', X'90FA15826216B746BE22DB004E6BE753', 0, NULL, NULL, 2, NULL, 0, 'In a medium bowl with an electric mixer, combine the cream cheese, mayonnaise, and garlic; beat until smooth. Fold in the drained minced clams along with the remaining seasonings. Transfer the dip mixture to a serving bowl or container.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'238AE5A94BDBDB4E9AD8D2F19F7D5044', X'90FA15826216B746BE22DB004E6BE753', X'90FA15826216B746BE22DB004E6BE753', 1, NULL, NULL, 2, NULL, 0, 'Cover the container and refrigerate the clam dip for about 2 hours, or until it is thoroughly chilled.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'E2CCDB06F0D95243951BC679336E5FC6', X'90FA15826216B746BE22DB004E6BE753', X'90FA15826216B746BE22DB004E6BE753', 2, NULL, NULL, 2, NULL, 0, 'Serve the chilled dip with chips, crackers, or fresh sliced vegetables.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'19699A5EA0ED84498DE8AE18E45DB22C', X'AC6D9935A5333C40B183B060BF5B3383', X'AC6D9935A5333C40B183B060BF5B3383', 0, NULL, NULL, 2, NULL, 0, 'In? a large saucepan, over medium-high heat cook the bacon until almost crisp. Reduce the heat to low, add 1 tablespoon of the butter, the onions, and saut until soft, about 5 minutes. Be careful not to brown the onions. Add the flour and cook for 2 more minutes.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'8774BFA4DD670F44992CF40C477DD55C', X'AC6D9935A5333C40B183B060BF5B3383', X'AC6D9935A5333C40B183B060BF5B3383', 1, NULL, NULL, 2, NULL, 0, 'Whisk in water and reserved clam juice, and once combined add the potatoes. Season with the salt and pepper, increase heat to medium, bring to a simmer, and cook uncovered until potatoes are tender.');
INSERT INTO `VWR INTERNAL Seafoodrecipes recipeInstructions` (row_id, parent_row_id, out_parent_row_id, source_index, template_url, template_content_value, status, status_message, segment, step) VALUES (X'A90E46268496A1409B98FD6CAC135D96', X'AC6D9935A5333C40B183B060BF5B3383', X'AC6D9935A5333C40B183B060BF5B3383', 2, NULL, NULL, 2, NULL, 0, 'Reduce heat to very low, pour in half-and-half, add the rest of the butter, and clams, and cook until heated through. Do not boil! Turn off heat, adjust seasoning, and serve hot.');
